<div id=\"_mcePaste\">}</div>\r\n<div id=\"_mcePaste\">?&gt;</div>\r\n&lt;?php\r\n/*\r\nEmailAddressValidator Class        http://code.google.com/p/php-email-address-validation/\r\nReleased under New BSD license        http://www.opensource.org/licenses/bsd-license.php                Sample Code        ----------------        $validator = new EmailAddressValidator;        if ($validator-&gt;check_email_address(\'test@example.org\')) {            // Email address is technically valid        }\r\n*/\r\nclass EmailAddressValidator {\r\n/**         * Check email address validity         * @param   strEmailAddress     Email address to be checked         * @return  True if email is valid, false if not         */        public function check_email_address($strEmailAddress) {                        // If magic quotes is \"on\", email addresses with quote marks will            // fail validation because of added escape characters. Uncommenting            // the next three lines will allow for this issue.            //if (get_magic_quotes_gpc()) {             //    $strEmailAddress = stripslashes($strEmailAddress);             //}\r\n// Control characters are not allowed            if (preg_match(\'/[\\x00-\\x1F\\x7F-\\xFF]/\', $strEmailAddress)) {                return false;            }\r\n// Check email length - min 3 (a@a), max 256            if (!$this-&gt;check_text_length($strEmailAddress, 3, 256)) {                return false;            }\r\n// Split it into sections using last instance of \"@\"            $intAtSymbol = strrpos($strEmailAddress, \'@\');            if ($intAtSymbol === false) {                // No \"@\" symbol in email.                return false;            }            $arrEmailAddress[0] = substr($strEmailAddress, 0, $intAtSymbol);            $arrEmailAddress[1] = substr($strEmailAddress, $intAtSymbol + 1);\r\n// Count the \"@\" symbols. Only one is allowed, except where             // contained in quote marks in the local part. Quickest way to            // check this is to remove anything in quotes. We also remove            // characters escaped with backslash, and the backslash            // character.            $arrTempAddress[0] = preg_replace(\'/\\./\'                                             ,\'\'                                             ,$arrEmailAddress[0]);            $arrTempAddress[0] = preg_replace(\'/\"[^\"]+\"/\'                                             ,\'\'                                             ,$arrTempAddress[0]);            $arrTempAddress[1] = $arrEmailAddress[1];            $strTempAddress = $arrTempAddress[0] . $arrTempAddress[1];            // Then check - should be no \"@\" symbols.            if (strrpos($strTempAddress, \'@\') !== false) {                // \"@\" symbol found                return false;            }\r\n// Check local portion            if (!$this-&gt;check_local_portion($arrEmailAddress[0])) {                return false;            }\r\n// Check domain portion            if (!$this-&gt;check_domain_portion($arrEmailAddress[1])) {                return false;            }\r\n// If we\'re still here, all checks above passed. Email is valid.            return true;\r\n}\r\n/**         * Checks email section before \"@\" symbol for validity         * @param   strLocalPortion     Text to be checked         * @return  True if local portion is valid, false if not         */        protected function check_local_portion($strLocalPortion) {            // Local portion can only be from 1 to 64 characters, inclusive.            // Please note that servers are encouraged to accept longer local            // parts than 64 characters.            if (!$this-&gt;check_text_length($strLocalPortion, 1, 64)) {                return false;            }            // Local portion must be:            // 1) a dot-atom (strings separated by periods)            // 2) a quoted string            // 3) an obsolete format string (combination of the above)            $arrLocalPortion = explode(\'.\', $strLocalPortion);            for ($i = 0, $max = sizeof($arrLocalPortion); $i &lt; $max; $i++) {                 if (!preg_match(\'.^(\'                                .    \'([A-Za-z0-9!#$%&amp;\\\'*+/=?^_`{|}~-]\'                                 .    \'[A-Za-z0-9!#$%&amp;\\\'*+/=?^_`{|}~-]{0,63})\'                                .\'|\'                                .    \'(\"[^\\\\\\\"]{0,62}\")\'                                .\')$.\'                                ,$arrLocalPortion[$i])) {                    return false;                }            }            return true;        }\r\n/**         * Checks email section after \"@\" symbol for validity         * @param   strDomainPortion     Text to be checked         * @return  True if domain portion is valid, false if not         */        protected function check_domain_portion($strDomainPortion) {            // Total domain can only be from 1 to 255 characters, inclusive            if (!$this-&gt;check_text_length($strDomainPortion, 1, 255)) {                return false;            }            // Check if domain is IP, possibly enclosed in square brackets.            if (preg_match(\'/^(25[0-5]|2[0-4][0-9]|1[0-9][0-9]|[1-9]?[0-9])\'               .\'(\\.(25[0-5]|2[0-4][0-9]|1[0-9][0-9]|[1-9]?[0-9])){3}$/\'               ,$strDomainPortion) ||                 preg_match(\'/^\\[(25[0-5]|2[0-4][0-9]|1[0-9][0-9]|[1-9]?[0-9])\'               .\'(\\.(25[0-5]|2[0-4][0-9]|1[0-9][0-9]|[1-9]?[0-9])){3}\\]$/\'               ,$strDomainPortion)) {                return true;            } else {                $arrDomainPortion = explode(\'.\', $strDomainPortion);                if (sizeof($arrDomainPortion) &lt; 2) {                    return false; // Not enough parts to domain                }                for ($i = 0, $max = sizeof($arrDomainPortion); $i &lt; $max; $i++) {                    // Each portion must be between 1 and 63 characters, inclusive                    if (!$this-&gt;check_text_length($arrDomainPortion[$i], 1, 63)) {                        return false;                    }                    if (!preg_match(\'/^(([A-Za-z0-9][A-Za-z0-9-]{0,61}[A-Za-z0-9])|\'                       .\'([A-Za-z0-9]+))$/\', $arrDomainPortion[$i])) {                        return false;                    }                    if ($i == $max - 1) { // TLD cannot be only numbers                        if (strlen(preg_replace(\'/[0-9]/\', \'\', $arrDomainPortion[$i])) &lt;= 0) {                            return false;                        }                    }                }            }            return true;        }\r\n/**         * Check given text length is between defined bounds         * @param   strText     Text to be checked         * @param   intMinimum  Minimum acceptable length         * @param   intMaximum  Maximum acceptable length         * @return  True if string is within bounds (inclusive), false if not         */        protected function check_text_length($strText, $intMinimum, $intMaximum) {            // Minimum and maximum are both inclusive            $intTextLength = strlen($strText);            if (($intTextLength &lt; $intMinimum) || ($intTextLength &gt; $intMaximum)) {                return false;            } else {                return true;            }        }\r\n}\r\n?&gt;','php-email-address-validation','','publish','open','open','','php-email-address-validation','','','2010-09-19 06:57:00','2010-09-19 14:57:00','',0,'http://www.wordpress.cn/?p=148',0,'post','',0),(149,1,'2010-09-19 06:56:10','2010-09-19 14:56:10','<div id=\"_mcePaste\">&lt;?php</div>\n<div id=\"_mcePaste\">/*</div>\n<div id=\"_mcePaste\">EmailAddressValidator Class</div>\n<div id=\"_mcePaste\">http://code.google.com/p/php-email-address-validation/</div>\n<div id=\"_mcePaste\">Released under New BSD license</div>\n<div id=\"_mcePaste\">http://www.opensource.org/licenses/bsd-license.php</div>\n<div id=\"_mcePaste\">Sample Code</div>\n<di