    break ; \r\n        } \r\n     if ($ip>$this->StartIp) \r\n        $RangB= $RecNo; \r\n     else \r\n        $RangE= $RecNo; \r\n     } \r\n     $this->getStartIp ( $RangB ) ; \r\n     $this->getEndIp ( ) ; \r\n  \r\n     if ( ( $this->StartIp <= $ip ) && ( $this->EndIp >= $ip ) ){ \r\n        $nRet=0 ; \r\n        $this->getCountry ( ) ; \r\n        //这样不太好..............所以.......... \r\n        $this->Local=str_replace(\"（我们一定要解放台湾！！！）\", \"\", $this->Local); \r\n     } \r\n     else{ \r\n        $nRet=3 ; \r\n        $this->Country=\'未知\' ; \r\n        $this->Local=\'\' ; \r\n     } \r\n     fclose ( $this->fp ); \r\n$this->Country=preg_replace(\"/(CZ88.NET)|(纯真网络)/\",\"\",$this->Country); \r\n$this->Local=preg_replace(\"/(CZ88.NET)|(纯真网络)/\",\"\",$this->Local); \r\n//////////////看看 $nRet在上面的值是什么0和3，于是将下面的行注释掉 \r\n        return $nRet ; \r\n  \r\n     //return \"$this->Country $this->Local\";#如此直接返回位置和国家便可以了 \r\n    } \r\n  \r\n    function IpToInt($Ip) { \r\n     $array=explode(\'.\',$Ip); \r\n     $Int=($array[0] * 256*256*256) + ($array[1]*256*256) + ($array[2]*256) + $array[3]; \r\n     return $Int; \r\n    } \r\n} \r\n function GetIP(){//获取IP \r\n  return $_SERVER[REMOTE_ADDR]?$_SERVER[REMOTE_ADDR]:$GLOBALS[HTTP_SERVER_VARS][REMOTE_ADDR]; \r\n }\r\n\r\n/*\r\nfunction get_real_ip()\r\n{\r\n        $ip=false;\r\n        if(!empty($_SERVER[\"HTTP_CLIENT_IP\"]))\r\n        {\r\n                $ip = $_SERVER[\"HTTP_CLIENT_IP\"];\r\n        }\r\n        if (!empty($_SERVER[\'HTTP_X_FORWARDED_FOR\']))\r\n        {\r\n                $ips = explode (\", \", $_SERVER[\'HTTP_X_FORWARDED_FOR\']);\r\n                if ($ip)\r\n                {\r\n                        array_unshift($ips, $ip); $ip = FALSE;\r\n                }\r\n                for ($i = 0; $i < count($ips); $i++)\r\n                {\r\n                        if (!eregi (\"^(10|172.16|192.168).\", $ips[$i]))\r\n                        {\r\n                                $ip = $ips[$i];\r\n                                break;\r\n                        }\r\n                }\r\n        }\r\n        return ($ip ? $ip : $_SERVER[\'REMOTE_ADDR\']);\r\n}\r\n*/\r\n\r\nfunction is_ip($str) {\r\n    $ip = explode(\".\", $str);\r\n    if (count($ip)<4 || count($ip)>4) return 0;\r\n    foreach($ip as $ip_addr) {\r\n        if ( !is_numeric($ip_addr) ) return 0;\r\n        if ( $ip_addr<0 || $ip_addr>255 ) return 0;\r\n    }        \r\n    return 1;\r\n}\r\n\r\n// 调用方法\r\n\r\n/*\r\n\r\n $ip = get_real_ip();\r\n  if(is_ip($ip)){\r\n   $QQWry=new QQWry;\r\n   \r\n   $ifErr=$QQWry->QQWry($ip); \r\n   if( strpos($QQWry->Country,\'长沙\') ) \r\n   {\r\n    echo \'长沙\';\r\n   }\r\n   else\r\n   {\r\n    echo $ip;\r\n   }\r\n    \r\n  }\r\n  else\r\n  {\r\n   echo $ip;\r\n  }\r\n */\r\n?>','php读取qqwry纯真IP库','','inherit','open','open','','171-revision-3','','','2010-09-20 18:49:48','2010-09-21 02:49:48','',171,'http://www.wordpress.cn/?p=175',0,'revision','',0),(177,1,'2010-09-20 22:38:10','2010-09-21 06:38:10','Web层\r\n主体架构可以基于 Struts 1.X/2.X，当然有很多更好的控制层框架供选择，以快速敏捷为准则吧。 \r\n抽象出核心库封装 控制器和中间层 的操作。 \r\n在大规模集群环境下，session复制会引起严重的性能问题。考虑用 集群缓存 + cookie验证 代替session实现权限控制吧。 \r\nCache层\r\n配置 Memcache 组成集群缓存 \r\n对 Memcache 客户端进行封装 \r\nMemcached 节点组成池，调用示意：opList (BizName, 策略 ...) \r\n中间层\r\n“中间层”可以理解为基于应用和数据之间的层次。它被设计用来为Web应用提供：数据缓存 和 对应用透明的数据访问——即应用不需要考虑数据表拆分的问题。以服务的方式提供对存储层的高性能调用以及分布式计算。可供选择的框架：\r\nICE \r\nHadoop \r\n直接基于Memcache开发（减少复杂度，推荐） \r\n存储\r\n推荐MySQL，理由：免费，经过实践检验，有大量成熟的案例、解决方案、技术支持。\r\n小规模：一个 data table 维护存储服务器阵列，内容 -> mount …… \r\n大规模：Master-Slave模式+MySQL Proxy，实现数据库读写分离。在中间层的包装下，可做如下扩展，以支持更大规模的数据存取： \r\n数据库/表水平拆分，例 User -> User33% + User33% + User34% \r\n数据库/表垂直拆分，例 User -> UserBaseInfo + UserAddrInfo \r\n也可考虑使用 LongStore (龙存) 解决方案，由龙存管理存储阵列……\r\n部署\r\n划分子域名，每个子域名一个Web应用包，互不干扰 \r\n静态资源（css, js, image ...）使用专门的静态服务器 \r\n负载均衡\r\n小规模：DNS轮询。\r\n大规模：F5， 2*X 台F5服务器，F5是L4/L7层交换机，每台至少可处理200万连接（与服务器内存有关）。\r\nNgnix是L7层交换，LVS负载均衡也是一种方案\r\nWeb中间件选择\r\nTomcat - 最高400并发 \r\nApache - 最高2000并发 \r\nNgnix - 优于Apache \r\n采用方案：Ngnix + Resin ，理由：\r\nResin提供更为快速的servlet引擎 - 选择Resin。 \r\ngzip问题 - Resin在单独处理gzip时存在内存溢出的隐患，因此要加一层 Ngnix。 \r\nNgnix 能减少单独使用Resin时的内存占用 - Resin建立1000个连接使用1000个线程；加Ngnix后，透过其“异步连接”、“建立长连接”机制使Resin内存压力大大减小。 \r\nNgnix 针对Linux系统有性能优化措施 - 0 Copy, send file ... \r\n因此采用：1 Ngnix + 1 Resin，一对一。\r\n静态服务器采用：Squid + Apache, why? because Squid has cache ability ...\r\n新变化 - Nginx从0.7.48版本开始，支持了类似Squid的缓存功能。这个缓存是把URL及相关组合当作Key，用md5编码哈希后保存在硬盘上，所以它可以支持任意URL链接，同时也支持 404/301/302 这样的非200状态码。虽然目前官方的Nginx Web缓存服务只能为指定URL或状态码设置过期时间，不支持类似Squid的PURGE指令，手动清除指定缓存页面，但是，通过一个第三方的Nginx模块，可以清除指定URL的缓存。\r\nNginx的Web缓存服务主要由proxy_cache相关指令集和fastcgi_cache相关指令集构成，前者用于反向代理时，对后端内容源服务器进行缓存，后者主要用于对FastCGI的动态程序进行缓存。两者的功能基本上一样。\r\n最新的Nginx 0.8.31版本，proxy_cache和fastcgi_cache已经比较完善，加上第三方的ngx_cache_purge模块（用于清除指定URL的缓存），已经可以完全取代Squid。有的网站已经在生产环境使用了 Nginx 的 proxy_cache 缓存功能超过两个月，十分稳定，速度不逊于 Squid。\r\n在功能上，Nginx已经具备Squid所拥有的Web缓存加速功能、清除指定URL缓存的功能。而在性能上，Nginx对多核CPU的利用，胜过Squid不少。另外，在反向代理、负载均衡、健康检查、后端服务器故障转移、Rewrite重写、易用性上，Nginx也比Squid强大得多。这使得一台Nginx可以同时作为\"负载均衡服务器\"与\"Web缓存服务器\"来使用。以下是配置片段供参考：\r\nview plaincopy to clipboardprint?\r\nhttp    \r\n{   \r\n  ...   \r\n  client_body_buffer_size  512k;   \r\n  proxy_connect_timeout    5;   \r\n  proxy_read_timeout       60;   \r\n  proxy_send_timeout       5;   \r\n  proxy_buffer_size        16k;   \r\n  proxy_buffers            4 64k;   \r\n  proxy_busy_buffers_size 128k;   \r\n  proxy_temp_file_write_size 128k;   \r\n  ...  \r\n  #注：proxy_temp_path和proxy_cache_path指定的路径必须在同一分区   \r\n  proxy_temp_path   /data0/proxy_temp_dir;  \r\n  #设置Web缓存区名称为cache_one，内存缓存空间大小为200MB，1天清理一次缓存，硬盘缓存空间大小为30GB。   \r\n  proxy_cache_path  /data0/proxy_cache_dir  levels=1:2   keys_zone=cache_one:200m inactive=1d max_size=30g;   \r\n}   \r\nserver   \r\n{   \r\n  ...   \r\n  location /   \r\n  {  \r\n    #如果后端的服务器返回502、504、执行超时等错误，自动将请求转发到upstream负载均衡池中的另一台服务器，实现故障转移。   \r\n    proxy_next_upstream http_502 http_504 error timeout invalid_header;   \r\n    proxy_cache cache_one;  \r\n    #对不同的HTTP状态码设置不同的缓存时间   \r\n    proxy_cache_valid  200 304 12h;   \r\n    proxy_cache_valid  301 302 1h;  \r\n    #以域名、URI、参数组合成Web缓存的Key值，Nginx根据Key值哈希，存储缓存内容到二级缓存目录内  \r\n    proxy_cache_key $host$uri$is_args$args;   \r\n    proxy_set_header Host  $host;   \r\n    proxy_set_header X-Forwarded-For  $remote_addr;   \r\n    proxy_pass http://backend_server;   \r\n    expires      1d;   \r\n  }  \r\n  #用于清除缓存，假设一个URL为http://192.168.1.44/test.txt，通过访问http://192.168.4.44/purge/test.txt就可以清除该URL的缓存。   \r\n  location ~ /purge(/.*)   \r\n  {  \r\n    #设置只允许指定的IP或IP段才可以清除URL缓存。   \r\n    allow            127.0.0.1;   \r\n    allow            192.168.0.0/16;   \r\n    deny            all;   \r\n    proxy_cache_purge    cache_one   $host$1$is_args$args;   \r\n  }      \r\n  #扩展名以.php、.jsp、.cgi结尾的动态应用程序不缓存。   \r\n  location ~ .*\\.(php|jsp|cgi)?$   \r\n  {   \r\n    proxy_set_header Host  $host;   \r\n    proxy_set_header X-Forwarded-For  $remote_addr;   \r\n    proxy_pass http://backend_server;   \r\n  }   \r\n}  \r\nhttp \r\n{\r\n  ...\r\n  client_body_buffer_size  512k;\r\n  proxy_connect_timeout    5;\r\n  proxy_read_timeout       60;\r\n  proxy_send_timeout       5;\r\n  proxy_buffer_size        16k;\r\n  proxy_buffers            4 64k;\r\n  proxy_busy_buffers_size 128k;\r\n  proxy_temp_file_write_size 128k;\r\n  ...\r\n  #注：proxy_temp_path和proxy_cache_path指定的路径必须在同一分区\r\n  proxy_temp_path   /data0/proxy_temp_dir;\r\n  #设置Web缓存区名称为cache_one，内存缓存空间大小为200MB，1天清理一次缓存，硬盘缓存空间大小为30GB。\r\n  proxy_cache_path  /data0/proxy_cache_dir  levels=1:2   keys_zone=cache_one:200m inactive=1d max_size=30g;\r\n}\r\nserver\r\n{\r\n  ...\r\n  location /\r\n  {\r\n    #如果后端的服务器返回502、504、执行超时等错误，自动将请求转发到upstream负载均衡池中的另一台服务器，实现故障转移。\r\n    proxy_next_upstream http_502 http_504 error timeout invalid_header;\r\n    proxy_cache cache_one;\r\n    #对不同的HTTP状态码设置不同的缓存时间\r\n    proxy_cache_valid  200 304 12h;\r\n    proxy_cache_valid  301 302 1h;\r\n    #以域名、URI、参数组合成Web缓存的Key值，Nginx根据Key值哈希，存储缓存内容到二级缓存目录内\r\n    proxy_cache_key $host$uri$is_args$args;\r\n    proxy_set_header Host  $host;\r\n    proxy_set_header X-Forwarded-For  $remote_addr;\r\n    proxy_pass http://backend_server;\r\n    expires      1d;\r\n  }\r\n  #用于清除缓存，假设一个URL为http://192.168.1.44/test.txt，通过访问http://192.168.4.44/purge/test.txt就可以清除该URL的缓存。\r\n  location ~ /purge(/.*)\r\n  {\r\n    #设置只允许指定的IP或IP段才可以清除URL缓存。\r\n    allow            127.0.0.1;\r\n    allow            192.168.0.0/16;\r\n    deny            all;\r\n    proxy_cache_purge    cache_one   $host$1$is_args$args;\r\n  }    \r\n  #扩展名以.php、.jsp、.cgi结尾的动态应用程序不缓存。\r\n  location ~ .*\\.(php|jsp|cgi)?$\r\n  {\r\n    proxy_set_header Host  $host;\r\n    proxy_set_header X-Forwarded-For  $remote_addr;\r\n    proxy_pass http://backend_server;\r\n  }\r\n}\r\n同时，对于影响页面展现的静态资源，例如：css, js 等可以放在具有优质带宽的IDC（IDC=互联网数据中心，优质/高速的带宽也比较贵，正所谓一份价钱一分货）；其他的静态资源，如图片等可以放在价格相对低廉的IDC中，以域名区分两种静态资源，节省每一分钱。\r\n网络拓扑图\r\n         / Ngnix - 1:1 - Resin \r\nF5 --\r\n         \\ Squid - 1:n - Apache\r\n监控统计平台\r\n业务统计 - 用户访问统计 \r\n软件性能 - 应用系统监控，例如：请求响应时间…… \r\n硬件/网络性能 - Ganglia监控 \r\n其它要点\r\nIE浏览器对同一域名（包括子域名）只能建立2个连接，连接多了只能排队…… \r\n双F5架构，两台职能划分不同，镜像，心跳接管…… \r\nRaid存储阵列…… \r\nLinux操作系统及其优化……\r\n ','基于Java技术的大型网站架构方案','','publish','open','open','','%e5%9f%ba%e4%ba%8ejava%e6%8a%80%e6%9c%af%e7%9a%84%e5%a4%a7%e5%9e%8b%e7%bd%91%e7%ab%99%e6%9e%b6%e6%9e%84%e6%96%b9%e6%a1%88','','','2010-09-20 22:38:10','2010-09-21 06:38:10','',0,'http://www.wordpress.cn/?p=177',0,'post','',0),(176,1,'2010-09-20 18:51:45','2010-09-21 02:51:45','qqwry纯真IP库,支持在线更新20100325\r\n\r\n使用方法\r\n&lt;?php   \r\n\r\ndefine(\'__QQWRY__\' , dirname(__FILE__).\"/qqwry/QQWry.Dat\");  \r\nclass QQWry{ \r\n    var $StartIP=0; \r\n    var $EndIP=0; \r\n    var $Country=\'\'; \r\n    var $Local=\'\'; \r\n  \r\n    var $CountryFlag=0; \r\n    var $fp; \r\n  \r\n    var $FirstStartIp=0; \r\n    var $LastStartIp=0; \r\n    var $EndIpOff=0 ; \r\n  \r\n    function getStartIp($RecNo){ \r\n     $offset=$this->FirstStartIp+$RecNo * 7 ; \r\n     @fseek($this->fp,$offset,SEEK_SET) ; \r\n     $buf=fread($this->fp ,7) ; \r\n     $this->EndIpOff=ord($buf[4]) + (ord($buf[5])*256) + (ord($buf[6])* 256*256); \r\n     $this->StartIp=ord($buf[0]) + (ord($buf[1])*256) + (ord($buf[2])*256*256) + (ord($buf[3])*256*256*256); \r\n     return $this->StartIp; \r\n    } \r\n  \r\n    function getEndIp(){ \r\n     @fseek ( $this->fp , $this->EndIpOff , SEEK_SET ) ; \r\n     $buf=fread ( $this->fp , 5 ) ; \r\n     $this->EndIp=ord($buf[0]) + (ord($buf[1])*256) + (ord($buf[2])*256*256) + (ord($buf[3])*256*256*256); \r\n     $this->CountryFlag=ord ( $buf[4] ) ; \r\n     return $this->EndIp ; \r\n    } \r\n  \r\n    function getCountry(){ \r\n     switch ( $this->CountryFlag ) { \r\n        case 1: \r\n        case 2: \r\n         $this->Country=$this->getFlagStr ( $this->EndIpOff+4) ; \r\n         //echo sprintf(\'EndIpOffset=(%x)\',$this->EndIpOff ); \r\n         $this->Local=( 1 == $this->CountryFlag )? \'\' : $this->getFlagStr ( $this->EndIpOff+8); \r\n         break ; \r\n        default : \r\n         $this->Country=$this->getFlagStr ($this->EndIpOff+4) ; \r\n         $this->Local=$this->getFlagStr ( ftell ( $this->fp )) ; \r\n     } \r\n    } \r\n  \r\n  \r\n    function getFlagStr ($offset){ \r\n     $flag=0 ; \r\n     while(1){ \r\n        @fseek($this->fp ,$offset,SEEK_SET) ; \r\n        $flag=ord(fgetc($this->fp ) ) ; \r\n        if ( $flag == 1 || $flag == 2 ) { \r\n         $buf=fread ($this->fp , 3 ) ; \r\n         if ($flag==2){ \r\n            $this->CountryFlag=2; \r\n            $this->EndIpOff=$offset - 4 ; \r\n         } \r\n         $offset=ord($buf[0]) + (ord($buf[1])*256) + (ord($buf[2])* 256*256); \r\n        } \r\n        else{ \r\n         break ; \r\n        } \r\n  \r\n     } \r\n     if($offset<12) \r\n        return \'\'; \r\n     @fseek($this->fp , $offset , SEEK_SET ) ; \r\n  \r\n     return $this->getStr(); \r\n    } \r\n  \r\n    function getStr ( ) \r\n    { \r\n     $str=\'\' ; \r\n     while ( 1 ) { \r\n        $c=fgetc ( $this->fp ) ; \r\n        //echo \"$cn\" ; \r\n  \r\n        if(ord($c[0])== 0 ) \r\n         break ; \r\n        $str.= $c ; \r\n     } \r\n     //echo \"$str n\"; \r\n     return $str ; \r\n    } \r\n  \r\n  \r\n    function qqwry ($dotip=\'\') { \r\n        if(!$dotip)return; \r\n            if(ereg(\"^(127)\",$dotip)){$this->Country=\'本地网络\';return;} \r\n            elseif(ereg(\"^(192)\",$dotip)) {$this->Country=\'局域网\';return;} \r\n  \r\n     $nRet; \r\n     $ip=$this->IpToInt ( $dotip ); \r\n     $this->fp= fopen(__QQWRY__, \"rb\"); \r\n     if ($this->fp == NULL) { \r\n         $szLocal= \"OpenFileError\"; \r\n        return 1; \r\n  \r\n     } \r\n     @fseek ( $this->fp , 0 , SEEK_SET ) ; \r\n     $buf=fread ( $this->fp , 8 ) ; \r\n     $this->FirstStartIp=ord($buf[0]) + (ord($buf[1])*256) + (ord($buf[2])*256*256) + (ord($buf[3])*256*256*256); \r\n     $this->LastStartIp=ord($buf[4]) + (ord($buf[5])*256) + (ord($buf[6])*256*256) + (ord($buf[7])*256*256*256); \r\n  \r\n     $RecordCount= floor( ( $this->LastStartIp - $this->FirstStartIp ) / 7); \r\n     if ($RecordCount <= 1){ \r\n        $this->Country=\"FileDataError\"; \r\n        fclose($this->fp) ; \r\n        return 2 ; \r\n     } \r\n  \r\n     $RangB= 0; \r\n     $RangE= $RecordCount; \r\n     // Match ... \r\n     while ($RangB < $RangE-1) \r\n     { \r\n     $RecNo= floor(($RangB + $RangE) / 2); \r\n     $this->getStartIp ( $RecNo ) ; \r\n  \r\n        if ( $ip == $this->StartIp ) \r\n        { \r\n         $RangB=$RecNo ; \r\n         break ; \r\n        } \r\n     if ($ip>$this->StartIp) \r\n        $RangB= $RecNo; \r\n     else \r\n        $RangE= $RecNo; \r\n     } \r\n     $this->getStartIp ( $RangB ) ; \r\n     $this->getEndIp ( ) ; \r\n  \r\n     if ( ( $this->StartIp <= $ip ) && ( $this->EndIp >= $ip ) ){ \r\n        $nRet=0 ; \r\n        $this->getCountry ( ) ; \r\n        //这样不太好..............所以.......... \r\n        $this->Local=str_replace(\"（我们一定要解放台湾！！！）\", \"\", $this->Local); \r\n     } \r\n     else{ \r\n        $nRet=3 ; \r\n        $this->Country=\'未知\' ; \r\n        $this->Local=\'\' ; \r\n     } \r\n     fclose ( $this->fp ); \r\n$this->Country=preg_replace(\"/(CZ88.NET)|(纯真网络)/\",\"\",$this->Country); \r\n$this->Local=preg_replace(\"/(CZ88.NET)|(纯真网络)/\",\"\",$this->Local); \r\n//////////////看看 $nRet在上面的值是什么0和3，于是将下面的行注释掉 \r\n        return $nRet ; \r\n  \r\n     //return \"$this->Country $this->Local\";#如此直接返回位置和国家便可以了 \r\n    } \r\n  \r\n    function IpToInt($Ip) { \r\n     $array=explode(\'.\',$Ip); \r\n     $Int=($array[0] * 256*256*256) + ($array[1]*256*256) + ($array[2]*256) + $array[3]; \r\n     return $Int; \r\n    } \r\n} \r\n function GetIP(){//获取IP \r\n  return $_SERVER[REMOTE_ADDR]?$_SERVER[REMOTE_ADDR]:$GLOBALS[HTTP_SERVER_VARS][REMOTE_ADDR]; \r\n }\r\n\r\n/*\r\nfunction get_real_ip()\r\n{\r\n        $ip=false;\r\n        if(!empty($_SERVER[\"HTTP_CLIENT_IP\"]))\r\n        {\r\n                $ip = $_SERVER[\"HTTP_CLIENT_IP\"];\r\n        }\r\n        if (!empty($_SERVER[\'HTTP_X_FORWARDED_FOR\']))\r\n        {\r\n                $ips = explode (\", \", $_SERVER[\'HTTP_X_FORWARDED_FOR\']);\r\n                if ($ip)\r\n                {\r\n                        array_unshift($ips, $ip); $ip = FALSE;\r\n                }\r\n                for ($i = 0; $i < count($ips); $i++)\r\n                {\r\n                        if (!eregi (\"^(10|172.16|192.168).\", $ips[$i]))\r\n                        {\r\n                                $ip = $ips[$i];\r\n                                break;\r\n                        }\r\n                }\r\n        }\r\n        return ($ip ? $ip : $_SERVER[\'REMOTE_ADDR\']);\r\n}\r\n*/\r\n\r\nfunction is_ip($str) {\r\n    $ip = explode(\".\", $str);\r\n    if (count($ip)<4 || count($ip)>4) return 0;\r\n    foreach($ip as $ip_addr) {\r\n        if ( !is_numeric($ip_addr) ) return 0;\r\n        if ( $ip_addr<0 || $ip_addr>255 ) return 0;\r\n    }        \r\n    return 1;\r\n}\r\n\r\n// 调用方法\r\n\r\n/*\r\n\r\n $ip = get_real_ip();\r\n  if(is_ip($ip)){\r\n   $QQWry=new QQWry;\r\n   \r\n   $ifErr=$QQWry->QQWry($ip); \r\n   if( strpos($QQWry->Country,\'长沙\') ) \r\n   {\r\n    echo \'长沙\';\r\n   }\r\n   else\r\n   {\r\n    echo $ip;\r\n   }\r\n    \r\n  }\r\n  else\r\n  {\r\n   echo $ip;\r\n  }\r\n */\r\n?>','php读取qqwry纯真IP库','','inherit','open','open','','171-revision-4','','','2010-09-20 18:51:45','2010-09-21 02:51:45','',171,'http://www.wordpress.cn/?p=176',0,'revision','',0),(178,1,'2010-09-20 22:38:03','2010-09-21 06:38:03','Web层\n主体架构可以基于 Struts 1.X/2.X，当然有很多更好的控制层框架供选择，以快速敏捷为准则吧。 \n抽象出核心库封装 控制器和中间层 的操作。 \n在大规模集群环境下，session复制会引起严重的性能问题。考虑用 集群缓存 + cookie验证 代替session实现权限控制吧。 \nCache层\n配置 Memcache 组成集群缓存 \n对 Memcache 客户端进行封装 \nMemcached 节点组成池，调用示意：opList (BizName, 策略 ...) \n中间层\n“中间层”可以理解为基于应用和数据之间的层次。它被设计用来为Web应用提供：数据缓存 和 对应用透明的数据访问——即应用不需要考虑数据表拆分的问题。以服务的方式提供对存储层的高性能调用以及分布式计算。可供选择的框架：\nICE \nHadoop \n直接基于Memcache开发（减少复杂度，推荐） \n存储\n推荐MySQL，理由：免费，经过实践检验，有大量成熟的案例、解决方案、技术支持。\n小规模：一个 data table 维护存储服务器阵列，内容 -> mount …… \n大规模：Master-Slave模式+MySQL Proxy，实现数据库读写分离。在中间层的包装下，可做如下扩展，以支持更大规模的数据存取： \n数据库/表水平拆分，例 User -> User33% + User33% + User34% \n数据库/表垂直拆分，例 User -> UserBaseInfo + UserAddrInfo \n也可考虑使用 LongStore (龙存) 解决方案，由龙存管理存储阵列……\n部署\n划分子域名，每个子域名一个Web应用包，互不干扰 \n静态资源（css, js, image ...）使用专门的静态服务器 \n负载均衡\n小规模：DNS轮询。\n大规模：F5， 2*X 台F5服务器，F5是L4/L7层交换机，每台至少可处理200万连接（与服务器内存有关）。\nNgnix是L7层交换，LVS负载均衡也是一种方案\nWeb中间件选择\nTomcat - 最高400并发 \nApache - 最高2000并发 \nNgnix - 优于Apache \n采用方案：Ngnix + Resin ，理由：\nResin提供更为快速的servlet引擎 - 选择Resin。 \ngzip问题 - Resin在单独处理gzip时存在内存溢出的隐患，因此要加一层 Ngnix。 \nNgnix 能减少单独使用Resin时的内存占用 - Resin建立1000个连接使用1000个线程；加Ngnix后，透过其“异步连接”、“建立长连接”机制使Resin内存压力大大减小。 \nNgnix 针对Linux系统有性能优化措施 - 0 Copy, send file ... \n因此采用：1 Ngnix + 1 Resin，一对一。\n静态服务器采用：Squid + Apache, why? because Squid has cache ability ...\n新变化 - Nginx从0.7.48版本开始，支持了类似Squid的缓存功能。这个缓存是把URL及相关组合当作Key，用md5编码哈希后保存在硬盘上，所以它可以支持任意URL链接，同时也支持 404/301/302 这样的非200状态码。虽然目前官方的Nginx Web缓存服务只能为指定URL或状态码设置过期时间，不支持类似Squid的PURGE指令，手动清除指定缓存页面，但是，通过一个第三方的Nginx模块，可以清除指定URL的缓存。\nNginx的Web缓存服务主要由proxy_cache相关指令集和fastcgi_cache相关指令集构成，前者用于反向代理时，对后端内容源服务器进行缓存，后者主要用于对FastCGI的动态程序进行缓存。两者的功能基本上一样。\n最新的Nginx 0.8.31版本，proxy_cache和fastcgi_cache已经比较完善，加上第三方的ngx_cache_purge模块（用于清除指定URL的缓存），已经可以完全取代Squid。有的网站已经在生产环境使用了 Nginx 的 proxy_cache 缓存功能超过两个月，十分稳定，速度不逊于 Squid。\n在功能上，Nginx已经具备Squid所拥有的Web缓存加速功能、清除指定URL缓存的功能。而在性能上，Nginx对多核CPU的利用，胜过Squid不少。另外，在反向代理、负载均衡、健康检查、后端服务器故障转移、Rewrite重写、易用性上，Nginx也比Squid强大得多。这使得一台Nginx可以同时作为\"负载均衡服务器\"与\"Web缓存服务器\"来使用。以下是配置片段供参考：\nview plaincopy to clipboardprint?\nhttp    \n{   \n  ...   \n  client_body_buffer_size  512k;   \n  proxy_connect_timeout    5;   \n  proxy_read_timeout       60;   \n  proxy_send_timeout       5;   \n  proxy_buffer_size        16k;   \n  proxy_buffers            4 64k;   \n  proxy_busy_buffers_size 128k;   \n  proxy_temp_file_write_size 128k;   \n  ...  \n  #注：proxy_temp_path和proxy_cache_path指定的路径必须在同一分区   \n  proxy_temp_path   /data0/proxy_temp_dir;  \n  #设置Web缓存区名称为cache_one，内存缓存空间大小为200MB，1天清理一次缓存，硬盘缓存空间大小为30GB。   \n  proxy_cache_path  /data0/proxy_cache_dir  levels=1:2   keys_zone=cache_one:200m inactive=1d max_size=30g;   \n}   \nserver   \n{   \n  ...   \n  location /   \n  {  \n    #如果后端的服务器返回502、504、执行超时等错误，自动将请求转发到upstream负载均衡池中的另一台服务器，实现故障转移。   \n    proxy_next_upstream http_502 http_504 error timeout invalid_header;   \n    proxy_cache cache_one;  \n    #对不同的HTTP状态码设置不同的缓存时间   \n    proxy_cache_valid  200 304 12h;   \n    proxy_cache_valid  301 302 1h;  \n    #以域名、URI、参数组合成Web缓存的Key值，Nginx根据Key值哈希，存储缓存内容到二级缓存目录内  \n    proxy_cache_key $host$uri$is_args$args;   \n    proxy_set_header Host  $host;   \n    proxy_set_header X-Forwarded-For  $remote_addr;   \n    proxy_pass http://backend_server;   \n    expires      1d;   \n  }  \n  #用于清除缓存，假设一个URL为http://192.168.1.44/test.txt，通过访问http://192.168.4.44/purge/test.txt就可以清除该URL的缓存。   \n  location ~ /purge(/.*)   \n  {  \n    #设置只允许指定的IP或IP段才可以清除URL缓存。   \n    allow            127.0.0.1;   \n    allow            192.168.0.0/16;   \n    deny            all;   \n    proxy_cache_purge    cache_one   $host$1$is_args$args;   \n  }      \n  #扩展名以.php、.jsp、.cgi结尾的动态应用程序不缓存。   \n  location ~ .*\\.(php|jsp|cgi)?$   \n  {   \n    proxy_set_header Host  $host;   \n    proxy_set_header X-Forwarded-For  $remote_addr;   \n    proxy_pass http://backend_server;   \n  }   \n}  \nhttp \n{\n  ...\n  client_body_buffer_size  512k;\n  proxy_connect_timeout    5;\n  proxy_read_timeout       60;\n  proxy_send_timeout       5;\n  proxy_buffer_size        16k;\n  proxy_buffers            4 64k;\n  proxy_busy_buffers_size 128k;\n  proxy_temp_file_write_size 128k;\n  ...\n  #注：proxy_temp_path和proxy_cache_path指定的路径必须在同一分区\n  proxy_temp_path   /data0/proxy_temp_dir;\n  #设置Web缓存区名称为cache_one，内存缓存空间大小为200MB，1天清理一次缓存，硬盘缓存空间大小为30GB。\n  proxy_cache_path  /data0/proxy_cache_dir  levels=1:2   keys_zone=cache_one:200m inactive=1d max_size=30g;\n}\nserver\n{\n  ...\n  location /\n  {\n    #如果后端的服务器返回502、504、执行超时等错误，自动将请求转发到upstream负载均衡池中的另一台服务器，实现故障转移。\n    proxy_next_upstream http_502 http_504 error timeout invalid_header;\n    proxy_cache cache_one;\n    #对不同的HTTP状态码设置不同的缓存时间\n    proxy_cache_valid  200 304 12h;\n    proxy_cache_valid  301 302 1h;\n    #以域名、URI、参数组合成Web缓存的Key值，Nginx根据Key值哈希，存储缓存内容到二级缓存目录内\n    proxy_cache_key $host$uri$is_args$args;\n    proxy_set_header Host  $host;\n    proxy_set_header X-Forwarded-For  $remote_addr;\n    proxy_pass http://backend_server;\n    expires      1d;\n  }\n  #用于清除缓存，假设一个URL为http://192.168.1.44/test.txt，通过访问http://192.168.4.44/purge/test.txt就可以清除该URL的缓存。\n  location ~ /purge(/.*)\n  {\n    #设置只允许指定的IP或IP段才可以清除URL缓存。\n    allow            127.0.0.1;\n    allow            192.168.0.0/16;\n    deny            all;\n    proxy_cache_purge    cache_one   $host$1$is_args$args;\n  }    \n  #扩展名以.php、.jsp、.cgi结尾的动态应用程序不缓存。\n  location ~ .*\\.(php|jsp|cgi)?$\n  {\n    proxy_set_header Host  $host;\n    proxy_set_header X-Forwarded-For  $remote_addr;\n    proxy_pass http://backend_server;\n  }\n}\n同时，对于影响页面展现的静态资源，例如：css, js 等可以放在具有优质带宽的IDC（IDC=互联网数据中心，优质/高速的带宽也比较贵，正所谓一份价钱一分货）；其他的静态资源，如图片等可以放在价格相对低廉的IDC中，以域名区分两种静态资源，节省每一分钱。\n网络拓扑图\n         / Ngnix - 1:1 - Resin \nF5 --\n         \\ Squid - 1:n - Apache\n监控统计平台\n业务统计 - 用户访问统计 \n软件性能 - 应用系统监控，例如：请求响应时间…… \n硬件/网络性能 - Ganglia监控 \n其它要点\nIE浏览器对同一域名（包括子域名）只能建立2个连接，连接多了只能排队…… \n双F5架构，两台职能划分不同，镜像，心跳接管…… \nRaid存储阵列…… \nLinux操作系统及其优化……\n ','基于Java技术的大型网站架构方案','','inherit','open','open','','177-revision','','','2010-09-20 22:38:03','2010-09-21 06:38:03','',177,'http://www.wordpress.cn/?p=178',0,'revision','',0),(179,1,'2010-09-20 22:43:12','2010-09-21 06:43:12','堆设置\r\n-Xmx3550m：设置JVM最大堆内存 为3550M。 \r\n-Xms3550m：设置JVM初始堆内存 为3550M。此值可以设置与-Xmx相同，以避免每次垃圾回收完成后JVM重新分配内存。 \r\n-Xss128k：设置每个线程的栈 大小。JDK5.0以后每个线程栈大小为1M，之前每个线程栈大小为256K。应当根据应用的线程所需内存大小进行调整。在相同物理内存下，减小这个值能生成更多的线程。但是操作系统对一个进程内的线程数还是有限制的，不能无限生成，经验值在3000~5000左右。 \r\n-Xmn2g：设置堆内存年轻代 大小为2G。整个堆内存大小 = 年轻代大小 + 年老代大小 + 持久代大小 。持久代一般固定大小为64m，所以增大年轻代后，将会减小年老代大小。此值对系统性能影响较大，Sun官方推荐配置为整个堆的3/8。 \r\n-XX:PermSize=256M：设置堆内存持久代 初始值为256M。(貌似是Eclipse等IDE的初始化参数) \r\n-XX:MaxNewSize=size：新生成的对象能占用内存的最大值。 \r\n-XX:MaxPermSize=512M：设置持久代最大值为512M。 \r\n-XX:NewRatio=4：设置堆内存年轻代（包括Eden和两个Survivor区）与堆内存年老代的比值（除去持久代） 。设置为4，则年轻代所占与年老代所占的比值为1:4。 \r\n-XX:SurvivorRatio=4：设置堆内存年轻代中Eden区与Survivor区大小的比值 。设置为4，则两个Survivor区（JVM堆内存年轻代中默认有2个Survivor区）与一个Eden区的比值为2:4，一个Survivor区占整个年轻代的1/6。 \r\n-XX:MaxTenuringThreshold=7：表示一个对象如果在救助空间（Survivor区）移动7次还没有被回收就放入年老代。 \r\n如果设置为0的话，则年轻代对象不经过Survivor区，直接进入年老代，对于年老代比较多的应用，这样做可以提高效率。 \r\n如果将此值设置为一个较大值，则年轻代对象会在Survivor区进行多次复制，这样可以增加对象在年轻代存活时间，增加对象在年轻代即被回收的概率。 \r\n回收器选择\r\nJVM给了三种选择：串行收集器、并行收集器、并发收集器，但是串行收集器只适用于小数据量的情况，所以这里的选择主要针对并行收集器和并发收集器。\r\n默认情况下，JDK5.0以前都是使用串行收集器，如果想使用其他收集器需要在启动时加入相应参数。JDK5.0以后，JVM会根据当前系统配置进行智能判断。\r\n串行收集器 \r\n-XX:+UseSerialGC：设置串行收集器 \r\n并行收集器(吞吐量优先) \r\n-XX:+UseParallelGC：选择垃圾收集器为并行收集器。此配置仅对年轻代有效。即上述配置下，年轻代使用并发收集，而年老代仍旧使用串行收集。 \r\n-XX:ParallelGCThreads=20：配置并行收集器的线程数，即：同时多少个线程一起进行垃圾回收。此值最好配置与处理器数目相等。 \r\n-XX:+UseParallelOldGC：配置年老代垃圾收集方式为并行收集。JDK6.0支持对年老代并行收集。 \r\n-XX:MaxGCPauseMillis=100：设置每次年轻代垃圾回收的最长时间（单位毫秒），如果无法满足此时间，JVM会自动调整年轻代大小，以满足此值。 \r\n-XX:+UseAdaptiveSizePolicy：设置此选项后，并行收集器会自动选择年轻代区大小和相应的Survivor区比例，以达到目标系统规定的最低响应时间或者收集频率等。 \r\n此参数建议使用并行收集器时，一直打开。 \r\n并发收集器(响应时间优先) \r\n-XX:+UseParNewGC：设置年轻代为并发收集。可与CMS收集同时使用。JDK5.0以上，JVM会根据系统配置自行设置，所以无需再设置此值。 \r\nCMS，全称Concurrent Low Pause Collector，是jdk1.4后期版本开始引入的新gc算法，在jdk5和jdk6中得到了进一步改进，它的主要适合场景是对响应时间的重要性需求大于对吞吐量的要求，能够承受垃圾回收线程和应用线程共享处理器资源，并且应用中存在比较多的长生命周期的对象的应用。CMS是用于对tenured generation的回收，也就是年老代的回收，目标是尽量减少应用的暂停时间，减少FullGC发生的几率，利用和应用程序线程并发的垃圾回收线程来标记清除年老代。 \r\n-XX:+UseConcMarkSweepGC：设置年老代为并发收集。测试中配置这个以后，-XX:NewRatio=4的配置失效了。所以，此时年轻代大小最好用-Xmn设置。 \r\n-XX:CMSFullGCsBeforeCompaction=：由于并发收集器不对内存空间进行压缩、整理，所以运行一段时间以后会产生“碎片”，使得运行效率降低。此参数设置运行次FullGC以后对内存空间进行压缩、整理。 \r\n-XX:+UseCMSCompactAtFullCollection：打开对年老代的压缩。可能会影响性能，但是可以消除内存碎片。 \r\n-XX:+CMSIncrementalMode：设置为增量收集模式。一般适用于单CPU情况。 \r\n-XX:CMSInitiatingOccupancyFraction=70：表示年老代空间到70%时就开始执行CMS，确保年老代有足够的空间接纳来自年轻代的对象。 \r\n注：如果使用 throughput collector 和 concurrent low pause collector 这两种垃圾收集器，需要适当的挺高内存大小，为多线程做准备。\r\n其它\r\n-XX:+ScavengeBeforeFullGC：新生代GC优先于Full GC执行。 \r\n-XX:-DisableExplicitGC：禁止调用System.gc()，但JVM的gc仍然有效。 \r\n-XX:+MaxFDLimit：最大化文件描述符的数量限制。 \r\n-XX:+UseThreadPriorities：启用本地线程优先级API，即使 java.lang.Thread.setPriority() 生效，反之无效。 \r\n-XX:SoftRefLRUPolicyMSPerMB=0：“软引用”的对象在最后一次被访问后能存活0毫秒（默认为1秒）。 \r\n-XX:TargetSurvivorRatio=90：允许90%的Survivor空间被占用（默认为50%）。提高对于Survivor的使用率——超过就会尝试垃圾回收。 \r\n辅助信息\r\n-XX:-CITime：打印消耗在JIT编译的时间 \r\n-XX:ErrorFile=./hs_err_pid.log：保存错误日志或者数据到指定文件中 \r\n-XX:-ExtendedDTraceProbes：开启solaris特有的dtrace探针 \r\n-XX:HeapDumpPath=./java_pid.hprof：指定导出堆信息时的路径或文件名 \r\n-XX:-HeapDumpOnOutOfMemoryError：当首次遭遇内存溢出时导出此时堆中相关信息 \r\n-XX:OnError=\";\"：出现致命ERROR之后运行自定义命令 \r\n-XX:OnOutOfMemoryError=\";\"：当首次遭遇内存溢出时执行自定义命令 \r\n-XX:-PrintClassHistogram：遇到Ctrl-Break后打印类实例的柱状信息，与jmap -histo功能相同 \r\n-XX:-PrintConcurrentLocks：遇到Ctrl-Break后打印并发锁的相关信息，与jstack -l功能相同 \r\n-XX:-PrintCommandLineFlags：打印在命令行中出现过的标记 \r\n-XX:-PrintCompilation：当一个方法被编译时打印相关信息 \r\n-XX:-PrintGC：每次GC时打印相关信息 \r\n-XX:-PrintGC Details：每次GC时打印详细信息 \r\n-XX:-PrintGCTimeStamps：打印每次GC的时间戳 \r\n-XX:-TraceClassLoading：跟踪类的加载信息 \r\n-XX:-TraceClassLoadingPreorder：跟踪被引用到的所有类的加载信息 \r\n-XX:-TraceClassResolution：跟踪常量池 \r\n-XX:-TraceClassUnloading：跟踪类的卸载信息 \r\n-XX:-TraceLoaderConstraints：跟踪类加载器约束的相关信息 \r\nJVM服务调优实战 \r\n服务器：8 cup, 8G mem\r\ne.g. \r\njava -Xmx3550m -Xms3550m -Xss128k -XX:NewRatio=4 -XX:SurvivorRatio=4 -XX:MaxPermSize=16m -XX:MaxTenuringThreshold=0\r\n调优方案：\r\n-Xmx5g：设置JVM最大可用内存为5G。 \r\n-Xms5g：设置JVM初始内存为5G。此值可以设置与-Xmx相同，以避免每次垃圾回收完成后JVM重新分配内存。 \r\n-Xmn2g：设置年轻代大小为2G。整个堆内存大小 = 年轻代大小 + 年老代大小 + 持久代大小 。持久代一般固定大小为64m，所以增大年轻代后，将会减小年老代大小。此值对系统性能影响较大，Sun官方推荐配置为整个堆的3/8。 \r\n-XX:+UseParNewGC：设置年轻代为并行收集。可与CMS收集同时使用。JDK5.0以上，JVM会根据系统配置自行设置，所以无需再设置此值。 \r\n-XX:ParallelGCThreads=8：配置并行收集器的线程数，即：同时多少个线程一起进行垃圾回收。此值最好配置与处理器数目相等。 \r\n-XX:SurvivorRatio=6：设置年轻代中Eden区与Survivor区的大小比值。根据经验设置为6，则两个Survivor区与一个Eden区的比值为2:6，一个Survivor区占整个年轻代的1/8。 \r\n-XX:MaxTenuringThreshold=30：设置垃圾最大年龄（次数）。如果设置为0的话，则年轻代对象不经过Survivor区直接进入年老代。对于年老代比较多的应用，可以提高效率。如果将此值设置为一个较大值，则年轻代对象会在Survivor区进行多次复制，这样可以增加对象再年轻代的存活时间，增加在年轻代即被回收的概率。设置为30表示一个对象如果在Survivor空间移动30次还没有被回收就放入年老代。 \r\n-XX:+UseConcMarkSweepGC：设置年老代为并发收集。测试配置这个参数以后，参数-XX:NewRatio=4就失效了，所以，此时年轻代大小最好用-Xmn设置，因此这个参数不建议使用。 \r\n参考资料 - JVM堆内存的分代 \r\n虚拟机的堆内存共划分为三个代：年轻代（Young Generation）、年老代（Old Generation）和持久代（Permanent Generation）。其中持久代主要存放的是Java类的类信息，与垃圾收集器要收集的Java对象关系不大。所以，年轻代和年老代的划分才是对垃圾收集影响比较大的。\r\n年轻代 \r\n所有新生成的对象首先都是放在年轻代的。年轻代的目标就是尽可能快速的收集掉那些生命周期短的对象。年轻代分三个区。一个Eden区，两个 Survivor区(一般而言)。\r\n大部分对象在Eden区中生成。当Eden区满时，还存活的对象将被复制到Survivor区（两个中的一个），当一个Survivor区满时，此区的存活对象将被复制到另外一个Survivor区，当另一个Survivor区也满了的时候，从前一个Survivor区复制过来的并且此时还存活的对象，将被复制“年老区(Tenured)”。\r\n需要注意，两个Survivor区是对称的，没先后关系，所以同一个Survivor区中可能同时存在从Eden区复制过来对象，和从另一个Survivor区复制过来的对象；而复制到年老区的只有从前一个Survivor区（相对的）过来的对象。而且，Survivor区总有一个是空的。特殊的情况下，根据程序需要，Survivor区是可以配置为多个的（多于两个），这样可以增加对象在年轻代中的存在时间，减少被放到年老代的可能。\r\n年老代 \r\n在年轻代中经历了N（可配置）次垃圾回收后仍然存活的对象，就会被放到年老代中。因此，可以认为年老代中存放的都是一些生命周期较长的对象。\r\n持久代 \r\n用于存放静态数据，如 Java Class, Method 等。持久代对垃圾回收没有显著影响，但是有些应用可能动态生成或者调用一些Class，例如 Hibernate 等，在这种时候需要设置一个比较大的持久代空间来存放这些运行过程中动态增加的类型。持久代大小通过 -XX:MaxPermSize= 进行设置。','如何调优JVM - 优化Java虚拟机','','publish','open','open','','%e5%a6%82%e4%bd%95%e8%b0%83%e4%bc%98jvm-%e4%bc%98%e5%8c%96java%e8%99%9a%e6%8b%9f%e6%9c%ba','','','2010-09-20 22:43:12','2010-09-21 06:43:12','',0,'http://www.wordpress.cn/?p=179',0,'post','',0),(180,1,'2010-09-20 22:43:02','2010-09-21 06:43:02','堆设置\n-Xmx3550m：设置JVM最大堆内存 为3550M。 \n-Xms3550m：设置JVM初始堆内存 为3550M。此值可以设置与-Xmx相同，以避免每次垃圾回收完成后JVM重新分配内存。 \n-Xss128k：设置每个线程的栈 大小。JDK5.0以后每个线程栈大小为1M，之前每个线程栈大小为256K。应当根据应用的线程所需内存大小进行调整。在相同物理内存下，减小这个值能生成更多的线程。但是操作系统对一个进程内的线程数还是有限制的，不能无限生成，经验值在3000~5000左右。 \n-Xmn2g：设置堆内存年轻代 大小为2G。整个堆内存大小 = 年轻代大小 + 年老代大小 + 持久代大小 。持久代一般固定大小为64m，所以增大年轻代后，将会减小年老代大小。此值对系统性能影响较大，Sun官方推荐配置为整个堆的3/8。 \n-XX:PermSize=256M：设置堆内存持久代 初始值为256M。(貌似是Eclipse等IDE的初始化参数) \n-XX:MaxNewSize=size：新生成的对象能占用内存的最大值。 \n-XX:MaxPermSize=512M：设置持久代最大值为512M。 \n-XX:NewRatio=4：设置堆内存年轻代（包括Eden和两个Survivor区）与堆内存年老代的比值（除去持久代） 。设置为4，则年轻代所占与年老代所占的比值为1:4。 \n-XX:SurvivorRatio=4：设置堆内存年轻代中Eden区与Survivor区大小的比值 。设置为4，则两个Survivor区（JVM堆内存年轻代中默认有2个Survivor区）与一个Eden区的比值为2:4，一个Survivor区占整个年轻代的1/6。 \n-XX:MaxTenuringThreshold=7：表示一个对象如果在救助空间（Survivor区）移动7次还没有被回收就放入年老代。 \n如果设置为0的话，则年轻代对象不经过Survivor区，直接进入年老代，对于年老代比较多的应用，这样做可以提高效率。 \n如果将此值设置为一个较大值，则年轻代对象会在Survivor区进行多次复制，这样可以增加对象在年轻代存活时间，增加对象在年轻代即被回收的概率。 \n回收器选择\nJVM给了三种选择：串行收集器、并行收集器、并发收集器，但是串行收集器只适用于小数据量的情况，所以这里的选择主要针对并行收集器和并发收集器。\n默认情况下，JDK5.0以前都是使用串行收集器，如果想使用其他收集器需要在启动时加入相应参数。JDK5.0以后，JVM会根据当前系统配置进行智能判断。\n串行收集器 \n-XX:+UseSerialGC：设置串行收集器 \n并行收集器(吞吐量优先) \n-XX:+UseParallelGC：选择垃圾收集器为并行收集器。此配置仅对年轻代有效。即上述配置下，年轻代使用并发收集，而年老代仍旧使用串行收集。 \n-XX:ParallelGCThreads=20：配置并行收集器的线程数，即：同时多少个线程一起进行垃圾回收。此值最好配置与处理器数目相等。 \n-XX:+UseParallelOldGC：配置年老代垃圾收集方式为并行收集。JDK6.0支持对年老代并行收集。 \n-XX:MaxGCPauseMillis=100：设置每次年轻代垃圾回收的最长时间（单位毫秒），如果无法满足此时间，JVM会自动调整年轻代大小，以满足此值。 \n-XX:+UseAdaptiveSizePolicy：设置此选项后，并行收集器会自动选择年轻代区大小和相应的Survivor区比例，以达到目标系统规定的最低响应时间或者收集频率等。 \n此参数建议使用并行收集器时，一直打开。 \n并发收集器(响应时间优先) \n-XX:+UseParNewGC：设置年轻代为并发收集。可与CMS收集同时使用。JDK5.0以上，JVM会根据系统配置自行设置，所以无需再设置此值。 \nCMS，全称Concurrent Low Pause Collector，是jdk1.4后期版本开始引入的新gc算法，在jdk5和jdk6中得到了进一步改进，它的主要适合场景是对响应时间的重要性需求大于对吞吐量的要求，能够承受垃圾回收线程和应用线程共享处理器资源，并且应用中存在比较多的长生命周期的对象的应用。CMS是用于对tenured generation的回收，也就是年老代的回收，目标是尽量减少应用的暂停时间，减少FullGC发生的几率，利用和应用程序线程并发的垃圾回收线程来标记清除年老代。 \n-XX:+UseConcMarkSweepGC：设置年老代为并发收集。测试中配置这个以后，-XX:NewRatio=4的配置失效了。所以，此时年轻代大小最好用-Xmn设置。 \n-XX:CMSFullGCsBeforeCompaction=：由于并发收集器不对内存空间进行压缩、整理，所以运行一段时间以后会产生“碎片”，使得运行效率降低。此参数设置运行次FullGC以后对内存空间进行压缩、整理。 \n-XX:+UseCMSCompactAtFullCollection：打开对年老代的压缩。可能会影响性能，但是可以消除内存碎片。 \n-XX:+CMSIncrementalMode：设置为增量收集模式。一般适用于单CPU情况。 \n-XX:CMSInitiatingOccupancyFraction=70：表示年老代空间到70%时就开始执行CMS，确保年老代有足够的空间接纳来自年轻代的对象。 \n注：如果使用 throughput collector 和 concurrent low pause collector 这两种垃圾收集器，需要适当的挺高内存大小，为多线程做准备。\n其它\n-XX:+ScavengeBeforeFullGC：新生代GC优先于Full GC执行。 \n-XX:-DisableExplicitGC：禁止调用System.gc()，但JVM的gc仍然有效。 \n-XX:+MaxFDLimit：最大化文件描述符的数量限制。 \n-XX:+UseThreadPriorities：启用本地线程优先级API，即使 java.lang.Thread.setPriority() 生效，反之无效。 \n-XX:SoftRefLRUPolicyMSPerMB=0：“软引用”的对象在最后一次被访问后能存活0毫秒（默认为1秒）。 \n-XX:TargetSurvivorRatio=90：允许90%的Survivor空间被占用（默认为50%）。提高对于Survivor的使用率——超过就会尝试垃圾回收。 \n辅助信息\n-XX:-CITime：打印消耗在JIT编译的时间 \n-XX:ErrorFile=./hs_err_pid.log：保存错误日志或者数据到指定文件中 \n-XX:-ExtendedDTraceProbes：开启solaris特有的dtrace探针 \n-XX:HeapDumpPath=./java_pid.hprof：指定导出堆信息时的路径或文件名 \n-XX:-HeapDumpOnOutOfMemoryError：当首次遭遇内存溢出时导出此时堆中相关信息 \n-XX:OnError=\";\"：出现致命ERROR之后运行自定义命令 \n-XX:OnOutOfMemoryError=\";\"：当首次遭遇内存溢出时执行自定义命令 \n-XX:-PrintClassHistogram：遇到Ctrl-Break后打印类实例的柱状信息，与jmap -histo功能相同 \n-XX:-PrintConcurrentLocks：遇到Ctrl-Break后打印并发锁的相关信息，与jstack -l功能相同 \n-XX:-PrintCommandLineFlags：打印在命令行中出现过的标记 \n-XX:-PrintCompilation：当一个方法被编译时打印相关信息 \n-XX:-PrintGC：每次GC时打印相关信息 \n-XX:-PrintGC Details：每次GC时打印详细信息 \n-XX:-PrintGCTimeStamps：打印每次GC的时间戳 \n-XX:-TraceClassLoading：跟踪类的加载信息 \n-XX:-TraceClassLoadingPreorder：跟踪被引用到的所有类的加载信息 \n-XX:-TraceClassResolution：跟踪常量池 \n-XX:-TraceClassUnloading：跟踪类的卸载信息 \n-XX:-TraceLoaderConstraints：跟踪类加载器约束的相关信息 \nJVM服务调优实战 \n服务器：8 cup, 8G mem\ne.g. \njava -Xmx3550m -Xms3550m -Xss128k -XX:NewRatio=4 -XX:SurvivorRatio=4 -XX:MaxPermSize=16m -XX:MaxTenuringThreshold=0\n调优方案：\n-Xmx5g：设置JVM最大可用内存为5G。 \n-Xms5g：设置JVM初始内存为5G。此值可以设置与-Xmx相同，以避免每次垃圾回收完成后JVM重新分配内存。 \n-Xmn2g：设置年轻代大小为2G。整个堆内存大小 = 年轻代大小 + 年老代大小 + 持久代大小 。持久代一般固定大小为64m，所以增大年轻代后，将会减小年老代大小。此值对系统性能影响较大，Sun官方推荐配置为整个堆的3/8。 \n-XX:+UseParNewGC：设置年轻代为并行收集。可与CMS收集同时使用。JDK5.0以上，JVM会根据系统配置自行设置，所以无需再设置此值。 \n-XX:ParallelGCThreads=8：配置并行收集器的线程数，即：同时多少个线程一起进行垃圾回收。此值最好配置与处理器数目相等。 \n-XX:SurvivorRatio=6：设置年轻代中Eden区与Survivor区的大小比值。根据经验设置为6，则两个Survivor区与一个Eden区的比值为2:6，一个Survivor区占整个年轻代的1/8。 \n-XX:MaxTenuringThreshold=30：设置垃圾最大年龄（次数）。如果设置为0的话，则年轻代对象不经过Survivor区直接进入年老代。对于年老代比较多的应用，可以提高效率。如果将此值设置为一个较大值，则年轻代对象会在Survivor区进行多次复制，这样可以增加对象再年轻代的存活时间，增加在年轻代即被回收的概率。设置为30表示一个对象如果在Survivor空间移动30次还没有被回收就放入年老代。 \n-XX:+UseConcMarkSweepGC：设置年老代为并发收集。测试配置这个参数以后，参数-XX:NewRatio=4就失效了，所以，此时年轻代大小最好用-Xmn设置，因此这个参数不建议使用。 \n参考资料 - JVM堆内存的分代 \n虚拟机的堆内存共划分为三个代：年轻代（Young Generation）、年老代（Old Generation）和持久代（Permanent Generation）。其中持久代主要存放的是Java类的类信息，与垃圾收集器要收集的Java对象关系不大。所以，年轻代和年老代的划分才是对垃圾收集影响比较大的。\n年轻代 \n所有新生成的对象首先都是放在年轻代的。年轻代的目标就是尽可能快速的收集掉那些生命周期短的对象。年轻代分三个区。一个Eden区，两个 Survivor区(一般而言)。\n大部分对象在Eden区中生成。当Eden区满时，还存活的对象将被复制到Survivor区（两个中的一个），当一个Survivor区满时，此区的存活对象将被复制到另外一个Survivor区，当另一个Survivor区也满了的时候，从前一个Survivor区复制过来的并且此时还存活的对象，将被复制“年老区(Tenured)”。\n需要注意，两个Survivor区是对称的，没先后关系，所以同一个Survivor区中可能同时存在从Eden区复制过来对象，和从另一个Survivor区复制过来的对象；而复制到年老区的只有从前一个Survivor区（相对的）过来的对象。而且，Survivor区总有一个是空的。特殊的情况下，根据程序需要，Survivor区是可以配置为多个的（多于两个），这样可以增加对象在年轻代中的存在时间，减少被放到年老代的可能。\n年老代 \n在年轻代中经历了N（可配置）次垃圾回收后仍然存活的对象，就会被放到年老代中。因此，可以认为年老代中存放的都是一些生命周期较长的对象。\n持久代 \n用于存放静态数据，如 Java Class, Method 等。持久代对垃圾回收没有显著影响，但是有些应用可能动态生成或者调用一些Class，例如 Hibernate 等，在这种时候需要设置一个比较大的持久代空间来存放这些运行过程中动态增加的类型。持久代大小通过 -XX:MaxPermSize= 进行设置。','如何调优JVM - 优化Java虚拟机（大全+实例）','','inherit','open','open','','179-revision','','','2010-09-20 22:43:02','2010-09-21 06:43:02','',179,'http://www.wordpress.cn/?p=180',0,'revision','',0),(181,1,'2010-09-20 22:57:40','2010-09-21 06:57:40','应该理解个人工作风格，并且牢记这些实践经验。以下所列的项目应该成为与人相处的第二种本能。也是每个想成功的项目经理必备的常识：\r\n尊重每一个雇员，包括供应商、合作伙伴 \r\n虚心倾听 \r\n做出见识广博的决策 \r\n不要当众批评别人 \r\n了解自己的实力和做事的先后顺序 \r\n真诚地听取团队成员的意见和建议 \r\n对目标和交付产品有清楚的了解 \r\n在IT团队中提倡合作和信息共享 \r\n了解每个人的做事风格及他们的优缺点 \r\n表扬应以团队成员喜欢的方式，真诚地表达 \r\n将负面影响视为成长的机会 \r\n以积极的方式提供指导 \r\n掌握并认识到项目管理工具的作用\r\n微软的Project在项目进度管理上有其独特的优势——直观的展现。 \r\nXPlanner是一个基于Web的XP团队计划和跟踪工具。它基于XP独特的开发概念：iteration->user stories->tasks...。XPlanner支持XP开发流程，并解决利用XP思想来开发项目所碰到的问题。当然，由于其比较简单，这个工具在过滤器设置、历史归档等人性化设计方面还有待提高。 \r\nJIRA是一款专业的缺陷跟踪/项目管理工具。软件开发过程中的Bug、技术支持问题、项目任务、变更请求，都可以在JIRA系统中区分优先级，进行分配、跟踪和管理。因此，JIRA不仅是一个问题跟踪软件（这是它的设计初衷），也可以作为一个可扩展的平台，用户完全可以对JIRA进行灵活的配置和定制，以满足项目管理的多种实际需要。 \r\n别忘了Email。在很多情况下，通过Email进行通信已经取代了电话和邮寄备忘录。许多公司有自己的Email系统，还有许多公司使用Lotus Notes。不要忘记人们工作方式的差别，性格内向的人更愿意通过Email沟通。这样他们可以有时间思考问题的答案而不是在会议上立刻做出反应。 \r\n作为一个项目经理，你必须会作报告（report）和介绍（presentation）。因此，精通微软的PPT工具是必不可少的。 \r\n当然还有很多其它优秀的工具。无论使用何种工具，它需要有如下功能：\r\n能够与处于不同地理位置的人取得联系 \r\n能够有效地通知团队（包括供应商、合作伙伴）范围进度的变更 \r\n能很快地解决小问题 \r\n项目经理的专业标准\r\n项目经理可以多才多艺，但更需要体现的是他们在授权和沟通方面的能力。\r\n使原来想积极工作的员工变得消极的做法可以毁了项目经理。\r\n项目经理在技术方面的能力应该用于指导和培训员工，如果项目经理参与编程或设计，那他不是在开发他的团队，也不是在做项目经理。\r\n项目经理面试技巧\r\n如果你没有管理经验？\r\n你从未正式管理过一个项目，或可能是非正式的管理过项目。你可以提及你是怎么不得不在没有授权的情况下领导一个大型的开发团队进行工作的（如果你真的有这方面经验的话）。需要强调的是如果你没有一个稳固的技术的基础，你的工程任务和估计决定可能被过分简单化。\r\n\r\n如果你的技术在未来的环境中是落伍或不同的？\r\n你不需要理解技术环境的内部是如何工作的，但是你应该理解一般的概念、特征和具有决定环境和发现弱点的能力。\r\n许多项目管理技巧是超出技术范围的。\r\n因此，如果你的技术是落伍的，你仍然能强调你在技术上能负独立责任。\r\n提及你管理的应用类型和及其商业作用。 \r\n提及你管理的团队是如何有效地完成目标的。 \r\n强调你的管理哲学。 \r\n提到上级、与你地位同等的人、你的用户和部下是如何评价你的管理能力的。 \r\n记住并提起任何你掌握的商务领域知识。 \r\n在面试时应该将你对技能落后的恐惧抛在一旁。因为一旦你拥有这个工作，你将能向公司内的专家询问，在所有组织中都有各方面正式或非正式的专家。你需要到处打听一下，把他们找出来。','项目经理必备的常识','','publish','open','open','','%e9%a1%b9%e7%9b%ae%e7%bb%8f%e7%90%86%e5%bf%85%e5%a4%87%e7%9a%84%e5%b8%b8%e8%af%86','','','2010-09-20 22:57:41','2010-09-21 06:57:41','',0,'http://www.wordpress.cn/?p=181',0,'post','',0),(182,1,'2010-09-20 22:57:34','2010-09-21 06:57:34','应该理解个人工作风格，并且牢记这些实践经验。以下所列的项目应该成为与人相处的第二种本能。也是每个想成功的项目经理必备的常识：\n尊重每一个雇员，包括供应商、合作伙伴 \n虚心倾听 \n做出见识广博的决策 \n不要当众批评别人 \n了解自己的实力和做事的先后顺序 \n真诚地听取团队成员的意见和建议 \n对目标和交付产品有清楚的了解 \n在IT团队中提倡合作和信息共享 \n了解每个人的做事风格及他们的优缺点 \n表扬应以团队成员喜欢的方式，真诚地表达 \n将负面影响视为成长的机会 \n以积极的方式提供指导 \n掌握并认识到项目管理工具的作用\n微软的Project在项目进度管理上有其独特的优势——直观的展现。 \nXPlanner是一个基于Web的XP团队计划和跟踪工具。它基于XP独特的开发概念：iteration->user stories->tasks...。XPlanner支持XP开发流程，并解决利用XP思想来开发项目所碰到的问题。当然，由于其比较简单，这个工具在过滤器设置、历史归档等人性化设计方面还有待提高。 \nJIRA是一款专业的缺陷跟踪/项目管理工具。软件开发过程中的Bug、技术支持问题、项目任务、变更请求，都可以在JIRA系统中区分优先级，进行分配、跟踪和管理。因此，JIRA不仅是一个问题跟踪软件（这是它的设计初衷），也可以作为一个可扩展的平台，用户完全可以对JIRA进行灵活的配置和定制，以满足项目管理的多种实际需要。 \n别忘了Email。在很多情况下，通过Email进行通信已经取代了电话和邮寄备忘录。许多公司有自己的Email系统，还有许多公司使用Lotus Notes。不要忘记人们工作方式的差别，性格内向的人更愿意通过Email沟通。这样他们可以有时间思考问题的答案而不是在会议上立刻做出反应。 \n作为一个项目经理，你必须会作报告（report）和介绍（presentation）。因此，精通微软的PPT工具是必不可少的。 \n当然还有很多其它优秀的工具。无论使用何种工具，它需要有如下功能：\n能够与处于不同地理位置的人取得联系 \n能够有效地通知团队（包括供应商、合作伙伴）范围进度的变更 \n能很快地解决小问题 \n项目经理的专业标准\n项目经理可以多才多艺，但更需要体现的是他们在授权和沟通方面的能力。\n使原来想积极工作的员工变得消极的做法可以毁了项目经理。\n项目经理在技术方面的能力应该用于指导和培训员工，如果项目经理参与编程或设计，那他不是在开发他的团队，也不是在做项目经理。\n项目经理面试技巧\n如果你没有管理经验？\n你从未正式管理过一个项目，或可能是非正式的管理过项目。你可以提及你是怎么不得不在没有授权的情况下领导一个大型的开发团队进行工作的（如果你真的有这方面经验的话）。需要强调的是如果你没有一个稳固的技术的基础，你的工程任务和估计决定可能被过分简单化。\n\n如果你的技术在未来的环境中是落伍或不同的？\n你不需要理解技术环境的内部是如何工作的，但是你应该理解一般的概念、特征和具有决定环境和发现弱点的能力。\n许多项目管理技巧是超出技术范围的。\n因此，如果你的技术是落伍的，你仍然能强调你在技术上能负独立责任。\n提及你管理的应用类型和及其商业作用。 \n提及你管理的团队是如何有效地完成目标的。 \n强调你的管理哲学。 \n提到上级、与你地位同等的人、你的用户和部下是如何评价你的管理能力的。 \n记住并提起任何你掌握的商务领域知识。 \n在面试时应该将你对技能落后的恐惧抛在一旁。因为一旦你拥有这个工作，你将能向公司内的专家询问，在所有组织中都有各方面正式或非正式的专家。你需要到处打听一下，把他们找出来。','项目经理必备的常识','','inherit','open','open','','181-revision','','','2010-09-20 22:57:34','2010-09-21 06:57:34','',181,'http://www.wordpress.cn/?p=182',0,'revision','',0),(209,1,'2010-10-07 18:13:01','2010-10-08 02:13:01','','MySQL数据库性能优化的关键参数','','inherit','open','open','','207-revision','','','2010-10-07 18:13:01','2010-10-08 02:13:01','',207,'http://www.wordpress.cn/?p=209',0,'revision','',0),(187,1,'2010-09-23 17:50:46','2010-09-24 01:50:46','#BEGIN CONFIG INFO\r\n#DESCR: 4GB RAM, 只使用InnoDB, ACID, 少量的连接, 队列负载大\r\n#TYPE: SYSTEM\r\n#END CONFIG INFO\r\n#\r\n# 此mysql配置文件例子针对4G内存。\r\n# 主要使用INNODB\r\n#处理复杂队列并且连接数量较少的mysql服务器\r\n#\r\n# 将此文件复制到/etc/my.cnf 作为全局设置,\r\n# mysql-data-dir/my.cnf 作为服务器指定设置\r\n# (@localstatedir@ for this installation) 或者放入\r\n# ~/.my.cnf 作为用户设置.\r\n#\r\n# 在此配置文件中, 你可以使用所有程序支持的长选项.\r\n# 如果想获悉程序支持的所有选项\r\n# 请在程序后加上”–help”参数运行程序.\r\n#\r\n# 关于独立选项更多的细节信息可以在手册内找到\r\n#\r\n#\r\n# 以下选项会被MySQL客户端应用读取.\r\n# 注意只有MySQL附带的客户端应用程序保证可以读取这段内容.\r\n# 如果你想你自己的MySQL应用程序获取这些值\r\n# 需要在MySQL客户端库初始化的时候指定这些选项\r\n#\r\n\r\n[client]\r\n#password = [your_password]\r\nport = @MYSQL_TCP_PORT@\r\nsocket = @MYSQL_UNIX_ADDR@\r\n# *** 应用定制选项 ***\r\n#\r\n# MySQL 服务端\r\n#\r\n[mysqld]\r\n# 一般配置选项\r\nport = @MYSQL_TCP_PORT@\r\nsocket = @MYSQL_UNIX_ADDR@\r\n# back_log 是操作系统在监听队列中所能保持的连接数,\r\n# 队列保存了在MySQL连接管理器线程处理之前的连接.\r\n# 如果你有非常高的连接率并且出现”connection refused” 报错,\r\n# 你就应该增加此处的值.\r\n# 检查你的操作系统文档来获取这个变量的最大值.\r\n# 如果将back_log设定到比你操作系统限制更高的值,将会没有效果\r\nback_log = 50\r\n# 不在TCP/IP端口上进行监听.\r\n# 如果所有的进程都是在同一台服务器连接到本地的mysqld,\r\n# 这样设置将是增强安全的方法\r\n# 所有mysqld的连接都是通过Unix sockets 或者命名管道进行的.\r\n# 注意在windows下如果没有打开命名管道选项而只是用此项\r\n# (通过 “enable-named-pipe” 选项) 将会导致mysql服务没有任何作用!\r\n#skip-networking\r\n# MySQL 服务所允许的同时会话数的上限\r\n# 其中一个连接将被SUPER权限保留作为管理员登录.\r\n# 即便已经达到了连接数的上限.\r\nmax_connections = 100\r\n# 每个客户端连接最大的错误允许数量,如果达到了此限制.\r\n# 这个客户端将会被MySQL服务阻止直到执行了”FLUSH HOSTS” 或者服务重启\r\n# 非法的密码以及其他在链接时的错误会增加此值.\r\n# 查看 “Aborted_connects” 状态来获取全局计数器.\r\nmax_connect_errors = 10\r\n# 所有线程所打开表的数量.\r\n# 增加此值就增加了mysqld所需要的文件描述符的数量\r\n# 这样你需要确认在[mysqld_safe]中 “open-files-limit” 变量设置打开文件数量允许至少4096\r\ntable_cache = 2048\r\n# 允许外部文件级别的锁. 打开文件锁会对性能造成负面影响\r\n# 所以只有在你在同样的文件上运行多个数据库实例时才使用此选项(注意仍会有其他约束!)\r\n# 或者你在文件层面上使用了其他一些软件依赖来锁定MyISAM表\r\n#external-locking\r\n# 服务所能处理的请求包的最大大小以及服务所能处理的最大的请求大小(当与大的BLOB字段一起工作时相当必要)\r\n# 每个连接独立的大小.大小动态增加\r\nmax_allowed_packet = 16M\r\n# 在一个事务中binlog为了记录SQL状态所持有的cache大小\r\n# 如果你经常使用大的,多声明的事务,你可以增加此值来获取更大的性能.\r\n# 所有从事务来的状态都将被缓冲在binlog缓冲中然后在提交后一次性写入到binlog中\r\n# 如果事务比此值大, 会使用磁盘上的临时文件来替代.\r\n# 此缓冲在每个连接的事务第一次更新状态时被创建\r\nbinlog_cache_size = 1M\r\n# 独立的内存表所允许的最大容量.\r\n# 此选项为了防止意外创建一个超大的内存表导致永尽所有的内存资源.\r\nmax_heap_table_size = 64M\r\n# 排序缓冲被用来处理类似ORDER BY以及GROUP BY队列所引起的排序\r\n# 如果排序后的数据无法放入排序缓冲,\r\n# 一个用来替代的基于磁盘的合并分类会被使用\r\n# 查看 “Sort_merge_passes” 状态变量.\r\n# 在排序发生时由每个线程分配\r\nsort_buffer_size = 8M\r\n# 此缓冲被使用来优化全联合(full JOINs 不带索引的联合).\r\n# 类似的联合在极大多数情况下有非常糟糕的性能表现,\r\n# 但是将此值设大能够减轻性能影响.\r\n# 通过 “Select_full_join” 状态变量查看全联合的数量\r\n# 当全联合发生时,在每个线程中分配\r\njoin_buffer_size = 8M\r\n# 我们在cache中保留多少线程用于重用\r\n# 当一个客户端断开连接后,如果cache中的线程还少于thread_cache_size,\r\n# 则客户端线程被放入cache中.\r\n# 这可以在你需要大量新连接的时候极大的减少线程创建的开销\r\n# (一般来说如果你有好的线程模型的话,这不会有明显的性能提升.)\r\nthread_cache_size = 8\r\n# 此允许应用程序给予线程系统一个提示在同一时间给予渴望被运行的线程的数量.\r\n# 此值只对于支持 thread_concurrency() 函数的系统有意义( 例如Sun Solaris).\r\n# 你可可以尝试使用 [CPU数量]*(2..4) 来作为thread_concurrency的值\r\nthread_concurrency = 8\r\n# 查询缓冲常被用来缓冲 SELECT 的结果并且在下一次同样查询的时候不再执行直接返回结果.\r\n# 打开查询缓冲可以极大的提高服务器速度, 如果你有大量的相同的查询并且很少修改表.\r\n# 查看 “Qcache_lowmem_prunes” 状态变量来检查是否当前值对于你的负载来说是否足够高.\r\n# 注意: 在你表经常变化的情况下或者如果你的查询原文每次都不同,\r\n# 查询缓冲也许引起性能下降而不是性能提升.\r\nquery_cache_size = 64M\r\n# 只有小于此设定值的结果才会被缓冲\r\n# 此设置用来保护查询缓冲,防止一个极大的结果集将其他所有的查询结果都覆盖.\r\nquery_cache_limit = 2M\r\n# 被全文检索索引的最小的字长.\r\n# 你也许希望减少它,如果你需要搜索更短字的时候.\r\n# 注意在你修改此值之后,\r\n# 你需要重建你的 FULLTEXT 索引\r\nft_min_word_len = 4\r\n# 如果你的系统支持 memlock() 函数,你也许希望打开此选项用以让运行中的mysql在在内存高度紧张的时候,数据在内存中保持锁定并且防止可能被swapping out\r\n# 此选项对于性能有益\r\n#memlock\r\n# 当创建新表时作为默认使用的表类型,\r\n# 如果在创建表示没有特别执行表类型,将会使用此值\r\ndefault_table_type = MYISAM\r\n# 线程使用的堆大小. 此容量的内存在每次连接时被预留.\r\n# MySQL 本身常不会需要超过64K的内存\r\n# 如果你使用你自己的需要大量堆的UDF函数\r\n# 或者你的操作系统对于某些操作需要更多的堆,\r\n# 你也许需要将其设置的更高一点.\r\nthread_stack = 192K\r\n# 设定默认的事务隔离级别.可用的级别如下:\r\n# READ-UNCOMMITTED, READ-COMMITTED, REPEATABLE-READ, SERIALIZABLE\r\ntransaction_isolation = REPEATABLE-READ\r\n# 内部(内存中)临时表的最大大小\r\n# 如果一个表增长到比此值更大,将会自动转换为基于磁盘的表.\r\n# 此限制是针对单个表的,而不是总和.\r\ntmp_table_size = 64M\r\n# 打开二进制日志功能.\r\n# 在复制(replication)配置中,作为MASTER主服务器必须打开此项\r\n# 如果你需要从你最后的备份中做基于时间点的恢复,你也同样需要二进制日志.\r\nlog-bin=mysql-bin\r\n# 如果你在使用链式从服务器结构的复制模式 (A->B->C),\r\n# 你需要在服务器B上打开此项.\r\n# 此选项打开在从线程上重做过的更新的日志,\r\n# 并将其写入从服务器的二进制日志.\r\n#log_slave_updates\r\n# 打开全查询日志. 所有的由服务器接收到的查询 (甚至对于一个错误语法的查询)\r\n# 都会被记录下来. 这对于调试非常有用, 在生产环境中常常关闭此项.\r\n#log\r\n# 将警告打印输出到错误log文件. 如果你对于MySQL有任何问题\r\n# 你应该打开警告log并且仔细审查错误日志,查出可能的原因.\r\n#log_warnings\r\n# 记录慢速查询. 慢速查询是指消耗了比 “long_query_time” 定义的更多时间的查询.\r\n# 如果 log_long_format 被打开,那些没有使用索引的查询也会被记录.\r\n# 如果你经常增加新查询到已有的系统内的话. 一般来说这是一个好主意,\r\nlog_slow_queries\r\n# 所有的使用了比这个时间(以秒为单位)更多的查询会被认为是慢速查询.\r\n# 不要在这里使用”1″, 否则会导致所有的查询,甚至非常快的查询页被记录下来(由于MySQL 目前时间的精确度只能达到秒的级别).\r\nlong_query_time = 2\r\n# 在慢速日志中记录更多的信息.\r\n# 一般此项最好打开.\r\n# 打开此项会记录使得那些没有使用索引的查询也被作为到慢速查询附加到慢速日志里\r\nlog_long_format\r\n# 此目录被MySQL用来保存临时文件.例如,\r\n# 它被用来处理基于磁盘的大型排序,和内部排序一样.\r\n# 以及简单的临时表.\r\n# 如果你不创建非常大的临时文件,将其放置到 swapfs/tmpfs 文件系统上也许比较好\r\n# 另一种选择是你也可以将其放置在独立的磁盘上.\r\n# 你可以使用”;”来放置多个路径\r\n# 他们会按照roud-robin方法被轮询使用.\r\n#tmpdir = /tmp\r\n\r\n# *** 复制有关的设置\r\n\r\n# 唯一的服务辨识号,数值位于 1 到 2^32-1之间.\r\n# 此值在master和slave上都需要设置.\r\n# 如果 “master-host” 没有被设置,则默认为1, 但是如果忽略此选项,MySQL不会作为master生效.\r\nserver-id = 1\r\n# 复制的Slave (去掉master段的注释来使其生效)\r\n#\r\n# 为了配置此主机作为复制的slave服务器,你可以选择两种方法:\r\n#\r\n# 1) 使用 CHANGE MASTER TO 命令 (在我们的手册中有完整描述) -\r\n# 语法如下:\r\n#\r\n# CHANGE MASTER TO MASTER_HOST=, MASTER_PORT=\r\n,\r\n# MASTER_USER=, MASTER_PASSWORD=\r\n;\r\n#\r\n# 你需要替换掉 , ,\r\n等被尖括号包围的字段以及使用master的端口号替换\r\n(默认3306).\r\n#\r\n# 例子:\r\n#\r\n# CHANGE MASTER TO MASTER_HOST=’125.564.12.1′, MASTER_PORT=3306,\r\n# MASTER_USER=’joe’, MASTER_PASSWORD=’secret’;\r\n#\r\n# 或者\r\n#\r\n# 2) 设置以下的变量. 不论如何, 在你选择这种方法的情况下, 然后第一次启动复制(甚至不成功的情况下,\r\n# 例如如果你输入错密码在master-password字段并且slave无法连接),\r\n# slave会创建一个 master.info 文件,并且之后任何对于包含在此文件内的参数的变化都会被忽略\r\n# 并且由 master.info 文件内的内容覆盖, 除非你关闭slave服务, 删除 master.info 并且重启slave 服务.\r\n# 由于这个原因,你也许不想碰一下的配置(注释掉的) 并且使用 CHANGE MASTER TO (查看上面) 来代替\r\n#\r\n# 所需要的唯一id号位于 2 和 2^32 – 1之间\r\n# (并且和master不同)\r\n# 如果master-host被设置了.则默认值是2\r\n# 但是如果省略,则不会生效\r\n#server-id = 2\r\n#\r\n# 复制结构中的master – 必须\r\n#master-host =\r\n#\r\n# 当连接到master上时slave所用来认证的用户名 – 必须\r\n#master-user =\r\n#\r\n# 当连接到master上时slave所用来认证的密码 – 必须\r\n#master-password =\r\n#\r\n# master监听的端口.\r\n# 可选 – 默认是3306\r\n#master-port =\r\n# 使得slave只读.只有用户拥有SUPER权限和在上面的slave线程能够修改数据.\r\n# 你可以使用此项去保证没有应用程序会意外的修改slave而不是master上的数据\r\n#read_only\r\n\r\n#*** MyISAM 相关选项\r\n\r\n# 关键词缓冲的大小, 一般用来缓冲MyISAM表的索引块.\r\n# 不要将其设置大于你可用内存的30%,\r\n# 因为一部分内存同样被OS用来缓冲行数据\r\n# 甚至在你并不使用MyISAM 表的情况下, 你也需要仍旧设置起 8-64M 内存由于它同样会被内部临时磁盘表使用.\r\nkey_buffer_size = 32M\r\n# 用来做MyISAM表全表扫描的缓冲大小.\r\n# 当全表扫描需要时,在对应线程中分配.\r\nread_buffer_size = 2M\r\n# 当在排序之后,从一个已经排序好的序列中读取行时,行数据将从这个缓冲中读取来防止磁盘寻道.\r\n# 如果你增高此值,可以提高很多ORDER BY的性能.\r\n# 当需要时由每个线程分配\r\nread_rnd_buffer_size = 16M\r\n# MyISAM 使用特殊的类似树的cache来使得突发插入\r\n# (这些插入是,INSERT SELECT, INSERT VALUES (), (), , 以及 LOAD DATA\r\n# INFILE) 更快. 此变量限制每个进程中缓冲树的字节数.\r\n# 设置为 0 会关闭此优化.\r\n# 为了最优化不要将此值设置大于 “key_buffer_size”.\r\n# 当突发插入被检测到时此缓冲将被分配.\r\nbulk_insert_buffer_size = 64M\r\n# 此缓冲当MySQL需要在 REPAIR, OPTIMIZE, ALTER 以及 LOAD DATA INFILE 到一个空表中引起重建索引时被分配.\r\n# 这在每个线程中被分配.所以在设置大值时需要小心.\r\nmyisam_sort_buffer_size = 128M\r\n# MySQL重建索引时所允许的最大临时文件的大小 (当 REPAIR, ALTER TABLE 或者 LOAD DATA INFILE).\r\n# 如果文件大小比此值更大,索引会通过键值缓冲创建(更慢)\r\nmyisam_max_sort_file_size = 10G\r\n# 如果被用来更快的索引创建索引所使用临时文件大于制定的值,那就使用键值缓冲方法.\r\n# 这主要用来强制在大表中长字串键去使用慢速的键值缓冲方法来创建索引.\r\nmyisam_max_extra_sort_file_size = 10G\r\n# 如果一个表拥有超过一个索引, MyISAM 可以通过并行排序使用超过一个线程去修复他们.\r\n# 这对于拥有多个CPU以及大量内存情况的用户,是一个很好的选择.\r\nmyisam_repair_threads = 1\r\n# 自动检查和修复没有适当关闭的 MyISAM 表.\r\nmyisam_recover\r\n\r\n# 默认关闭 Federated\r\nskip-federated\r\n# *** BDB 相关选项 ***\r\n# 如果你运行的MySQL服务有BDB支持但是你不准备使用的时候使用此选项. 这会节省内存并且可能加速一些事.\r\nskip-bdb\r\n\r\n# *** INNODB 相关选项 ***\r\n# 如果你的MySQL服务包含InnoDB支持但是并不打算使用的话,\r\n# 使用此选项会节省内存以及磁盘空间,并且加速某些部分\r\n#skip-innodb\r\n# 附加的内存池被InnoDB用来保存 metadata 信息\r\n# 如果InnoDB为此目的需要更多的内存,它会开始从OS这里申请内存.\r\n# 由于这个操作在大多数现代操作系统上已经足够快, 你一般不需要修改此值.\r\n# SHOW INNODB STATUS 命令会显示当先使用的数量.\r\ninnodb_additional_mem_pool_size = 16M\r\n# InnoDB使用一个缓冲池来保存索引和原始数据, 不像 MyISAM.\r\n# 这里你设置越大,你在存取表里面数据时所需要的磁盘I/O越少.\r\n# 在一个独立使用的数据库服务器上,你可以设置这个变量到服务器物理内存大小的80%\r\n# 不要设置过大,否则,由于物理内存的竞争可能导致操作系统的换页颠簸.\r\n# 注意在32位系统上你每个进程可能被限制在 2-3.5G 用户层面内存限制,\r\n# 所以不要设置的太高.\r\ninnodb_buffer_pool_size = 2G\r\n# InnoDB 将数据保存在一个或者多个数据文件中成为表空间.\r\n# 如果你只有单个逻辑驱动保存你的数据,一个单个的自增文件就足够好了.\r\n# 其他情况下.每个设备一个文件一般都是个好的选择.\r\n# 你也可以配置InnoDB来使用裸盘分区 – 请参考手册来获取更多相关内容\r\ninnodb_data_file_path = ibdata1:10M:autoextend\r\n# 设置此选项如果你希望InnoDB表空间文件被保存在其他分区.\r\n# 默认保存在MySQL的datadir中.\r\n#innodb_data_home_dir =\r\n# 用来同步IO操作的IO线程的数量. This value is\r\n# 此值在Unix下被硬编码为4,但是在Windows磁盘I/O可能在一个大数值下表现的更好.\r\ninnodb_file_io_threads = 4\r\n# 如果你发现InnoDB表空间损坏, 设置此值为一个非零值可能帮助你导出你的表.\r\n# 从1开始并且增加此值知道你能够成功的导出表.\r\n#innodb_force_recovery=1\r\n# 在InnoDb核心内的允许线程数量.\r\n# 最优值依赖于应用程序,硬件以及操作系统的调度方式.\r\n# 过高的值可能导致线程的互斥颠簸.\r\ninnodb_thread_concurrency = 16\r\n# 如果设置为1 ,InnoDB会在每次提交后刷新(fsync)事务日志到磁盘上,\r\n# 这提供了完整的ACID行为.\r\n# 如果你愿意对事务安全折衷, 并且你正在运行一个小的食物, 你可以设置此值到0或者2来减少由事务日志引起的磁盘I/O\r\n# 0代表日志只大约每秒写入日志文件并且日志文件刷新到磁盘.\r\n# 2代表日志写入日志文件在每次提交后,但是日志文件只有大约每秒才会刷新到磁盘上.\r\ninnodb_flush_log_at_trx_commit = 1\r\n# 加速InnoDB的关闭. 这会阻止InnoDB在关闭时做全清除以及插入缓冲合并.\r\n# 这可能极大增加关机时间, 但是取而代之的是InnoDB可能在下次启动时做这些操作.\r\n#innodb_fast_shutdown\r\n# 用来缓冲日志数据的缓冲区的大小.\r\n# 当此值快满时, InnoDB将必须刷新数据到磁盘上.\r\n# 由于基本上每秒都会刷新一次,所以没有必要将此值设置的太大(甚至对于长事务而言)\r\ninnodb_log_buffer_size = 8M\r\n# 在日志组中每个日志文件的大小.\r\n# 你应该设置日志文件总合大小到你缓冲池大小的25%~100%\r\n# 来避免在日志文件覆写上不必要的缓冲池刷新行为.\r\n# 不论如何, 请注意一个大的日志文件大小会增加恢复进程所需要的时间.\r\ninnodb_log_file_size = 256M\r\n# 在日志组中的文件总数.\r\n# 通常来说2~3是比较好的.\r\ninnodb_log_files_in_group = 3\r\n# InnoDB的日志文件所在位置. 默认是MySQL的datadir.\r\n# 你可以将其指定到一个独立的硬盘上或者一个RAID1卷上来提高其性能\r\n#innodb_log_group_home_dir\r\n# 在InnoDB缓冲池中最大允许的脏页面的比例.\r\n# 如果达到限额, InnoDB会开始刷新他们防止他们妨碍到干净数据页面.\r\n# 这是一个软限制,不被保证绝对执行.\r\ninnodb_max_dirty_pages_pct = 90\r\n# InnoDB用来刷新日志的方法.\r\n# 表空间总是使用双重写入刷新方法\r\n# 默认值是 “fdatasync”, 另一个是 “O_DSYNC”.\r\n#innodb_flush_method=O_DSYNC\r\n# 在被回滚前,一个InnoDB的事务应该等待一个锁被批准多久.\r\n# InnoDB在其拥有的锁表中自动检测事务死锁并且回滚事务.\r\n# 如果你使用 LOCK TABLES 指令, 或者在同样事务中使用除了InnoDB以外的其他事务安全的存储引擎\r\n# 那么一个死锁可能发生而InnoDB无法注意到.\r\n# 这种情况下这个timeout值对于解决这种问题就非常有帮助.\r\ninnodb_lock_wait_timeout = 120\r\n\r\n[mysqldump]\r\n# 不要在将内存中的整个结果写入磁盘之前缓存. 在导出非常巨大的表时需要此项\r\nquick\r\nmax_allowed_packet = 16M\r\n[mysql]\r\nno-auto-rehash\r\n# 仅仅允许使用键值的 UPDATEs 和 DELETEs .\r\n#safe-updates\r\n[isamchk]\r\nkey_buffer = 512M\r\nsort_buffer_size = 512M\r\nread_buffer = 8M\r\nwrite_buffer = 8M\r\n[myisamchk]\r\nkey_buffer = 512M\r\nsort_buffer_size = 512M\r\nread_buffer = 8M\r\nwrite_buffer = 8M\r\n[mysqlhotcopy]\r\ninteractive-timeout\r\n[mysqld_safe]\r\n# 增加每个进程的可打开文件数量.\r\n# 警告: 确认你已经将全系统限制设定的足够高!\r\n# 打开大量表需要将此值设b\r\nopen-files-limit = 8192\r\n======================================================================\r\n\r\nMysql参数优化对于新手来讲，是比较难懂的东西，其实这个参数优化，是个很复杂的东西，对于不同的网站，及其在线量，访问量，帖子数量，网络情况，以及机器硬件配置都有关系，优化不可能一次性完成，需要不断的观察以及调试，才有可能得到最佳效果。\r\n下面先说我的服务器的硬件以及论坛情况，\r\nCPU: 2颗四核Intel Xeon 2.00GHz\r\n内存: 4GB DDR\r\n硬盘: SCSI 146GB\r\n论坛：在线会员 一般在 5000 人左右 – 最高记录是 13264.\r\n下面，我们根据以上硬件配置结合一份已经做过一次优化的my.cnf进行分析说明：有些参数可能还得根据论坛的变化情况以及程序员的程序进行再调整。\r\n[mysqld]\r\nport = 3306\r\nserverid = 1\r\nsocket = /tmp/mysql.sock\r\nskip-locking # 避免MySQL的外部锁定，减少出错几率增强稳定性。 skip-name-resolve\r\n禁止MySQL对外部连接进行DNS解析，使用这一选项可以消除MySQL进行DNS解析的时间。但需要注意，如果开启该选项，则所有远程主机连接授权都要使用IP地址方式，否则MySQL将无法正常处理连接请求!\r\nback_log = 500\r\n要求 MySQL 能有的连接数量。当主要MySQL线程在一个很短时间内得到非常多的连接请求，这就起作用，然后主线程花些时间(尽管很短)检查连接并且启动一个新线程。\r\nback_log值指出在MySQL暂时停止回答新请求之前的短时间内多少个请求可以被存在堆栈中。只有如果期望在一个短时间内有很多连接，你需要增加它，换句话说，这值对到来的TCP/IP连接的侦听队列的大小。你的操作系统在这个队列大小上有它自己的限制。试图设定back_log高于你的操作系统的限制将是无效的。当你观察你的主机进程列表，发现大量 264084 | unauthenticated user | xxx.xxx.xxx.xxx | NULL | Connect | NULL | login | NULL 的待连接进程时，就要加大 back_log 的值了。默认数值是50，我把它改为500。\r\nkey_buffer_size = 384M\r\n# key_buffer_size指定用于索引的缓冲区大小，增加它可得到更好处理的索引(对所有读和多重写)，到你能负担得起那样多。如果你使它太大，系统将开始换页并且真的变慢了。对于内存在4GB左右的服务器该参数可设置为384M或512M。通过检查状态值Key_read_requests和 Key_reads,可以知道key_buffer_size设置是否合理。比例key_reads / key_read_requests应该尽可能的低，至少是1:100，1:1000更好(上述状态值可以使用SHOW STATUS LIKE ‘key_read%’获得)。注意：该参数值设置的过大反而会是服务器整体效率降低!\r\nmax_allowed_packet = 32M\r\n增加该变量的值十分安全，这是因为仅当需要时才会分配额外内存。例如，仅当你发出长查询或mysqld必须返回大的结果行时mysqld才会分配更多内存。该变量之所以取较小默认值是一种预防措施，以捕获客户端和服务器之间的错误信息包，并确保不会因偶然使用大的信息包而导致内存溢出。\r\ntable_cache = 512\r\ntable_cache指定表高速缓存的大小。每当MySQL访问一个表时，如果在表缓冲区中还有空间，该表就被打开并放入其中，这样可以更快地访问表内容。通过检查峰值时间的状态值Open_tables和Opened_tables，可以决定是否需要增加table_cache的值。如果你发现 open_tables等于table_cache，并且opened_tables在不断增长，那么你就需要增加table_cache的值了(上述状态值可以使用SHOW STATUS LIKE ‘Open%tables’获得)。注意，不能盲目地把table_cache设置成很大的值。如果设置得太高，可能会造成文件描述符不足，从而造成性能不稳定或者连接失败。\r\nsort_buffer_size = 4M\r\n查询排序时所能使用的缓冲区大小。注意：该参数对应的分配内存是每连接独占!如果有100个连接，那么实际分配的总共排序缓冲区大小为100 × 4 = 400MB。所以，对于内存在4GB左右的服务器推荐设置为4-8M。\r\nread_buffer_size = 4M\r\n读查询操作所能使用的缓冲区大小。和sort_buffer_size一样，该参数对应的分配内存也是每连接独享!\r\njoin_buffer_size = 8M\r\n联合查询操作所能使用的缓冲区大小，和sort_buffer_size一样，该参数对应的分配内存也是每连接独享!\r\nmyisam_sort_buffer_size = 64M\r\nMyISAM表发生变化时重新排序所需的缓冲\r\nquery_cache_size = 64M\r\n指定MySQL查询缓冲区的大小。可以通过在MySQL控制台执行以下命令观察：\r\n# > SHOW VARIABLES LIKE ‘%query_cache%’; # > SHOW STATUS LIKE ‘Qcache%’; # 如果Qcache_lowmem_prunes的值非常大，则表明经常出现缓冲不够的情况;\r\n如果Qcache_hits的值非常大，则表明查询缓冲使用非常频繁，如果该值较小反而会影响效率，那么可以考虑不用查询缓冲;Qcache_free_blocks，如果该值非常大，则表明缓冲区中碎片很多。\r\nthread_cache_size = 64\r\n可以复用的保存在中的线程的数量。如果有，新的线程从缓存中取得，当断开连接的时候如果有空间，客户的线置在缓存中。如果有很多新的线程，为了提高性能可以这个变量值。通过比较 Connections 和 Threads_created 状态的变量，可以看到这个变量的作用\r\ntmp_table_size = 256M\r\nmax_connections = 1000\r\n指定MySQL允许的最大连接进程数。如果在访问论坛时经常出现Too Many Connections的错误提示，则需要增大该参数值。\r\nmax_connect_errors = 10000000\r\n对于同一主机，如果有超出该参数值个数的中断错误连接，则该主机将被禁止连接。如需对该主机进行解禁，执行：FLUSH HOST;。\r\nwait_timeout = 10\r\n指定一个请求的最大连接时间，对于4GB左右内存的服务器可以设置为5-10。\r\nthread_concurrency = 8\r\n该参数取值为服务器逻辑CPU数量×2，在本例中，服务器有2颗物理CPU，而每颗物理CPU又支持H.T超线程，所以实际取值为4 × 2 = 8\r\nskip-networking\r\n开启该选项可以彻底关闭MySQL的TCP/IP连接方式，如果WEB服务器是以远程连接的方式访问MySQL数据库服务器则不要开启该选项!否则将无法正常连接!\r\nlong_query_time = 10\r\nlog-slow-queries =\r\nlog-queries-not-using-indexes\r\n开启慢查询日志( slow query log )\r\n慢查询日志对于跟踪有问题的查询非常有用。它记录所有查过long_query_time的查询，如果需要，还可以记录不使用索引的记录。下面是一个慢查询日志的例子：\r\n开启慢查询日志，需要设置参数log_slow_queries、long_query_times、log-queries-not-using-indexes。\r\nlog_slow_queries指定日志文件，如果不提供文件名，MySQL将自己产生缺省文件名。long_query_times指定慢查询的阈值，缺省是10秒。log-queries-not-using-indexes是4.1.0以后引入的参数，它指示记录不使用索引的查询。设置 long_query_time=10\r\n外附上使用show status命令查看mysql状态相关的值及其含义：\r\n使用show status命令\r\n含义如下:\r\naborted_clients 客户端非法中断连接次数\r\naborted_connects 连接mysql失败次数\r\ncom_xxx xxx命令执行次数,有很多条\r\nconnections 连接mysql的数量\r\nCreated_tmp_disk_tables 在磁盘上创建的临时表\r\nCreated_tmp_tables 在内存里创建的临时表\r\nCreated_tmp_files 临时文件数\r\nKey_read_requests The number of requests to read a key block from the cache\r\nKey_reads The number of physical reads of a key block from disk\r\nMax_used_connections 同时使用的连接数\r\nOpen_tables 开放的表\r\nOpen_files 开放的文件\r\nOpened_tables 打开的表\r\nQuestions 提交到server的查询数\r\nSort_merge_passes 如果这个值很大,应该增加my.cnf中的sort_buffer值\r\nUptime 服务器已经工作的秒数\r\n提升性能的建议:\r\n1.如果opened_tables太大,应该把my.cnf中的table_cache变大\r\n\r\n2.如果Key_reads太大,则应该把my.cnf中key_buffer_size变大.可以用Key_reads/Key_read_requests计算出cache失败率\r\n\r\n3.如果Handler_read_rnd太大,则你写的SQL语句里很多查询都是要扫描整个表,而没有发挥索引的键的作用\r\n\r\n4.如果Threads_created太大,就要增加my.cnf中thread_cache_size的值.可以用Threads_created/Connections计算cache命中率\r\n\r\n5.如果Created_tmp_disk_tables太大,就要增加my.cnf中tmp_table_size的值,用基于内存的临时表代替基于磁盘的\r\n\r\n===================================================================\r\n存储引擎是什么？MySQL中的数据用各种不同的技术存储在文件(或者内 正确的编译方法固然重要，但它只是提高MySQL服务器性能工作的一部分。MySQL服务器的许多参数会影响服务器的性能表现，而且我们可以把这些参数保存到配置文件，使得每次MySQL服务器启动时这些参数都自动发挥作用。这个配置文件就是my.cnf。\r\nMySQL服务器提供了my.cnf文件的几个示例，它们可以在/usr/local/mysql/share/mysql/目录下找到，名字分别为 my-small.cnf、my-medium.cnf、my-large.cnf以及my-huge.cnf。文件名字中关于规模的说明描述了该配置文件适用的系统类型。例如，如果运行MySQL服务器的系统内存不多，而且MySQL只是偶尔使用，那么使用my-small.cnf配置文件最为理想，这个配置文件告诉mysqld daemon使用最少的系统资源。反之，如果MySQL服务器用于支持一个大规模的在线商场，系统拥有2G的内存，那么使用mysql-huge.cnf 最为合适。\r\n要使用上述示例配置文件，我们应该先复制一个最适合要求的配置文件，并把它命名为my.cnf。这个复制得到的配置文件可以按照如下三种方式使用：\r\n全局：把这个my.cnf文件复制到服务器的/etc目录，此时文件中所定义的参数将全局有效，即对该服务器上运行的所有MySQL数据库服务器都有效。\r\n局部：把这个my.cnf文件复制到[MYSQL-INSTALL-DIR]/var/将使该文件只对指定的服务器有效，其中[MYSQL-INSTALL-DIR]表示安装MySQL的目录。\r\n用户：最后，我们还可以把该文件的作用范围局限到指定的用户，这只需把my.cnf文件复制到用户的根目录即可。\r\n那么，如何设置my.cnf文件中的参数呢？或者进一步说，哪些参数是我们可以设置的呢？所有这些参数都对MySQL服务器有着全局性的影响，但同时每一个参数都和MySQL的特定部分关系较为密切。例如，max_connections参数属于mysqld一类。那么，如何才能得知这一点呢？这只需执行如下命令：\r\n\r\n% >/usr/local/mysql/libexec/mysqld –help\r\n该命令将显示出和mysqld有关的各种选项和参数。要寻找这些参数非常方便，因为这些参数都在“Possible variables for option –set-variable (-O) are”这行内容的后面。找到这些参数之后，我们就可以在my.cnf文件中按照如下方式设置所有这些参数：\r\n\r\nset-variable = max_connections=100\r\n\r\n这行代码的效果是：同时连接MySQL服务器的最大连接数量限制为100。不要忘了在my.cnf文件[mysqld]小节加上一个set-variable指令，具体请参见配置文件中的示例。','MySQL配置文件my.cnf中文详解','','publish','open','open','','mysql%e9%85%8d%e7%bd%ae%e6%96%87%e4%bb%b6my-cnf%e4%b8%ad%e6%96%87%e8%af%a6%e8%a7%a3','','','2010-09-23 17:50:46','2010-09-24 01:50:46','',0,'http://www.wordpress.cn/?p=187',0,'post','',0),(188,1,'2010-09-23 17:50:19','2010-09-24 01:50:19','#BEGIN CONFIG INFO\n#DESCR: 4GB RAM, 只使用InnoDB, ACID, 少量的连接, 队列负载大\n#TYPE: SYSTEM\n#END CONFIG INFO\n#\n# 此mysql配置文件例子针对4G内存。\n# 主要使用INNODB\n#处理复杂队列并且连接数量较少的mysql服务器\n#\n# 将此文件复制到/etc/my.cnf 作为全局设置,\n# mysql-data-dir/my.cnf 作为服务器指定设置\n# (@localstatedir@ for this installation) 或者放入\n# ~/.my.cnf 作为用户设置.\n#\n# 在此配置文件中, 你可以使用所有程序支持的长选项.\n# 如果想获悉程序支持的所有选项\n# 请在程序后加上”–help”参数运行程序.\n#\n# 关于独立选项更多的细节信息可以在手册内找到\n#\n#\n# 以下选项会被MySQL客户端应用读取.\n# 注意只有MySQL附带的客户端应用程序保证可以读取这段内容.\n# 如果你想你自己的MySQL应用程序获取这些值\n# 需要在MySQL客户端库初始化的时候指定这些选项\n#\n\n[client]\n#password = [your_password]\nport = @MYSQL_TCP_PORT@\nsocket = @MYSQL_UNIX_ADDR@\n# *** 应用定制选项 ***\n#\n# MySQL 服务端\n#\n[mysqld]\n# 一般配置选项\nport = @MYSQL_TCP_PORT@\nsocket = @MYSQL_UNIX_ADDR@\n# back_log 是操作系统在监听队列中所能保持的连接数,\n# 队列保存了在MySQL连接管理器线程处理之前的连接.\n# 如果你有非常高的连接率并且出现”connection refused” 报错,\n# 你就应该增加此处的值.\n# 检查你的操作系统文档来获取这个变量的最大值.\n# 如果将back_log设定到比你操作系统限制更高的值,将会没有效果\nback_log = 50\n# 不在TCP/IP端口上进行监听.\n# 如果所有的进程都是在同一台服务器连接到本地的mysqld,\n# 这样设置将是增强安全的方法\n# 所有mysqld的连接都是通过Unix sockets 或者命名管道进行的.\n# 注意在windows下如果没有打开命名管道选项而只是用此项\n# (通过 “enable-named-pipe” 选项) 将会导致mysql服务没有任何作用!\n#skip-networking\n# MySQL 服务所允许的同时会话数的上限\n# 其中一个连接将被SUPER权限保留作为管理员登录.\n# 即便已经达到了连接数的上限.\nmax_connections = 100\n# 每个客户端连接最大的错误允许数量,如果达到了此限制.\n# 这个客户端将会被MySQL服务阻止直到执行了”FLUSH HOSTS” 或者服务重启\n# 非法的密码以及其他在链接时的错误会增加此值.\n# 查看 “Aborted_connects” 状态来获取全局计数器.\nmax_connect_errors = 10\n# 所有线程所打开表的数量.\n# 增加此值就增加了mysqld所需要的文件描述符的数量\n# 这样你需要确认在[mysqld_safe]中 “open-files-limit” 变量设置打开文件数量允许至少4096\ntable_cache = 2048\n# 允许外部文件级别的锁. 打开文件锁会对性能造成负面影响\n# 所以只有在你在同样的文件上运行多个数据库实例时才使用此选项(注意仍会有其他约束!)\n# 或者你在文件层面上使用了其他一些软件依赖来锁定MyISAM表\n#external-locking\n# 服务所能处理的请求包的最大大小以及服务所能处理的最大的请求大小(当与大的BLOB字段一起工作时相当必要)\n# 每个连接独立的大小.大小动态增加\nmax_allowed_packet = 16M\n# 在一个事务中binlog为了记录SQL状态所持有的cache大小\n# 如果你经常使用大的,多声明的事务,你可以增加此值来获取更大的性能.\n# 所有从事务来的状态都将被缓冲在binlog缓冲中然后在提交后一次性写入到binlog中\n# 如果事务比此值大, 会使用磁盘上的临时文件来替代.\n# 此缓冲在每个连接的事务第一次更新状态时被创建\nbinlog_cache_size = 1M\n# 独立的内存表所允许的最大容量.\n# 此选项为了防止意外创建一个超大的内存表导致永尽所有的内存资源.\nmax_heap_table_size = 64M\n# 排序缓冲被用来处理类似ORDER BY以及GROUP BY队列所引起的排序\n# 如果排序后的数据无法放入排序缓冲,\n# 一个用来替代的基于磁盘的合并分类会被使用\n# 查看 “Sort_merge_passes” 状态变量.\n# 在排序发生时由每个线程分配\nsort_buffer_size = 8M\n# 此缓冲被使用来优化全联合(full JOINs 不带索引的联合).\n# 类似的联合在极大多数情况下有非常糟糕的性能表现,\n# 但是将此值设大能够减轻性能影响.\n# 通过 “Select_full_join” 状态变量查看全联合的数量\n# 当全联合发生时,在每个线程中分配\njoin_buffer_size = 8M\n# 我们在cache中保留多少线程用于重用\n# 当一个客户端断开连接后,如果cache中的线程还少于thread_cache_size,\n# 则客户端线程被放入cache中.\n# 这可以在你需要大量新连接的时候极大的减少线程创建的开销\n# (一般来说如果你有好的线程模型的话,这不会有明显的性能提升.)\nthread_cache_size = 8\n# 此允许应用程序给予线程系统一个提示在同一时间给予渴望被运行的线程的数量.\n# 此值只对于支持 thread_concurrency() 函数的系统有意义( 例如Sun Solaris).\n# 你可可以尝试使用 [CPU数量]*(2..4) 来作为thread_concurrency的值\nthread_concurrency = 8\n# 查询缓冲常被用来缓冲 SELECT 的结果并且在下一次同样查询的时候不再执行直接返回结果.\n# 打开查询缓冲可以极大的提高服务器速度, 如果你有大量的相同的查询并且很少修改表.\n# 查看 “Qcache_lowmem_prunes” 状态变量来检查是否当前值对于你的负载来说是否足够高.\n# 注意: 在你表经常变化的情况下或者如果你的查询原文每次都不同,\n# 查询缓冲也许引起性能下降而不是性能提升.\nquery_cache_size = 64M\n# 只有小于此设定值的结果才会被缓冲\n# 此设置用来保护查询缓冲,防止一个极大的结果集将其他所有的查询结果都覆盖.\nquery_cache_limit = 2M\n# 被全文检索索引的最小的字长.\n# 你也许希望减少它,如果你需要搜索更短字的时候.\n# 注意在你修改此值之后,\n# 你需要重建你的 FULLTEXT 索引\nft_min_word_len = 4\n# 如果你的系统支持 memlock() 函数,你也许希望打开此选项用以让运行中的mysql在在内存高度紧张的时候,数据在内存中保持锁定并且防止可能被swapping out\n# 此选项对于性能有益\n#memlock\n# 当创建新表时作为默认使用的表类型,\n# 如果在创建表示没有特别执行表类型,将会使用此值\ndefault_table_type = MYISAM\n# 线程使用的堆大小. 此容量的内存在每次连接时被预留.\n# MySQL 本身常不会需要超过64K的内存\n# 如果你使用你自己的需要大量堆的UDF函数\n# 或者你的操作系统对于某些操作需要更多的堆,\n# 你也许需要将其设置的更高一点.\nthread_stack = 192K\n# 设定默认的事务隔离级别.可用的级别如下:\n# READ-UNCOMMITTED, READ-COMMITTED, REPEATABLE-READ, SERIALIZABLE\ntransaction_isolation = REPEATABLE-READ\n# 内部(内存中)临时表的最大大小\n# 如果一个表增长到比此值更大,将会自动转换为基于磁盘的表.\n# 此限制是针对单个表的,而不是总和.\ntmp_table_size = 64M\n# 打开二进制日志功能.\n# 在复制(replication)配置中,作为MASTER主服务器必须打开此项\n# 如果你需要从你最后的备份中做基于时间点的恢复,你也同样需要二进制日志.\nlog-bin=mysql-bin\n# 如果你在使用链式从服务器结构的复制模式 (A->B->C),\n# 你需要在服务器B上打开此项.\n# 此选项打开在从线程上重做过的更新的日志,\n# 并将其写入从服务器的二进制日志.\n#log_slave_updates\n# 打开全查询日志. 所有的由服务器接收到的查询 (甚至对于一个错误语法的查询)\n# 都会被记录下来. 这对于调试非常有用, 在生产环境中常常关闭此项.\n#log\n# 将警告打印输出到错误log文件. 如果你对于MySQL有任何问题\n# 你应该打开警告log并且仔细审查错误日志,查出可能的原因.\n#log_warnings\n# 记录慢速查询. 慢速查询是指消耗了比 “long_query_time” 定义的更多时间的查询.\n# 如果 log_long_format 被打开,那些没有使用索引的查询也会被记录.\n# 如果你经常增加新查询到已有的系统内的话. 一般来说这是一个好主意,\nlog_slow_queries\n# 所有的使用了比这个时间(以秒为单位)更多的查询会被认为是慢速查询.\n# 不要在这里使用”1″, 否则会导致所有的查询,甚至非常快的查询页被记录下来(由于MySQL 目前时间的精确度只能达到秒的级别).\nlong_query_time = 2\n# 在慢速日志中记录更多的信息.\n# 一般此项最好打开.\n# 打开此项会记录使得那些没有使用索引的查询也被作为到慢速查询附加到慢速日志里\nlog_long_format\n# 此目录被MySQL用来保存临时文件.例如,\n# 它被用来处理基于磁盘的大型排序,和内部排序一样.\n# 以及简单的临时表.\n# 如果你不创建非常大的临时文件,将其放置到 swapfs/tmpfs 文件系统上也许比较好\n# 另一种选择是你也可以将其放置在独立的磁盘上.\n# 你可以使用”;”来放置多个路径\n# 他们会按照roud-robin方法被轮询使用.\n#tmpdir = /tmp\n\n# *** 复制有关的设置\n\n# 唯一的服务辨识号,数值位于 1 到 2^32-1之间.\n# 此值在master和slave上都需要设置.\n# 如果 “master-host” 没有被设置,则默认为1, 但是如果忽略此选项,MySQL不会作为master生效.\nserver-id = 1\n# 复制的Slave (去掉master段的注释来使其生效)\n#\n# 为了配置此主机作为复制的slave服务器,你可以选择两种方法:\n#\n# 1) 使用 CHANGE MASTER TO 命令 (在我们的手册中有完整描述) -\n# 语法如下:\n#\n# CHANGE MASTER TO MASTER_HOST=, MASTER_PORT=\n,\n# MASTER_USER=, MASTER_PASSWORD=\n;\n#\n# 你需要替换掉 , ,\n等被尖括号包围的字段以及使用master的端口号替换\n(默认3306).\n#\n# 例子:\n#\n# CHANGE MASTER TO MASTER_HOST=’125.564.12.1′, MASTER_PORT=3306,\n# MASTER_USER=’joe’, MASTER_PASSWORD=’secret’;\n#\n# 或者\n#\n# 2) 设置以下的变量. 不论如何, 在你选择这种方法的情况下, 然后第一次启动复制(甚至不成功的情况下,\n# 例如如果你输入错密码在master-password字段并且slave无法连接),\n# slave会创建一个 master.info 文件,并且之后任何对于包含在此文件内的参数的变化都会被忽略\n# 并且由 master.info 文件内的内容覆盖, 除非你关闭slave服务, 删除 master.info 并且重启slave 服务.\n# 由于这个原因,你也许不想碰一下的配置(注释掉的) 并且使用 CHANGE MASTER TO (查看上面) 来代替\n#\n# 所需要的唯一id号位于 2 和 2^32 – 1之间\n# (并且和master不同)\n# 如果master-host被设置了.则默认值是2\n# 但是如果省略,则不会生效\n#server-id = 2\n#\n# 复制结构中的master – 必须\n#master-host =\n#\n# 当连接到master上时slave所用来认证的用户名 – 必须\n#master-user =\n#\n# 当连接到master上时slave所用来认证的密码 – 必须\n#master-password =\n#\n# master监听的端口.\n# 可选 – 默认是3306\n#master-port =\n# 使得slave只读.只有用户拥有SUPER权限和在上面的slave线程能够修改数据.\n# 你可以使用此项去保证没有应用程序会意外的修改slave而不是master上的数据\n#read_only\n\n#*** MyISAM 相关选项\n\n# 关键词缓冲的大小, 一般用来缓冲MyISAM表的索引块.\n# 不要将其设置大于你可用内存的30%,\n# 因为一部分内存同样被OS用来缓冲行数据\n# 甚至在你并不使用MyISAM 表的情况下, 你也需要仍旧设置起 8-64M 内存由于它同样会被内部临时磁盘表使用.\nkey_buffer_size = 32M\n# 用来做MyISAM表全表扫描的缓冲大小.\n# 当全表扫描需要时,在对应线程中分配.\nread_buffer_size = 2M\n# 当在排序之后,从一个已经排序好的序列中读取行时,行数据将从这个缓冲中读取来防止磁盘寻道.\n# 如果你增高此值,可以提高很多ORDER BY的性能.\n# 当需要时由每个线程分配\nread_rnd_buffer_size = 16M\n# MyISAM 使用特殊的类似树的cache来使得突发插入\n# (这些插入是,INSERT SELECT, INSERT VALUES (), (), , 以及 LOAD DATA\n# INFILE) 更快. 此变量限制每个进程中缓冲树的字节数.\n# 设置为 0 会关闭此优化.\n# 为了最优化不要将此值设置大于 “key_buffer_size”.\n# 当突发插入被检测到时此缓冲将被分配.\nbulk_insert_buffer_size = 64M\n# 此缓冲当MySQL需要在 REPAIR, OPTIMIZE, ALTER 以及 LOAD DATA INFILE 到一个空表中引起重建索引时被分配.\n# 这在每个线程中被分配.所以在设置大值时需要小心.\nmyisam_sort_buffer_size = 128M\n# MySQL重建索引时所允许的最大临时文件的大小 (当 REPAIR, ALTER TABLE 或者 LOAD DATA INFILE).\n# 如果文件大小比此值更大,索引会通过键值缓冲创建(更慢)\nmyisam_max_sort_file_size = 10G\n# 如果被用来更快的索引创建索引所使用临时文件大于制定的值,那就使用键值缓冲方法.\n# 这主要用来强制在大表中长字串键去使用慢速的键值缓冲方法来创建索引.\nmyisam_max_extra_sort_file_size = 10G\n# 如果一个表拥有超过一个索引, MyISAM 可以通过并行排序使用超过一个线程去修复他们.\n# 这对于拥有多个CPU以及大量内存情况的用户,是一个很好的选择.\nmyisam_repair_threads = 1\n# 自动检查和修复没有适当关闭的 MyISAM 表.\nmyisam_recover\n\n# 默认关闭 Federated\nskip-federated\n# *** BDB 相关选项 ***\n# 如果你运行的MySQL服务有BDB支持但是你不准备使用的时候使用此选项. 这会节省内存并且可能加速一些事.\nskip-bdb\n\n# *** INNODB 相关选项 ***\n# 如果你的MySQL服务包含InnoDB支持但是并不打算使用的话,\n# 使用此选项会节省内存以及磁盘空间,并且加速某些部分\n#skip-innodb\n# 附加的内存池被InnoDB用来保存 metadata 信息\n# 如果InnoDB为此目的需要更多的内存,它会开始从OS这里申请内存.\n# 由于这个操作在大多数现代操作系统上已经足够快, 你一般不需要修改此值.\n# SHOW INNODB STATUS 命令会显示当先使用的数量.\ninnodb_additional_mem_pool_size = 16M\n# InnoDB使用一个缓冲池来保存索引和原始数据, 不像 MyISAM.\n# 这里你设置越大,你在存取表里面数据时所需要的磁盘I/O越少.\n# 在一个独立使用的数据库服务器上,你可以设置这个变量到服务器物理内存大小的80%\n# 不要设置过大,否则,由于物理内存的竞争可能导致操作系统的换页颠簸.\n# 注意在32位系统上你每个进程可能被限制在 2-3.5G 用户层面内存限制,\n# 所以不要设置的太高.\ninnodb_buffer_pool_size = 2G\n# InnoDB 将数据保存在一个或者多个数据文件中成为表空间.\n# 如果你只有单个逻辑驱动保存你的数据,一个单个的自增文件就足够好了.\n# 其他情况下.每个设备一个文件一般都是个好的选择.\n# 你也可以配置InnoDB来使用裸盘分区 – 请参考手册来获取更多相关内容\ninnodb_data_file_path = ibdata1:10M:autoextend\n# 设置此选项如果你希望InnoDB表空间文件被保存在其他分区.\n# 默认保存在MySQL的datadir中.\n#innodb_data_home_dir =\n# 用来同步IO操作的IO线程的数量. This value is\n# 此值在Unix下被硬编码为4,但是在Windows磁盘I/O可能在一个大数值下表现的更好.\ninnodb_file_io_threads = 4\n# 如果你发现InnoDB表空间损坏, 设置此值为一个非零值可能帮助你导出你的表.\n# 从1开始并且增加此值知道你能够成功的导出表.\n#innodb_force_recovery=1\n# 在InnoDb核心内的允许线程数量.\n# 最优值依赖于应用程序,硬件以及操作系统的调度方式.\n# 过高的值可能导致线程的互斥颠簸.\ninnodb_thread_concurrency = 16\n# 如果设置为1 ,InnoDB会在每次提交后刷新(fsync)事务日志到磁盘上,\n# 这提供了完整的ACID行为.\n# 如果你愿意对事务安全折衷, 并且你正在运行一个小的食物, 你可以设置此值到0或者2来减少由事务日志引起的磁盘I/O\n# 0代表日志只大约每秒写入日志文件并且日志文件刷新到磁盘.\n# 2代表日志写入日志文件在每次提交后,但是日志文件只有大约每秒才会刷新到磁盘上.\ninnodb_flush_log_at_trx_commit = 1\n# 加速InnoDB的关闭. 这会阻止InnoDB在关闭时做全清除以及插入缓冲合并.\n# 这可能极大增加关机时间, 但是取而代之的是InnoDB可能在下次启动时做这些操作.\n#innodb_fast_shutdown\n# 用来缓冲日志数据的缓冲区的大小.\n# 当此值快满时, InnoDB将必须刷新数据到磁盘上.\n# 由于基本上每秒都会刷新一次,所以没有必要将此值设置的太大(甚至对于长事务而言)\ninnodb_log_buffer_size = 8M\n# 在日志组中每个日志文件的大小.\n# 你应该设置日志文件总合大小到你缓冲池大小的25%~100%\n# 来避免在日志文件覆写上不必要的缓冲池刷新行为.\n# 不论如何, 请注意一个大的日志文件大小会增加恢复进程所需要的时间.\ninnodb_log_file_size = 256M\n# 在日志组中的文件总数.\n# 通常来说2~3是比较好的.\ninnodb_log_files_in_group = 3\n# InnoDB的日志文件所在位置. 默认是MySQL的datadir.\n# 你可以将其指定到一个独立的硬盘上或者一个RAID1卷上来提高其性能\n#innodb_log_group_home_dir\n# 在InnoDB缓冲池中最大允许的脏页面的比例.\n# 如果达到限额, InnoDB会开始刷新他们防止他们妨碍到干净数据页面.\n# 这是一个软限制,不被保证绝对执行.\ninnodb_max_dirty_pages_pct = 90\n# InnoDB用来刷新日志的方法.\n# 表空间总是使用双重写入刷新方法\n# 默认值是 “fdatasync”, 另一个是 “O_DSYNC”.\n#innodb_flush_method=O_DSYNC\n# 在被回滚前,一个InnoDB的事务应该等待一个锁被批准多久.\n# InnoDB在其拥有的锁表中自动检测事务死锁并且回滚事务.\n# 如果你使用 LOCK TABLES 指令, 或者在同样事务中使用除了InnoDB以外的其他事务安全的存储引擎\n# 那么一个死锁可能发生而InnoDB无法注意到.\n# 这种情况下这个timeout值对于解决这种问题就非常有帮助.\ninnodb_lock_wait_timeout = 120\n\n[mysqldump]\n# 不要在将内存中的整个结果写入磁盘之前缓存. 在导出非常巨大的表时需要此项\nquick\nmax_allowed_packet = 16M\n[mysql]\nno-auto-rehash\n# 仅仅允许使用键值的 UPDATEs 和 DELETEs .\n#safe-updates\n[isamchk]\nkey_buffer = 512M\nsort_buffer_size = 512M\nread_buffer = 8M\nwrite_buffer = 8M\n[myisamchk]\nkey_buffer = 512M\nsort_buffer_size = 512M\nread_buffer = 8M\nwrite_buffer = 8M\n[mysqlhotcopy]\ninteractive-timeout\n[mysqld_safe]\n# 增加每个进程的可打开文件数量.\n# 警告: 确认你已经将全系统限制设定的足够高!\n# 打开大量表需要将此值设b\nopen-files-limit = 8192\n======================================================================\n\nMysql参数优化对于新手来讲，是比较难懂的东西，其实这个参数优化，是个很复杂的东西，对于不同的网站，及其在线量，访问量，帖子数量，网络情况，以及机器硬件配置都有关系，优化不可能一次性完成，需要不断的观察以及调试，才有可能得到最佳效果。\n下面先说我的服务器的硬件以及论坛情况，\nCPU: 2颗四核Intel Xeon 2.00GHz\n内存: 4GB DDR\n硬盘: SCSI 146GB\n论坛：在线会员 一般在 5000 人左右 – 最高记录是 13264.\n下面，我们根据以上硬件配置结合一份已经做过一次优化的my.cnf进行分析说明：有些参数可能还得根据论坛的变化情况以及程序员的程序进行再调整。\n[mysqld]\nport = 3306\nserverid = 1\nsocket = /tmp/mysql.sock\nskip-locking # 避免MySQL的外部锁定，减少出错几率增强稳定性。 skip-name-resolve\n禁止MySQL对外部连接进行DNS解析，使用这一选项可以消除MySQL进行DNS解析的时间。但需要注意，如果开启该选项，则所有远程主机连接授权都要使用IP地址方式，否则MySQL将无法正常处理连接请求!\nback_log = 500\n要求 MySQL 能有的连接数量。当主要MySQL线程在一个很短时间内得到非常多的连接请求，这就起作用，然后主线程花些时间(尽管很短)检查连接并且启动一个新线程。\nback_log值指出在MySQL暂时停止回答新请求之前的短时间内多少个请求可以被存在堆栈中。只有如果期望在一个短时间内有很多连接，你需要增加它，换句话说，这值对到来的TCP/IP连接的侦听队列的大小。你的操作系统在这个队列大小上有它自己的限制。试图设定back_log高于你的操作系统的限制将是无效的。当你观察你的主机进程列表，发现大量 264084 | unauthenticated user | xxx.xxx.xxx.xxx | NULL | Connect | NULL | login | NULL 的待连接进程时，就要加大 back_log 的值了。默认数值是50，我把它改为500。\nkey_buffer_size = 384M\n# key_buffer_size指定用于索引的缓冲区大小，增加它可得到更好处理的索引(对所有读和多重写)，到你能负担得起那样多。如果你使它太大，系统将开始换页并且真的变慢了。对于内存在4GB左右的服务器该参数可设置为384M或512M。通过检查状态值Key_read_requests和 Key_reads,可以知道key_buffer_size设置是否合理。比例key_reads / key_read_requests应该尽可能的低，至少是1:100，1:1000更好(上述状态值可以使用SHOW STATUS LIKE ‘key_read%’获得)。注意：该参数值设置的过大反而会是服务器整体效率降低!\nmax_allowed_packet = 32M\n增加该变量的值十分安全，这是因为仅当需要时才会分配额外内存。例如，仅当你发出长查询或mysqld必须返回大的结果行时mysqld才会分配更多内存。该变量之所以取较小默认值是一种预防措施，以捕获客户端和服务器之间的错误信息包，并确保不会因偶然使用大的信息包而导致内存溢出。\ntable_cache = 512\ntable_cache指定表高速缓存的大小。每当MySQL访问一个表时，如果在表缓冲区中还有空间，该表就被打开并放入其中，这样可以更快地访问表内容。通过检查峰值时间的状态值Open_tables和Opened_tables，可以决定是否需要增加table_cache的值。如果你发现 open_tables等于table_cache，并且opened_tables在不断增长，那么你就需要增加table_cache的值了(上述状态值可以使用SHOW STATUS LIKE ‘Open%tables’获得)。注意，不能盲目地把table_cache设置成很大的值。如果设置得太高，可能会造成文件描述符不足，从而造成性能不稳定或者连接失败。\nsort_buffer_size = 4M\n查询排序时所能使用的缓冲区大小。注意：该参数对应的分配内存是每连接独占!如果有100个连接，那么实际分配的总共排序缓冲区大小为100 × 4 = 400MB。所以，对于内存在4GB左右的服务器推荐设置为4-8M。\nread_buffer_size = 4M\n读查询操作所能使用的缓冲区大小。和sort_buffer_size一样，该参数对应的分配内存也是每连接独享!\njoin_buffer_size = 8M\n联合查询操作所能使用的缓冲区大小，和sort_buffer_size一样，该参数对应的分配内存也是每连接独享!\nmyisam_sort_buffer_size = 64M\nMyISAM表发生变化时重新排序所需的缓冲\nquery_cache_size = 64M\n指定MySQL查询缓冲区的大小。可以通过在MySQL控制台执行以下命令观察：\n# > SHOW VARIABLES LIKE ‘%query_cache%’; # > SHOW STATUS LIKE ‘Qcache%’; # 如果Qcache_lowmem_prunes的值非常大，则表明经常出现缓冲不够的情况;\n如果Qcache_hits的值非常大，则表明查询缓冲使用非常频繁，如果该值较小反而会影响效率，那么可以考虑不用查询缓冲;Qcache_free_blocks，如果该值非常大，则表明缓冲区中碎片很多。\nthread_cache_size = 64\n可以复用的保存在中的线程的数量。如果有，新的线程从缓存中取得，当断开连接的时候如果有空间，客户的线置在缓存中。如果有很多新的线程，为了提高性能可以这个变量值。通过比较 Connections 和 Threads_created 状态的变量，可以看到这个变量的作用\ntmp_table_size = 256M\nmax_connections = 1000\n指定MySQL允许的最大连接进程数。如果在访问论坛时经常出现Too Many Connections的错误提示，则需要增大该参数值。\nmax_connect_errors = 10000000\n对于同一主机，如果有超出该参数值个数的中断错误连接，则该主机将被禁止连接。如需对该主机进行解禁，执行：FLUSH HOST;。\nwait_timeout = 10\n指定一个请求的最大连接时间，对于4GB左右内存的服务器可以设置为5-10。\nthread_concurrency = 8\n该参数取值为服务器逻辑CPU数量×2，在本例中，服务器有2颗物理CPU，而每颗物理CPU又支持H.T超线程，所以实际取值为4 × 2 = 8\nskip-networking\n开启该选项可以彻底关闭MySQL的TCP/IP连接方式，如果WEB服务器是以远程连接的方式访问MySQL数据库服务器则不要开启该选项!否则将无法正常连接!\nlong_query_time = 10\nlog-slow-queries =\nlog-queries-not-using-indexes\n开启慢查询日志( slow query log )\n慢查询日志对于跟踪有问题的查询非常有用。它记录所有查过long_query_time的查询，如果需要，还可以记录不使用索引的记录。下面是一个慢查询日志的例子：\n开启慢查询日志，需要设置参数log_slow_queries、long_query_times、log-queries-not-using-indexes。\nlog_slow_queries指定日志文件，如果不提供文件名，MySQL将自己产生缺省文件名。long_query_times指定慢查询的阈值，缺省是10秒。log-queries-not-using-indexes是4.1.0以后引入的参数，它指示记录不使用索引的查询。设置 long_query_time=10\n外附上使用show status命令查看mysql状态相关的值及其含义：\n使用show status命令\n含义如下:\naborted_clients 客户端非法中断连接次数\naborted_connects 连接mysql失败次数\ncom_xxx xxx命令执行次数,有很多条\nconnections 连接mysql的数量\nCreated_tmp_disk_tables 在磁盘上创建的临时表\nCreated_tmp_tables 在内存里创建的临时表\nCreated_tmp_files 临时文件数\nKey_read_requests The number of requests to read a key block from the cache\nKey_reads The number of physical reads of a key block from disk\nMax_used_connections 同时使用的连接数\nOpen_tables 开放的表\nOpen_files 开放的文件\nOpened_tables 打开的表\nQuestions 提交到server的查询数\nSort_merge_passes 如果这个值很大,应该增加my.cnf中的sort_buffer值\nUptime 服务器已经工作的秒数\n提升性能的建议:\n1.如果opened_tables太大,应该把my.cnf中的table_cache变大\n\n2.如果Key_reads太大,则应该把my.cnf中key_buffer_size变大.可以用Key_reads/Key_read_requests计算出cache失败率\n\n3.如果Handler_read_rnd太大,则你写的SQL语句里很多查询都是要扫描整个表,而没有发挥索引的键的作用\n\n4.如果Threads_created太大,就要增加my.cnf中thread_cache_size的值.可以用Threads_created/Connections计算cache命中率\n\n5.如果Created_tmp_disk_tables太大,就要增加my.cnf中tmp_table_size的值,用基于内存的临时表代替基于磁盘的\n\n===================================================================\n存储引擎是什么？MySQL中的数据用各种不同的技术存储在文件(或者内 正确的编译方法固然重要，但它只是提高MySQL服务器性能工作的一部分。MySQL服务器的许多参数会影响服务器的性能表现，而且我们可以把这些参数保存到配置文件，使得每次MySQL服务器启动时这些参数都自动发挥作用。这个配置文件就是my.cnf。\nMySQL服务器提供了my.cnf文件的几个示例，它们可以在/usr/local/mysql/share/mysql/目录下找到，名字分别为 my-small.cnf、my-medium.cnf、my-large.cnf以及my-huge.cnf。文件名字中关于规模的说明描述了该配置文件适用的系统类型。例如，如果运行MySQL服务器的系统内存不多，而且MySQL只是偶尔使用，那么使用my-small.cnf配置文件最为理想，这个配置文件告诉mysqld daemon使用最少的系统资源。反之，如果MySQL服务器用于支持一个大规模的在线商场，系统拥有2G的内存，那么使用mysql-huge.cnf 最为合适。\n要使用上述示例配置文件，我们应该先复制一个最适合要求的配置文件，并把它命名为my.cnf。这个复制得到的配置文件可以按照如下三种方式使用：\n全局：把这个my.cnf文件复制到服务器的/etc目录，此时文件中所定义的参数将全局有效，即对该服务器上运行的所有MySQL数据库服务器都有效。\n局部：把这个my.cnf文件复制到[MYSQL-INSTALL-DIR]/var/将使该文件只对指定的服务器有效，其中[MYSQL-INSTALL-DIR]表示安装MySQL的目录。\n用户：最后，我们还可以把该文件的作用范围局限到指定的用户，这只需把my.cnf文件复制到用户的根目录即可。\n那么，如何设置my.cnf文件中的参数呢？或者进一步说，哪些参数是我们可以设置的呢？所有这些参数都对MySQL服务器有着全局性的影响，但同时每一个参数都和MySQL的特定部分关系较为密切。例如，max_connections参数属于mysqld一类。那么，如何才能得知这一点呢？这只需执行如下命令：\n\n% >/usr/local/mysql/libexec/mysqld –help\n该命令将显示出和mysqld有关的各种选项和参数。要寻找这些参数非常方便，因为这些参数都在“Possible variables for option –set-variable (-O) are”这行内容的后面。找到这些参数之后，我们就可以在my.cnf文件中按照如下方式设置所有这些参数：\n\nset-variable = max_connections=100\n\n这行代码的效果是：同时连接MySQL服务器的最大连接数量限制为100。不要忘了在my.cnf文件[mysqld]小节加上一个set-variable指令，具体请参见配置文件中的示例。','MySQL配置文件my.cnf中文详解','','inherit','open','open','','187-revision','','','2010-09-23 17:50:19','2010-09-24 01:50:19','',187,'http://www.wordpress.cn/?p=188',0,'revision','',0),(225,1,'2010-10-25 17:41:39','2010-10-26 01:41:39','大数据量的问题是很多面试笔试中经常出现的问题，比如baidu google 腾讯 这样的一些涉及到海量数据的公司经常会问到。 \r\n\r\n下面的方法是我对海量数据的处理方法进行了一个一般性的总结，当然这些方法可能并不能完全覆盖所有的问题，但是这样的一些方法也基本可以处理绝大多数遇到的问题。下面的一些问题基本直接来源于公司的面试笔试题目，方法不一定最优，如果你有更好的处理方法，欢迎与我讨论。 \r\n\r\n1.Bloom filter \r\n\r\n适用范围：可以用来实现数据字典，进行数据的判重，或者集合求交集 \r\n\r\n基本原理及要点： \r\n对于原理来说很简单，位数组+k个独立hash函数。将hash函数对应的值的位数组置1，查找时如果发现所有hash函数对应位都是1说明存在，很明显这个过程并不保证查找的结果是100%正确的。同时也不支持删除一个已经插入的关键字，因为该关键字对应的位会牵动到其他的关键字。所以一个简单的改进就是 counting Bloom filter，用一个counter数组代替位数组，就可以支持删除了。 \r\n\r\n还有一个比较重要的问题，如何根据输入元素个数n，确定位数组m的大小及hash函数个数。当hash函数个数k=(ln2)*(m/n)时错误率最小。在错误率不大于E的情况下，m至少要等于n*lg(1/E)才能表示任意n个元素的集合。但m还应该更大些，因为还要保证bit数组里至少一半为0，则m应该>=nlg(1/E)*lge 大概就是nlg(1/E)1.44倍(lg表示以2为底的对数)。 \r\n\r\n举个例子我们假设错误率为0.01，则此时m应大概是n的13倍。这样k大概是8个。 \r\n\r\n注意这里m与n的单位不同，m是bit为单位，而n则是以元素个数为单位(准确的说是不同元素的个数)。通常单个元素的长度都是有很多bit的。所以使用bloom filter内存上通常都是节省的。 \r\n\r\n扩展： \r\nBloom filter将集合中的元素映射到位数组中，用k（k为哈希函数个数）个映射位是否全1表示元素在不在这个集合中。Counting bloom filter（CBF）将位数组中的每一位扩展为一个counter，从而支持了元素的删除操作。Spectral Bloom Filter（SBF）将其与集合元素的出现次数关联。SBF采用counter中的最小值来近似表示元素的出现频率。 \r\n\r\n问题实例：给你A,B两个文件，各存放50亿条URL，每条URL占用64字节，内存限制是4G，让你找出A,B文件共同的URL。如果是三个乃至n个文件呢？ \r\n\r\n根据这个问题我们来计算下内存的占用，4G=2^32大概是40亿*8大概是340亿，n=50亿，如果按出错率0.01算需要的大概是650亿个bit。现在可用的是340亿，相差并不多，这样可能会使出错率上升些。另外如果这些urlip是一一对应的，就可以转换成ip，则大大简单了。 \r\n\r\n2.Hashing \r\n\r\n适用范围：快速查找，删除的基本数据结构，通常需要总数据量可以放入内存 \r\n\r\n基本原理及要点： \r\nhash函数选择，针对字符串，整数，排列，具体相应的hash方法。 \r\n碰撞处理，一种是open hashing，也称为拉链法；另一种就是closed hashing，也称开地址法，opened addressing。 \r\n\r\n扩展： \r\nd-left hashing中的d是多个的意思，我们先简化这个问题，看一看2-left hashing。2-left hashing指的是将一个哈希表分成长度相等的两半，分别叫做T1和T2，给T1和T2分别配备一个哈希函数，h1和h2。在存储一个新的key时，同时用两个哈希函数进行计算，得出两个地址h1[key]和h2[key]。这时需要检查T1中的h1[key]位置和T2中的h2[key]位置，哪一个位置已经存储的（有碰撞的）key比较多，然后将新key存储在负载少的位置。如果两边一样多，比如两个位置都为空或者都存储了一个key，就把新key 存储在左边的T1子表中，2-left也由此而来。在查找一个key时，必须进行两次hash，同时查找两个位置。 \r\n\r\n问题实例： \r\n1).海量日志数据，提取出某日访问百度次数最多的那个IP。 \r\n\r\nIP的数目还是有限的，最多2^32个，所以可以考虑使用hash将ip直接存入内存，然后进行统计。 \r\n\r\n3.bit-map \r\n\r\n适用范围：可进行数据的快速查找，判重，删除，一般来说数据范围是int的10倍以下 \r\n\r\n基本原理及要点：使用bit数组来表示某些元素是否存在，比如8位电话号码 \r\n\r\n扩展：bloom filter可以看做是对bit-map的扩展 \r\n\r\n问题实例： \r\n\r\n1)已知某个文件内包含一些电话号码，每个号码为8位数字，统计不同号码的个数。 \r\n\r\n8位最多99 999 999，大概需要99m个bit，大概10几m字节的内存即可。 \r\n\r\n2)2.5亿个整数中找出不重复的整数的个数，内存空间不足以容纳这2.5亿个整数。 \r\n\r\n将bit-map扩展一下，用2bit表示一个数即可，0表示未出现，1表示出现一次，2表示出现2次及以上。或者我们不用2bit来进行表示，我们用两个bit-map即可模拟实现这个2bit-map。 \r\n\r\n4.堆 \r\n\r\n适用范围：海量数据前n大，并且n比较小，堆可以放入内存 \r\n\r\n基本原理及要点：最大堆求前n小，最小堆求前n大。方法，比如求前n小，我们比较当前元素与最大堆里的最大元素，如果它小于最大元素，则应该替换那个最大元素。这样最后得到的n个元素就是最小的n个。适合大数据量，求前n小，n的大小比较小的情况，这样可以扫描一遍即可得到所有的前n元素，效率很高。 \r\n\r\n扩展：双堆，一个最大堆与一个最小堆结合，可以用来维护中位数。 \r\n\r\n问题实例： \r\n1)100w个数中找最大的前100个数。 \r\n\r\n用一个100个元素大小的最小堆即可。 \r\n\r\n5.双层桶划分 \r\n\r\n适用范围：第k大，中位数，不重复或重复的数字 \r\n\r\n基本原理及要点：因为元素范围很大，不能利用直接寻址表，所以通过多次划分，逐步确定范围，然后最后在一个可以接受的范围内进行。可以通过多次缩小，双层只是一个例子。 \r\n\r\n扩展： \r\n\r\n问题实例： \r\n1).2.5亿个整数中找出不重复的整数的个数，内存空间不足以容纳这2.5亿个整数。 \r\n\r\n有点像鸽巢原理，整数个数为2^32,也就是，我们可以将这2^32个数，划分为2^8个区域(比如用单个文件代表一个区域)，然后将数据分离到不同的区域，然后不同的区域在利用bitmap就可以直接解决了。也就是说只要有足够的磁盘空间，就可以很方便的解决。 \r\n\r\n2).5亿个int找它们的中位数。 \r\n\r\n这个例子比上面那个更明显。首先我们将int划分为2^16个区域，然后读取数据统计落到各个区域里的数的个数，之后我们根据统计结果就可以判断中位数落到那个区域，同时知道这个区域中的第几大数刚好是中位数。然后第二次扫描我们只统计落在这个区域中的那些数就可以了。 \r\n\r\n实际上，如果不是int是int64，我们可以经过3次这样的划分即可降低到可以接受的程度。即可以先将int64分成2^24个区域，然后确定区域的第几大数，在将该区域分成2^20个子区域，然后确定是子区域的第几大数，然后子区域里的数的个数只有2^20，就可以直接利用direct addr table进行统计了。 \r\n\r\n6.数据库索引 \r\n\r\n适用范围：大数据量的增删改查 \r\n\r\n基本原理及要点：利用数据的设计实现方法，对海量数据的增删改查进行处理。 \r\n扩展： \r\n问题实例： \r\n\r\n\r\n7.倒排索引(Inverted index) \r\n\r\n适用范围：搜索引擎，关键字查询 \r\n\r\n基本原理及要点：为何叫倒排索引？一种索引方法，被用来存储在全文搜索下某个单词在一个文档或者一组文档中的存储位置的映射。 \r\n\r\n以英文为例，下面是要被索引的文本： \r\nT0 = \"it is what it is\" \r\nT1 = \"what is it\" \r\nT2 = \"it is a banana\" \r\n我们就能得到下面的反向文件索引： \r\n\"a\":      {2} \r\n\"banana\": {2} \r\n\"is\":     {0, 1, 2} \r\n\"it\":     {0, 1, 2} \r\n\"what\":   {0, 1} \r\n检索的条件\"what\", \"is\" 和 \"it\" 将对应集合的交集。 \r\n\r\n正向索引开发出来用来存储每个文档的单词的列表。正向索引的查询往往满足每个文档有序频繁的全文查询和每个单词在校验文档中的验证这样的查询。在正向索引中，文档占据了中心的位置，每个文档指向了一个它所包含的索引项的序列。也就是说文档指向了它包含的那些单词，而反向索引则是单词指向了包含它的文档，很容易看到这个反向的关系。 \r\n\r\n扩展： \r\n\r\n问题实例：文档检索系统，查询那些文件包含了某单词，比如常见的学术论文的关键字搜索。 \r\n\r\n8.外排序 \r\n\r\n适用范围：大数据的排序，去重 \r\n\r\n基本原理及要点：外排序的归并方法，置换选择 败者树原理，最优归并树 \r\n\r\n扩展： \r\n\r\n问题实例： \r\n1).有一个1G大小的一个文件，里面每一行是一个词，词的大小不超过16个字节，内存限制大小是1M。返回频数最高的100个词。 \r\n\r\n这个数据具有很明显的特点，词的大小为16个字节，但是内存只有1m做hash有些不够，所以可以用来排序。内存可以当输入缓冲区使用。 \r\n\r\n9.trie树 \r\n\r\n适用范围：数据量大，重复多，但是数据种类小可以放入内存 \r\n\r\n基本原理及要点：实现方式，节点孩子的表示方式 \r\n\r\n扩展：压缩实现。 \r\n\r\n问题实例： \r\n1).有10个文件，每个文件1G， 每个文件的每一行都存放的是用户的query，每个文件的query都可能重复。要你按照query的频度排序 。 \r\n\r\n2).1000万字符串，其中有些是相同的(重复),需要把重复的全部去掉，保留没有重复的字符串。请问怎么设计和实现？ \r\n\r\n3).寻找热门查询：查询串的重复度比较高，虽然总数是1千万，但如果除去重复后，不超过3百万个，每个不超过255字节。 \r\n\r\n10.分布式处理 mapreduce \r\n\r\n适用范围：数据量大，但是数据种类小可以放入内存 \r\n\r\n基本原理及要点：将数据交给不同的机器去处理，数据划分，结果归约。 \r\n\r\n扩展： \r\n\r\n问题实例： \r\n\r\n1).The canonical example application of MapReduce is a process to count the appearances of \r\n\r\neach different word in a set of documents: \r\nvoid map(String name, String document): \r\n  // name: document name \r\n  // document: document contents \r\n  for each word w in document: \r\n    EmitIntermediate(w, 1); \r\n  \r\nvoid reduce(String word, Iterator partialCounts): \r\n  // key: a word \r\n  // values: a list of aggregated partial counts \r\n  int result = 0; \r\n  for each v in partialCounts: \r\n    result += ParseInt(v); \r\n  Emit(result); \r\nHere, each document is split in words, and each word is counted initially with a \"1\" value by \r\n\r\nthe Map function, using the word as the result key. The framework puts together all the pairs \r\n\r\nwith the same key and feeds them to the same call to Reduce, thus this function just needs to \r\n\r\nsum all of its input values to find the total appearances of that word. \r\n\r\n2).海量数据分布在100台电脑中，想个办法高效统计出这批数据的TOP10。 \r\n\r\n3).一共有N个机器，每个机器上有N个数。每个机器最多存O(N)个数并对它们操作。如何找到N^2个数的中数(median)？ \r\n\r\n\r\n经典问题分析 \r\n\r\n上千万or亿数据（有重复），统计其中出现次数最多的前N个数据,分两种情况：可一次读入内存，不可一次读入。 \r\n\r\n可用思路：trie树+堆，数据库索引，划分子集分别统计，hash，分布式计算，近似统计，外排序 \r\n\r\n所谓的是否能一次读入内存，实际上应该指去除重复后的数据量。如果去重后数据可以放入内存，我们可以为数据建立字典，比如通过 map，hashmap，trie，然后直接进行统计即可。当然在更新每条数据的出现次数的时候，我们可以利用一个堆来维护出现次数最多的前N个数据，当然这样导致维护次数增加，不如完全统计后在求前N大效率高。 \r\n\r\n如果数据无法放入内存。一方面我们可以考虑上面的字典方法能否被改进以适应这种情形，可以做的改变就是将字典存放到硬盘上，而不是内存，这可以参考数据库的存储方法。 \r\n\r\n当然还有更好的方法，就是可以采用分布式计算，基本上就是map-reduce过程，首先可以根据数据值或者把数据hash(md5)后的值，将数据按照范围划分到不同的机子，最好可以让数据划分后可以一次读入内存，这样不同的机子负责处理各种的数值范围，实际上就是map。得到结果后，各个机子只需拿出各自的出现次数最多的前N个数据，然后汇总，选出所有的数据中出现次数最多的前N个数据，这实际上就是reduce过程。 \r\n\r\n实际上可能想直接将数据均分到不同的机子上进行处理，这样是无法得到正确的解的。因为一个数据可能被均分到不同的机子上，而另一个则可能完全聚集到一个机子上，同时还可能存在具有相同数目的数据。比如我们要找出现次数最多的前100个，我们将1000万的数据分布到10台机器上，找到每台出现次数最多的前 100个，归并之后这样不能保证找到真正的第100个，因为比如出现次数最多的第100个可能有1万个，但是它被分到了10台机子，这样在每台上只有1千个，假设这些机子排名在1000个之前的那些都是单独分布在一台机子上的，比如有1001个，这样本来具有1万个的这个就会被淘汰，即使我们让每台机子选出出现次数最多的1000个再归并，仍然会出错，因为可能存在大量个数为1001个的发生聚集。因此不能将数据随便均分到不同机子上，而是要根据hash 后的值将它们映射到不同的机子上处理，让不同的机器处理一个数值范围。 \r\n\r\n而外排序的方法会消耗大量的IO，效率不会很高。而上面的分布式方法，也可以用于单机版本，也就是将总的数据根据值的范围，划分成多个不同的子文件，然后逐个处理。处理完毕之后再对这些单词的及其出现频率进行一个归并。实际上就可以利用一个外排序的归并过程。 \r\n\r\n另外还可以考虑近似计算，也就是我们可以通过结合自然语言属性，只将那些真正实际中出现最多的那些词作为一个字典，使得这个规模可以放入内存。','大数据量，海量数据 处理方法总结','','publish','open','open','','%e5%a4%a7%e6%95%b0%e6%8d%ae%e9%87%8f%ef%bc%8c%e6%b5%b7%e9%87%8f%e6%95%b0%e6%8d%ae-%e5%a4%84%e7%90%86%e6%96%b9%e6%b3%95%e6%80%bb%e7%bb%93','','','2010-10-25 17:41:39','2010-10-26 01:41:39','',0,'http://www.wordpress.cn/?p=225',0,'post','',0),(190,1,'2010-09-23 22:16:37','2010-09-24 06:16:37','freebsd7.1配置apache2.2.11，使用mod_cband控制apache站点的流量。\r\n下载：mod_cband\r\nURL:http://codee.pl/download/cband/\r\n \r\n系统已经安装了apache，配置mod_cband。\r\n#tar xzvf mod-cband-0.9.7.5.tgz\r\n#cd mod-cband-0.9.7.5\r\n./configure --with-apxs=/usr/local/apache/bin/apxs    //指定一下 apxs的路径，否则可能会出错\r\n#make\r\n#make install\r\n安装成功后提示：\r\nchmod 755 /usr/local/apache/modules/mod_cband.so\r\n \r\n打开/usr/local/apache/conf/httpd.conf \r\n“LoadModule cband_module       modules/mod_cband.so”已经添加。\r\n然后在虚拟主机配置文件中，按以下方法修改：\r\n<VirtualHost *:80>\r\nServerName www.example.com\r\nServerAdmin webmaster@example.com\r\nDocumentRoot /home/www/html/example_com\r\nCBandLimit 100M     //限制虚拟主机总访问带宽为100Mb。\r\nCBandSpeed 1024 10 30       //CBandSpeed限定总的最大链接速度为1024kb,最多并发处理10个请求每秒和30个链接每秒\r\nCBandRemoteSpeed 20kb/s 5 3     // CBandRemoteSpeed限定一个用户最多能有20kb的链接速度,最多并发5个请求每秒和3个链接每秒\r\nCBandPeriod 4W   //设定多久对所记录的全局访问带宽进行重设（清零）。4W=4 weeks 4周（一个月）\r\n<Location /cband-status>\r\nSetHandler cband-status\r\n</Location>\r\n \r\n# 开启了mod_cband的实时监测功能，可以通过http://server_name/cband-status进行直观的观测。（全局监测）\r\n<Location /cband-status-me>\r\nSetHandler cband-status-me\r\n</Location>\r\n \r\n# 开启了mod_cband的实时监测功能，可以通过http://server_name/cband-status-me进行直观的观测。（单一监测）\r\n</VirtualHost>','使用mod_cband控制Apache2带宽和流量 ','','publish','open','open','','%e4%bd%bf%e7%94%a8mod_cband%e6%8e%a7%e5%88%b6apache2%e5%b8%a6%e5%ae%bd%e5%92%8c%e6%b5%81%e9%87%8f','','','2010-09-23 22:16:37','2010-09-24 06:16:37','',0,'http://www.wordpress.cn/?p=190',0,'post','',0),(191,1,'2010-09-23 22:16:20','2010-09-24 06:16:20','','使用mod_cband控制Apache2带宽和流量 ','','inherit','open','open','','190-revision','','','2010-09-23 22:16:20','2010-09-24 06:16:20','',190,'http://www.wordpress.cn/?p=191',0,'revision','',0),(192,1,'2010-09-23 22:46:53','2010-09-24 06:46:53','自从看《学技术的不能自废武功》这篇文章后，我深有感触，于是对自己以前学过的知识又重新温习了一次，结果终于发现了一些新的感悟，现将其感悟发表，于大家共勉：关于 sql 行转列的感悟\r\n<a href=\"http://www.wordpress.cn/wp-content/uploads/2010/09/1.bmp\"><img src=\"http://www.wordpress.cn/wp-content/uploads/2010/09/1.bmp\" alt=\"\" title=\"1\" class=\"alignnone size-full wp-image-193\" /></a>\r\n(表1)\r\n要将其转变为：\r\n\r\n<a href=\"http://www.wordpress.cn/wp-content/uploads/2010/09/2.bmp\"><img src=\"http://www.wordpress.cn/wp-content/uploads/2010/09/2.bmp\" alt=\"\" title=\"2\" class=\"alignnone size-full wp-image-194\" /></a> \r\n(表2)\r\n可以看得出来，表2列是由表1的行数据转化而来的，只有姓名列是表1中的列。\r\n一步一步的来嘛，其他的列难搞，如果表2只有1列姓名的话，那么very easy了，不就是一个简单的分组嘛。\r\nSelect 姓名 From 表1 Group By 姓名\r\n \r\n现在sql语句的架子搭起来了，无论以后如何变化，分组是少不了的。\r\n那么开始下一步，想这个结果集中再添加1列，多了我们不加，因为你不论是能处理语文，还是数学，还是英语列，那么其他的列只要原样照抄就可以了，顶多修改一下参数而已。\r\n从语文列开始吧，这一列的数据都可以从表1中找到，如果我们只要“语文”这一列，那么也好写。\r\nSql语句疑似：\r\nSelect 姓名,case  when 科目=\'语文\' then 成绩 end From 成绩表 Group By 姓名\r\n那就F5吧。\r\n嗯，啊，...挂了。\r\n赶紧看错误提示：\r\n服务器: 消息 8120，级别 16，状态 1，行 1\r\n列 \'成绩表.成绩\' 在选择列表中无效，因为该列既不包含在聚合函数中，也不包含在 GROUP BY 子句中。\r\n服务器: 消息 8120，级别 16，状态 1，行 1\r\n列 \'成绩表.科目\' 在选择列表中无效，因为该列既不包含在聚合函数中，也不包含在 GROUP BY 子句中。\r\n从错误信息中，可知字段成绩和科目还必须在聚合函数中或Group By中，如何在Group By中呢？\r\n\r\n  \r\n数据倒是有了，可是行多了点，看来只能从聚合入手了。\r\n到底是那个聚合？聚合有max,min,avg,sum,count，max和min首先干掉，我们不是要最大值，最小值，难道是平均值？难道是个数？难道是求和？？？\r\n那就试试呗。\r\nSelect 姓名,sum(case when 科目=\'语文\' then 成绩 end) From 表1 Group By 姓名\r\n\r\n \r\n嘿，真不错。\r\n \r\n该有的都有了，就是没列名，好办，给个别名嘛。\r\nSelect 姓名,sum(case when 科目=\'语文\' then 成绩 end) As \'语文\' From表1 Group By 姓名\r\n\r\n那么其他几列也就好办了。\r\n Select 姓名,\r\n sum(case when 科目=\'语文\' then 成绩 end) As \'语文\' ,\r\n sum(case when 科目=\'数学\' then 成绩 end) As \'数学\',\r\n sum(case when 科目=\'英语\' then 成绩 end) As \'英语\'\r\n From  表1 Group By 姓名\r\n \r\n\r\n 可是结果有NULL，那还不好办，你的case when then end没写完，还有else没写呢！\r\nSelect 姓名,\r\n sum(case when 科目=\'语文\' then 成绩 else 0 end) As \'语文\' ,\r\n sum(case when 科目=\'数学\' then 成绩 else 0 end) As \'数学\',\r\n sum(case when 科目=\'英语\' then 成绩 else 0 end) As \'英语\'\r\n From 表1 Group By 姓名\r\n \r\n\r\n \r\n终于修成正果了。\r\n \r\n真的？\r\n当然是假的？\r\n怎么假？\r\n这不废话么！你现在写的Sql语句只能支持科目表中只有语文数学英语，那万一课程中加了一个叫物理的，那你不得改程序了吗？！怎么办？凉拌。\r\n也就是说这个Sql语句中有变化的地方在红字部分：\r\nSelect 姓名,\r\n sum(case when 科目=\'语文\' then 成绩 else 0 end) As \'语文\' ,\r\n sum(case when 科目=\'数学\' then 成绩 else 0 end) As \'数学\',\r\n sum(case when 科目=\'英语\' then 成绩 else 0 end) As \'英语\'\r\n From  表1 Group By 姓名红字部分要从哪里来？如果专心看，那么应该能从中间看出来，哪些个“As \'语文\'”，哪些个“科目=\'语文\'”，其实是数据表中的值，如果仅仅要查询“语文，数学，英语”的话，那么easy啊。\r\nSelect 课程 From 表1 Group By 科目\r\n可是我们要的结果是：\r\nsum(case when 科目=\'语文\' then 成绩 else 0 end) As \'语文\' ,\r\n sum(case when 科目=\'数学\' then 成绩 else 0 end) As \'数学\',\r\n sum(case when 科目=\'英语\' then 成绩 else 0 end) As \'英语\'\r\n……………..\r\n\r\n\r\n那不简单，就是些字符串嘛。\r\nSelect \',sum(case when 科目=\'\'\'+trim(科目)+ \'\'\' then 成绩 else 0 end) As \'\'\'+科目+\'\'\'\' From 表1 Group By 课程\r\n结果为：\r\n ,sum(case when 科目=\'语文\' then 成绩 else 0 end) As \'语文\' ,\r\n ,sum(case when 科目=\'数学\' then 成绩 else 0 end) As \'数学\',\r\n ,sum(case when 科目=\'英语\' then 成绩 else 0 end) As \'英语\'\r\n\r\n \r\n哈哈，只要将这一段和刚才的Select 姓名拼在一起就可以了。\r\n现在麻烦来了，拼没有问题。\r\ndeclare @sql nvarchar(4000)\r\nset @sql=\'Select 姓名 \'\r\nSelect @sql=@sql+\',sum(case when 科目=\'\'\'+科目+ \'\'\' then 成绩 else 0 end) As \'\'\'+科目+\'\'\'\'\r\n        From表1 Group By 科目\r\nPrint @sql\r\n这一段的结果是：\r\nSelect 姓名 ,sum(case when 科目=\'数学\' then 成绩 else 0 end) As \'数学\',sum(case when 科目=\'英语\' then 成绩 else 0 end) As \'英语\',sum(case when 科目=\'语文\' then 成绩 else 0 end) As \'语文\'\r\n跟我们的要求比较接近了，还要附加一点东西：\r\nSelect 姓名 ,sum(case when 科目=\'数学\' then 成绩 else 0 end) As \'数学\',sum(case when 科目=\'英语\' then 成绩 else 0 end) As \'英语\',sum(case when 科目=\'语文\' then 成绩 else 0 end) As \'语文\' From 表1 Group By 姓名\r\n现在麻烦的是，如何执行这个字符串，现在我们将sql语句放在了 @sql 这个变量中了，怎么执行？\r\n灵机1动！\r\nSqlServer中有个专门将字符串作为sql语句执行的存储过程---- sp_executesql。\r\n就是它了。\r\n最终版本：\r\ndeclare @sql nvarchar(4000)\r\nset @sql=\'Select 姓名 \'\r\nSelect @sql=@sql+\',sum(case when 科目=\'\'\'+科目+ \'\'\' then 成绩 else 0 end) As \'\'\'+科目+\'\'\'\'\r\n        From 成绩表 Group By 科目\r\nPrint @sql\r\nset @sql=@sql+\' From 成绩表 Group By 姓名\'\r\nPrint @sql\r\nexecute sp_executesql @sql\r\n当然科目信息，不一定会从成绩表中获取，很有可能是一个专门的表格，比如叫“科目表”，那么就更easy了。\r\ndeclare @sql nvarchar(4000)\r\nset @sql=\'Select 姓名 \'\r\nSelect @sql=@sql+\',sum(case when 科目=\'\'\'+科目+ \'\'\' then 成绩 else 0 end) As \'\'\'+科目+\'\'\'\'\r\n        From 科目表\r\nPrint @sql\r\nset @sql=@sql+\' From 表1 Group By 姓名\'\r\nPrint @sql\r\nexecute sp_executesql @sql\r\n功德圆满。','关于 sql 行转列的感悟','','publish','open','open','','192','','','2010-09-23 22:47:05','2010-09-24 06:47:05','',0,'http://www.wordpress.cn/?p=192',0,'post','',0),(193,1,'2010-09-23 22:44:53','2010-09-24 06:44:53','','1','','inherit','open','open','','1','','','2010-09-23 22:44:53','2010-09-24 06:44:53','',192,'http://www.wordpress.cn/wp-content/uploads/2010/09/1.bmp',0,'attachment','image/bmp',0),(194,1,'2010-09-23 22:46:02','2010-09-24 06:46:02','','2','','inherit','open','open','','2','','','2010-09-23 22:46:02','2010-09-24 06:46:02','',192,'http://www.wordpress.cn/wp-content/uploads/2010/09/2.bmp',0,'attachment','image/bmp',0),(195,1,'2010-09-23 22:46:20','2010-09-24 06:46:20','自从看《学技术的不能自废武功》这篇文章后，我深有感触，于是对自己以前学过的知识又重新温习了一次，结果终于发现了一些新的感悟，现将其感悟发表，于大家共勉：关于 sql 行转列的感悟\n\n(表1)\n要将其转变为：\n\n<a href=\"http://www.wordpress.cn/wp-content/uploads/2010/09/2.bmp\"><img src=\"http://www.wordpress.cn/wp-content/uploads/2010/09/2.bmp\" alt=\"\" title=\"2\" class=\"alignnone size-full wp-image-194\" /></a> \n(表2)\n可以看得出来，表2列是由表1的行数据转化而来的，只有姓名列是表1中的列。\n一步一步的来嘛，其他的列难搞，如果表2只有1列姓名的话，那么very easy了，不就是一个简单的分组嘛。\nSelect 姓名 From 表1 Group By 姓名\n \n现在sql语句的架子搭起来了，无论以后如何变化，分组是少不了的。\n那么开始下一步，想这个结果集中再添加1列，多了我们不加，因为你不论是能处理语文，还是数学，还是英语列，那么其他的列只要原样照抄就可以了，顶多修改一下参数而已。\n从语文列开始吧，这一列的数据都可以从表1中找到，如果我们只要“语文”这一列，那么也好写。\nSql语句疑似：\nSelect 姓名,case  when 科目=\'语文\' then 成绩 end From 成绩表 Group By 姓名\n那就F5吧。\n嗯，啊，...挂了。\n赶紧看错误提示：\n服务器: 消息 8120，级别 16，状态 1，行 1\n列 \'成绩表.成绩\' 在选择列表中无效，因为该列既不包含在聚合函数中，也不包含在 GROUP BY 子句中。\n服务器: 消息 8120，级别 16，状态 1，行 1\n列 \'成绩表.科目\' 在选择列表中无效，因为该列既不包含在聚合函数中，也不包含在 GROUP BY 子句中。\n从错误信息中，可知字段成绩和科目还必须在聚合函数中或Group By中，如何在Group By中呢？\n\n  \n数据倒是有了，可是行多了点，看来只能从聚合入手了。\n到底是那个聚合？聚合有max,min,avg,sum,count，max和min首先干掉，我们不是要最大值，最小值，难道是平均值？难道是个数？难道是求和？？？\n那就试试呗。\nSelect 姓名,sum(case when 科目=\'语文\' then 成绩 end) From 表1 Group By 姓名\n\n \n嘿，真不错。\n \n该有的都有了，就是没列名，好办，给个别名嘛。\nSelect 姓名,sum(case when 科目=\'语文\' then 成绩 end) As \'语文\' From表1 Group By 姓名\n\n那么其他几列也就好办了。\n Select 姓名,\n sum(case when 科目=\'语文\' then 成绩 end) As \'语文\' ,\n sum(case when 科目=\'数学\' then 成绩 end) As \'数学\',\n sum(case when 科目=\'英语\' then 成绩 end) As \'英语\'\n From  表1 Group By 姓名\n \n\n 可是结果有NULL，那还不好办，你的case when then end没写完，还有else没写呢！\nSelect 姓名,\n sum(case when 科目=\'语文\' then 成绩 else 0 end) As \'语文\' ,\n sum(case when 科目=\'数学\' then 成绩 else 0 end) As \'数学\',\n sum(case when 科目=\'英语\' then 成绩 else 0 end) As \'英语\'\n From 表1 Group By 姓名\n \n\n \n终于修成正果了。\n \n真的？\n当然是假的？\n怎么假？\n这不废话么！你现在写的Sql语句只能支持科目表中只有语文数学英语，那万一课程中加了一个叫物理的，那你不得改程序了吗？！怎么办？凉拌。\n也就是说这个Sql语句中有变化的地方在红字部分：\nSelect 姓名,\n sum(case when 科目=\'语文\' then 成绩 else 0 end) As \'语文\' ,\n sum(case when 科目=\'数学\' then 成绩 else 0 end) As \'数学\',\n sum(case when 科目=\'英语\' then 成绩 else 0 end) As \'英语\'\n From  表1 Group By 姓名红字部分要从哪里来？如果专心看，那么应该能从中间看出来，哪些个“As \'语文\'”，哪些个“科目=\'语文\'”，其实是数据表中的值，如果仅仅要查询“语文，数学，英语”的话，那么easy啊。\nSelect 课程 From 表1 Group By 科目\n可是我们要的结果是：\nsum(case when 科目=\'语文\' then 成绩 else 0 end) As \'语文\' ,\n sum(case when 科目=\'数学\' then 成绩 else 0 end) As \'数学\',\n sum(case when 科目=\'英语\' then 成绩 else 0 end) As \'英语\'\n……………..\n\n\n那不简单，就是些字符串嘛。\nSelect \',sum(case when 科目=\'\'\'+trim(科目)+ \'\'\' then 成绩 else 0 end) As \'\'\'+科目+\'\'\'\' From 表1 Group By 课程\n结果为：\n ,sum(case when 科目=\'语文\' then 成绩 else 0 end) As \'语文\' ,\n ,sum(case when 科目=\'数学\' then 成绩 else 0 end) As \'数学\',\n ,sum(case when 科目=\'英语\' then 成绩 else 0 end) As \'英语\'\n\n \n哈哈，只要将这一段和刚才的Select 姓名拼在一起就可以了。\n现在麻烦来了，拼没有问题。\ndeclare @sql nvarchar(4000)\nset @sql=\'Select 姓名 \'\nSelect @sql=@sql+\',sum(case when 科目=\'\'\'+科目+ \'\'\' then 成绩 else 0 end) As \'\'\'+科目+\'\'\'\'\n        From表1 Group By 科目\nPrint @sql\n这一段的结果是：\nSelect 姓名 ,sum(case when 科目=\'数学\' then 成绩 else 0 end) As \'数学\',sum(case when 科目=\'英语\' then 成绩 else 0 end) As \'英语\',sum(case when 科目=\'语文\' then 成绩 else 0 end) As \'语文\'\n跟我们的要求比较接近了，还要附加一点东西：\nSelect 姓名 ,sum(case when 科目=\'数学\' then 成绩 else 0 end) As \'数学\',sum(case when 科目=\'英语\' then 成绩 else 0 end) As \'英语\',sum(case when 科目=\'语文\' then 成绩 else 0 end) As \'语文\' From 表1 Group By 姓名\n现在麻烦的是，如何执行这个字符串，现在我们将sql语句放在了 @sql 这个变量中了，怎么执行？\n灵机1动！\nSqlServer中有个专门将字符串作为sql语句执行的存储过程---- sp_executesql。\n就是它了。\n最终版本：\ndeclare @sql nvarchar(4000)\nset @sql=\'Select 姓名 \'\nSelect @sql=@sql+\',sum(case when 科目=\'\'\'+科目+ \'\'\' then 成绩 else 0 end) As \'\'\'+科目+\'\'\'\'\n        From 成绩表 Group By 科目\nPrint @sql\nset @sql=@sql+\' From 成绩表 Group By 姓名\'\nPrint @sql\nexecute sp_executesql @sql\n当然科目信息，不一定会从成绩表中获取，很有可能是一个专门的表格，比如叫“科目表”，那么就更easy了。\ndeclare @sql nvarchar(4000)\nset @sql=\'Select 姓名 \'\nSelect @sql=@sql+\',sum(case when 科目=\'\'\'+科目+ \'\'\' then 成绩 else 0 end) As \'\'\'+科目+\'\'\'\'\n        From 科目表\nPrint @sql\nset @sql=@sql+\' From 表1 Group By 姓名\'\nPrint @sql\nexecute sp_executesql @sql\n功德圆满。','','','inherit','open','open','','192-revision','','','2010-09-23 22:46:20','2010-09-24 06:46:20','',192,'http://www.wordpress.cn/?p=195',0,'revision','',0),(196,1,'2010-09-23 22:46:53','2010-09-24 06:46:53','自从看《学技术的不能自废武功》这篇文章后，我深有感触，于是对自己以前学过的知识又重新温习了一次，结果终于发现了一些新的感悟，现将其感悟发表，于大家共勉：关于 sql 行转列的感悟\r\n<a href=\"http://www.wordpress.cn/wp-content/uploads/2010/09/1.bmp\"><img src=\"http://www.wordpress.cn/wp-content/uploads/2010/09/1.bmp\" alt=\"\" title=\"1\" class=\"alignnone size-full wp-image-193\" /></a>\r\n(表1)\r\n要将其转变为：\r\n\r\n<a href=\"http://www.wordpress.cn/wp-content/uploads/2010/09/2.bmp\"><img src=\"http://www.wordpress.cn/wp-content/uploads/2010/09/2.bmp\" alt=\"\" title=\"2\" class=\"alignnone size-full wp-image-194\" /></a> \r\n(表2)\r\n可以看得出来，表2列是由表1的行数据转化而来的，只有姓名列是表1中的列。\r\n一步一步的来嘛，其他的列难搞，如果表2只有1列姓名的话，那么very easy了，不就是一个简单的分组嘛。\r\nSelect 姓名 From 表1 Group By 姓名\r\n \r\n现在sql语句的架子搭起来了，无论以后如何变化，分组是少不了的。\r\n那么开始下一步，想这个结果集中再添加1列，多了我们不加，因为你不论是能处理语文，还是数学，还是英语列，那么其他的列只要原样照抄就可以了，顶多修改一下参数而已。\r\n从语文列开始吧，这一列的数据都可以从表1中找到，如果我们只要“语文”这一列，那么也好写。\r\nSql语句疑似：\r\nSelect 姓名,case  when 科目=\'语文\' then 成绩 end From 成绩表 Group By 姓名\r\n那就F5吧。\r\n嗯，啊，...挂了。\r\n赶紧看错误提示：\r\n服务器: 消息 8120，级别 16，状态 1，行 1\r\n列 \'成绩表.成绩\' 在选择列表中无效，因为该列既不包含在聚合函数中，也不包含在 GROUP BY 子句中。\r\n服务器: 消息 8120，级别 16，状态 1，行 1\r\n列 \'成绩表.科目\' 在选择列表中无效，因为该列既不包含在聚合函数中，也不包含在 GROUP BY 子句中。\r\n从错误信息中，可知字段成绩和科目还必须在聚合函数中或Group By中，如何在Group By中呢？\r\n\r\n  \r\n数据倒是有了，可是行多了点，看来只能从聚合入手了。\r\n到底是那个聚合？聚合有max,min,avg,sum,count，max和min首先干掉，我们不是要最大值，最小值，难道是平均值？难道是个数？难道是求和？？？\r\n那就试试呗。\r\nSelect 姓名,sum(case when 科目=\'语文\' then 成绩 end) From 表1 Group By 姓名\r\n\r\n \r\n嘿，真不错。\r\n \r\n该有的都有了，就是没列名，好办，给个别名嘛。\r\nSelect 姓名,sum(case when 科目=\'语文\' then 成绩 end) As \'语文\' From表1 Group By 姓名\r\n\r\n那么其他几列也就好办了。\r\n Select 姓名,\r\n sum(case when 科目=\'语文\' then 成绩 end) As \'语文\' ,\r\n sum(case when 科目=\'数学\' then 成绩 end) As \'数学\',\r\n sum(case when 科目=\'英语\' then 成绩 end) As \'英语\'\r\n From  表1 Group By 姓名\r\n \r\n\r\n 可是结果有NULL，那还不好办，你的case when then end没写完，还有else没写呢！\r\nSelect 姓名,\r\n sum(case when 科目=\'语文\' then 成绩 else 0 end) As \'语文\' ,\r\n sum(case when 科目=\'数学\' then 成绩 else 0 end) As \'数学\',\r\n sum(case when 科目=\'英语\' then 成绩 else 0 end) As \'英语\'\r\n From 表1 Group By 姓名\r\n \r\n\r\n \r\n终于修成正果了。\r\n \r\n真的？\r\n当然是假的？\r\n怎么假？\r\n这不废话么！你现在写的Sql语句只能支持科目表中只有语文数学英语，那万一课程中加了一个叫物理的，那你不得改程序了吗？！怎么办？凉拌。\r\n也就是说这个Sql语句中有变化的地方在红字部分：\r\nSelect 姓名,\r\n sum(case when 科目=\'语文\' then 成绩 else 0 end) As \'语文\' ,\r\n sum(case when 科目=\'数学\' then 成绩 else 0 end) As \'数学\',\r\n sum(case when 科目=\'英语\' then 成绩 else 0 end) As \'英语\'\r\n From  表1 Group By 姓名红字部分要从哪里来？如果专心看，那么应该能从中间看出来，哪些个“As \'语文\'”，哪些个“科目=\'语文\'”，其实是数据表中的值，如果仅仅要查询“语文，数学，英语”的话，那么easy啊。\r\nSelect 课程 From 表1 Group By 科目\r\n可是我们要的结果是：\r\nsum(case when 科目=\'语文\' then 成绩 else 0 end) As \'语文\' ,\r\n sum(case when 科目=\'数学\' then 成绩 else 0 end) As \'数学\',\r\n sum(case when 科目=\'英语\' then 成绩 else 0 end) As \'英语\'\r\n……………..\r\n\r\n\r\n那不简单，就是些字符串嘛。\r\nSelect \',sum(case when 科目=\'\'\'+trim(科目)+ \'\'\' then 成绩 else 0 end) As \'\'\'+科目+\'\'\'\' From 表1 Group By 课程\r\n结果为：\r\n ,sum(case when 科目=\'语文\' then 成绩 else 0 end) As \'语文\' ,\r\n ,sum(case when 科目=\'数学\' then 成绩 else 0 end) As \'数学\',\r\n ,sum(case when 科目=\'英语\' then 成绩 else 0 end) As \'英语\'\r\n\r\n \r\n哈哈，只要将这一段和刚才的Select 姓名拼在一起就可以了。\r\n现在麻烦来了，拼没有问题。\r\ndeclare @sql nvarchar(4000)\r\nset @sql=\'Select 姓名 \'\r\nSelect @sql=@sql+\',sum(case when 科目=\'\'\'+科目+ \'\'\' then 成绩 else 0 end) As \'\'\'+科目+\'\'\'\'\r\n        From表1 Group By 科目\r\nPrint @sql\r\n这一段的结果是：\r\nSelect 姓名 ,sum(case when 科目=\'数学\' then 成绩 else 0 end) As \'数学\',sum(case when 科目=\'英语\' then 成绩 else 0 end) As \'英语\',sum(case when 科目=\'语文\' then 成绩 else 0 end) As \'语文\'\r\n跟我们的要求比较接近了，还要附加一点东西：\r\nSelect 姓名 ,sum(case when 科目=\'数学\' then 成绩 else 0 end) As \'数学\',sum(case when 科目=\'英语\' then 成绩 else 0 end) As \'英语\',sum(case when 科目=\'语文\' then 成绩 else 0 end) As \'语文\' From 表1 Group By 姓名\r\n现在麻烦的是，如何执行这个字符串，现在我们将sql语句放在了 @sql 这个变量中了，怎么执行？\r\n灵机1动！\r\nSqlServer中有个专门将字符串作为sql语句执行的存储过程---- sp_executesql。\r\n就是它了。\r\n最终版本：\r\ndeclare @sql nvarchar(4000)\r\nset @sql=\'Select 姓名 \'\r\nSelect @sql=@sql+\',sum(case when 科目=\'\'\'+科目+ \'\'\' then 成绩 else 0 end) As \'\'\'+科目+\'\'\'\'\r\n        From 成绩表 Group By 科目\r\nPrint @sql\r\nset @sql=@sql+\' From 成绩表 Group By 姓名\'\r\nPrint @sql\r\nexecute sp_executesql @sql\r\n当然科目信息，不一定会从成绩表中获取，很有可能是一个专门的表格，比如叫“科目表”，那么就更easy了。\r\ndeclare @sql nvarchar(4000)\r\nset @sql=\'Select 姓名 \'\r\nSelect @sql=@sql+\',sum(case when 科目=\'\'\'+科目+ \'\'\' then 成绩 else 0 end) As \'\'\'+科目+\'\'\'\'\r\n        From 科目表\r\nPrint @sql\r\nset @sql=@sql+\' From 表1 Group By 姓名\'\r\nPrint @sql\r\nexecute sp_executesql @sql\r\n功德圆满。','','','inherit','open','open','','192-revision-2','','','2010-09-23 22:46:53','2010-09-24 06:46:53','',192,'http://www.wordpress.cn/?p=196',0,'revision','',0),(207,1,'2010-10-07 18:13:18','2010-10-08 02:13:18','我们在进行数据库管理和开发中经常会遇到性能问题，这就涉及到MySQL的性能优化。通过在网络上查找资料和笔者自己的尝试，我认为以下系统参数是比较关键的：\r\n\r\n关键参数一：back_log\r\n\r\n要求 MySQL 能有的连接数量。当主要MySQL线程在一个很短时间内得到非常多的连接请求，这就起作用，然后主线程花些时间(尽管很短)检查连接并且启动一个新线程。\r\n\r\nback_log 值指出在MySQL暂时停止回答新请求之前的短时间内多少个请求可以被存在堆栈中。只有如果期望在一个短时间内有很多连接，你需要增加它，换句话说，这值 对到来的TCP/IP连接的侦听队列的大小。你的操作系统在这个队列大小上有它自己的限制。 试图设定back_log高于你的操作系统的限制将是无效的。\r\n\r\n当你观察你的主机进程列表，发现大量 264084 | unauthenticated user | xxx.xxx.xxx.xxx | NULL | Connect | NULL | login | NULL 的待连接进程时，就要加大 back_log 的值了。默认数值是50，我把它改为500。\r\n\r\n关键参数二：interactive_timeout\r\n\r\n服务器在关闭它前在一个交互连接上等待行动的秒数。一个交互的客户被定义为对 mysql_real_connect()使用 CLIENT_INTERACTIVE 选项的客户。 默认数值是28800，我把它改为7200。\r\n\r\n关键参数三：key_buffer_size\r\n\r\n索引块是缓冲的并且被所有的线程共享。key_buffer_size是用于索引块的缓冲区大小，增加它可得到更好处理的索引(对所有读和多重写)，到你 能负担得起那样多。如果你使它太大，系统将开始换页并且真的变慢了。默认数值是8388600(8M)，我的MySQL主机有2GB内存，所以我把它改为 402649088(400MB)。\r\n\r\n关键参数四：max_connections\r\n\r\n允许的同时客户的数量。增加该值增加 mysqld 要求的文件描述符的数量。这个数字应该增加，否则，你将经常看到 Too many connections 错误。 默认数值是100，我把它改为1024 。\r\n\r\n关键参数五：record_buffer\r\n\r\n每个进行一个顺序扫描的线程为其扫描的每张表分配这个大小的一个缓冲区。如果你做很多顺序扫描，你可能想要增加该值。默认数值是131072(128K)，我把它改为16773120 (16M)\r\n\r\n关键参数六：sort_buffer\r\n\r\n每个需要进行排序的线程分配该大小的一个缓冲区。增加这值加速ORDER BY或GROUP BY操作。默认数值是2097144(2M)，我把它改为 16777208 (16M)。\r\n\r\n关键参数七：table_cache\r\n\r\n为所有线程打开表的数量。增加该值能增加mysqld要求的文件描述符的数量。MySQL对每个唯一打开的表需要2个文件描述符。默认数值是64，我把它改为512。\r\n\r\n关键参数八：thread_cache_size\r\n\r\n可以复用的保存在中的线程的数量。如果有，新的线程从缓存中取得，当断开连接的时候如果有空间，客户的线置在缓存中。如果有很多新的线程，为了提高性能可 以这个变量值。通过比较 Connections 和 Threads_created 状态的变量，可以看到这个变量的作用。我把它设置为 80。\r\n\r\n关键参数九：wait_timeout\r\n\r\n服务器在关闭它之前在一个连接上等待行动的秒数。 默认数值是28800，我把它改为7200。\r\n\r\n注：参数的调整可以通过修改 /etc/my.cnf 文件并重启 MySQL 实现。这是一个比较谨慎的工作，上面的结果也仅仅是我的一些看法，你可以根据你自己主机的硬件情况(特别是内存大小)进一步修改。','MySQL数据库性能优化的关键参数','','publish','open','open','','mysql%e6%95%b0%e6%8d%ae%e5%ba%93%e6%80%a7%e8%83%bd%e4%bc%98%e5%8c%96%e7%9a%84%e5%85%b3%e9%94%ae%e5%8f%82%e6%95%b0','','','2010-10-07 18:13:18','2010-10-08 02:13:18','',0,'http://www.wordpress.cn/?p=207',0,'post','',0),(198,1,'2010-09-23 23:13:55','2010-09-24 07:13:55','因apache的被ddos攻击和恶意刷新导致apache运行异常，占用资源过大，现在新发现一个很好的解决方法\r\n\r\n一些朋友的应用也遇到过类似的烦恼，基本上都通过安装mod_evasive模块得到了较好的解决。\r\n下面我就来以我在CentOS 5.4上安装基于Apache2.2.9的mod_evasive经过给大家分享一下经验，顺便进一步讲述一下mod_evasive的特性。\r\nmod_evasive通过对来访IP地址和访问URI建立内部动态哈希表来检测是否有攻击，如果有如下的行为将拒绝该IP的访问：\r\n1. 每秒对同一页面的请求数超过平时（原文：Requesting the same page more than a few times per second）。\r\n2. 每秒同一个子进程有超过50次的并发请求。\r\n3. 临时被拒绝（在blacklist中）的时候还不断进行请求。\r\nmod_evasive可以非常方便的和防火墙、路由器等进行整合，进一步提高抗拒绝服务的能力。\r\n和别的防攻击工具一样，mod_evasive同样收到带宽、系统处理能力等因素的影响，所以要想应对大规模的攻击，最好的方式就是把mod_evasive和您的防火墙和路由器进行整合，而不是简单的安装成为独立的Apache模块。\r\n \r\nmod_evasive在apache2.2.9上的安装方法：\r\n \r\n一、使用源码安装：\r\n1、下载最新源码包mod_evasive_1.10.1.tar.gz\r\n# cd /usr/local/src\r\n# wget http://www.zdziarski.com/blog/wp-content/uploads/2010/02/mod_evasive_1.10.1.tar.gz\r\n# tar -zxvf mod_evasive_1.10.1.tar.gz\r\n# cd mod_evasive\r\n# /usr/local/apache/bin/apxs -i -a -c mod_evasive20.c\r\n说明：动态编译apache, 这里编译mod_evasive20.c是应对2.X版本的apache,如果是1.x版本的apache则编译mod_evasive.c就行了\r\n \r\n最后出现\r\n#############################################################\r\nLibraries have been installed in:\r\n   /usr/local/apache/modules\r\n \r\nIf you ever happen to want to link against installed libraries\r\nin a given directory, LIBDIR, you must either use libtool, and\r\nspecify the full pathname of the library, or use the `-LLIBDIR\'\r\nflag during linking and do at least one of the following:\r\n   - add LIBDIR to the `LD_LIBRARY_PATH\' environment variable\r\n     during execution\r\n   - add LIBDIR to the `LD_RUN_PATH\' environment variable\r\n     during linking\r\n   - use the `-Wl,-rpath -Wl,LIBDIR\' linker flag\r\n   - have your system administrator add LIBDIR to `/etc/ld.so.conf\'\r\n \r\nSee any operating system documentation about shared libraries for\r\nmore information, such as the ld(1) and ld.so(8) manual pages.\r\n----------------------------------------------------------------------\r\nchmod 755 /usr/local/apache/modules/mod_evasive20.so\r\n[activating module `evasive20\' in /usr/local/apache/conf/httpd.conf]\r\n[root@localhost mod_evasive]#\r\n \r\n##############################################################\r\n表示已经编译写入了apache模块\r\n查看httpd.conf文件中已经自动添加了\r\nLoadModule evasive20_module   modules/mod_evasive20.so\r\n \r\n至此，完成了mod_evasive的安装，重启apache服务后，它就开始工作了，这个时候您如果不作任何别的设置，它也可以使用默认配置为您提供良好的防攻击能力，当然，您也可以自己进行一些参数的定制配置，添加如下参数：\r\n \r\n<IfModule mod_evasive20.c>\r\n    DOSHashTableSize    3097\r\n    DOSPageCount        5\r\n    DOSSiteCount        50\r\n    DOSPageInterval     2\r\n    DOSSiteInterval     2\r\n    DOSBlockingPeriod   10\r\n</IfModule>\r\n \r\n参数简单说明：\r\nDOSHashTableSize 3097 记录和存放黑名单的哈西表大小，如果服务器访问量很大，可以加大该值\r\nDOSPageCount 5 同一个页面在同一时间内可以被统一个用户访问的次数，超过该数字就会被列为攻击，同一时间的数值可以在DosPageInterval参数中设置。\r\nDOSSiteCount 50 同一个用户在同一个网站内可以同时打开的访问数，同一个时间的数值在DOSSiteInterval中设置。\r\nDOSPageInterval 2 设置DOSPageCount中时间长度标准，默认值为1。\r\nDOSSiteInterval 2 设置DOSSiteCount中时间长度标准。\r\nDOSBlockingPeriod 10 被封时间间隔秒，这中间会收到 403 (Forbidden) 的返回。\r\n \r\n其他可选参数：\r\nDOSEmailNotify 125869495@qq.com 设置受到攻击时接收攻击信息提示的邮箱地址。\r\nDOSSystemCommand “su – someuser -c ‘/sbin/… %s …’” 受到攻击时Apache运行用户执行的系统命令\r\nDOSLogDir “/var/lock/mod_dosevasive” 攻击日志存放目录/var/lock/mod_dosevasive 一般就可以存放到/tmp目录\r\n \r\n最后，尝试用压力测试工具看看效果吧，你会想象到不错的效果','apache2.2.X防御DDOS攻击和恶意刷新网站技巧','','publish','open','open','','apache2-2-x%e9%98%b2%e5%be%a1ddos%e6%94%bb%e5%87%bb%e5%92%8c%e6%81%b6%e6%84%8f%e5%88%b7%e6%96%b0%e7%bd%91%e7%ab%99%e6%8a%80%e5%b7%a7','','','2010-09-23 23:13:55','2010-09-24 07:13:55','',0,'http://www.wordpress.cn/?p=198',0,'post','',0),(199,1,'2010-09-23 23:13:48','2010-09-24 07:13:48','因apache的被ddos攻击和恶意刷新导致apache运行异常，占用资源过大，现在新发现一个很好的解决方法\n\n一些朋友的应用也遇到过类似的烦恼，基本上都通过安装mod_evasive模块得到了较好的解决。\n下面我就来以我在CentOS 5.4上安装基于Apache2.2.9的mod_evasive经过给大家分享一下经验，顺便进一步讲述一下mod_evasive的特性。\nmod_evasive通过对来访IP地址和访问URI建立内部动态哈希表来检测是否有攻击，如果有如下的行为将拒绝该IP的访问：\n1. 每秒对同一页面的请求数超过平时（原文：Requesting the same page more than a few times per second）。\n2. 每秒同一个子进程有超过50次的并发请求。\n3. 临时被拒绝（在blacklist中）的时候还不断进行请求。\nmod_evasive可以非常方便的和防火墙、路由器等进行整合，进一步提高抗拒绝服务的能力。\n和别的防攻击工具一样，mod_evasive同样收到带宽、系统处理能力等因素的影响，所以要想应对大规模的攻击，最好的方式就是把mod_evasive和您的防火墙和路由器进行整合，而不是简单的安装成为独立的Apache模块。\n \nmod_evasive在apache2.2.9上的安装方法：\n \n一、使用源码安装：\n1、下载最新源码包mod_evasive_1.10.1.tar.gz\n# cd /usr/local/src\n# wget http://www.zdziarski.com/blog/wp-content/uploads/2010/02/mod_evasive_1.10.1.tar.gz\n# tar -zxvf mod_evasive_1.10.1.tar.gz\n# cd mod_evasive\n# /usr/local/apache/bin/apxs -i -a -c mod_evasive20.c\n说明：动态编译apache, 这里编译mod_evasive20.c是应对2.X版本的apache,如果是1.x版本的apache则编译mod_evasive.c就行了\n \n最后出现\n#############################################################\nLibraries have been installed in:\n   /usr/local/apache/modules\n \nIf you ever happen to want to link against installed libraries\nin a given directory, LIBDIR, you must either use libtool, and\nspecify the full pathname of the library, or use the `-LLIBDIR\'\nflag during linking and do at least one of the following:\n   - add LIBDIR to the `LD_LIBRARY_PATH\' environment variable\n     during execution\n   - add LIBDIR to the `LD_RUN_PATH\' environment variable\n     during linking\n   - use the `-Wl,-rpath -Wl,LIBDIR\' linker flag\n   - have your system administrator add LIBDIR to `/etc/ld.so.conf\'\n \nSee any operating system documentation about shared libraries for\nmore information, such as the ld(1) and ld.so(8) manual pages.\n----------------------------------------------------------------------\nchmod 755 /usr/local/apache/modules/mod_evasive20.so\n[activating module `evasive20\' in /usr/local/apache/conf/httpd.conf]\n[root@localhost mod_evasive]#\n \n##############################################################\n表示已经编译写入了apache模块\n查看httpd.conf文件中已经自动添加了\nLoadModule evasive20_module   modules/mod_evasive20.so\n \n至此，完成了mod_evasive的安装，重启apache服务后，它就开始工作了，这个时候您如果不作任何别的设置，它也可以使用默认配置为您提供良好的防攻击能力，当然，您也可以自己进行一些参数的定制配置，添加如下参数：\n \n<IfModule mod_evasive20.c>\n    DOSHashTableSize    3097\n    DOSPageCount        5\n    DOSSiteCount        50\n    DOSPageInterval     2\n    DOSSiteInterval     2\n    DOSBlockingPeriod   10\n</IfModule>\n \n参数简单说明：\nDOSHashTableSize 3097 记录和存放黑名单的哈西表大小，如果服务器访问量很大，可以加大该值\nDOSPageCount 5 同一个页面在同一时间内可以被统一个用户访问的次数，超过该数字就会被列为攻击，同一时间的数值可以在DosPageInterval参数中设置。\nDOSSiteCount 50 同一个用户在同一个网站内可以同时打开的访问数，同一个时间的数值在DOSSiteInterval中设置。\nDOSPageInterval 2 设置DOSPageCount中时间长度标准，默认值为1。\nDOSSiteInterval 2 设置DOSSiteCount中时间长度标准。\nDOSBlockingPeriod 10 被封时间间隔秒，这中间会收到 403 (Forbidden) 的返回。\n \n其他可选参数：\nDOSEmailNotify 125869495@qq.com 设置受到攻击时接收攻击信息提示的邮箱地址。\nDOSSystemCommand “su – someuser -c ‘/sbin/… %s …’” 受到攻击时Apache运行用户执行的系统命令\nDOSLogDir “/var/lock/mod_dosevasive” 攻击日志存放目录/var/lock/mod_dosevasive 一般就可以存放到/tmp目录\n \n最后，尝试用压力测试工具看看效果吧，你会想象到不错的效果','apache2.2.X防御DDOS攻击和恶意刷新网站技巧','','inherit','open','open','','198-revision','','','2010-09-23 23:13:48','2010-09-24 07:13:48','',198,'http://www.wordpress.cn/?p=199',0,'revision','',0),(200,1,'2010-09-23 23:15:30','2010-09-24 07:15:30','一般情况下，软件的漏洞信息和特定版本是相关的，因此，软件的版本号对攻击者来说是很有价值的。\r\n在默认情况下，系统会把Apache版本模块都显示出来（http返回头信息）。如果列举目录的话，会显示域名信息如：\r\n \r\n\r\n[root@localhost tmp]# curl -I localhost\r\n \r\nHTTP/1.1 403 Forbidden\r\n \r\nDate: Wed, 21 Jul 2010 13:09:33 GMT\r\n \r\nServer: Apache/2.2.9 (CentOS)\r\n \r\nAccept-Ranges: bytes\r\n \r\nContent-Length: 5043\r\n \r\nConnection: close\r\n \r\nContent-Type: text/html; charset=UTF-8\r\n隐藏方法：\r\n\r\n\r\n1、隐藏Apache版本号的方法是修改Apache的配置文件，如RedHat系的Linux默认是：\r\n \r\n\r\nvim /etc/httpd/conf/httpd.conf\r\n分别搜索关键字ServerTokens和ServerSignature，修改：\r\n \r\n\r\nServerTokens OS 修改为 ServerTokens ProductOnly\r\nServerSignature On 修改为 ServerSignature Off\r\n2、重启或重新加载Apache就可以了。\r\n \r\n\r\napachectl restart\r\n测试一下，如下：\r\n \r\n\r\n[root@localhost tmp]# curl -I 192.168.80.128:88\r\n \r\nHTTP/1.1 403 Forbidden\r\n \r\nDate: Wed, 21 Jul 2010 13:23:22 GMT\r\n \r\nServer: Apache\r\n \r\nAccept-Ranges: bytes\r\n \r\nContent-Length: 5043\r\n \r\nConnection: close\r\n \r\nContent-Type: text/html; charset=UTF-8\r\n版本号与操作系统信息已经隐藏了。\r\n3、上面的方法是默认情况下安装的Apache，如果是编译安装的，还可以用修改源码编译的方法：\r\n进入Apache的源码目录下的include目录，然后编辑ap_release.h这个文件，你会看到有如下变量：\r\n \r\n\r\n\r\n \r\n#define AP_SERVER_BASEVENDOR “Apache Software Foundation”\r\n \r\n#define AP_SERVER_BASEPROJECT “Apache HTTP Server”\r\n \r\n#define AP_SERVER_BASEPRODUCT “Apache”\r\n \r\n#define AP_SERVER_MAJORVERSION_NUMBER 2\r\n \r\n#define AP_SERVER_MINORVERSION_NUMBER 2\r\n \r\n#define AP_SERVER_PATCHLEVEL_NUMBER 9\r\n \r\n#define AP_SERVER_DEVBUILD_BOOLEAN 0\r\n可以根据自己喜好，修改或隐藏版本号与名字。\r\n\r\n本人修改的部分操作：\r\n\r\n一、文件操作\r\n\r\n1、修改文件：srcdir/include/ap_release.h\r\n\r\n#define AP_SERVER_BASEVENDOR “这里填写开发组织名，例如：Microsoft Corp.”\r\n#define AP_SERVER_BASEPRODUCT “这里填写服务器软件名，例如：Microsoft-IIS”\r\n#define AP_SERVER_MAJORVERSION “主版本，例如：6″\r\n#define AP_SERVER_MINORVERSION “次版本，例如：0″\r\n#define AP_SERVER_PATCHLEVEL “修正版本，例如：0″\r\n\r\n2、修改文件：srcdir/include/os.h\r\n\r\n#define PLATFORM “这里填写操作系统的名称，例如：Win32″\r\n\r\n3、修改文件：srcdir/os/PLATFORM/os.h\r\n\r\n#define PLATFORM “这里填写操作系统的名称，例如：Win32″\r\n\r\n4、修改文件：srcdir/modules/generators/mod_info.c\r\n\r\nSearch “Apache Server Information” replace with “这里填写在查看server-info现实的页面标题，例如：Microsoft-IIS Information”\r\n\r\n5、修改文件：srcdir/modules/generators/mod_status.c\r\n\r\nSearch “Apache Server Status” replace with “这里填写在查看server-status现实的页面标题，例如：Microsoft-IIS Status”\r\n\r\n说明：\r\n\r\n1、path/to/filename 表示要对这个文件进行操作\r\n2、Search “A” replace with “B” 表示在文件之中搜索A，然后使用B替换\r\n\r\n二、编译操作\r\n\r\n按照一般正常步骤编译安装\r\n\r\n结果：\r\n/server-info：\r\n\r\nMicrosoft-IIS Information\r\n…\r\nServer Version： Microsoft-IIS/6.0/7.0 (Win32)\r\n\r\n/server-status：\r\n\r\nApache Server Status for 127.0.0.1\r\nServer Version： Microsoft-IIS/6.0/7.0 (Win32)\r\nServer Built： Aug 17 2010 16：31：12\r\n\r\n页面底部显示：(例如在以上两个测试和indexes情况之下)\r\n\r\nMicrosoft-IIS/6.0/7.0 (Win32) Server at 127.0.0.1 Port 80','修改apache编译文件和隐藏方法','','publish','open','open','','%e4%bf%ae%e6%94%b9apache%e7%bc%96%e8%af%91%e6%96%87%e4%bb%b6%e5%92%8c%e9%9a%90%e8%97%8f%e6%96%b9%e6%b3%95','','','2010-09-23 23:15:30','2010-09-24 07:15:30','',0,'http://www.wordpress.cn/?p=200',0,'post','',0),(201,1,'2010-09-23 23:15:25','2010-09-24 07:15:25','一般情况下，软件的漏洞信息和特定版本是相关的，因此，软件的版本号对攻击者来说是很有价值的。\n在默认情况下，系统会把Apache版本模块都显示出来（http返回头信息）。如果列举目录的话，会显示域名信息如：\n \n\n[root@localhost tmp]# curl -I localhost\n \nHTTP/1.1 403 Forbidden\n \nDate: Wed, 21 Jul 2010 13:09:33 GMT\n \nServer: Apache/2.2.9 (CentOS)\n \nAccept-Ranges: bytes\n \nContent-Length: 5043\n \nConnection: close\n \nContent-Type: text/html; charset=UTF-8\n隐藏方法：\n\n\n1、隐藏Apache版本号的方法是修改Apache的配置文件，如RedHat系的Linux默认是：\n \n\nvim /etc/httpd/conf/httpd.conf\n分别搜索关键字ServerTokens和ServerSignature，修改：\n \n\nServerTokens OS 修改为 ServerTokens ProductOnly\nServerSignature On 修改为 ServerSignature Off\n2、重启或重新加载Apache就可以了。\n \n\napachectl restart\n测试一下，如下：\n \n\n[root@localhost tmp]# curl -I 192.168.80.128:88\n \nHTTP/1.1 403 Forbidden\n \nDate: Wed, 21 Jul 2010 13:23:22 GMT\n \nServer: Apache\n \nAccept-Ranges: bytes\n \nContent-Length: 5043\n \nConnection: close\n \nContent-Type: text/html; charset=UTF-8\n版本号与操作系统信息已经隐藏了。\n3、上面的方法是默认情况下安装的Apache，如果是编译安装的，还可以用修改源码编译的方法：\n进入Apache的源码目录下的include目录，然后编辑ap_release.h这个文件，你会看到有如下变量：\n \n\n\n \n#define AP_SERVER_BASEVENDOR “Apache Software Foundation”\n \n#define AP_SERVER_BASEPROJECT “Apache HTTP Server”\n \n#define AP_SERVER_BASEPRODUCT “Apache”\n \n#define AP_SERVER_MAJORVERSION_NUMBER 2\n \n#define AP_SERVER_MINORVERSION_NUMBER 2\n \n#define AP_SERVER_PATCHLEVEL_NUMBER 9\n \n#define AP_SERVER_DEVBUILD_BOOLEAN 0\n可以根据自己喜好，修改或隐藏版本号与名字。\n\n本人修改的部分操作：\n\n一、文件操作\n\n1、修改文件：srcdir/include/ap_release.h\n\n#define AP_SERVER_BASEVENDOR “这里填写开发组织名，例如：Microsoft Corp.”\n#define AP_SERVER_BASEPRODUCT “这里填写服务器软件名，例如：Microsoft-IIS”\n#define AP_SERVER_MAJORVERSION “主版本，例如：6″\n#define AP_SERVER_MINORVERSION “次版本，例如：0″\n#define AP_SERVER_PATCHLEVEL “修正版本，例如：0″\n\n2、修改文件：srcdir/include/os.h\n\n#define PLATFORM “这里填写操作系统的名称，例如：Win32″\n\n3、修改文件：srcdir/os/PLATFORM/os.h\n\n#define PLATFORM “这里填写操作系统的名称，例如：Win32″\n\n4、修改文件：srcdir/modules/generators/mod_info.c\n\nSearch “Apache Server Information” replace with “这里填写在查看server-info现实的页面标题，例如：Microsoft-IIS Information”\n\n5、修改文件：srcdir/modules/generators/mod_status.c\n\nSearch “Apache Server Status” replace with “这里填写在查看server-status现实的页面标题，例如：Microsoft-IIS Status”\n\n说明：\n\n1、path/to/filename 表示要对这个文件进行操作\n2、Search “A” replace with “B” 表示在文件之中搜索A，然后使用B替换\n\n二、编译操作\n\n按照一般正常步骤编译安装\n\n结果：\n/server-info：\n\nMicrosoft-IIS Information\n…\nServer Version： Microsoft-IIS/6.0/7.0 (Win32)\n\n/server-status：\n\nApache Server Status for 127.0.0.1\nServer Version： Microsoft-IIS/6.0/7.0 (Win32)\nServer Built： Aug 17 2010 16：31：12\n\n页面底部显示：(例如在以上两个测试和indexes情况之下)\n\nMicrosoft-IIS/6.0/7.0 (Win32) Server at 127.0.0.1 Port 80','修改apache编译文件和隐藏方法','','inherit','open','open','','200-revision','','','2010-09-23 23:15:25','2010-09-24 07:15:25','',200,'http://www.wordpress.cn/?p=201',0,'revision','',0),(202,1,'2010-09-24 18:38:22','2010-09-25 02:38:22','试验环境:\r\n\r\n主服务器:CentOS 5.2 Mysql 5.1.35 源码 IP:192.168.1.22\r\n\r\n从服务器:CentOS 5.2 Mysql 5.1.35 源码 IP:192.168.1.33\r\n\r\n配置:\r\n\r\n一、主服务器\r\n\r\n1.1、创建一个复制用户，具有replication slave 权限。\r\n\r\nmysql>grant replication slave on *.* to ‘repl’@’192.168.1.22′ identified by ‘repl’; \r\n1.2、编辑my.cnf文件\r\n\r\nvi /etc/my.cnf\r\n\r\n添加\r\n\r\nserver-id=1\r\n\r\n并开启log-bin二进制日志文件\r\n\r\nlog-bin=mysql-bin\r\n\r\n注:需要把默认的server-id=1去掉\r\n\r\n1.3、启动mysql数据库\r\n\r\nmysqld_safe –user=mysql & \r\n1.4、设置读锁\r\n\r\nmysql>flush tables with read lock; \r\n1.5、得到binlog日志文件名和偏移量\r\n\r\nmysql>show master status;  \r\n+——————+———-+————–+——————+  \r\n| File | Position | Binlog_Do_DB | Binlog_Ignore_DB |  \r\n+——————+———-+————–+——————+  \r\n| mysql-bin.0000010 | 106| | |  \r\n+——————+———-+————–+——————+ \r\n1.6、备份要同步的数据库\r\n\r\nmysqldump test > test.sql \r\n1.7、解锁\r\n\r\nmysql>unlock tables; \r\n二、从服务器\r\n\r\n2.1、编辑my.cnf文件\r\n\r\nvi /etc/my.cnf\r\n\r\n添加\r\n\r\nserver-id=2\r\n\r\n注:需要把默认的server-id=1去掉\r\n\r\n2.2、启动从数据库\r\n\r\nmysqld_safe –user=mysql & \r\n2.3、对从数据库进行相应设置\r\n\r\nmysql> change master to  \r\n-> master_host=’192.168.1.22′  \r\n-> master_user=’repl’  \r\n-> master_password=’repl’  \r\n-> master_log_file=’mysql-bin.0000010′  \r\n-> master_log_pos=106; \r\n2.4、启动从服务器slave线程\r\n\r\nmysql>start slave; \r\n执行show processlist命令显示以下进程:\r\n\r\nmysql>show processlist\\G  \r\n \r\n*************************** 2. row ***************************  \r\nId: 2  \r\nUser: system user  \r\nHost:  \r\ndb: NULL  \r\nCommand: Connect  \r\nTime: 2579  \r\nState: Has read all relay log; waiting for the slave I/O thread to update it \r\nInfo: NULL表示slave已经连接上master，开始接受并执行日志\r\n\r\n2.5、查看slave线程状态\r\n\r\nmysql>show slave status;  \r\n*************************** 1. row ***************************  \r\nSlave_IO_State: Waiting for master to send event  \r\nMaster_Host: 192.168.1.22  \r\nMaster_User: repl  \r\nMaster_Port: 3306  \r\nConnect_Retry: 60  \r\nMaster_Log_File: mysql-bin.0000010  \r\nRead_Master_Log_Pos: 106  \r\nRelay_Log_File: centos-relay-bin.000002  \r\nRelay_Log_Pos: 529  \r\nRelay_Master_Log_File: mysql-bin.0000010  \r\nSlave_IO_Running: Yes  \r\nSlave_SQL_Running: Yes  \r\nReplicate_Do_DB:  \r\nReplicate_Ignore_DB:  \r\nReplicate_Do_Table:  \r\nReplicate_Ignore_Table:  \r\nReplicate_Wild_Do_Table:  \r\nReplicate_Wild_Ignore_Table:  \r\nLast_Errno: 0  \r\nLast_Error:  \r\nSkip_Counter: 0  \r\nExec_Master_Log_Pos: 106  \r\nRelay_Log_Space: 830  \r\nUntil_Condition: None  \r\nUntil_Log_File:  \r\nUntil_Log_Pos: 0  \r\nMaster_SSL_Allowed: No  \r\nMaster_SSL_CA_File:  \r\nMaster_SSL_CA_Path:  \r\nMaster_SSL_Cert:  \r\nMaster_SSL_Cipher:  \r\nMaster_SSL_Key:  \r\nSeconds_Behind_Master: 0  \r\nMaster_SSL_Verify_Server_Cert: No  \r\nLast_IO_Errno: 0  \r\nLast_IO_Error:  \r\nLast_SQL_Errno: 0  \r\nLast_SQL_Error:  \r\n1 row in set (0.00 sec) \r\n验证是否配置正确\r\n\r\n在从服务器上执行\r\n\r\nshow slave status\\G;  \r\nWaiting for master to send event  \r\nSlave_IO_Running: Yes  \r\nSlave_SQL_Running: Yes \r\n如以上二行同时为Yes 说明配置成功\r\n\r\n测试：\r\n\r\n1、在主服务器test数据库中创建user表\r\n\r\nmysql>use test;  \r\nmysql>create table user(id int); \r\n2、在从服务器中查看user表\r\n\r\nmysql>use test;  \r\nmysql> show tables like ‘user’;  \r\n+———————-+  \r\n| Tables_in_test(user) |  \r\n+———————-+  \r\n| user |  \r\n+———————-+  \r\n1 row in set (0.00 sec) \r\n说明主从数据同步成功。\r\n\r\n常见问题归纳：\r\n\r\n1.在从数据库中查看slave状态时出现\r\n\r\nThe slave I/O thread stops because master and slave have equal MySQL server ids; these ids must be different for replication to work (or the –replicate-same-server-id option must be used on slave but this does not always make sense; please check the manual before using it)\r\n\r\n说明方从服务器里my.cnf中的server-id有相同的。\r\n\r\n解决办法:\r\n\r\n修改my.cnf里的server-id，并重启数据库服务。my.cnf文件默认有server-id=1\r\n\r\n其它说明\r\n\r\n主服务器my.cnf\r\n\r\n#binlog-do-db=需要备份的数据库名，可写多行\r\n\r\n#binlog-ignore-db=不需要备份的数据库名，可写多行\r\n\r\n从服务器my.cnf\r\n\r\n# replicate-do-db=test 需要备份的数据库名\r\n\r\n# replicate-ignore-db=mysql 忽略的数据库\r\n\r\n# master-connect-retry=60 如果从服务器发现主服务器断掉，重新连接的时间差(秒)\r\n\r\n以下设置也可直接修改my.cnf配置文件\r\n\r\nlog-bin=mysql-bin  \r\nmaster-host=192.168.1.22  \r\nmaster-user=repl \r\nmaster-password=repl \r\nmaster-port=3306 \r\n主从服务器同步维护\r\n\r\n由于各种原因，导致主从数据不一致，在负载低的时候，进行手动同步.\r\n\r\n在主服务器上执行\r\n\r\nmysql>flush tables with read lock;  \r\nQuery OK,rows affected (0.01 sec)  \r\nmysql>show master status;  \r\n+——————+———-+————–+——————+  \r\n| File | Position | Binlog_Do_DB | Binlog_Ignore_DB |  \r\n+——————+———-+————–+——————+  \r\n| mysql-bin.0000011 | 260| | |  \r\n+——————+———-+————–+——————+ \r\n在从服务器上执行\r\n\r\n先得到当前主服务器的二进制文件名和偏移量,执行命令使从服务器与主服务器同步\r\n\r\nmysql>select master_pos_wait(‘mysql-bin.0000011′,’260′);  \r\n+————————————————–+  \r\n| master_pos_wait(‘mysql-bin.0000011′,’260′) |  \r\n+————————————————–+  \r\n| 0 |  \r\n+————————————————–+  \r\n1 row in set (0.01 sec) \r\n同步完成后，在主服务器上执行解锁\r\n\r\nmysql>unlock tables; \r\n切换主从服务器\r\n\r\n当主服务器出现故障时，可将从服务器当主服务器来使用.步骤如下:\r\n\r\n1、保证所有从数据库都已经执行了relay log中的全部更新，在从服务器中执行\r\n\r\nstop slave io_thread,用show processlist检查，查看状态是否是Has read all relay log,表示更新完成.\r\n\r\nmysql>stop slave io_thread;  \r\nQuery OK,0 affected (0.00 sec)  \r\nmysql>show processlist\\G;  \r\n*************************** 2. row ***************************  \r\nId: 2  \r\nUser: system user  \r\nHost:  \r\ndb: NULL  \r\nCommand: Connect  \r\nTime: 4757  \r\nState: Has read all relay log; waiting for the slave I/O thread to update it  \r\nInfo: NULL \r\n2、在从服务器上执行stop slave，reset master命令，重置成主数据库\r\n\r\nmysql>stop slave;  \r\nQuery OK,0 affected (0.00 sec)  \r\nmysql>reset master;  \r\nQuery OK,0 affected (0.00 sec) \r\n3、删除新的主服务器数据库目录中的master.info和relay-log.info文件，否则下次重启时还会按照从服务器来启动.','实战MySQL主从数据库同步 增强系统安全性','','publish','open','open','','%e5%ae%9e%e6%88%98mysql%e4%b8%bb%e4%bb%8e%e6%95%b0%e6%8d%ae%e5%ba%93%e5%90%8c%e6%ad%a5-%e5%a2%9e%e5%bc%ba%e7%b3%bb%e7%bb%9f%e5%ae%89%e5%85%a8%e6%80%a7','','','2010-09-24 18:38:22','2010-09-25 02:38:22','',0,'http://www.wordpress.cn/?p=202',0,'post','',0),(203,1,'2010-09-24 18:38:03','2010-09-25 02:38:03','','实战MySQL主从数据库同步 增强系统安全性','','inherit','open','open','','202-revision','','','2010-09-24 18:38:03','2010-09-25 02:38:03','',202,'http://www.wordpress.cn/?p=203',0,'revision','',0),(210,1,'2010-10-07 19:25:49','2010-10-08 03:25:49','如果您是才接触My<a href=\"http://database.51cto.com/art/201009/223213.htm\">SQL</a>数据库的新人，那么MySQL中多表删除是您一定需要掌握的，下面就将为详细介绍MySQL中多表删除的方法，供您参考，希望对你学习掌握MySQL中多表删除能有所帮助。\r\n\r\n1、从MySQL数据表t1中把那些id值在数据表t2里有匹配的记录全删除掉\r\n\r\nDELETE t1 FROM t1，t2 WHERE t1.id=t2.id 或DELETE FROM t1 USING t1，t2 WHERE t1.id=t2.id\r\n\r\n2、从MySQL数据表t1里在数据表t2里没有匹配的记录查找出来并删除掉\r\n\r\nDELETE t1 FROM t1 LEFT JOIN T2 ON t1.id=t2.id WHERE t2.id IS NULL 或\r\n\r\nDELETE FROM t1，USING t1 LEFT JOIN T2 ON t1.id=t2.id WHERE t2.id IS NULL\r\n\r\n3、从两个表中找出相同记录的数据并把两个表中的数据都删除掉\r\n\r\nDELETE t1，t2 from t1 LEFT JOIN t2 ON t1.id=t2.id WHERE t1.id=25\r\n\r\n注意此处的delete t1，t2 from 中的t1，t2不能是别名\r\n\r\n如：delete t1，t2 from table_name as t1 left join table2_name as t2 on t1.id=t2.id where table_name.id=25 在数据里面执行是错误的（MySQL 版本不小于5.0在5.0中是可以的）\r\n\r\n上述语句改写成\r\n\r\ndelete table_name，table2_name from table_name as t1 left join table2_name as t2 on t1.id=t2.id where table_name.id=25 在数据里面执行是错误的（MySQL 版本小于5.0在5.0中是可以的）\r\n\r\n以上就是MySQL中多表删除的方法介绍。','MySQL中多表删除方法','','publish','open','open','','mysql%e4%b8%ad%e5%a4%9a%e8%a1%a8%e5%88%a0%e9%99%a4%e6%96%b9%e6%b3%95','','','2010-10-07 19:25:49','2010-10-08 03:25:49','',0,'http://www.wordpress.cn/?p=210',0,'post','',0),(211,1,'2010-10-07 19:25:45','2010-10-08 03:25:45','如果您是才接触My<a href=\"http://database.51cto.com/art/201009/223213.htm\">SQL</a>数据库的新人，那么MySQL中多表删除是您一定需要掌握的，下面就将为详细介绍MySQL中多表删除的方法，供您参考，希望对你学习掌握MySQL中多表删除能有所帮助。\n\n1、从MySQL数据表t1中把那些id值在数据表t2里有匹配的记录全删除掉\n\nDELETE t1 FROM t1，t2 WHERE t1.id=t2.id 或DELETE FROM t1 USING t1，t2 WHERE t1.id=t2.id\n\n2、从MySQL数据表t1里在数据表t2里没有匹配的记录查找出来并删除掉\n\nDELETE t1 FROM t1 LEFT JOIN T2 ON t1.id=t2.id WHERE t2.id IS NULL 或\n\nDELETE FROM t1，USING t1 LEFT JOIN T2 ON t1.id=t2.id WHERE t2.id IS NULL\n\n3、从两个表中找出相同记录的数据并把两个表中的数据都删除掉\n\nDELETE t1，t2 from t1 LEFT JOIN t2 ON t1.id=t2.id WHERE t1.id=25\n\n注意此处的delete t1，t2 from 中的t1，t2不能是别名\n\n如：delete t1，t2 from table_name as t1 left join table2_name as t2 on t1.id=t2.id where table_name.id=25 在数据里面执行是错误的（MySQL 版本不小于5.0在5.0中是可以的）\n\n上述语句改写成\n\ndelete table_name，table2_name from table_name as t1 left join table2_name as t2 on t1.id=t2.id where table_name.id=25 在数据里面执行是错误的（MySQL 版本小于5.0在5.0中是可以的）\n\n以上就是MySQL中多表删除的方法介绍。','MySQL中多表删除方法','','inherit','open','open','','210-revision','','','2010-10-07 19:25:45','2010-10-08 03:25:45','',210,'http://www.wordpress.cn/?p=211',0,'revision','',0),(212,1,'2010-10-07 19:28:18','2010-10-08 03:28:18','如果您是才接触My<a href=\"http://database.51cto.com/art/201009/223213.htm\">SQL</a>数据库的新人，那么MySQL中多表删除是您一定需要掌握的，下面就将为详细介绍MySQL中多表删除的方法，供您参考，希望对你学习掌握MySQL中多表删除能有所帮助。\r\n\r\n1、从MySQL数据表t1中把那些id值在数据表t2里有匹配的记录全删除掉\r\n\r\nDELETE t1 FROM t1，t2 WHERE t1.id=t2.id 或DELETE FROM t1 USING t1，t2 WHERE t1.id=t2.id\r\n\r\n2、从MySQL数据表t1里在数据表t2里没有匹配的记录查找出来并删除掉\r\n\r\nDELETE t1 FROM t1 LEFT JOIN T2 ON t1.id=t2.id WHERE t2.id IS NULL 或\r\n\r\nDELETE FROM t1，USING t1 LEFT JOIN T2 ON t1.id=t2.id WHERE t2.id IS NULL\r\n\r\n3、从两个表中找出相同记录的数据并把两个表中的数据都删除掉\r\n\r\nDELETE t1，t2 from t1 LEFT JOIN t2 ON t1.id=t2.id WHERE t1.id=25\r\n\r\n注意此处的delete t1，t2 from 中的t1，t2不能是别名\r\n\r\n如：delete t1，t2 from table_name as t1 left join table2_name as t2 on t1.id=t2.id where table_name.id=25 在数据里面执行是错误的（MySQL 版本不小于5.0在5.0中是可以的）\r\n\r\n上述语句改写成\r\n\r\ndelete table_name，table2_name from table_name as t1 left join table2_name as t2 on t1.id=t2.id where table_name.id=25 在数据里面执行是错误的（MySQL 版本小于5.0在5.0中是可以的）\r\n\r\n以上就是MySQL中多表删除的方法介绍。','MySQL中多表删除方法','','inherit','open','open','','210-autosave','','','2010-10-07 19:28:18','2010-10-08 03:28:18','',210,'http://www.wordpress.cn/?p=212',0,'revision','',0),(213,1,'2010-10-08 17:41:53','2010-10-09 01:41:53','考虑到在本机上备份数据，一旦该机器硬盘出现故障，数据无法取出。远程手动备份数据费时费力且不及时。最好的方法就是通过脚本实现远程自动互备。但远程无论是通过SSH登陆，还是通过scp拷贝文件都需要输入密码。为了克服这个问题，首先需要实现不需要密码的SSH登陆，这样就可以使用rsync，scp，rexec等命令来做的远程备份了。\r\n\r\n1. 设置无需密码的ssh登陆，方法如下：\r\n\r\n假设A，B两服务器，现在需要在A机上用root登陆B机，而不需要输入密码，那我们可按照下面的步骤来做：\r\n\r\n1)在A机上生成钥匙对,执行以下命令：\r\n\r\nssh-keygen -t rsa\r\n\r\nGenerating public/private rsa key pair.\r\n\r\nEnter file in which to save the key (/root/.ssh/id_rsa): /root/.ssh/id_rsa\r\n\r\nEnter passphrase (empty for no passphrase):直接回车\r\n\r\nEnter same passphrase again:直接回车\r\n\r\nYour identification has been saved in /root/.ssh/id_rsa.\r\n\r\nYour public key has been saved in /root/.ssh/id_rsa.pub.\r\n\r\nThe key fingerprint is:\r\n\r\nf6:61:a8:27:35:cf:4c:6d:13:22:70:cf:4c:c8:a0:23 root@host1\r\n\r\n这样，在/root/.ssh/路径下会生成id_rsa，和id_rsa.pub，其中id_rsa是密钥，id_rsa.pub是公钥。\r\n\r\n2)把在A机生成的id_rsa.pub拷贝到B机上，假设拷贝到B机的临时目录下，如：\r\n\r\nscp /root/.ssh/id_rsa.pub root@218.242.214.20:/tmp\r\n\r\n3)用root帐号登陆B机，进入其主目录，创建authorized_keys文件，并设置好权限。\r\n\r\ncd ~/.ssh\r\n\r\ncat /tmp/id_rsa.pub &gt;&gt;authorized_keys\r\n\r\nchmod 400 authorized_keys\r\n\r\nrm -f /tmp/id_rsa.pub\r\n\r\n4)测试\r\n\r\n在A机上转到root帐号，尝试登录B机。看看是不是不要密码.\r\n\r\n说明：\r\n\r\nauthorized_keys文件的权限很重要，如果设置为777，那么登录的时候，还是需要提供密码的。\r\n\r\n记得将临时目录下的id_rsa.pub删除，养成个好习惯。\r\n\r\n本方法在Red Hat9.0上测试通过。\r\n\r\n2. 编辑crontab文件\r\n\r\nvi /etc/crontab\r\n\r\n如设置每天凌晨3：00执行cron.daily中的脚本：\r\n\r\n00 3 * * * root run-parts /etc/cron.daily\r\n\r\n3.编辑cron.daily中的脚本\r\n\r\ncd /etc/cron.daily/\r\n\r\nvi backupdb\r\n\r\npg_dump -U postgres voipack &gt; /voipack.sql\r\n\r\npg_dump -U postgres regserver &gt; /regserver.sql\r\n\r\ntar -cvjf /aavm.tgz.bz2 /usr/local/aavm\r\n\r\ntar -cvjf /oracle.tgz.bz2 /var/oracle\r\n\r\nscp /voipack.sql root@218.242.214.20:/root/218.242.214.23_backup\r\n\r\nscp /regserver.sql root@218.242.214.20:/root/218.242.214.23_backup\r\n\r\nscp /aavm.tgz.bz2 root@218.242.214.20:/root/218.242.214.23_backup\r\n\r\nscp /oracle.tgz.bz2 root@218.242.214.20:/root/218.242.214.23_backup\r\n\r\n将23上产生的备份文件copy到218.242.214.20:/root/218.242.214.23_backup路径下\r\n\r\n同样的方法可以将假设B服务器上的数据备份到A服务器，实现双机的互备','Linux 下通过脚本实现远程自动备份','','publish','open','open','','linux-%e4%b8%8b%e9%80%9a%e8%bf%87%e8%84%9a%e6%9c%ac%e5%ae%9e%e7%8e%b0%e8%bf%9c%e7%a8%8b%e8%87%aa%e5%8a%a8%e5%a4%87%e4%bb%bd','','','2010-10-08 17:41:53','2010-10-09 01:41:53','',0,'http://www.wordpress.cn/?p=213',0,'post','',0),(214,1,'2010-10-08 17:35:52','2010-10-09 01:35:52','考虑到在本机上备份数据，一旦该机器硬盘出现故障，数据无法取出。远程手动备份数据费时费力且不及时。最好的方法就是通过脚本实现远程自动互备。但远程无论是通过SSH登陆，还是通过scp拷贝文件都需要输入密码。为了克服这个问题，首先需要实现不需要密码的SSH登陆，这样就可以使用rsync，scp，rexec等命令来做的远程备份了。\n\n1. 设置无需密码的ssh登陆，方法如下：\n\n假设A，B两服务器，现在需要在A机上用root登陆B机，而不需要输入密码，那我们可按照下面的步骤来做：\n\n1)在A机上生成钥匙对,执行以下命令：\n\nssh-keygen -t rsa\n\nGenerating public/private rsa key pair.\n\nEnter file in which to save the key (/root/.ssh/id_rsa): /root/.ssh/id_rsa\n\nEnter passphrase (empty for no passphrase):直接回车\n\nEnter same passphrase again:直接回车\n\nYour identification has been saved in /root/.ssh/id_rsa.\n\nYour public key has been saved in /root/.ssh/id_rsa.pub.\n\nThe key fingerprint is:\n\nf6:61:a8:27:35:cf:4c:6d:13:22:70:cf:4c:c8:a0:23 root@host1\n\n这样，在/root/.ssh/路径下会生成id_rsa，和id_rsa.pub，其中id_rsa是密钥，id_rsa.pub是公钥。\n\n2)把在A机生成的id_rsa.pub拷贝到B机上，假设拷贝到B机的临时目录下，如：\n\nscp /root/.ssh/id_rsa.pub root@218.242.214.20:/tmp\n\n3)用root帐号登陆B机，进入其主目录，创建authorized_keys文件，并设置好权限。\n\ncd ~/.ssh\n\ncat /tmp/id_rsa.pub &gt;&gt;authorized_keys\n\nchmod 400 authorized_keys\n\nrm -f /tmp/id_rsa.pub\n\n4)测试\n\n在A机上转到root帐号，尝试登录B机。看看是不是不要密码.\n\n说明：\n\nauthorized_keys文件的权限很重要，如果设置为777，那么登录的时候，还是需要提供密码的。\n\n记得将临时目录下的id_rsa.pub删除，养成个好习惯。\n\n本方法在Red Hat9.0上测试通过。\n\n2. 编辑crontab文件\n\nvi /etc/crontab\n\n如设置每天凌晨3：00执行cron.daily中的脚本：\n\n00 3 * * * root run-parts /etc/cron.daily\n\n3.编辑cron.daily中的脚本\n\ncd /etc/cron.daily/\n\nvi backupdb\n\npg_dump -U postgres voipack &gt; /voipack.sql\n\npg_dump -U postgres regserver &gt; /regserver.sql\n\ntar -cvjf /aavm.tgz.bz2 /usr/local/aavm\n\ntar -cvjf /oracle.tgz.bz2 /var/oracle\n\nscp /voipack.sql root@218.242.214.20:/root/218.242.214.23_backup\n\nscp /regserver.sql root@218.242.214.20:/root/218.242.214.23_backup\n\nscp /aavm.tgz.bz2 root@218.242.214.20:/root/218.242.214.23_backup\n\nscp /oracle.tgz.bz2 root@218.242.214.20:/root/218.242.214.23_backup\n\n将23上产生的备份文件copy到218.242.214.20:/root/218.242.214.23_backup路径下\n\n同样的方法可以将假设B服务器上的数据备份到A服务器，实现双机的互备','Linux 下通过脚本实现远程自动备份','','inherit','open','open','','213-revision','','','2010-10-08 17:35:52','2010-10-09 01:35:52','',213,'http://www.wordpress.cn/?p=214',0,'revision','',0),(232,1,'2010-11-05 18:05:02','2010-11-06 02:05:02','首先我们先介绍一下为什么要让 Apache 与 Tomcat 之间进行连接。事实上 Tomcat 本身已经提供了 HTTP 服务，该服务默认的端口是 8080，装好 tomcat 后通过 8080 端口可以直接使用 Tomcat 所运行的应用程序，你也可以将该端口改为 80。\r\n\r\n既然 Tomcat 本身已经可以提供这样的服务，我们为什么还要引入 Apache 或者其他的一些专门的 HTTP 服务器呢？原因有下面几个：\r\n\r\n1. 提升对静态文件的处理性能\r\n\r\n2. 利用 Web 服务器来做负载均衡以及容错\r\n\r\n3. 无缝的升级应用程序\r\n\r\n这三点对一个 web 网站来说是非常之重要的，我们希望我们的网站不仅是速度快，而且要稳定，不能因为某个 Tomcat 宕机或者是升级程序导致用户访问不了，而能完成这几个功能的、最好的 HTTP 服务器也就只有 apache 的 http server 了，它跟 tomcat 的结合是最紧密和可靠的。\r\n\r\n接下来我们介绍三种方法将 apache 和 tomcat 整合在一起。\r\n\r\n<a name=\"N10058\">JK</a>\r\n\r\n这是最常见的方式，你可以在网上找到很多关于配置JK的网页，当然最全的还是其官方所提供的文档。JK 本身有两个版本分别是 1 和 2，目前 1 最新的版本是 1.2.19，而版本 2 早已经废弃了，以后不再有新版本的推出了，所以建议你采用版本 1。\r\n\r\nJK 是通过 AJP 协议与 Tomcat 服务器进行通讯的，Tomcat 默认的 AJP Connector 的端口是 8009。JK 本身提供了一个监控以及管理的页面 jkstatus，通过 jkstatus 可以监控 JK 目前的工作状态以及对到 tomcat 的连接进行设置，如下图所示：\r\n<a name=\"N10066\"><strong>图 1：监控以及管理的页面 jkstatus</strong></a>\r\n<img src=\"http://www.ibm.com/developerworks/cn/opensource/os-lo-apache-tomcat/images/image002.jpg\" border=\"0\" alt=\"图 1：监控以及管理的页面 jkstatus\" width=\"554\" height=\"228\" />\r\n\r\n在这个图中我们可以看到当前JK配了两个连接分别到 8109 和 8209 端口上，目前 s2 这个连接是停止状态，而 s1 这个连接自上次重启后已经处理了 47 万多个请求，流量达到 6.2 个 G，最大的并发数有 13 等等。我们也可以利用 jkstatus 的管理功能来切换 JK 到不同的 Tomcat 上，例如将 s2 启用，并停用 s1，这个在更新应用程序的时候非常有用，而且整个切换过程对用户来说是透明的，也就达到了无缝升级的目的。关于 JK 的配置文章网上已经非常多了，这里我们不再详细的介绍整个配置过程，但我要讲一下配置的思路，只要明白了配置的思路，JK 就是一个非常灵活的组件。\r\n\r\nJK 的配置最关键的有三个文件，分别是\r\n\r\n<strong>httpd.conf </strong>\r\nApache 服务器的配置文件，用来加载 JK 模块以及指定 JK 配置文件信息\r\n\r\n<strong>workers.properties</strong>\r\n到 Tomcat 服务器的连接定义文件\r\n\r\n<strong>uriworkermap.properties</strong>\r\nURI 映射文件，用来指定哪些 URL 由 Tomcat 处理，你也可以直接在 httpd.conf 中配置这些 URI，但是独立这些配置的好处是 JK 模块会定期更新该文件的内容，使得我们修改配置的时候无需重新启动 Apache 服务器。\r\n\r\n其中第二、三个配置文件名都可以自定义。下面是一个典型的 httpd.conf 对 JK 的配置\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<pre># (httpd.conf)\r\n# 加载 mod_jk 模块\r\nLoadModule jk_module modules/mod_jk.so\r\n\r\n#\r\n# Configure mod_jk\r\n#\r\n\r\nJkWorkersFile conf/workers.properties\r\nJkMountFile conf/uriworkermap.properties\r\nJkLogFile logs/mod_jk.log\r\nJkLogLevel warn</pre>\r\n</tr>\r\n</tbody>\r\n</table>\r\n \r\n\r\n接下来我们在 Apache 的 conf 目录下新建两个文件分别是 workers.properties、uriworkermap.properties。这两个文件的内容大概如下\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<pre>#\r\n# workers.properties\r\n#\r\n\r\n# list the workers by name\r\n\r\nworker.list=DLOG4J, status\r\n\r\n# localhost server 1\r\n# ------------------------\r\nworker.s1.port=8109\r\nworker.s1.host=localhost\r\nworker.s1.type=ajp13\r\n\r\n# localhost server 2\r\n# ------------------------\r\nworker.s2.port=8209\r\nworker.s2.host=localhost\r\nworker.s2.type=ajp13\r\nworker.s2.stopped=1\r\n\r\nworker.DLOG4J.type=lb\r\nworker.retries=3\r\nworker.DLOG4J.balanced_workers=s1, s2\r\nworker.DLOG4J.sticky_session=1\r\n\r\nworker.status.type=status</pre>\r\n</tr>\r\n</tbody>\r\n</table>\r\n \r\n\r\n以上的 workers.properties 配置就是我们前面那个屏幕抓图的页面所用的配置。首先我们配置了两个类型为 ajp13 的 worker 分别是 s1 和 s2，它们指向同一台服务器上运行在两个不同端口 8109 和 8209 的 Tomcat 上。接下来我们配置了一个类型为 lb（也就是负载均衡的意思）的 worker，它的名字是 DLOG4J，这是一个逻辑的 worker，它用来管理前面配置的两个物理连接 s1 和 s2。最后还配置了一个类型为 status 的 worker，这是用来监控 JK 本身的模块。有了这三个 worker 还不够，我们还需要告诉 JK，哪些 worker 是可用的，所以就有 <strong>worker.list = DLOG4J, status</strong> 这行配置。\r\n\r\n接下来便是 URI 的映射配置了，我们需要指定哪些链接是由 Tomcat 处理的，哪些是由 Apache 直接处理的，看看下面这个文件你就能明白其中配置的意义\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<pre>/*=DLOG4J\r\n/jkstatus=status\r\n\r\n!/*.gif=DLOG4J\r\n!/*.jpg=DLOG4J\r\n!/*.png=DLOG4J\r\n!/*.css=DLOG4J\r\n!/*.js=DLOG4J\r\n!/*.htm=DLOG4J\r\n!/*.html=DLOG4J</pre>\r\n</tr>\r\n</tbody>\r\n</table>\r\n \r\n\r\n相信你已经明白了一大半了：所有的请求都由 DLOG4J 这个 worker 进行处理，但是有几个例外，/jkstatus 请求由 status 这个 worker 处理。另外这个配置中每一行数据前面的感叹号是什么意思呢？感叹号表示接下来的 URI 不要由 JK 进行处理，也就是 Apache 直接处理所有的图片、css 文件、js 文件以及静态 html 文本文件。\r\n\r\n通过对 workers.properties 和 uriworkermap.properties 的配置，可以有各种各样的组合来满足我们前面提出对一个 web 网站的要求。您不妨动手试试！\r\n\r\n<hr />回页首\r\n\r\n<a name=\"N100C4\">http_proxy</a>\r\n\r\n这是利用 Apache 自带的 mod_proxy 模块使用代理技术来连接 Tomcat。在配置之前请确保是否使用的是 2.2.x 版本的 Apache 服务器。因为 2.2.x 版本对这个模块进行了重写，大大的增强了其功能和稳定性。\r\n\r\nhttp_proxy 模式是基于 HTTP 协议的代理，因此它要求 Tomcat 必须提供 HTTP 服务，也就是说必须启用 Tomcat 的 HTTP Connector。一个最简单的配置如下\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<pre>ProxyPass /images !\r\nProxyPass /css !\r\nProxyPass /js !\r\nProxyPass / http://localhost:8080/</pre>\r\n</tr>\r\n</tbody>\r\n</table>\r\n \r\n\r\n在这个配置中，我们把所有 http://localhost 的请求代理到 http://localhost:8080/ ，这也就是 Tomcat 的访问地址，除了 images、css、js 几个目录除外。我们同样可以利用 mod_proxy 来做负载均衡，再看看下面这个配置\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<pre>ProxyPass /images !\r\nProxyPass /css ! \r\nProxyPass /js !\r\n\r\nProxyPass / balancer://example/\r\n&lt;Proxy balancer://example/&gt;\r\nBalancerMember http://server1:8080/\r\nBalancerMember http://server2:8080/\r\nBalancerMember http://server3:8080/\r\n&lt;/Proxy&gt;</pre>\r\n</tr>\r\n</tbody>\r\n</table>\r\n \r\n\r\n配置比 JK 简单多了，而且它也可以通过一个页面来监控集群运行的状态，并做一些简单的维护设置。\r\n<a name=\"N100EA\"><strong>图 2：监控集群运行状态</strong></a>\r\n<img src=\"http://www.ibm.com/developerworks/cn/opensource/os-lo-apache-tomcat/images/image004.gif\" border=\"0\" alt=\"图 2：监控集群运行状态\" width=\"553\" height=\"427\" />\r\n\r\n<hr />回页首\r\n\r\n<a name=\"N100FA\">ajp_proxy</a>\r\n\r\najp_proxy 连接方式其实跟 http_proxy 方式一样，都是由 mod_proxy 所提供的功能。配置也是一样，只需要把 http:// 换成 ajp:// ，同时连接的是 Tomcat 的 AJP Connector 所在的端口。上面例子的配置可以改为：\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<pre>ProxyPass /images !\r\nProxyPass /css ! \r\nProxyPass /js !\r\n\r\nProxyPass / balancer://example/\r\n&lt;Proxy balancer://example/&gt;\r\nBalancerMember ajp://server1:8080/\r\nBalancerMember ajp://server2:8080/\r\nBalancerMember ajp://server3:8080/\r\n&lt;/Proxy&gt;</pre>\r\n</tr>\r\n</tbody>\r\n</table>\r\n \r\n\r\n采用 proxy 的连接方式，需要在 Apache 上加载所需的模块，mod_proxy 相关的模块有 mod_proxy.so、mod_proxy_connect.so、mod_proxy_http.so、mod_proxy_ftp.so、mod_proxy_ajp.so， 其中 mod_proxy_ajp.so 只在 Apache 2.2.x 中才有。如果是采用 http_proxy 方式则需要加载 mod_proxy.so 和 mod_proxy_http.so；如果是 ajp_proxy 则需要加载 mod_proxy.so 和 mod_proxy_ajp.so这两个模块。\r\n\r\n<hr />回页首\r\n\r\n<a name=\"N1010F\">三者比较</a>\r\n\r\n相对于 JK 的连接方式，后两种在配置上是比较简单的，灵活性方面也一点都不逊色。但就稳定性而言就不像 JK 这样久经考验，毕竟 Apache 2.2.3 推出的时间并不长，采用这种连接方式的网站还不多，因此，如果是应用于关键的互联网网站，还是建议采用 JK 的连接方式。','Apache HTTP Server 与 Tomcat 的三种连接方式介绍','','publish','open','open','','apache-http-server-%e4%b8%8e-tomcat-%e7%9a%84%e4%b8%89%e7%a7%8d%e8%bf%9e%e6%8e%a5%e6%96%b9%e5%bc%8f%e4%bb%8b%e7%bb%8d','','','2010-11-05 18:05:02','2010-11-06 02:05:02','',0,'http://www.wordpress.cn/?p=232',0,'post','',0),(216,1,'2010-10-08 23:59:00','2010-10-09 07:59:00','<ol>\r\n	<li>/**</li>\r\n	<li> *A pagination generation class</li>\r\n	<li> *@class  : Pagination</li>\r\n	<li> *@version: 1.0.0</li>\r\n	<li> *@author : huixinchen at baidu.com</li>\r\n	<li> *@useage:</li>\r\n	<li> *      $pagi                   =  new Pagination($url_prefix, $page_size, $mesgs_count, $pagination_size=10, array $conf);</li>\r\n	<li> *  $navigation_str = $pagi-&gt;generate($current_page_number);</li>\r\n	<li> */</li>\r\n	<li>class Pagination{</li>\r\n	<li> private $page, $total_page, $total, $page_size, $size;</li>\r\n	<li> private $prev_str = \"&amp;lt\", $next_str = \"&amp;gt\";</li>\r\n	<li> private $class, $selected_class = \"selected\", $prev_class=\"prev\", $next_class=\"next\",</li>\r\n	<li> $de_prev_class=\"de_prev\", $de_next_class=\"de_next\";</li>\r\n	<li> private $url_prefix=\"\", $split_char=\"?\";</li>\r\n	<li> private $para_name = \"page\", $target = \"\";</li>\r\n	<li> public function Pagination($url_prefix, $page_size, $total, $size=10, $conf=array()){</li>\r\n	<li> $this-&gt;page       = 1;</li>\r\n	<li> $this-&gt;page_size  = $page_size;</li>\r\n	<li> $this-&gt;total      = $total;</li>\r\n	<li> $this-&gt;total_page = intval(ceil($total/$page_size));</li>\r\n	<li> $this-&gt;size               = $size;</li>\r\n	<li> if(!empty($conf)){</li>\r\n	<li> $configure = array(\"prev_str\", \"next_str\", \"class\", \"selected_class\");</li>\r\n	<li> foreach($conf as $key =&gt; $val){</li>\r\n	<li> if(in_array($key, $configure)){</li>\r\n	<li> $this-&gt;$val = $val;</li>\r\n	<li> }</li>\r\n	<li> }</li>\r\n	<li> }</li>\r\n	<li> $this-&gt;url_prefix = $url_prefix;</li>\r\n	<li> if(strstr($url_prefix, \'?\') !== false){</li>\r\n	<li> $this-&gt;url_prefix .= \"&amp;\" . $this-&gt;para_name . \"=\";</li>\r\n	<li> }else{</li>\r\n	<li> $this-&gt;url_prefix .= \"?\" . $this-&gt;para_name . \"=\";</li>\r\n	<li> }</li>\r\n	<li> }</li>\r\n	<li></li>\r\n	<li> public function generate($page){</li>\r\n	<li> $this-&gt;page = $page;</li>\r\n	<li> if(isset($this-&gt;page[$page])){</li>\r\n	<li> return $this-&gt;page_str[$page];</li>\r\n	<li> }</li>\r\n	<li> $page_start = 1;</li>\r\n	<li> $half           = intval($this-&gt;size/2);</li>\r\n	<li> $page_start = max(1, $page - $half);</li>\r\n	<li> $page_end       = min($page_start + $this-&gt;size - 1, $this-&gt;total_page);</li>\r\n	<li> $page_start = max(1, $page_end - $this-&gt;size + 1);</li>\r\n	<li></li>\r\n	<li> $this-&gt;page_str[$page] = $this-&gt;build_nav_str($page_start, $page_end);</li>\r\n	<li> return $this-&gt;page_str[$page];</li>\r\n	<li> }</li>\r\n	<li></li>\r\n	<li> private function build_nav_str($page_start, $page_end){</li>\r\n	<li> $page_nums = range($page_start, $page_end);</li>\r\n	<li> $target    = $this-&gt;target? \" target=\\\"{$this-&gt;target}\\\"\" : \"\";</li>\r\n	<li> if($this-&gt;page == 1){</li>\r\n	<li> $page_str = &lt;&lt;&lt;HTML</li>\r\n	<li> &lt;span class=\"{$this-&gt;de_prev_class}\"&gt; {$this-&gt;prev_str} &lt;/span&gt;</li>\r\n	<li>HTML;</li>\r\n	<li> }else{</li>\r\n	<li> $page     = $this-&gt;page - 1;</li>\r\n	<li> $page_str = &lt;&lt;&lt;HTML</li>\r\n	<li> &lt;span class=\"{$this-&gt;prev_class}\"&gt; &lt;a href=\"{$this-&gt;url_prefix}{$page}\"{$this-&gt;target}&gt;{$this-&gt;prev_str}&lt;/a&gt;&lt;/span&gt;</li>\r\n	<li>HTML;</li>\r\n	<li> }</li>\r\n	<li> foreach($page_nums as $p){</li>\r\n	<li> $page_str .= ($p == $this-&gt;page) ? &lt;&lt;&lt;HTML</li>\r\n	<li> &lt;span class=\"{$this-&gt;selected_class}\"&gt;{$p}&lt;/span&gt;</li>\r\n	<li>HTML</li>\r\n	<li> : &lt;&lt;&lt;HTML</li>\r\n	<li> &lt;span class=\"{$this-&gt;class}\"&gt;&lt;a href=\"{$this-&gt;url_prefix}{$p}\"{$this-&gt;target}&gt;{$p}&lt;/a&gt;&lt;/span&gt;</li>\r\n	<li>HTML;</li>\r\n	<li></li>\r\n	<li> }</li>\r\n	<li> if($this-&gt;page == $this-&gt;total_page){</li>\r\n	<li> $page_str .= &lt;&lt;&lt;HTML</li>\r\n	<li> &lt;span class=\"{$this-&gt;de_next_class}\"&gt; {$this-&gt;next_str} &lt;/span&gt;</li>\r\n	<li>HTML;</li>\r\n	<li> }else{</li>\r\n	<li> $page      = $this-&gt;page + 1;</li>\r\n	<li> $page_str .= &lt;&lt;&lt;HTML</li>\r\n	<li> &lt;span class=\"{$this-&gt;next_class}\"&gt; &lt;a href=\"{$this-&gt;url_prefix}{$page}\"{$this-&gt;target}&gt;{$this-&gt;next_str}&lt;/a&gt;&lt;/span&gt;</li>\r\n	<li>HTML;</li>\r\n	<li> }</li>\r\n	<li> return $page_str;</li>\r\n	<li> }</li>\r\n	<li></li>\r\n	<li> public function tidy_str(){</li>\r\n	<li> ;//void</li>\r\n	<li> }</li>\r\n	<li></li>\r\n	<li> public function __call($func_name, $arguments){</li>\r\n	<li> if(isset($this-&gt;$func_name)){</li>\r\n	<li> return $this-&gt;$func_name;</li>\r\n	<li> }</li>\r\n	<li> }</li>\r\n	<li></li>\r\n	<li> public function __destruct(){</li>\r\n	<li> unset($this-&gt;page_str);</li>\r\n	<li> unset($this);</li>\r\n	<li> }</li>\r\n	<li>}</li>\r\n</ol>','一个巧妙的php分页方法','','publish','open','open','','%e4%b8%80%e4%b8%aa%e5%b7%a7%e5%a6%99%e7%9a%84php%e5%88%86%e9%a1%b5%e6%96%b9%e6%b3%95','','','2010-10-08 23:59:00','2010-10-09 07:59:00','',0,'http://www.wordpress.cn/?p=216',0,'post','',0),(217,1,'2010-10-08 23:58:51','2010-10-09 07:58:51','<ol>\n	<li>/**</li>\n	<li> *A pagination generation class</li>\n	<li> *@class  : Pagination</li>\n	<li> *@version: 1.0.0</li>\n	<li> *@author : huixinchen at baidu.com</li>\n	<li> *@useage:</li>\n	<li> *      $pagi                   =  new Pagination($url_prefix, $page_size, $mesgs_count, $pagination_size=10, array $conf);</li>\n	<li> *  $navigation_str = $pagi-&gt;generate($current_page_number);</li>\n	<li> */</li>\n	<li>class Pagination{</li>\n	<li> private $page, $total_page, $total, $page_size, $size;</li>\n	<li> private $prev_str = \"&amp;lt\", $next_str = \"&amp;gt\";</li>\n	<li> private $class, $selected_class = \"selected\", $prev_class=\"prev\", $next_class=\"next\",</li>\n	<li> $de_prev_class=\"de_prev\", $de_next_class=\"de_next\";</li>\n	<li> private $url_prefix=\"\", $split_char=\"?\";</li>\n	<li> private $para_name = \"page\", $target = \"\";</li>\n	<li> public function Pagination($url_prefix, $page_size, $total, $size=10, $conf=array()){</li>\n	<li> $this-&gt;page       = 1;</li>\n	<li> $this-&gt;page_size  = $page_size;</li>\n	<li> $this-&gt;total      = $total;</li>\n	<li> $this-&gt;total_page = intval(ceil($total/$page_size));</li>\n	<li> $this-&gt;size               = $size;</li>\n	<li> if(!empty($conf)){</li>\n	<li> $configure = array(\"prev_str\", \"next_str\", \"class\", \"selected_class\");</li>\n	<li> foreach($conf as $key =&gt; $val){</li>\n	<li> if(in_array($key, $configure)){</li>\n	<li> $this-&gt;$val = $val;</li>\n	<li> }</li>\n	<li> }</li>\n	<li> }</li>\n	<li> $this-&gt;url_prefix = $url_prefix;</li>\n	<li> if(strstr($url_prefix, \'?\') !== false){</li>\n	<li> $this-&gt;url_prefix .= \"&amp;\" . $this-&gt;para_name . \"=\";</li>\n	<li> }else{</li>\n	<li> $this-&gt;url_prefix .= \"?\" . $this-&gt;para_name . \"=\";</li>\n	<li> }</li>\n	<li> }</li>\n	<li></li>\n	<li> public function generate($page){</li>\n	<li> $this-&gt;page = $page;</li>\n	<li> if(isset($this-&gt;page[$page])){</li>\n	<li> return $this-&gt;page_str[$page];</li>\n	<li> }</li>\n	<li> $page_start = 1;</li>\n	<li> $half           = intval($this-&gt;size/2);</li>\n	<li> $page_start = max(1, $page - $half);</li>\n	<li> $page_end       = min($page_start + $this-&gt;size - 1, $this-&gt;total_page);</li>\n	<li> $page_start = max(1, $page_end - $this-&gt;size + 1);</li>\n	<li></li>\n	<li> $this-&gt;page_str[$page] = $this-&gt;build_nav_str($page_start, $page_end);</li>\n	<li> return $this-&gt;page_str[$page];</li>\n	<li> }</li>\n	<li></li>\n	<li> private function build_nav_str($page_start, $page_end){</li>\n	<li> $page_nums = range($page_start, $page_end);</li>\n	<li> $target    = $this-&gt;target? \" target=\\\"{$this-&gt;target}\\\"\" : \"\";</li>\n	<li> if($this-&gt;page == 1){</li>\n	<li> $page_str = &lt;&lt;&lt;HTML</li>\n	<li> &lt;span class=\"{$this-&gt;de_prev_class}\"&gt; {$this-&gt;prev_str} &lt;/span&gt;</li>\n	<li>HTML;</li>\n	<li> }else{</li>\n	<li> $page     = $this-&gt;page - 1;</li>\n	<li> $page_str = &lt;&lt;&lt;HTML</li>\n	<li> &lt;span class=\"{$this-&gt;prev_class}\"&gt; &lt;a href=\"{$this-&gt;url_prefix}{$page}\"{$this-&gt;target}&gt;{$this-&gt;prev_str}&lt;/a&gt;&lt;/span&gt;</li>\n	<li>HTML;</li>\n	<li> }</li>\n	<li> foreach($page_nums as $p){</li>\n	<li> $page_str .= ($p == $this-&gt;page) ? &lt;&lt;&lt;HTML</li>\n	<li> &lt;span class=\"{$this-&gt;selected_class}\"&gt;{$p}&lt;/span&gt;</li>\n	<li>HTML</li>\n	<li> : &lt;&lt;&lt;HTML</li>\n	<li> &lt;span class=\"{$this-&gt;class}\"&gt;&lt;a href=\"{$this-&gt;url_prefix}{$p}\"{$this-&gt;target}&gt;{$p}&lt;/a&gt;&lt;/span&gt;</li>\n	<li>HTML;</li>\n	<li></li>\n	<li> }</li>\n	<li> if($this-&gt;page == $this-&gt;total_page){</li>\n	<li> $page_str .= &lt;&lt;&lt;HTML</li>\n	<li> &lt;span class=\"{$this-&gt;de_next_class}\"&gt; {$this-&gt;next_str} &lt;/span&gt;</li>\n	<li>HTML;</li>\n	<li> }else{</li>\n	<li> $page      = $this-&gt;page + 1;</li>\n	<li> $page_str .= &lt;&lt;&lt;HTML</li>\n	<li> &lt;span class=\"{$this-&gt;next_class}\"&gt; &lt;a href=\"{$this-&gt;url_prefix}{$page}\"{$this-&gt;target}&gt;{$this-&gt;next_str}&lt;/a&gt;&lt;/span&gt;</li>\n	<li>HTML;</li>\n	<li> }</li>\n	<li> return $page_str;</li>\n	<li> }</li>\n	<li></li>\n	<li> public function tidy_str(){</li>\n	<li> ;//void</li>\n	<li> }</li>\n	<li></li>\n	<li> public function __call($func_name, $arguments){</li>\n	<li> if(isset($this-&gt;$func_name)){</li>\n	<li> return $this-&gt;$func_name;</li>\n	<li> }</li>\n	<li> }</li>\n	<li></li>\n	<li> public function __destruct(){</li>\n	<li> unset($this-&gt;page_str);</li>\n	<li> unset($this);</li>\n	<li> }</li>\n	<li>}</li>\n</ol>','个巧妙的分页方法','','inherit','open','open','','216-revision','','','2010-10-08 23:58:51','2010-10-09 07:58:51','',216,'http://www.wordpress.cn/?p=217',0,'revision','',0),(218,1,'2010-10-09 00:01:01','2010-10-09 08:01:01','<ol>\r\n	<li>/**</li>\r\n	<li> *A pagination generation class</li>\r\n	<li> *@class  : Pagination</li>\r\n	<li> *@version: 1.0.0</li>\r\n	<li> *@author : huixinchen at baidu.com</li>\r\n	<li> *@useage:</li>\r\n	<li> *      $pagi                   =  new Pagination($url_prefix, $page_size, $mesgs_count, $pagination_size=10, array $conf);</li>\r\n	<li> *  $navigation_str = $pagi-&gt;generate($current_page_number);</li>\r\n	<li> */</li>\r\n	<li>class Pagination{</li>\r\n	<li> private $page, $total_page, $total, $page_size, $size;</li>\r\n	<li> private $prev_str = \"&amp;lt\", $next_str = \"&amp;gt\";</li>\r\n	<li> private $class, $selected_class = \"selected\", $prev_class=\"prev\", $next_class=\"next\",</li>\r\n	<li> $de_prev_class=\"de_prev\", $de_next_class=\"de_next\";</li>\r\n	<li> private $url_prefix=\"\", $split_char=\"?\";</li>\r\n	<li> private $para_name = \"page\", $target = \"\";</li>\r\n	<li> public function Pagination($url_prefix, $page_size, $total, $size=10, $conf=array()){</li>\r\n	<li> $this-&gt;page       = 1;</li>\r\n	<li> $this-&gt;page_size  = $page_size;</li>\r\n	<li> $this-&gt;total      = $total;</li>\r\n	<li> $this-&gt;total_page = intval(ceil($total/$page_size));</li>\r\n	<li> $this-&gt;size               = $size;</li>\r\n	<li> if(!empty($conf)){</li>\r\n	<li> $configure = array(\"prev_str\", \"next_str\", \"class\", \"selected_class\");</li>\r\n	<li> foreach($conf as $key =&gt; $val){</li>\r\n	<li> if(in_array($key, $configure)){</li>\r\n	<li> $this-&gt;$val = $val;</li>\r\n	<li> }</li>\r\n	<li> }</li>\r\n	<li> }</li>\r\n	<li> $this-&gt;url_prefix = $url_prefix;</li>\r\n	<li> if(strstr($url_prefix, \'?\') !== false){</li>\r\n	<li> $this-&gt;url_prefix .= \"&amp;\" . $this-&gt;para_name . \"=\";</li>\r\n	<li> }else{</li>\r\n	<li> $this-&gt;url_prefix .= \"?\" . $this-&gt;para_name . \"=\";</li>\r\n	<li> }</li>\r\n	<li> }</li>\r\n	<li> public function generate($page){</li>\r\n	<li> $this-&gt;page = $page;</li>\r\n	<li> if(isset($this-&gt;page[$page])){</li>\r\n	<li> return $this-&gt;page_str[$page];</li>\r\n	<li> }</li>\r\n	<li> $page_start = 1;</li>\r\n	<li> $half           = intval($this-&gt;size/2);</li>\r\n	<li> $page_start = max(1, $page - $half);</li>\r\n	<li> $page_end       = min($page_start + $this-&gt;size - 1, $this-&gt;total_page);</li>\r\n	<li> $page_start = max(1, $page_end - $this-&gt;size + 1);</li>\r\n	<li> $this-&gt;page_str[$page] = $this-&gt;build_nav_str($page_start, $page_end);</li>\r\n	<li> return $this-&gt;page_str[$page];</li>\r\n	<li> }</li>\r\n	<li> private function build_nav_str($page_start, $page_end){</li>\r\n	<li> $page_nums = range($page_start, $page_end);</li>\r\n	<li> $target    = $this-&gt;target? \" target=\\\"{$this-&gt;target}\\\"\" : \"\";</li>\r\n	<li> if($this-&gt;page == 1){</li>\r\n	<li> $page_str = &lt;&lt;&lt;HTML</li>\r\n	<li> &lt;span class=\"{$this-&gt;de_prev_class}\"&gt; {$this-&gt;prev_str} &lt;/span&gt;</li>\r\n	<li>HTML;</li>\r\n	<li> }else{</li>\r\n	<li> $page     = $this-&gt;page - 1;</li>\r\n	<li> $page_str = &lt;&lt;&lt;HTML</li>\r\n	<li> &lt;span class=\"{$this-&gt;prev_class}\"&gt; &lt;a href=\"{$this-&gt;url_prefix}{$page}\"{$this-&gt;target}&gt;{$this-&gt;prev_str}&lt;/a&gt;&lt;/span&gt;</li>\r\n	<li>HTML;</li>\r\n	<li> }</li>\r\n	<li> foreach($page_nums as $p){</li>\r\n	<li> $page_str .= ($p == $this-&gt;page) ? &lt;&lt;&lt;HTML</li>\r\n	<li> &lt;span class=\"{$this-&gt;selected_class}\"&gt;{$p}&lt;/span&gt;</li>\r\n	<li>HTML</li>\r\n	<li> : &lt;&lt;&lt;HTML</li>\r\n	<li> &lt;span class=\"{$this-&gt;class}\"&gt;&lt;a href=\"{$this-&gt;url_prefix}{$p}\"{$this-&gt;target}&gt;{$p}&lt;/a&gt;&lt;/span&gt;</li>\r\n	<li>HTML;</li>\r\n	<li> }</li>\r\n	<li> if($this-&gt;page == $this-&gt;total_page){</li>\r\n	<li> $page_str .= &lt;&lt;&lt;HTML</li>\r\n	<li> &lt;span class=\"{$this-&gt;de_next_class}\"&gt; {$this-&gt;next_str} &lt;/span&gt;</li>\r\n	<li>HTML;</li>\r\n	<li> }else{</li>\r\n	<li> $page      = $this-&gt;page + 1;</li>\r\n	<li> $page_str .= &lt;&lt;&lt;HTML</li>\r\n	<li> &lt;span class=\"{$this-&gt;next_class}\"&gt; &lt;a href=\"{$this-&gt;url_prefix}{$page}\"{$this-&gt;target}&gt;{$this-&gt;next_str}&lt;/a&gt;&lt;/span&gt;</li>\r\n	<li>HTML;</li>\r\n	<li> }</li>\r\n	<li> return $page_str;</li>\r\n	<li> }</li>\r\n	<li> public function tidy_str(){</li>\r\n	<li> ;//void</li>\r\n	<li> }</li>\r\n	<li> public function __call($func_name, $arguments){</li>\r\n	<li> if(isset($this-&gt;$func_name)){</li>\r\n	<li> return $this-&gt;$func_name;</li>\r\n	<li> }</li>\r\n	<li> }</li>\r\n	<li> public function __destruct(){</li>\r\n	<li> unset($this-&gt;page_str);</li>\r\n	<li> unset($this);</li>\r\n	<li> }</li>\r\n	<li>}</li>\r\n</ol>','一个巧妙的php分页方法','','inherit','open','open','','216-autosave','','','2010-10-09 00:01:01','2010-10-09 08:01:01','',216,'http://www.wordpress.cn/?p=218',0,'revision','',0),(219,1,'2010-10-09 00:27:40','2010-10-09 08:27:40','AOP简介\r\n \r\n　　AOP:advance online publication.在线杂志出版物。\r\n　　面向切面编程：Aspect Oriented Programming\r\n　　AOP是OOP的延续，是（Aspect Oriented Programming）的缩写，意思是面向切面编程。\r\n　　主要的功能是：日志记录，性能统计，安全控制，事务处理，异常处理等等。\r\n　　主要的意图是：将日志记录，性能统计，安全控制，事务处理，异常处理等代码从业务逻辑代码中划分出来，通过对这些行为的分离，我们希望可以将它们独立到非指导业务逻辑的方法中，进而改变这些行为的时候不影响业务逻辑的代码。\r\n　　可以通过预编译方式和运行期动态代理实现在不修改源代码的情况下给程序动态统一添加功能的一种技术。AOP实际是GoF设计模式的延续，设计模式孜孜不倦追求的是调用者和被调用者之间的解耦，AOP可以说也是这种目标的一种实现。\r\n　　在Spring中提供了面向切面编程的丰富支持，允许通过分离应用的业务逻辑与系统级服务（例如审计（auditing）和事务（transaction）管理）进行内聚性的开发。应用对象只实现它们应该做的——完成业务逻辑——仅此而已。它们并不负责（甚至是意识）其它的系统级关注点，例如日志或事务支持。 \r\n \r\n应用举例\r\n \r\n　　假设有在一个应用系统中，有一个共享的数据必须被并发同时访问，首先，将这个数据封装在数据对象中，称为Data Class，同时，将有多个访问类，专门用于在同一时刻访问这同一个数据对象。\r\n　　为了完成上述并发访问同一资源的功能，需要引入锁Lock的概念，也就是说，某个时刻，当有一个访问类访问这个数据对象时，这个数据对象必须上锁Locked，用完后就立即解锁unLocked，再供其它访问类访问。\r\n　　使用传统的编程习惯，我们会创建一个抽象类，所有的访问类继承这个抽象父类，如下：\r\n　　abstract class Worker{\r\n　　abstract void locked();\r\n　　abstract void accessDataObject();\r\n　　abstract void unlocked();\r\n　　} \r\n \r\n缺点：\r\n \r\n　　accessDataObject()方法需要有“锁”状态之类的相关代码。\r\n　　Java只提供了单继承，因此具体访问类只能继承这个父类，如果具体访问类还要继承其它父类，比如另外一个如Worker的父类，将无法方便实现。\r\n　　重用被打折扣，具体访问类因为也包含“锁”状态之类的相关代码，只能被重用在相关有“锁”的场合，重用范围很窄。\r\n　　仔细研究这个应用的“锁”，它其实有下列特性：\r\n　　“锁”功能不是具体访问类的首要或主要功能，访问类主要功能是访问数据对象，例如读取数据或更改动作。\r\n　　“锁”行为其实是和具体访问类的主要功能可以独立、区分开来的。\r\n　　“锁”功能其实是这个系统的一个纵向切面，涉及许多类、许多类的方法。如下图：\r\n　　因此，一个新的程序结构应该是关注系统的纵向切面，例如这个应用的“锁”功能，这个新的程序结构就是aspect（方面）\r\n　　在这个应用中，“锁”方面（aspect）应该有以下职责：\r\n　　提供一些必备的功能，对被访问对象实现加锁或解锁功能。以保证所有在修改数据对象的操作之前能够调用lock()加锁，在它使用完成后，调用unlock()解锁。 \r\n \r\nAOP应用范围\r\n \r\n　　很明显，AOP非常适合开发J2EE容器服务器，目前JBoss 4.0正是使用AOP框架进行开发。\r\n　　具体功能如下：\r\n　　Authentication 权限\r\n　　Caching 缓存\r\n　　Context passing 内容传递\r\n　　Error handling 错误处理\r\n　　Lazy loading 懒加载\r\n　　Debugging 调试\r\n　　logging, tracing, profiling and monitoring 记录跟踪 优化 校准\r\n　　Performance optimization 性能优化\r\n　　Persistence 持久化\r\n　　Resource pooling 资源池\r\n　　Synchronization 同步\r\n　　Transactions 事务\r\n　　【AOP有必要吗？】\r\n　　当然，上述应用范例在没有使用AOP情况下，也得到了解决，例如JBoss 3.XXX也提供了上述应用功能，但是没有使用AOP。\r\n　　但是，使用AOP可以让我们从一个更高的抽象概念来理解软件系统，AOP也许提供一种有价值的工具。可以这么说：因为使用AOP结构，现在JBoss 4.0的源码要比JBoss 3.X容易理解多了，这对于一个大型复杂系统来说是非常重要的。\r\n　　从另外一个方面说，好像不是所有的人都需要关心AOP，它可能是一种架构设计的选择，如果选择J2EE系统，AOP关注的上述通用方面都已经被J2EE容器实现了，J2EE应用系统开发者可能需要更多地关注行业应用方面aspect。 \r\n \r\nAOP具体实现\r\n \r\n　　AOP是一个概念，并没有设定具体语言的实现，它能克服那些只有单继承特性语言的缺点（如Java），目前AOP具体实现有以下几个项目：\r\n　　AspectJ (TM)： 创建于Xerox PARC. 有近十年历史，成熟\r\n　　缺点：过于复杂；破坏封装；需要专门的Java编译器。\r\n　　动态AOP：使用JDK的动态代理API或字节码Bytecode处理技术。\r\n　　基于动态代理API的具体项目有：\r\n　　JBoss 4.0 JBoss 4.0服务器\r\n　　nanning 这是以中国南宁命名的一个项目，搞不清楚为什么和中国相关？是中国人发起的？\r\n　　基于字节码的项目有：\r\n　　aspectwerkz ，spring\r\n \r\nAOP 能够给我们带来什么\r\n　　面向过程编程离我们已经有些遥远，面向对象编程正主宰着软件世界。当每个新的软件设计师都被要求掌握如何将需求功能转化成一个个类，并且定义它们的数据成员、行为，以及它们之间复杂的关系的时候，面向方面编程（Aspect-Oriented Programming，AOP）为我们带来了新的想法、新的思想、新的模式。\r\n　　如果说面向对象编程是关注将需求功能划分为不同的并且相对独立，封装良好的类，并让它们有着属于自己的行为，依靠继承和多态等来定义彼此的关系的话；那么面向方面编程则是希望能够将通用需求功能从不相关的类当中分离出来，能够使得很多类共享一个行为，一旦发生变化，不必修改很多类，而只需要修改这个行为即可。\r\n　　面向方面编程是一个令人兴奋不已的新模式。就开发软件系统而言，它的影响力必将会和有着十数年应用历史的面向对象编程一样巨大。面向方面编程和面向对象编程不但不是互相竞争的技术而且彼此还是很好的互补。面向对象编程主要用于为同一对象层次的公用行为建模。它的弱点是将公共行为应用于多个无关对象模型之间。而这恰恰是面向方面编程适合的地方。有了 AOP，我们可以定义交叉的关系，并将这些关系应用于跨模块的、彼此不同的对象模型。AOP 同时还可以让我们层次化功能性而不是嵌入功能性，从而使得代码有更好的可读性和易于维护。它会和面向对象编程合作得很好。\r\n　  AOP 的应用范围\r\n　　传统的程序通常表现出一些不能自然地适合单一的程序模块或者是几个紧密相关的程序模块的行为，AOP 将这种行为称为横切，它们跨越了给定编程模型中的典型职责界限。横切行为的实现都是分散的，软件设计师会发现这种行为难以用正常的逻辑来思考、实现和更改。最常见的一些横切行为如下面这些：\r\n　　日志记录，跟踪，优化和监控\r\n　　事务的处理\r\n　　持久化\r\n　　性能的优化\r\n　　资源池，如数据库连接池的管理\r\n　　系统统一的认证、权限管理等\r\n　　应用系统的异常捕捉及处理\r\n　　针对具体行业应用的横切行为\r\n　　目前，前面几种横切行为都已经得到了密切的关注，也出现了各种有价值的应用，但也许今后几年，AOP 对针对具体行业应用的贡献会成为令人关注的焦点。\r\n　　AOP 的具体实现\r\n　　AOP 是一个概念，一个规范，本身并没有设定具体语言的实现，这实际上提供了非常广阔的发展的空间。AspectJ是AOP的一个很悠久的实现，它能够和 Java 配合起来使用。\r\n　　介绍 AspectJ 的使用和编码不是本文的目的，你可以在 Google 上找到很多有关它的材料。\r\n　　这里只是重温 AspectJ 中几个必须要了解的概念：\r\n　　Aspect： Aspect 声明类似于 Java 中的类声明，在 Aspect 中会包含着一些 Pointcut 以及相应的 Advice。\r\n　　Joint point：表示在程序中明确定义的点，典型的包括方法调用，对类成员的访问以及异常处理程序块的执行等等，它自身还可以嵌套其它 joint point。\r\n　　Pointcut：表示一组 joint point，这些 joint point 或是通过逻辑关系组合起来，或是通过通配、正则表达式等方式集中起来，它定义了相应的 Advice 将要发生的地方。\r\n　　Advice：Advice 定义了在 pointcut 里面定义的程序点具体要做的操作，它通过 before、after 和 around 来区别是在每个 joint point 之前、之后还是代替执行的代码。\r\n　　下面要讨论的这些问题，也许正是接触了 AOP 之后所困惑的。\r\n　　AOP 帮助我们解决了新的问题没有？\r\n　　AOP 并没有帮助我们解决任何新的问题，它只是提供了一种更好的办法，能够用更少的工作量来解决现有的一些问题，并且使得系统更加健壮，可维护性更好。同时，它让我们在进行系统架构和模块设计的时候多了新的选择和新的思路。\r\n　　AOP 和 OOP 到底是什么关系\r\n　　很多人在初次接触 AOP 的时候可能会说，AOP 能做到的，一个定义良好的 OOP 的接口也一样能够做到，我想这个观点是值得商榷的。AOP和定义良好的 OOP 的接口可以说都是用来解决并且实现需求中的横切问题的方法。但是对于 OOP 中的接口来说，它仍然需要我们在相应的模块中去调用该接口中相关的方法，这是 OOP 所无法避免的，并且一旦接口不得不进行修改的时候，所有事情会变得一团糟；AOP 则不会这样，你只需要修改相应的 Aspect，再重新编织（weave）即可。 当然，AOP 也绝对不会代替 OOP。核心的需求仍然会由 OOP 来加以实现，而 AOP 将会和 OOP 整合起来，以此之长，补彼之短。\r\n　　AOP 适合工业化的应用吗？\r\n　　这个问题很难回答，其实最好的答案就是尝试，用成功的项目或是产品来回答。Jboss 4.0 就是完全采用 AOP 的思想来设计的 EJB 容器，它已经通过了 J2EE 的认证，并且在工业化应用中证明是一个优秀的产品。相信在不远的将来，会出现更多采用 AOP 思想设计的产品和行业应用。\r\n \r\n小结\r\n　　AOP 正向我们走来，我们需要关注的是怎么样使得它能够为我们的软件系统的设计和实现带来帮助。本文旨在给大家一点启发，能够在更多的领域更深入的应用 AOP 的思想。','AOP的理解','','publish','open','open','','aop%e7%9a%84%e7%90%86%e8%a7%a3','','','2010-10-09 00:27:40','2010-10-09 08:27:40','',0,'http://www.wordpress.cn/?p=219',0,'post','',0),(220,1,'2010-10-09 00:27:35','2010-10-09 08:27:35','AOP简介\n \n　　AOP:advance online publication.在线杂志出版物。\n　　面向切面编程：Aspect Oriented Programming\n　　AOP是OOP的延续，是（Aspect Oriented Programming）的缩写，意思是面向切面编程。\n　　主要的功能是：日志记录，性能统计，安全控制，事务处理，异常处理等等。\n　　主要的意图是：将日志记录，性能统计，安全控制，事务处理，异常处理等代码从业务逻辑代码中划分出来，通过对这些行为的分离，我们希望可以将它们独立到非指导业务逻辑的方法中，进而改变这些行为的时候不影响业务逻辑的代码。\n　　可以通过预编译方式和运行期动态代理实现在不修改源代码的情况下给程序动态统一添加功能的一种技术。AOP实际是GoF设计模式的延续，设计模式孜孜不倦追求的是调用者和被调用者之间的解耦，AOP可以说也是这种目标的一种实现。\n　　在Spring中提供了面向切面编程的丰富支持，允许通过分离应用的业务逻辑与系统级服务（例如审计（auditing）和事务（transaction）管理）进行内聚性的开发。应用对象只实现它们应该做的——完成业务逻辑——仅此而已。它们并不负责（甚至是意识）其它的系统级关注点，例如日志或事务支持。 \n \n应用举例\n \n　　假设有在一个应用系统中，有一个共享的数据必须被并发同时访问，首先，将这个数据封装在数据对象中，称为Data Class，同时，将有多个访问类，专门用于在同一时刻访问这同一个数据对象。\n　　为了完成上述并发访问同一资源的功能，需要引入锁Lock的概念，也就是说，某个时刻，当有一个访问类访问这个数据对象时，这个数据对象必须上锁Locked，用完后就立即解锁unLocked，再供其它访问类访问。\n　　使用传统的编程习惯，我们会创建一个抽象类，所有的访问类继承这个抽象父类，如下：\n　　abstract class Worker{\n　　abstract void locked();\n　　abstract void accessDataObject();\n　　abstract void unlocked();\n　　} \n \n缺点：\n \n　　accessDataObject()方法需要有“锁”状态之类的相关代码。\n　　Java只提供了单继承，因此具体访问类只能继承这个父类，如果具体访问类还要继承其它父类，比如另外一个如Worker的父类，将无法方便实现。\n　　重用被打折扣，具体访问类因为也包含“锁”状态之类的相关代码，只能被重用在相关有“锁”的场合，重用范围很窄。\n　　仔细研究这个应用的“锁”，它其实有下列特性：\n　　“锁”功能不是具体访问类的首要或主要功能，访问类主要功能是访问数据对象，例如读取数据或更改动作。\n　　“锁”行为其实是和具体访问类的主要功能可以独立、区分开来的。\n　　“锁”功能其实是这个系统的一个纵向切面，涉及许多类、许多类的方法。如下图：\n　　因此，一个新的程序结构应该是关注系统的纵向切面，例如这个应用的“锁”功能，这个新的程序结构就是aspect（方面）\n　　在这个应用中，“锁”方面（aspect）应该有以下职责：\n　　提供一些必备的功能，对被访问对象实现加锁或解锁功能。以保证所有在修改数据对象的操作之前能够调用lock()加锁，在它使用完成后，调用unlock()解锁。 \n \nAOP应用范围\n \n　　很明显，AOP非常适合开发J2EE容器服务器，目前JBoss 4.0正是使用AOP框架进行开发。\n　　具体功能如下：\n　　Authentication 权限\n　　Caching 缓存\n　　Context passing 内容传递\n　　Error handling 错误处理\n　　Lazy loading 懒加载\n　　Debugging 调试\n　　logging, tracing, profiling and monitoring 记录跟踪 优化 校准\n　　Performance optimization 性能优化\n　　Persistence 持久化\n　　Resource pooling 资源池\n　　Synchronization 同步\n　　Transactions 事务\n　　【AOP有必要吗？】\n　　当然，上述应用范例在没有使用AOP情况下，也得到了解决，例如JBoss 3.XXX也提供了上述应用功能，但是没有使用AOP。\n　　但是，使用AOP可以让我们从一个更高的抽象概念来理解软件系统，AOP也许提供一种有价值的工具。可以这么说：因为使用AOP结构，现在JBoss 4.0的源码要比JBoss 3.X容易理解多了，这对于一个大型复杂系统来说是非常重要的。\n　　从另外一个方面说，好像不是所有的人都需要关心AOP，它可能是一种架构设计的选择，如果选择J2EE系统，AOP关注的上述通用方面都已经被J2EE容器实现了，J2EE应用系统开发者可能需要更多地关注行业应用方面aspect。 \n \nAOP具体实现\n \n　　AOP是一个概念，并没有设定具体语言的实现，它能克服那些只有单继承特性语言的缺点（如Java），目前AOP具体实现有以下几个项目：\n　　AspectJ (TM)： 创建于Xerox PARC. 有近十年历史，成熟\n　　缺点：过于复杂；破坏封装；需要专门的Java编译器。\n　　动态AOP：使用JDK的动态代理API或字节码Bytecode处理技术。\n　　基于动态代理API的具体项目有：\n　　JBoss 4.0 JBoss 4.0服务器\n　　nanning 这是以中国南宁命名的一个项目，搞不清楚为什么和中国相关？是中国人发起的？\n　　基于字节码的项目有：\n　　aspectwerkz ，spring\n \nAOP 能够给我们带来什么\n　　面向过程编程离我们已经有些遥远，面向对象编程正主宰着软件世界。当每个新的软件设计师都被要求掌握如何将需求功能转化成一个个类，并且定义它们的数据成员、行为，以及它们之间复杂的关系的时候，面向方面编程（Aspect-Oriented Programming，AOP）为我们带来了新的想法、新的思想、新的模式。\n　　如果说面向对象编程是关注将需求功能划分为不同的并且相对独立，封装良好的类，并让它们有着属于自己的行为，依靠继承和多态等来定义彼此的关系的话；那么面向方面编程则是希望能够将通用需求功能从不相关的类当中分离出来，能够使得很多类共享一个行为，一旦发生变化，不必修改很多类，而只需要修改这个行为即可。\n　　面向方面编程是一个令人兴奋不已的新模式。就开发软件系统而言，它的影响力必将会和有着十数年应用历史的面向对象编程一样巨大。面向方面编程和面向对象编程不但不是互相竞争的技术而且彼此还是很好的互补。面向对象编程主要用于为同一对象层次的公用行为建模。它的弱点是将公共行为应用于多个无关对象模型之间。而这恰恰是面向方面编程适合的地方。有了 AOP，我们可以定义交叉的关系，并将这些关系应用于跨模块的、彼此不同的对象模型。AOP 同时还可以让我们层次化功能性而不是嵌入功能性，从而使得代码有更好的可读性和易于维护。它会和面向对象编程合作得很好。\n　  AOP 的应用范围\n　　传统的程序通常表现出一些不能自然地适合单一的程序模块或者是几个紧密相关的程序模块的行为，AOP 将这种行为称为横切，它们跨越了给定编程模型中的典型职责界限。横切行为的实现都是分散的，软件设计师会发现这种行为难以用正常的逻辑来思考、实现和更改。最常见的一些横切行为如下面这些：\n　　日志记录，跟踪，优化和监控\n　　事务的处理\n　　持久化\n　　性能的优化\n　　资源池，如数据库连接池的管理\n　　系统统一的认证、权限管理等\n　　应用系统的异常捕捉及处理\n　　针对具体行业应用的横切行为\n　　目前，前面几种横切行为都已经得到了密切的关注，也出现了各种有价值的应用，但也许今后几年，AOP 对针对具体行业应用的贡献会成为令人关注的焦点。\n　　AOP 的具体实现\n　　AOP 是一个概念，一个规范，本身并没有设定具体语言的实现，这实际上提供了非常广阔的发展的空间。AspectJ是AOP的一个很悠久的实现，它能够和 Java 配合起来使用。\n　　介绍 AspectJ 的使用和编码不是本文的目的，你可以在 Google 上找到很多有关它的材料。\n　　这里只是重温 AspectJ 中几个必须要了解的概念：\n　　Aspect： Aspect 声明类似于 Java 中的类声明，在 Aspect 中会包含着一些 Pointcut 以及相应的 Advice。\n　　Joint point：表示在程序中明确定义的点，典型的包括方法调用，对类成员的访问以及异常处理程序块的执行等等，它自身还可以嵌套其它 joint point。\n　　Pointcut：表示一组 joint point，这些 joint point 或是通过逻辑关系组合起来，或是通过通配、正则表达式等方式集中起来，它定义了相应的 Advice 将要发生的地方。\n　　Advice：Advice 定义了在 pointcut 里面定义的程序点具体要做的操作，它通过 before、after 和 around 来区别是在每个 joint point 之前、之后还是代替执行的代码。\n　　下面要讨论的这些问题，也许正是接触了 AOP 之后所困惑的。\n　　AOP 帮助我们解决了新的问题没有？\n　　AOP 并没有帮助我们解决任何新的问题，它只是提供了一种更好的办法，能够用更少的工作量来解决现有的一些问题，并且使得系统更加健壮，可维护性更好。同时，它让我们在进行系统架构和模块设计的时候多了新的选择和新的思路。\n　　AOP 和 OOP 到底是什么关系\n　　很多人在初次接触 AOP 的时候可能会说，AOP 能做到的，一个定义良好的 OOP 的接口也一样能够做到，我想这个观点是值得商榷的。AOP和定义良好的 OOP 的接口可以说都是用来解决并且实现需求中的横切问题的方法。但是对于 OOP 中的接口来说，它仍然需要我们在相应的模块中去调用该接口中相关的方法，这是 OOP 所无法避免的，并且一旦接口不得不进行修改的时候，所有事情会变得一团糟；AOP 则不会这样，你只需要修改相应的 Aspect，再重新编织（weave）即可。 当然，AOP 也绝对不会代替 OOP。核心的需求仍然会由 OOP 来加以实现，而 AOP 将会和 OOP 整合起来，以此之长，补彼之短。\n　　AOP 适合工业化的应用吗？\n　　这个问题很难回答，其实最好的答案就是尝试，用成功的项目或是产品来回答。Jboss 4.0 就是完全采用 AOP 的思想来设计的 EJB 容器，它已经通过了 J2EE 的认证，并且在工业化应用中证明是一个优秀的产品。相信在不远的将来，会出现更多采用 AOP 思想设计的产品和行业应用。\n \n小结\n　　AOP 正向我们走来，我们需要关注的是怎么样使得它能够为我们的软件系统的设计和实现带来帮助。本文旨在给大家一点启发，能够在更多的领域更深入的应用 AOP 的思想。','AOP的理解','','inherit','open','open','','219-revision','','','2010-10-09 00:27:35','2010-10-09 08:27:35','',219,'http://www.wordpress.cn/?p=220',0,'revision','',0),(221,1,'2010-10-09 00:27:51','2010-10-09 08:27:51','AOP简介\r\n \r\n　　AOP:advance online publication.在线杂志出版物。\r\n　　面向切面编程：Aspect Oriented Programming\r\n　　AOP是OOP的延续，是（Aspect Oriented Programming）的缩写，意思是面向切面编程。\r\n　　主要的功能是：日志记录，性能统计，安全控制，事务处理，异常处理等等。\r\n　　主要的意图是：将日志记录，性能统计，安全控制，事务处理，异常处理等代码从业务逻辑代码中划分出来，通过对这些行为的分离，我们希望可以将它们独立到非指导业务逻辑的方法中，进而改变这些行为的时候不影响业务逻辑的代码。\r\n　　可以通过预编译方式和运行期动态代理实现在不修改源代码的情况下给程序动态统一添加功能的一种技术。AOP实际是GoF设计模式的延续，设计模式孜孜不倦追求的是调用者和被调用者之间的解耦，AOP可以说也是这种目标的一种实现。\r\n　　在Spring中提供了面向切面编程的丰富支持，允许通过分离应用的业务逻辑与系统级服务（例如审计（auditing）和事务（transaction）管理）进行内聚性的开发。应用对象只实现它们应该做的——完成业务逻辑——仅此而已。它们并不负责（甚至是意识）其它的系统级关注点，例如日志或事务支持。 \r\n \r\n应用举例\r\n \r\n　　假设有在一个应用系统中，有一个共享的数据必须被并发同时访问，首先，将这个数据封装在数据对象中，称为Data Class，同时，将有多个访问类，专门用于在同一时刻访问这同一个数据对象。\r\n　　为了完成上述并发访问同一资源的功能，需要引入锁Lock的概念，也就是说，某个时刻，当有一个访问类访问这个数据对象时，这个数据对象必须上锁Locked，用完后就立即解锁unLocked，再供其它访问类访问。\r\n　　使用传统的编程习惯，我们会创建一个抽象类，所有的访问类继承这个抽象父类，如下：\r\n　　abstract class Worker{\r\n　　abstract void locked();\r\n　　abstract void accessDataObject();\r\n　　abstract void unlocked();\r\n　　} \r\n \r\n缺点：\r\n \r\n　　accessDataObject()方法需要有“锁”状态之类的相关代码。\r\n　　Java只提供了单继承，因此具体访问类只能继承这个父类，如果具体访问类还要继承其它父类，比如另外一个如Worker的父类，将无法方便实现。\r\n　　重用被打折扣，具体访问类因为也包含“锁”状态之类的相关代码，只能被重用在相关有“锁”的场合，重用范围很窄。\r\n　　仔细研究这个应用的“锁”，它其实有下列特性：\r\n　　“锁”功能不是具体访问类的首要或主要功能，访问类主要功能是访问数据对象，例如读取数据或更改动作。\r\n　　“锁”行为其实是和具体访问类的主要功能可以独立、区分开来的。\r\n　　“锁”功能其实是这个系统的一个纵向切面，涉及许多类、许多类的方法。如下图：\r\n　　因此，一个新的程序结构应该是关注系统的纵向切面，例如这个应用的“锁”功能，这个新的程序结构就是aspect（方面）\r\n　　在这个应用中，“锁”方面（aspect）应该有以下职责：\r\n　　提供一些必备的功能，对被访问对象实现加锁或解锁功能。以保证所有在修改数据对象的操作之前能够调用lock()加锁，在它使用完成后，调用unlock()解锁。 \r\n \r\nAOP应用范围\r\n \r\n　　很明显，AOP非常适合开发J2EE容器服务器，目前JBoss 4.0正是使用AOP框架进行开发。\r\n　　具体功能如下：\r\n　　Authentication 权限\r\n　　Caching 缓存\r\n　　Context passing 内容传递\r\n　　Error handling 错误处理\r\n　　Lazy loading 懒加载\r\n　　Debugging 调试\r\n　　logging, tracing, profiling and monitoring 记录跟踪 优化 校准\r\n　　Performance optimization 性能优化\r\n　　Persistence 持久化\r\n　　Resource pooling 资源池\r\n　　Synchronization 同步\r\n　　Transactions 事务\r\n　　【AOP有必要吗？】\r\n　　当然，上述应用范例在没有使用AOP情况下，也得到了解决，例如JBoss 3.XXX也提供了上述应用功能，但是没有使用AOP。\r\n　　但是，使用AOP可以让我们从一个更高的抽象概念来理解软件系统，AOP也许提供一种有价值的工具。可以这么说：因为使用AOP结构，现在JBoss 4.0的源码要比JBoss 3.X容易理解多了，这对于一个大型复杂系统来说是非常重要的。\r\n　　从另外一个方面说，好像不是所有的人都需要关心AOP，它可能是一种架构设计的选择，如果选择J2EE系统，AOP关注的上述通用方面都已经被J2EE容器实现了，J2EE应用系统开发者可能需要更多地关注行业应用方面aspect。 \r\n \r\nAOP具体实现\r\n \r\n　　AOP是一个概念，并没有设定具体语言的实现，它能克服那些只有单继承特性语言的缺点（如Java），目前AOP具体实现有以下几个项目：\r\n　　AspectJ (TM)： 创建于Xerox PARC. 有近十年历史，成熟\r\n　　缺点：过于复杂；破坏封装；需要专门的Java编译器。\r\n　　动态AOP：使用JDK的动态代理API或字节码Bytecode处理技术。\r\n　　基于动态代理API的具体项目有：\r\n　　JBoss 4.0 JBoss 4.0服务器\r\n　　nanning 这是以中国南宁命名的一个项目，搞不清楚为什么和中国相关？是中国人发起的？\r\n　　基于字节码的项目有：\r\n　　aspectwerkz ，spring\r\n \r\nAOP 能够给我们带来什么\r\n　　面向过程编程离我们已经有些遥远，面向对象编程正主宰着软件世界。当每个新的软件设计师都被要求掌握如何将需求功能转化成一个个类，并且定义它们的数据成员、行为，以及它们之间复杂的关系的时候，面向方面编程（Aspect-Oriented Programming，AOP）为我们带来了新的想法、新的思想、新的模式。\r\n　　如果说面向对象编程是关注将需求功能划分为不同的并且相对独立，封装良好的类，并让它们有着属于自己的行为，依靠继承和多态等来定义彼此的关系的话；那么面向方面编程则是希望能够将通用需求功能从不相关的类当中分离出来，能够使得很多类共享一个行为，一旦发生变化，不必修改很多类，而只需要修改这个行为即可。\r\n　　面向方面编程是一个令人兴奋不已的新模式。就开发软件系统而言，它的影响力必将会和有着十数年应用历史的面向对象编程一样巨大。面向方面编程和面向对象编程不但不是互相竞争的技术而且彼此还是很好的互补。面向对象编程主要用于为同一对象层次的公用行为建模。它的弱点是将公共行为应用于多个无关对象模型之间。而这恰恰是面向方面编程适合的地方。有了 AOP，我们可以定义交叉的关系，并将这些关系应用于跨模块的、彼此不同的对象模型。AOP 同时还可以让我们层次化功能性而不是嵌入功能性，从而使得代码有更好的可读性和易于维护。它会和面向对象编程合作得很好。\r\n　  AOP 的应用范围\r\n　　传统的程序通常表现出一些不能自然地适合单一的程序模块或者是几个紧密相关的程序模块的行为，AOP 将这种行为称为横切，它们跨越了给定编程模型中的典型职责界限。横切行为的实现都是分散的，软件设计师会发现这种行为难以用正常的逻辑来思考、实现和更改。最常见的一些横切行为如下面这些：\r\n　　日志记录，跟踪，优化和监控\r\n　　事务的处理\r\n　　持久化\r\n　　性能的优化\r\n　　资源池，如数据库连接池的管理\r\n　　系统统一的认证、权限管理等\r\n　　应用系统的异常捕捉及处理\r\n　　针对具体行业应用的横切行为\r\n　　目前，前面几种横切行为都已经得到了密切的关注，也出现了各种有价值的应用，但也许今后几年，AOP 对针对具体行业应用的贡献会成为令人关注的焦点。\r\n　　AOP 的具体实现\r\n　　AOP 是一个概念，一个规范，本身并没有设定具体语言的实现，这实际上提供了非常广阔的发展的空间。AspectJ是AOP的一个很悠久的实现，它能够和 Java 配合起来使用。\r\n　　介绍 AspectJ 的使用和编码不是本文的目的，你可以在 Google 上找到很多有关它的材料。\r\n　　这里只是重温 AspectJ 中几个必须要了解的概念：\r\n　　Aspect： Aspect 声明类似于 Java 中的类声明，在 Aspect 中会包含着一些 Pointcut 以及相应的 Advice。\r\n　　Joint point：表示在程序中明确定义的点，典型的包括方法调用，对类成员的访问以及异常处理程序块的执行等等，它自身还可以嵌套其它 joint point。\r\n　　Pointcut：表示一组 joint point，这些 joint point 或是通过逻辑关系组合起来，或是通过通配、正则表达式等方式集中起来，它定义了相应的 Advice 将要发生的地方。\r\n　　Advice：Advice 定义了在 pointcut 里面定义的程序点具体要做的操作，它通过 before、after 和 around 来区别是在每个 joint point 之前、之后还是代替执行的代码。\r\n　　下面要讨论的这些问题，也许正是接触了 AOP 之后所困惑的。\r\n　　AOP 帮助我们解决了新的问题没有？\r\n　　AOP 并没有帮助我们解决任何新的问题，它只是提供了一种更好的办法，能够用更少的工作量来解决现有的一些问题，并且使得系统更加健壮，可维护性更好。同时，它让我们在进行系统架构和模块设计的时候多了新的选择和新的思路。\r\n　　AOP 和 OOP 到底是什么关系\r\n　　很多人在初次接触 AOP 的时候可能会说，AOP 能做到的，一个定义良好的 OOP 的接口也一样能够做到，我想这个观点是值得商榷的。AOP和定义良好的 OOP 的接口可以说都是用来解决并且实现需求中的横切问题的方法。但是对于 OOP 中的接口来说，它仍然需要我们在相应的模块中去调用该接口中相关的方法，这是 OOP 所无法避免的，并且一旦接口不得不进行修改的时候，所有事情会变得一团糟；AOP 则不会这样，你只需要修改相应的 Aspect，再重新编织（weave）即可。 当然，AOP 也绝对不会代替 OOP。核心的需求仍然会由 OOP 来加以实现，而 AOP 将会和 OOP 整合起来，以此之长，补彼之短。\r\n　　AOP 适合工业化的应用吗？\r\n　　这个问题很难回答，其实最好的答案就是尝试，用成功的项目或是产品来回答。Jboss 4.0 就是完全采用 AOP 的思想来设计的 EJB 容器，它已经通过了 J2EE 的认证，并且在工业化应用中证明是一个优秀的产品。相信在不远的将来，会出现更多采用 AOP 思想设计的产品和行业应用。\r\n \r\n小结\r\n　　AOP 正向我们走来，我们需要关注的是怎么样使得它能够为我们的软件系统的设计和实现带来帮助。本文旨在给大家一点启发，能够在更多的领域更深入的应用 AOP 的思想。','AOP的理解','','inherit','open','open','','219-autosave','','','2010-10-09 00:27:51','2010-10-09 08:27:51','',219,'http://www.wordpress.cn/?p=221',0,'revision','',0),(222,1,'2010-10-13 18:19:32','2010-10-14 02:19:32','解决Mysql主从不同步问题\r\n今天Mysql的主从数据库没有同步导致网页读不到数据， \r\n当网页不能读到数据时，猜想上数据库的原因。 \r\n首先在Master上用 \r\nshow processlist;   查看下进程是否Sleep太多。发现很正常。 \r\nshow master status; 也正常。 \r\n再跑到Slave上查看 \r\nshow slave status; \r\n错误提示： \r\nError \'Duplicate entry \'1\' for key 1\' on query. Default database: \'movivi1\'. Query: \'INSERT INTO `v1vid0_user_samename` VALUES(null,1,\'123\',\'11\',\'4545\',\'123\')\' \r\n\r\nSlave_SQL_Running 为 NO \r\nSeconds_Behind_Master 为 (null) \r\n\r\n可见是Slave不同步 \r\n\r\n解决： \r\nstop slave; \r\nset global sql_slave_skip_counter =1 ;\r\nstart slave;\r\n\r\n\r\n之后Slave会和Master去同步 主要看Seconds_Behind_Master是否为0，直到为0时就已经同步了。\r\n','解决Mysql主从不同步问题','','publish','open','open','','%e8%a7%a3%e5%86%b3mysql%e4%b8%bb%e4%bb%8e%e4%b8%8d%e5%90%8c%e6%ad%a5%e9%97%ae%e9%a2%98','','','2010-10-13 18:19:32','2010-10-14 02:19:32','',0,'http://www.wordpress.cn/?p=222',0,'post','',0),(223,1,'2010-10-13 18:19:27','2010-10-14 02:19:27','解决Mysql主从不同步问题\n今天Mysql的主从数据库没有同步导致网页读不到数据， \n当网页不能读到数据时，猜想上数据库的原因。 \n首先在Master上用 \nshow processlist;   查看下进程是否Sleep太多。发现很正常。 \nshow master status; 也正常。 \n再跑到Slave上查看 \nshow slave status; \n错误提示： \nError \'Duplicate entry \'1\' for key 1\' on query. Default database: \'movivi1\'. Query: \'INSERT INTO `v1vid0_user_samename` VALUES(null,1,\'123\',\'11\',\'4545\',\'123\')\' \n\nSlave_SQL_Running 为 NO \nSeconds_Behind_Master 为 (null) \n\n可见是Slave不同步 \n\n解决： \nstop slave; \nset global sql_slave_skip_counter =1 ;\nstart slave;\n\n\n之后Slave会和Master去同步 主要看Seconds_Behind_Master是否为0，直到为0时就已经同步了。\n','解决Mysql主从不同步问题','','inherit','open','open','','222-revision','','','2010-10-13 18:19:27','2010-10-14 02:19:27','',222,'http://www.wordpress.cn/?p=223',0,'revision','',0),(259,1,'2011-03-10 17:20:34','2011-03-11 01:20:34','当Maven项目很大，或者你运行诸如 mvn site 这样的命令的时候，maven运行需要很大的内存，在默认配置下，就可能遇到java的堆溢出。如：&lt;/p&gt;\r\n<div>[INFO] Building jar:\r\n/home/dl9pf/svn/mindquarry/mindquarry-jcr/mindquarry-jcr-changes/target/mindquarry-migration-with-dependencies.jar\r\n[INFO] ————————————————————————\r\n[ERROR] FATAL ERROR\r\n[INFO] ————————————————————————\r\n[INFO] Java heap space\r\n[INFO] ————————————————————————\r\n[INFO] Trace\r\njava.lang.OutOfMemoryError: Java heap space\r\nat java.lang.AbstractStringBuilder.expandCapacity(AbstractStringBuilder.java:99)\r\nat java.lang.AbstractStringBuilder.append(AbstractStringBuilder.java:518)\r\n…\r\nat org.codehaus.classworlds.Launcher.launchEnhanced(Launcher.java:315)\r\nat org.codehaus.classworlds.Launcher.launch(Launcher.java:255)\r\nat org.codehaus.classworlds.Launcher.mainWithExitCode(Launcher.java:430)\r\nat org.codehaus.classworlds.Launcher.main(Launcher.java:375)\r\n[INFO] ————————————————————————\r\n[INFO] Total time: 7 minutes 14 seconds\r\n[INFO] Finished at: Wed Sep 05 07:44:55 CEST 2007\r\n[INFO] Final Memory: 37M/63M\r\n[INFO] ————————————————————————</div>\r\n解决的方法是调整java的堆大小的值。&lt;/p&gt;\r\n<h2><strong>Windows环境中\r\n</strong></h2>\r\n<strong>找到文件<em>%M2_HOME%”bin”mvn.bat</em>\r\n，这就是启动Maven的脚本文件，在该文件中你能看到有一行注释为：&lt;/strong&gt;</strong>\r\n\r\n<strong>@REM set MAVEN_OPTS=-Xdebug -Xnoagent -Djava.compiler=NONE…</strong>\r\n\r\n<strong>它的意思是你可以设置一些Maven参数，我们就在注释下面加入一行：</strong>\r\n<div><strong>set MAVEN_OPTS= -Xms128m -Xmx512m</strong></div>\r\n<strong>之后，当你运行Maven命令如 mvn -version 的时候，你会看到如下的输出：</strong>\r\n<div><strong>E:”test&gt;mvn -version \r\n\r\nE:”test&gt;set MAVEN_OPTS= -Xms128m -Xmx512m\r\nMaven version: 2.0.9\r\nJava version: 1.6.0_07\r\nOS name: “windows 2003″ version: “5.2″ arch: “x86″ Family: “windows”\r\n\r\n</strong></div>\r\n<strong>我们看到，配置的Maven选项生效了，OutOfMemoryError也能得以相应的解决。&lt;/strong&gt;</strong>\r\n\r\n<strong> </strong>\r\n\r\n<strong> </strong>\r\n<h2><strong><strong>Linux环境中\r\n</strong>\r\n</strong></h2>\r\n<strong><strong>也可以通过设置环境变量解决该问题， 如，编辑文件<em> /etc/profile</em>\r\n如下</strong></strong>\r\n<div><strong><strong>MAVEN_OPTS=-Xmx512m\r\nexport JAVA_HOME MAVEN_HOME MAVEN_OPTS JAVA_BIN PATH CLASSPATH</strong></strong></div>\r\n<strong><strong> </strong></strong>\r\n\r\n<strong><strong> </strong></strong>\r\n<h2><strong><strong><strong>如果你使用Hudson\r\n</strong>\r\n</strong></strong></h2>\r\n<strong><strong>用 Hudson + Maven做持续集成，并不幸也遇到了类似的错误，那么上述两种方式都将不再起作用了，因为Hudson使用自己的maven-agent来启动Maven，不会去调用Maven的脚本，自然相应的配置也就无效了。&lt;/strong&gt;</strong></strong>\r\n\r\n<strong><strong>好在Hudson也给为我们提供了配置点，在Hudson的项目配置页面中，有一块Build区域，这里我们已经\r\n设置了Root\r\nPom和Goals。注意该区域的右下角有一个\"Advanced…”按钮，点击会看到MAVEN_OPTS输入框，这里输入”-Xmx512m”就\r\nOK了。&lt;/strong&gt;</strong></strong>\r\n\r\n<strong><strong> </strong></strong>\r\n<h2><strong><strong>m2eclipse中&lt;/strong&gt;</strong></strong></h2>\r\n<strong><strong>类似以上的方法都会失效，所幸m2eclipse提供了配置点。步骤如下：</strong></strong>\r\n\r\n<strong><strong>项目上右击 -&gt; Run As -&gt; Run Configurations -&gt; Maven Build 上右击 -&gt; New</strong></strong>\r\n\r\n<strong><strong>这时会看到一个maven运行配置对话框，这里面其它的配置我不多解释了，为了解决内存溢出的问题，我们可以选择第二个TAB: JRE，然后在VM arguments中输入配置如：-Xms128m -Xmx512m。&lt;/strong&gt;</strong></strong>\r\n\r\n<strong><strong> </strong></strong>\r\n\r\n<strong><strong> </strong></strong>\r\n\r\n<strong><strong>还有一种说法：</strong></strong>\r\n\r\n编辑maven”bin中的mvn文件：\r\n<ul>\r\n	<li>在exec “$JAVACMD” “之后加上 -Xmx256m “即可。</li>\r\n	<li>形式如：\r\n<blockquote>exec “$JAVACMD” “\r\n\r\n-Xmx256m “</blockquote>\r\n</li>\r\n</ul>','解决Maven中OutOfMemory错误','','publish','open','open','','%e8%a7%a3%e5%86%b3maven%e4%b8%adoutofmemory%e9%94%99%e8%af%af','','','2011-03-10 17:20:34','2011-03-11 01:20:34','',0,'http://www.wordpress.cn/?p=259',0,'post','',0),(228,1,'2010-10-27 17:20:12','2010-10-28 01:20:12','mysql主从同步备份策略\r\n环境：\r\n主从服务器上的MySQL数据库版本同为5.1.34\r\n主机IP：192.168.0.1\r\n从机IP：192.168.0.2\r\n\r\n一. MySQL主服务器配置\r\n\r\n1.编辑配置文件/etc/my.cnf\r\n# 确保有如下行\r\nserver-id = 1\r\nlog-bin=mysql-bin\r\nbinlog-do-db=mysql #需要备份的数据库名，如果备份多个数据库，重复设置这个选项即可\r\nbinlog-ignore-db=mysql #不需要备份的数据库名，如果备份多个数据库，重复设置这个选项即可\r\nlog-slave-updates #这个参数一定要加上，否则不会给更新的记录些到二进制文件里\r\nslave-skip-errors #是跳过错误，继续执行复制操作\r\n\r\n2.建立用户\r\nmysql&gt; grant replication slave on *.* to slave@192.168.0.2 identified by ‘111111′;\r\n# grant replication slave on *.* to ‘用户名’@\'主机’ identified by ‘密码’;\r\n# 可在Slave上做连接测试: mysql -h 192.168.0.1 -u test -p\r\n\r\n3.锁主库表\r\nmysql&gt; FLUSH TABLES WITH READ LOCK;\r\n\r\n4.显示主库信息\r\n记录File和Position，从库设置将会用到\r\n=====================\r\nmysql&gt; SHOW MASTER STATUS;\r\n\r\n+------------------+----------+--------------+------------------+\r\n| File             | Position | Binlog_do_db | Binlog_ignore_db |\r\n+------------------+----------+--------------+------------------+\r\n| mysql-bin.000001 | 106      |              |                  |\r\n+------------------+----------+--------------+------------------+\r\n\r\n5.另开一个终端，打包主库\r\ncd /usr/local/mysql #mysql库目录\r\ntar zcvf var.tar.gz var\r\n============================\r\n\r\n二.MySQL从服务器配置\r\n\r\n1、传输拿到主库数据包、解包\r\n# cd /usr/local/mysql\r\n# scp 192.168.0.1:/usr/local/mysql/var.tar.gz .\r\n# tar zxvf var.tar.gz\r\n\r\n2、查看修改var文件夹权限\r\n# chown -R mysql:mysql var\r\n\r\n3.编辑 /etc/my.cnf\r\nserver-id=2\r\nlog-bin=mysql-bin\r\nmaster-host=192.168.0.1\r\nmaster-user=slave\r\nmaster-password=111111\r\nmaster-port=3306\r\nreplicate-do-db=test #需要备份的数据库名\r\nreplicate-ignore-db=mysql #忽略的数据库\r\nmaster-connect-retry=60 #如果从服务器发现主服务器断掉，重新连接的时间差(秒)\r\nlog-slave-updates #这个参数一定要加上，否则不会给更新的记录些到二进制文件里\r\nslave-skip-errors #是跳过错误，继续执行复制操作\r\n\r\n4、验证连接MASTER\r\n# mysql -h192.168.0.1 -uslave -ppassword\r\nmysql&gt; show grants for slave@192.168.0.2;\r\n\r\n5、在SLAVE上设置同步\r\n设置连接MASTER MASTER_LOG_FILE为主库的File，MASTER_LOG_POS为主库的Position\r\n============================\r\nmysql&gt; slave stop;\r\nmysql&gt; CHANGE MASTER TO MASTER_HOST=\'192.168.0.1\',MASTER_USER=\'slave\',MASTER_PASSWORD=\'111111\',MASTER_LOG_FILE=\'mysql-bin.000001\',MASTER_LOG_POS=106;\r\n\r\n6、启动SLAVE服务\r\nmysql&gt; slave start;\r\n\r\n7、查看SLAVE状态\r\nmysql&gt; SHOW SLAVE STATUS\\G;\r\n\r\n其中 Slave_IO_Running 和 Slave_SQL_Running 两列的值都为 \"Yes\"，表明 Slave 的 I/O 和 SQL 线程都在正常运行。\r\n\r\n8、解锁主库表\r\nmysql&gt; UNLOCK TABLES;\r\n\r\n到此主从库搭建成功。可以在主库上插入数据测试同步是否正常。\r\n\r\n--------------------------\r\n附：一些错误信息的处理，主从服务器上的命令，及状态信息。\r\n在从服务器上使用show slave status\\G\r\nSlave_IO_Running,为No,\r\n则说明IO_THREAD没有启动，请执行start slave io_thread\r\nSlave_SQL_Running为No\r\n则复制出错,查看Last_error字段排除错误后执行start slave sql_thread\r\n查看Slave_IO_State字段空 //复制没有启动\r\nConnecting to master//没有连接上master\r\nWaiting for master to send event//已经连上\r\n主服务器上的相关命令：\r\nshow master status\r\nshow slave hosts\r\nshow logs\r\nshow binlog events\r\npurge logs to \'log_name\'\r\npurge logs before \'date\'\r\nreset master(老版本flush master)\r\nset sql_log_bin=\r\n\r\n从服务器上的相关命令:\r\nslave start\r\nslave stop\r\nSLAVE STOP IO_THREAD //此线程把master段的日志写到本地\r\nSLAVE start IO_THREAD\r\nSLAVE STOP SQL_THREAD //此线程把写到本地的日志应用于数据库\r\nSLAVE start SQL_THREAD\r\nreset slave\r\nSET GLOBAL SQL_SLAVE_SKIP_COUNTER\r\nload data from master\r\nshow slave status(SUPER,REPLICATION CLIENT)\r\nCHANGE MASTER TO MASTER_HOST=, MASTER_PORT=,MASTER_USER=, MASTER_PASSWORD= //动态改变master信息\r\nPURGE MASTER [before \'date\'] 删除master端已同步过的日志\r\n6.3.1 Master 同步线程状态\r\n以下列出了master的 Binlog Dump 线程 State 字段中最常见的几种状态。如果在master上没有 Binlog Dump 线程，那么同步就没有在运行。\r\n也就是说，没有slave连接上来。\r\nSending binlog event to slave\r\n事件是由二进制日志构成，一个事件通常由更新语句加上其他信息。线程读取到一个事件并正发送到slave上。\r\nFinished reading one binlog; switching to next binlog\r\n读取完了一个二进制日志，正切换到下一个。\r\nHas sent all binlog to slave; waiting for binlog to be updated\r\n已经读取完全部未完成更新日志，并且全部都发送到slave了。它处于空闲状态，正等待在master上执行新的更新操作以在二进制日志中产生新\r\n的事件，然后读取它们。\r\nWaiting to finalize termination\r\n当前线程停止了，这个时间很短。\r\n\r\n6.3.2 Slave的I/O线程状态\r\n以下列出了slave的I/O线程 State 字段中最常见的几种状态。从MySQL 4.1.1开始，这个状态在执行 SHOW SLAVE STATUS 语句结果的\r\nSlave_IO_State 字段也会出现。这意味着可以只执行 SHOW SLAVE STATUS 语句就能了解到更多的信息。\r\nConnecting to master\r\n该线程证尝试连接到master上。\r\nChecking master version\r\n确定连接到master后出现的一个短暂的状态。\r\nRegistering slave on master\r\n确定连接到master后出现的一个短暂的状态。\r\nRequesting binlog dump\r\n确定连接到master后出现的一个短暂的状态。该线程向master发送一个请求，告诉它要请求的二进制文件以及开始位置。\r\nWaiting to reconnect after a failed binlog dump request\r\n如果二进制日志转储(binary log dump)请求失败了(由于连接断开)，该线程在休眠时进入这个状态，并定期重连。重连的时间间隔由 --\r\nmaster-connect-retry 选项来指定。\r\nReconnecting after a failed binlog dump request\r\n该线程正尝试重连到master。\r\nWaiting for master to send event\r\n已经连接到master，正等待它发送二进制日志。如果master闲置时，这个状态可能会持续较长时间，如果它等待超过 slave_read_timeout 秒\r\n，就会发生超时。这时，它就会考虑断开连接，然后尝试重连。\r\nQueueing master event to the relay log\r\n已经读取到一个事件，正把它拷贝到中继日志中以备SQL线程处理。\r\nWaiting to reconnect after a failed master event read\r\n读日志时发生错误(由于连接断开)。该线程在重连之前休眠 master-connect-retry 秒。\r\nReconnecting after a failed master event read\r\n正尝试重连到master。当连接确定后，状态就变成 Waiting for master to send event。\r\nWaiting for the slave SQL thread to free enough relay log space\r\nrelay_log_space_limit 的值非零，中继日志的大小总和超过这个值了。I/O线程等待SQL线程先处理中继日志然后删除它们以释放足够的空间\r\n。\r\nWaiting for slave mutex on exit\r\n当前线程停止了，这个时间很短。\r\n\r\n6.3.3 Slave的SQL线程状态\r\n以下列出了slave的SQL线程 State 字段中最常见的几种状态：\r\nReading event from the relay log\r\n从中继日志里读到一个事件以备执行。\r\nHas read all relay log; waiting for the slave I/O thread to update it\r\n已经处理完中继日志中的全部事件了，正等待I/O线程写入更新的日志。\r\nWaiting for slave mutex on exit\r\n当前线程停止了，这个时间很短。\r\n\r\n--------------\r\n常见的一些问题：\r\n\r\n一.从库SLAVE启动问题\r\n由于一些错误操作导致 CHANGE MASTER 和 SLAVE 服务无法启动，系统报错如下：\r\n*****************************************************************\r\nCould not initialize master info structure; more error messages can be found in the MySQL error log.\r\n*****************************************************************\r\n无法初始化master info结构，MySQL错误日志记录了更详细的错误信息。\r\n解决方法：\r\n1、查看MySQL错误日志，如：同步的上一个Position是多少，很多情况下无法启动服务是由于mysql识别的同步始终停留在上一个Position上。\r\n2、查看master.info和relay-log.info，master.info 记录MASTER相关信息，relay-log.info 记录当前同步日志信息。\r\n3、停止myslq服务，删除master.info和relay-log.info。\r\n4、启动mysql服务。\r\n5、重新CHANGE MASTER，重新启动SLAVE服务。\r\n\r\n二.主从不能同步\r\nshow slave status;报错:Error xxx dosn\'t exist\r\n且show slave status\\G:\r\nSlave_SQL_Running: NO\r\nSeconds_Behind_Master: NULL\r\n解决方法:\r\nstop slave;\r\nset global sql_slave_skip_counter =1 ;\r\nstart slave;\r\n之后Slave会和Master去同步 主要看:\r\nSlave_IO_Running: Yes\r\nSlave_SQL_Running: Yes\r\nSeconds_Behind_Master是否为0，0就是已经同步了\r\n2,还需要做的一些优化与监视:\r\nshow full processlist; //查看mysql当前同步线程号\r\nskip-name-resolve       //跳过dns名称查询，有助于加快连接及同步的速度\r\nmax_connections=1000    //增大Mysql的连接数目，(默认100)\r\nmax_connect_errors=100 //增大Mysql的错误连接数目,(默认10)\r\n\r\n查看日志一些命令\r\n1, show master status\\G;\r\n在这里主要是看log-bin的文件是否相同。\r\nshow slave status\\G;\r\n在这里主要是看:\r\nSlave_IO_Running=Yes\r\nSlave_SQL_Running=Yes\r\n如果都是Yes,则说明配置成功.\r\n2,在master上输入show processlist\\G;\r\nmysql&gt; SHOW PROCESSLIST\\G\r\n*************************** 1. row ***************************\r\nId: 2\r\nUser: root\r\nHost: localhost:32931\r\ndb: NULL\r\nCommand: Binlog Dump\r\nTime: 94\r\nState: Has sent all binlog to slave; waiting for binlog to\r\nbe updated\r\nInfo: NULL\r\n如果出现Command: Binlog Dump,则说明配置成功.\r\n\r\nstop slave    #停止同步\r\nstart slave    #开始同步，从日志终止的位置开始更新。\r\nSET SQL_LOG_BIN=0|1 #主机端运行，需要super权限，用来开停日志，随意开停，会造成主机从机数据不一致，造成错误\r\nSET GLOBAL SQL_SLAVE_SKIP_COUNTER=n # 客户端运行，用来跳过几个事件，只有当同步进程出现错误而停止的时候才可以执行。\r\nRESET MASTER #主机端运行,清除所有的日志，这条命令就是原来的FLUSH MASTER\r\nRESET SLAVE   #从机运行，清除日志同步位置标志，并重新生成master.info\r\n虽然重新生成了master.info,但是并不起用，最好，将从机的mysql进程重启一下，\r\nLOAD TABLE tblname FROM MASTER #从机运行，从主机端重读指定的表的数据，每次只能读取一个，受timeout时间限制，需要调整timeout时间。执行这个命令需要同步账号有 reload和super权限。以及对相应的库有select权限。如果表比较大，要增加net_read_timeout 和 net_write_timeout的值\r\nLOAD DATA FROM MASTER #从机执行，从主机端重新读入所有的数据。执行这个命令需要同步账号有reload和super权限。以及对相应的库有select权限。如果表比较大，要增加net_read_timeout 和 net_write_timeout的值\r\nCHANGE MASTER TO master_def_list #在线改变一些主机设置，多个用逗号间隔,比如\r\nCHANGE MASTER TO\r\nMASTER_HOST=\'master2.mycompany.com\',\r\nMASTER_USER=\'replication\',\r\nMASTER_PASSWORD=\'bigs3cret\'\r\nMASTER_POS_WAIT() #从机运行\r\nSHOW MASTER STATUS #主机运行，看日志导出信息\r\nSHOW SLAVE HOSTS #主机运行，看连入的从机的情况。\r\nSHOW SLAVE STATUS (slave)\r\nSHOW MASTER LOGS (master)\r\nSHOW BINLOG EVENTS [ IN \'logname\' ] [ FROM pos ] [ LIMIT [offset,] rows ]\r\nPURGE [MASTER] LOGS TO \'logname\' ; PURGE [MASTER] LOGS BEFORE \'date\'\r\nshow binlog events; #查看主库二进制日志文件内容：\r\n注意：\r\n1.主辅库同步主要是通过二进制日志来实现同步的。\r\n2.在启动辅库的时候必须先把数据同步，并删除日志目录下的：master.info文件。因为master.info记录了上次要连接主库的信息，如果不删除，即使my.cnf里进行了修改，也不起作用。因为读取的还是master.info文件里的信息。','Mysql主从同步备份策略','','publish','open','open','','mysql%e4%b8%bb%e4%bb%8e%e5%90%8c%e6%ad%a5%e5%a4%87%e4%bb%bd%e7%ad%96%e7%95%a5','','','2010-10-27 17:20:12','2010-10-28 01:20:12','',0,'http://www.wordpress.cn/?p=228',0,'post','',0),(229,1,'2010-10-27 17:19:39','2010-10-28 01:19:39','','Mysql主从同步备份策略','','inherit','open','open','','228-revision','','','2010-10-27 17:19:39','2010-10-28 01:19:39','',228,'http://www.wordpress.cn/?p=229',0,'revision','',0),(230,1,'2010-10-27 17:21:20','2010-10-28 01:21:20','mysql主从同步备份策略\n环境：\n主从服务器上的MySQL数据库版本同为5.1.34\n主机IP：192.168.0.1\n从机IP：192.168.0.2\n\n一. MySQL主服务器配置\n\n1.编辑配置文件/etc/my.cnf\n# 确保有如下行\nserver-id = 1\nlog-bin=mysql-bin\nbinlog-do-db=mysql #需要备份的数据库名，如果备份多个数据库，重复设置这个选项即可\nbinlog-ignore-db=mysql #不需要备份的数据库名，如果备份多个数据库，重复设置这个选项即可\nlog-slave-updates #这个参数一定要加上，否则不会给更新的记录些到二进制文件里\nslave-skip-errors #是跳过错误，继续执行复制操作\n\n2.建立用户\nmysql&gt; grant replication slave on *.* to slave@192.168.0.2 identified by ‘111111′;\n# grant replication slave on *.* to ‘用户名’@\'主机’ identified by ‘密码’;\n# 可在Slave上做连接测试: mysql -h 192.168.0.1 -u test -p\n\n3.锁主库表\nmysql&gt; FLUSH TABLES WITH READ LOCK;\n\n4.显示主库信息\n记录File和Position，从库设置将会用到\n=====================\nmysql&gt; SHOW MASTER STATUS;\n\n+------------------+----------+--------------+------------------+\n| File             | Position | Binlog_do_db | Binlog_ignore_db |\n+------------------+----------+--------------+------------------+\n| mysql-bin.000001 | 106      |              |                  |\n+------------------+----------+--------------+------------------+\n\n5.另开一个终端，打包主库\ncd /usr/local/mysql #mysql库目录\ntar zcvf var.tar.gz var\n============================\n\n二.MySQL从服务器配置\n\n1、传输拿到主库数据包、解包\n# cd /usr/local/mysql\n# scp 192.168.0.1:/usr/local/mysql/var.tar.gz .\n# tar zxvf var.tar.gz\n\n2、查看修改var文件夹权限\n# chown -R mysql:mysql var\n\n3.编辑 /etc/my.cnf\nserver-id=2\nlog-bin=mysql-bin\nmaster-host=192.168.0.1\nmaster-user=slave\nmaster-password=111111\nmaster-port=3306\nreplicate-do-db=test #需要备份的数据库名\nreplicate-ignore-db=mysql #忽略的数据库\nmaster-connect-retry=60 #如果从服务器发现主服务器断掉，重新连接的时间差(秒)\nlog-slave-updates #这个参数一定要加上，否则不会给更新的记录些到二进制文件里\nslave-skip-errors #是跳过错误，继续执行复制操作\n\n4、验证连接MASTER\n# mysql -h192.168.0.1 -uslave -ppassword\nmysql&gt; show grants for slave@192.168.0.2;\n\n5、在SLAVE上设置同步\n设置连接MASTER MASTER_LOG_FILE为主库的File，MASTER_LOG_POS为主库的Position\n============================\nmysql&gt; slave stop;\nmysql&gt; CHANGE MASTER TO MASTER_HOST=\'192.168.0.1\',MASTER_USER=\'slave\',MASTER_PASSWORD=\'111111\',MASTER_LOG_FILE=\'mysql-bin.000001\',MASTER_LOG_POS=106;\n\n6、启动SLAVE服务\nmysql&gt; slave start;\n\n7、查看SLAVE状态\nmysql&gt; SHOW SLAVE STATUS\\G;\n\n其中 Slave_IO_Running 和 Slave_SQL_Running 两列的值都为 \"Yes\"，表明 Slave 的 I/O 和 SQL 线程都在正常运行。\n\n8、解锁主库表\nmysql&gt; UNLOCK TABLES;\n\n到此主从库搭建成功。可以在主库上插入数据测试同步是否正常。\n\n--------------------------\n附：一些错误信息的处理，主从服务器上的命令，及状态信息。\n在从服务器上使用show slave status\\G\nSlave_IO_Running,为No,\n则说明IO_THREAD没有启动，请执行start slave io_thread\nSlave_SQL_Running为No\n则复制出错,查看Last_error字段排除错误后执行start slave sql_thread\n查看Slave_IO_State字段空 //复制没有启动\nConnecting to master//没有连接上master\nWaiting for master to send event//已经连上\n主服务器上的相关命令：\nshow master status\nshow slave hosts\nshow logs\nshow binlog events\npurge logs to \'log_name\'\npurge logs before \'date\'\nreset master(老版本flush master)\nset sql_log_bin=\n\n从服务器上的相关命令:\nslave start\nslave stop\nSLAVE STOP IO_THREAD //此线程把master段的日志写到本地\nSLAVE start IO_THREAD\nSLAVE STOP SQL_THREAD //此线程把写到本地的日志应用于数据库\nSLAVE start SQL_THREAD\nreset slave\nSET GLOBAL SQL_SLAVE_SKIP_COUNTER\nload data from master\nshow slave status(SUPER,REPLICATION CLIENT)\nCHANGE MASTER TO MASTER_HOST=, MASTER_PORT=,MASTER_USER=, MASTER_PASSWORD= //动态改变master信息\nPURGE MASTER [before \'date\'] 删除master端已同步过的日志\n6.3.1 Master 同步线程状态\n以下列出了master的 Binlog Dump 线程 State 字段中最常见的几种状态。如果在master上没有 Binlog Dump 线程，那么同步就没有在运行。\n也就是说，没有slave连接上来。\nSending binlog event to slave\n事件是由二进制日志构成，一个事件通常由更新语句加上其他信息。线程读取到一个事件并正发送到slave上。\nFinished reading one binlog; switching to next binlog\n读取完了一个二进制日志，正切换到下一个。\nHas sent all binlog to slave; waiting for binlog to be updated\n已经读取完全部未完成更新日志，并且全部都发送到slave了。它处于空闲状态，正等待在master上执行新的更新操作以在二进制日志中产生新\n的事件，然后读取它们。\nWaiting to finalize termination\n当前线程停止了，这个时间很短。\n\n6.3.2 Slave的I/O线程状态\n以下列出了slave的I/O线程 State 字段中最常见的几种状态。从MySQL 4.1.1开始，这个状态在执行 SHOW SLAVE STATUS 语句结果的\nSlave_IO_State 字段也会出现。这意味着可以只执行 SHOW SLAVE STATUS 语句就能了解到更多的信息。\nConnecting to master\n该线程证尝试连接到master上。\nChecking master version\n确定连接到master后出现的一个短暂的状态。\nRegistering slave on master\n确定连接到master后出现的一个短暂的状态。\nRequesting binlog dump\n确定连接到master后出现的一个短暂的状态。该线程向master发送一个请求，告诉它要请求的二进制文件以及开始位置。\nWaiting to reconnect after a failed binlog dump request\n如果二进制日志转储(binary log dump)请求失败了(由于连接断开)，该线程在休眠时进入这个状态，并定期重连。重连的时间间隔由 --\nmaster-connect-retry 选项来指定。\nReconnecting after a failed binlog dump request\n该线程正尝试重连到master。\nWaiting for master to send event\n已经连接到master，正等待它发送二进制日志。如果master闲置时，这个状态可能会持续较长时间，如果它等待超过 slave_read_timeout 秒\n，就会发生超时。这时，它就会考虑断开连接，然后尝试重连。\nQueueing master event to the relay log\n已经读取到一个事件，正把它拷贝到中继日志中以备SQL线程处理。\nWaiting to reconnect after a failed master event read\n读日志时发生错误(由于连接断开)。该线程在重连之前休眠 master-connect-retry 秒。\nReconnecting after a failed master event read\n正尝试重连到master。当连接确定后，状态就变成 Waiting for master to send event。\nWaiting for the slave SQL thread to free enough relay log space\nrelay_log_space_limit 的值非零，中继日志的大小总和超过这个值了。I/O线程等待SQL线程先处理中继日志然后删除它们以释放足够的空间\n。\nWaiting for slave mutex on exit\n当前线程停止了，这个时间很短。\n\n6.3.3 Slave的SQL线程状态\n以下列出了slave的SQL线程 State 字段中最常见的几种状态：\nReading event from the relay log\n从中继日志里读到一个事件以备执行。\nHas read all relay log; waiting for the slave I/O thread to update it\n已经处理完中继日志中的全部事件了，正等待I/O线程写入更新的日志。\nWaiting for slave mutex on exit\n当前线程停止了，这个时间很短。\n\n--------------\n常见的一些问题：\n\n一.从库SLAVE启动问题\n由于一些错误操作导致 CHANGE MASTER 和 SLAVE 服务无法启动，系统报错如下：\n*****************************************************************\nCould not initialize master info structure; more error messages can be found in the MySQL error log.\n*****************************************************************\n无法初始化master info结构，MySQL错误日志记录了更详细的错误信息。\n解决方法：\n1、查看MySQL错误日志，如：同步的上一个Position是多少，很多情况下无法启动服务是由于mysql识别的同步始终停留在上一个Position上。\n2、查看master.info和relay-log.info，master.info 记录MASTER相关信息，relay-log.info 记录当前同步日志信息。\n3、停止myslq服务，删除master.info和relay-log.info。\n4、启动mysql服务。\n5、重新CHANGE MASTER，重新启动SLAVE服务。\n\n二.主从不能同步\nshow slave status;报错:Error xxx dosn\'t exist\n且show slave status\\G:\nSlave_SQL_Running: NO\nSeconds_Behind_Master: NULL\n解决方法:\nstop slave;\nset global sql_slave_skip_counter =1 ;\nstart slave;\n之后Slave会和Master去同步 主要看:\nSlave_IO_Running: Yes\nSlave_SQL_Running: Yes\nSeconds_Behind_Master是否为0，0就是已经同步了\n2,还需要做的一些优化与监视:\nshow full processlist; //查看mysql当前同步线程号\nskip-name-resolve       //跳过dns名称查询，有助于加快连接及同步的速度\nmax_connections=1000    //增大Mysql的连接数目，(默认100)\nmax_connect_errors=100 //增大Mysql的错误连接数目,(默认10)\n\n查看日志一些命令\n1, show master status\\G;\n在这里主要是看log-bin的文件是否相同。\nshow slave status\\G;\n在这里主要是看:\nSlave_IO_Running=Yes\nSlave_SQL_Running=Yes\n如果都是Yes,则说明配置成功.\n2,在master上输入show processlist\\G;\nmysql&gt; SHOW PROCESSLIST\\G\n*************************** 1. row ***************************\nId: 2\nUser: root\nHost: localhost:32931\ndb: NULL\nCommand: Binlog Dump\nTime: 94\nState: Has sent all binlog to slave; waiting for binlog to\nbe updated\nInfo: NULL\n如果出现Command: Binlog Dump,则说明配置成功.\n\nstop slave    #停止同步\nstart slave    #开始同步，从日志终止的位置开始更新。\nSET SQL_LOG_BIN=0|1 #主机端运行，需要super权限，用来开停日志，随意开停，会造成主机从机数据不一致，造成错误\nSET GLOBAL SQL_SLAVE_SKIP_COUNTER=n # 客户端运行，用来跳过几个事件，只有当同步进程出现错误而停止的时候才可以执行。\nRESET MASTER #主机端运行,清除所有的日志，这条命令就是原来的FLUSH MASTER\nRESET SLAVE   #从机运行，清除日志同步位置标志，并重新生成master.info\n虽然重新生成了master.info,但是并不起用，最好，将从机的mysql进程重启一下，\nLOAD TABLE tblname FROM MASTER #从机运行，从主机端重读指定的表的数据，每次只能读取一个，受timeout时间限制，需要调整timeout时间。执行这个命令需要同步账号有 reload和super权限。以及对相应的库有select权限。如果表比较大，要增加net_read_timeout 和 net_write_timeout的值\nLOAD DATA FROM MASTER #从机执行，从主机端重新读入所有的数据。执行这个命令需要同步账号有reload和super权限。以及对相应的库有select权限。如果表比较大，要增加net_read_timeout 和 net_write_timeout的值\nCHANGE MASTER TO master_def_list #在线改变一些主机设置，多个用逗号间隔,比如\nCHANGE MASTER TO\nMASTER_HOST=\'master2.mycompany.com\',\nMASTER_USER=\'replication\',\nMASTER_PASSWORD=\'bigs3cret\'\nMASTER_POS_WAIT() #从机运行\nSHOW MASTER STATUS #主机运行，看日志导出信息\nSHOW SLAVE HOSTS #主机运行，看连入的从机的情况。\nSHOW SLAVE STATUS (slave)\nSHOW MASTER LOGS (master)\nSHOW BINLOG EVENTS [ IN \'logname\' ] [ FROM pos ] [ LIMIT [offset,] rows ]\nPURGE [MASTER] LOGS TO \'logname\' ; PURGE [MASTER] LOGS BEFORE \'date\'\nshow binlog events; #查看主库二进制日志文件内容：\n注意：\n1.主辅库同步主要是通过二进制日志来实现同步的。\n2.在启动辅库的时候必须先把数据同步，并删除日志目录下的：master.info文件。因为master.info记录了上次要连接主库的信息，如果不删除，即使my.cnf里进行了修改，也不起作用。因为读取的还是master.info文件里的信息。','Mysql主从同步备份策略','','inherit','open','open','','228-autosave','','','2010-10-27 17:21:20','2010-10-28 01:21:20','',228,'http://www.wordpress.cn/?p=230',0,'revision','',0),(233,1,'2010-11-05 18:04:47','2010-11-06 02:04:47','首先我们先介绍一下为什么要让 Apache 与 Tomcat 之间进行连接。事实上 Tomcat 本身已经提供了 HTTP 服务，该服务默认的端口是 8080，装好 tomcat 后通过 8080 端口可以直接使用 Tomcat 所运行的应用程序，你也可以将该端口改为 80。\n\n既然 Tomcat 本身已经可以提供这样的服务，我们为什么还要引入 Apache 或者其他的一些专门的 HTTP 服务器呢？原因有下面几个：\n\n1. 提升对静态文件的处理性能\n\n2. 利用 Web 服务器来做负载均衡以及容错\n\n3. 无缝的升级应用程序\n\n这三点对一个 web 网站来说是非常之重要的，我们希望我们的网站不仅是速度快，而且要稳定，不能因为某个 Tomcat 宕机或者是升级程序导致用户访问不了，而能完成这几个功能的、最好的 HTTP 服务器也就只有 apache 的 http server 了，它跟 tomcat 的结合是最紧密和可靠的。\n\n接下来我们介绍三种方法将 apache 和 tomcat 整合在一起。\n\n<a name=\"N10058\">JK</a>\n\n这是最常见的方式，你可以在网上找到很多关于配置JK的网页，当然最全的还是其官方所提供的文档。JK 本身有两个版本分别是 1 和 2，目前 1 最新的版本是 1.2.19，而版本 2 早已经废弃了，以后不再有新版本的推出了，所以建议你采用版本 1。\n\nJK 是通过 AJP 协议与 Tomcat 服务器进行通讯的，Tomcat 默认的 AJP Connector 的端口是 8009。JK 本身提供了一个监控以及管理的页面 jkstatus，通过 jkstatus 可以监控 JK 目前的工作状态以及对到 tomcat 的连接进行设置，如下图所示：\n<a name=\"N10066\"><strong>图 1：监控以及管理的页面 jkstatus</strong></a>\n<img src=\"http://www.ibm.com/developerworks/cn/opensource/os-lo-apache-tomcat/images/image002.jpg\" border=\"0\" alt=\"图 1：监控以及管理的页面 jkstatus\" width=\"554\" height=\"228\" />\n\n在这个图中我们可以看到当前JK配了两个连接分别到 8109 和 8209 端口上，目前 s2 这个连接是停止状态，而 s1 这个连接自上次重启后已经处理了 47 万多个请求，流量达到 6.2 个 G，最大的并发数有 13 等等。我们也可以利用 jkstatus 的管理功能来切换 JK 到不同的 Tomcat 上，例如将 s2 启用，并停用 s1，这个在更新应用程序的时候非常有用，而且整个切换过程对用户来说是透明的，也就达到了无缝升级的目的。关于 JK 的配置文章网上已经非常多了，这里我们不再详细的介绍整个配置过程，但我要讲一下配置的思路，只要明白了配置的思路，JK 就是一个非常灵活的组件。\n\nJK 的配置最关键的有三个文件，分别是\n\n<strong>httpd.conf </strong>\nApache 服务器的配置文件，用来加载 JK 模块以及指定 JK 配置文件信息\n\n<strong>workers.properties</strong>\n到 Tomcat 服务器的连接定义文件\n\n<strong>uriworkermap.properties</strong>\nURI 映射文件，用来指定哪些 URL 由 Tomcat 处理，你也可以直接在 httpd.conf 中配置这些 URI，但是独立这些配置的好处是 JK 模块会定期更新该文件的内容，使得我们修改配置的时候无需重新启动 Apache 服务器。\n\n其中第二、三个配置文件名都可以自定义。下面是一个典型的 httpd.conf 对 JK 的配置\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"100%\">\n<tbody>\n<tr>\n<pre># (httpd.conf)\n# 加载 mod_jk 模块\nLoadModule jk_module modules/mod_jk.so\n\n#\n# Configure mod_jk\n#\n\nJkWorkersFile conf/workers.properties\nJkMountFile conf/uriworkermap.properties\nJkLogFile logs/mod_jk.log\nJkLogLevel warn</pre>\n</tr>\n</tbody>\n</table>\n \n\n接下来我们在 Apache 的 conf 目录下新建两个文件分别是 workers.properties、uriworkermap.properties。这两个文件的内容大概如下\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"100%\">\n<tbody>\n<tr>\n<pre>#\n# workers.properties\n#\n\n# list the workers by name\n\nworker.list=DLOG4J, status\n\n# localhost server 1\n# ------------------------\nworker.s1.port=8109\nworker.s1.host=localhost\nworker.s1.type=ajp13\n\n# localhost server 2\n# ------------------------\nworker.s2.port=8209\nworker.s2.host=localhost\nworker.s2.type=ajp13\nworker.s2.stopped=1\n\nworker.DLOG4J.type=lb\nworker.retries=3\nworker.DLOG4J.balanced_workers=s1, s2\nworker.DLOG4J.sticky_session=1\n\nworker.status.type=status</pre>\n</tr>\n</tbody>\n</table>\n \n\n以上的 workers.properties 配置就是我们前面那个屏幕抓图的页面所用的配置。首先我们配置了两个类型为 ajp13 的 worker 分别是 s1 和 s2，它们指向同一台服务器上运行在两个不同端口 8109 和 8209 的 Tomcat 上。接下来我们配置了一个类型为 lb（也就是负载均衡的意思）的 worker，它的名字是 DLOG4J，这是一个逻辑的 worker，它用来管理前面配置的两个物理连接 s1 和 s2。最后还配置了一个类型为 status 的 worker，这是用来监控 JK 本身的模块。有了这三个 worker 还不够，我们还需要告诉 JK，哪些 worker 是可用的，所以就有 <strong>worker.list = DLOG4J, status</strong> 这行配置。\n\n接下来便是 URI 的映射配置了，我们需要指定哪些链接是由 Tomcat 处理的，哪些是由 Apache 直接处理的，看看下面这个文件你就能明白其中配置的意义\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"100%\">\n<tbody>\n<tr>\n<pre>/*=DLOG4J\n/jkstatus=status\n\n!/*.gif=DLOG4J\n!/*.jpg=DLOG4J\n!/*.png=DLOG4J\n!/*.css=DLOG4J\n!/*.js=DLOG4J\n!/*.htm=DLOG4J\n!/*.html=DLOG4J</pre>\n</tr>\n</tbody>\n</table>\n \n\n相信你已经明白了一大半了：所有的请求都由 DLOG4J 这个 worker 进行处理，但是有几个例外，/jkstatus 请求由 status 这个 worker 处理。另外这个配置中每一行数据前面的感叹号是什么意思呢？感叹号表示接下来的 URI 不要由 JK 进行处理，也就是 Apache 直接处理所有的图片、css 文件、js 文件以及静态 html 文本文件。\n\n通过对 workers.properties 和 uriworkermap.properties 的配置，可以有各种各样的组合来满足我们前面提出对一个 web 网站的要求。您不妨动手试试！\n\n<hr />回页首\n\n<a name=\"N100C4\">http_proxy</a>\n\n这是利用 Apache 自带的 mod_proxy 模块使用代理技术来连接 Tomcat。在配置之前请确保是否使用的是 2.2.x 版本的 Apache 服务器。因为 2.2.x 版本对这个模块进行了重写，大大的增强了其功能和稳定性。\n\nhttp_proxy 模式是基于 HTTP 协议的代理，因此它要求 Tomcat 必须提供 HTTP 服务，也就是说必须启用 Tomcat 的 HTTP Connector。一个最简单的配置如下\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"100%\">\n<tbody>\n<tr>\n<pre>ProxyPass /images !\nProxyPass /css !\nProxyPass /js !\nProxyPass / http://localhost:8080/</pre>\n</tr>\n</tbody>\n</table>\n \n\n在这个配置中，我们把所有 http://localhost 的请求代理到 http://localhost:8080/ ，这也就是 Tomcat 的访问地址，除了 images、css、js 几个目录除外。我们同样可以利用 mod_proxy 来做负载均衡，再看看下面这个配置\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"100%\">\n<tbody>\n<tr>\n<pre>ProxyPass /images !\nProxyPass /css ! \nProxyPass /js !\n\nProxyPass / balancer://example/\n&lt;Proxy balancer://example/&gt;\nBalancerMember http://server1:8080/\nBalancerMember http://server2:8080/\nBalancerMember http://server3:8080/\n&lt;/Proxy&gt;</pre>\n</tr>\n</tbody>\n</table>\n \n\n配置比 JK 简单多了，而且它也可以通过一个页面来监控集群运行的状态，并做一些简单的维护设置。\n<a name=\"N100EA\"><strong>图 2：监控集群运行状态</strong></a>\n<img src=\"http://www.ibm.com/developerworks/cn/opensource/os-lo-apache-tomcat/images/image004.gif\" border=\"0\" alt=\"图 2：监控集群运行状态\" width=\"553\" height=\"427\" />\n\n<hr />回页首\n\n<a name=\"N100FA\">ajp_proxy</a>\n\najp_proxy 连接方式其实跟 http_proxy 方式一样，都是由 mod_proxy 所提供的功能。配置也是一样，只需要把 http:// 换成 ajp:// ，同时连接的是 Tomcat 的 AJP Connector 所在的端口。上面例子的配置可以改为：\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"100%\">\n<tbody>\n<tr>\n<pre>ProxyPass /images !\nProxyPass /css ! \nProxyPass /js !\n\nProxyPass / balancer://example/\n&lt;Proxy balancer://example/&gt;\nBalancerMember ajp://server1:8080/\nBalancerMember ajp://server2:8080/\nBalancerMember ajp://server3:8080/\n&lt;/Proxy&gt;</pre>\n</tr>\n</tbody>\n</table>\n \n\n采用 proxy 的连接方式，需要在 Apache 上加载所需的模块，mod_proxy 相关的模块有 mod_proxy.so、mod_proxy_connect.so、mod_proxy_http.so、mod_proxy_ftp.so、mod_proxy_ajp.so， 其中 mod_proxy_ajp.so 只在 Apache 2.2.x 中才有。如果是采用 http_proxy 方式则需要加载 mod_proxy.so 和 mod_proxy_http.so；如果是 ajp_proxy 则需要加载 mod_proxy.so 和 mod_proxy_ajp.so这两个模块。\n\n<hr />回页首\n\n<a name=\"N1010F\">三者比较</a>\n\n相对于 JK 的连接方式，后两种在配置上是比较简单的，灵活性方面也一点都不逊色。但就稳定性而言就不像 JK 这样久经考验，毕竟 Apache 2.2.3 推出的时间并不长，采用这种连接方式的网站还不多，因此，如果是应用于关键的互联网网站，还是建议采用 JK 的连接方式。','Apache HTTP Server 与 Tomcat 的三种连接方式介绍','','inherit','open','open','','232-revision','','','2010-11-05 18:04:47','2010-11-06 02:04:47','',232,'http://www.wordpress.cn/?p=233',0,'revision','',0),(234,1,'2010-11-05 18:05:08','2010-11-06 02:05:08','首先我们先介绍一下为什么要让 Apache 与 Tomcat 之间进行连接。事实上 Tomcat 本身已经提供了 HTTP 服务，该服务默认的端口是 8080，装好 tomcat 后通过 8080 端口可以直接使用 Tomcat 所运行的应用程序，你也可以将该端口改为 80。\r\n\r\n既然 Tomcat 本身已经可以提供这样的服务，我们为什么还要引入 Apache 或者其他的一些专门的 HTTP 服务器呢？原因有下面几个：\r\n\r\n1. 提升对静态文件的处理性能\r\n\r\n2. 利用 Web 服务器来做负载均衡以及容错\r\n\r\n3. 无缝的升级应用程序\r\n\r\n这三点对一个 web 网站来说是非常之重要的，我们希望我们的网站不仅是速度快，而且要稳定，不能因为某个 Tomcat 宕机或者是升级程序导致用户访问不了，而能完成这几个功能的、最好的 HTTP 服务器也就只有 apache 的 http server 了，它跟 tomcat 的结合是最紧密和可靠的。\r\n\r\n接下来我们介绍三种方法将 apache 和 tomcat 整合在一起。\r\n\r\n<a name=\"N10058\">JK</a>\r\n\r\n这是最常见的方式，你可以在网上找到很多关于配置JK的网页，当然最全的还是其官方所提供的文档。JK 本身有两个版本分别是 1 和 2，目前 1 最新的版本是 1.2.19，而版本 2 早已经废弃了，以后不再有新版本的推出了，所以建议你采用版本 1。\r\n\r\nJK 是通过 AJP 协议与 Tomcat 服务器进行通讯的，Tomcat 默认的 AJP Connector 的端口是 8009。JK 本身提供了一个监控以及管理的页面 jkstatus，通过 jkstatus 可以监控 JK 目前的工作状态以及对到 tomcat 的连接进行设置，如下图所示：\r\n<a name=\"N10066\"><strong>图 1：监控以及管理的页面 jkstatus</strong></a>\r\n<img src=\"http://www.ibm.com/developerworks/cn/opensource/os-lo-apache-tomcat/images/image002.jpg\" border=\"0\" alt=\"图 1：监控以及管理的页面 jkstatus\" width=\"554\" height=\"228\" />\r\n\r\n在这个图中我们可以看到当前JK配了两个连接分别到 8109 和 8209 端口上，目前 s2 这个连接是停止状态，而 s1 这个连接自上次重启后已经处理了 47 万多个请求，流量达到 6.2 个 G，最大的并发数有 13 等等。我们也可以利用 jkstatus 的管理功能来切换 JK 到不同的 Tomcat 上，例如将 s2 启用，并停用 s1，这个在更新应用程序的时候非常有用，而且整个切换过程对用户来说是透明的，也就达到了无缝升级的目的。关于 JK 的配置文章网上已经非常多了，这里我们不再详细的介绍整个配置过程，但我要讲一下配置的思路，只要明白了配置的思路，JK 就是一个非常灵活的组件。\r\n\r\nJK 的配置最关键的有三个文件，分别是\r\n\r\n<strong>httpd.conf </strong>\r\nApache 服务器的配置文件，用来加载 JK 模块以及指定 JK 配置文件信息\r\n\r\n<strong>workers.properties</strong>\r\n到 Tomcat 服务器的连接定义文件\r\n\r\n<strong>uriworkermap.properties</strong>\r\nURI 映射文件，用来指定哪些 URL 由 Tomcat 处理，你也可以直接在 httpd.conf 中配置这些 URI，但是独立这些配置的好处是 JK 模块会定期更新该文件的内容，使得我们修改配置的时候无需重新启动 Apache 服务器。\r\n\r\n其中第二、三个配置文件名都可以自定义。下面是一个典型的 httpd.conf 对 JK 的配置\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<pre># (httpd.conf)\r\n# 加载 mod_jk 模块\r\nLoadModule jk_module modules/mod_jk.so\r\n\r\n#\r\n# Configure mod_jk\r\n#\r\n\r\nJkWorkersFile conf/workers.properties\r\nJkMountFile conf/uriworkermap.properties\r\nJkLogFile logs/mod_jk.log\r\nJkLogLevel warn</pre>\r\n</tr>\r\n</tbody>\r\n</table>\r\n \r\n\r\n接下来我们在 Apache 的 conf 目录下新建两个文件分别是 workers.properties、uriworkermap.properties。这两个文件的内容大概如下\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<pre>#\r\n# workers.properties\r\n#\r\n\r\n# list the workers by name\r\n\r\nworker.list=DLOG4J, status\r\n\r\n# localhost server 1\r\n# ------------------------\r\nworker.s1.port=8109\r\nworker.s1.host=localhost\r\nworker.s1.type=ajp13\r\n\r\n# localhost server 2\r\n# ------------------------\r\nworker.s2.port=8209\r\nworker.s2.host=localhost\r\nworker.s2.type=ajp13\r\nworker.s2.stopped=1\r\n\r\nworker.DLOG4J.type=lb\r\nworker.retries=3\r\nworker.DLOG4J.balanced_workers=s1, s2\r\nworker.DLOG4J.sticky_session=1\r\n\r\nworker.status.type=status</pre>\r\n</tr>\r\n</tbody>\r\n</table>\r\n \r\n\r\n以上的 workers.properties 配置就是我们前面那个屏幕抓图的页面所用的配置。首先我们配置了两个类型为 ajp13 的 worker 分别是 s1 和 s2，它们指向同一台服务器上运行在两个不同端口 8109 和 8209 的 Tomcat 上。接下来我们配置了一个类型为 lb（也就是负载均衡的意思）的 worker，它的名字是 DLOG4J，这是一个逻辑的 worker，它用来管理前面配置的两个物理连接 s1 和 s2。最后还配置了一个类型为 status 的 worker，这是用来监控 JK 本身的模块。有了这三个 worker 还不够，我们还需要告诉 JK，哪些 worker 是可用的，所以就有 <strong>worker.list = DLOG4J, status</strong> 这行配置。\r\n\r\n接下来便是 URI 的映射配置了，我们需要指定哪些链接是由 Tomcat 处理的，哪些是由 Apache 直接处理的，看看下面这个文件你就能明白其中配置的意义\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<pre>/*=DLOG4J\r\n/jkstatus=status\r\n\r\n!/*.gif=DLOG4J\r\n!/*.jpg=DLOG4J\r\n!/*.png=DLOG4J\r\n!/*.css=DLOG4J\r\n!/*.js=DLOG4J\r\n!/*.htm=DLOG4J\r\n!/*.html=DLOG4J</pre>\r\n</tr>\r\n</tbody>\r\n</table>\r\n \r\n\r\n相信你已经明白了一大半了：所有的请求都由 DLOG4J 这个 worker 进行处理，但是有几个例外，/jkstatus 请求由 status 这个 worker 处理。另外这个配置中每一行数据前面的感叹号是什么意思呢？感叹号表示接下来的 URI 不要由 JK 进行处理，也就是 Apache 直接处理所有的图片、css 文件、js 文件以及静态 html 文本文件。\r\n\r\n通过对 workers.properties 和 uriworkermap.properties 的配置，可以有各种各样的组合来满足我们前面提出对一个 web 网站的要求。您不妨动手试试！\r\n\r\n<hr />回页首\r\n\r\n<a name=\"N100C4\">http_proxy</a>\r\n\r\n这是利用 Apache 自带的 mod_proxy 模块使用代理技术来连接 Tomcat。在配置之前请确保是否使用的是 2.2.x 版本的 Apache 服务器。因为 2.2.x 版本对这个模块进行了重写，大大的增强了其功能和稳定性。\r\n\r\nhttp_proxy 模式是基于 HTTP 协议的代理，因此它要求 Tomcat 必须提供 HTTP 服务，也就是说必须启用 Tomcat 的 HTTP Connector。一个最简单的配置如下\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<pre>ProxyPass /images !\r\nProxyPass /css !\r\nProxyPass /js !\r\nProxyPass / http://localhost:8080/</pre>\r\n</tr>\r\n</tbody>\r\n</table>\r\n \r\n\r\n在这个配置中，我们把所有 http://localhost 的请求代理到 http://localhost:8080/ ，这也就是 Tomcat 的访问地址，除了 images、css、js 几个目录除外。我们同样可以利用 mod_proxy 来做负载均衡，再看看下面这个配置\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<pre>ProxyPass /images !\r\nProxyPass /css ! \r\nProxyPass /js !\r\n\r\nProxyPass / balancer://example/\r\n&lt;Proxy balancer://example/&gt;\r\nBalancerMember http://server1:8080/\r\nBalancerMember http://server2:8080/\r\nBalancerMember http://server3:8080/\r\n&lt;/Proxy&gt;</pre>\r\n</tr>\r\n</tbody>\r\n</table>\r\n \r\n\r\n配置比 JK 简单多了，而且它也可以通过一个页面来监控集群运行的状态，并做一些简单的维护设置。\r\n<a name=\"N100EA\"><strong>图 2：监控集群运行状态</strong></a>\r\n<img src=\"http://www.ibm.com/developerworks/cn/opensource/os-lo-apache-tomcat/images/image004.gif\" border=\"0\" alt=\"图 2：监控集群运行状态\" width=\"553\" height=\"427\" />\r\n\r\n<hr />回页首\r\n\r\n<a name=\"N100FA\">ajp_proxy</a>\r\n\r\najp_proxy 连接方式其实跟 http_proxy 方式一样，都是由 mod_proxy 所提供的功能。配置也是一样，只需要把 http:// 换成 ajp:// ，同时连接的是 Tomcat 的 AJP Connector 所在的端口。上面例子的配置可以改为：\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<pre>ProxyPass /images !\r\nProxyPass /css ! \r\nProxyPass /js !\r\n\r\nProxyPass / balancer://example/\r\n&lt;Proxy balancer://example/&gt;\r\nBalancerMember ajp://server1:8080/\r\nBalancerMember ajp://server2:8080/\r\nBalancerMember ajp://server3:8080/\r\n&lt;/Proxy&gt;</pre>\r\n</tr>\r\n</tbody>\r\n</table>\r\n \r\n\r\n采用 proxy 的连接方式，需要在 Apache 上加载所需的模块，mod_proxy 相关的模块有 mod_proxy.so、mod_proxy_connect.so、mod_proxy_http.so、mod_proxy_ftp.so、mod_proxy_ajp.so， 其中 mod_proxy_ajp.so 只在 Apache 2.2.x 中才有。如果是采用 http_proxy 方式则需要加载 mod_proxy.so 和 mod_proxy_http.so；如果是 ajp_proxy 则需要加载 mod_proxy.so 和 mod_proxy_ajp.so这两个模块。\r\n\r\n<hr />回页首\r\n\r\n<a name=\"N1010F\">三者比较</a>\r\n\r\n相对于 JK 的连接方式，后两种在配置上是比较简单的，灵活性方面也一点都不逊色。但就稳定性而言就不像 JK 这样久经考验，毕竟 Apache 2.2.3 推出的时间并不长，采用这种连接方式的网站还不多，因此，如果是应用于关键的互联网网站，还是建议采用 JK 的连接方式。','Apache HTTP Server 与 Tomcat 的三种连接方式介绍','','inherit','open','open','','232-autosave','','','2010-11-05 18:05:08','2010-11-06 02:05:08','',232,'http://www.wordpress.cn/?p=234',0,'revision','',0),(235,1,'2010-11-08 23:22:08','2010-11-09 07:22:08','以下小结使用tomcat5.0.28测试通过，tomcat安装路径为C:\\Tomcat\r\n\r\n在默认安装后，tomcat的主目录是webapps/root目录，如果我们想改变tomcat的主目录的话可以这样做：\r\n\r\n1.打开C:\\Tomcat\\conf\\server.xml，在<host></host>之间加入代码：\r\n\r\n<Context path=\"\" docBase=\"E:\\tomcatApp\\JeasyCMS\" debug=\"0\"  reloadable=\"true\" crossContext=\"true\"/>\r\n\r\n这样重新启动tomcat，我们的主目录就被设置为jeasyCMS这个项目了。 \r\n2.另外tomcat会生成一个C:\\Tomcat\\conf\\Catalina\\localhost目录，里面自动生成了一个ROOT.xml(root大写)文件，我们打开文件可以看到这些代码：\r\n\r\n<?xml version=\'\'1.0\'\' encoding=\'\'utf-8\'\'?>\r\n<Context crossContext=\"true\" docBase=\"E:/tomcatApp/JeasyCMS\" path=\"\" reloadable=\"true\">\r\n</Context> \r\n3.注意：我们创建虚拟目录的时候也是这样来进行设置的，只是context这个标签的path属性不为空。 \r\n4.我们设置虚拟目录和主目录，都会在C:\\Tomcat\\conf\\Catalina\\localhost目录下生成相应的xml文件，因此，当我们在server.xml里面删除了主目录和虚拟目录的设置后，如果系统还没有生效，这时候我们就要察看一下这个目录下的xml文件是不是已经被删除了，如果没有被删除，他还是对tomcat起作用的，我们必须手动删除。 \r\n5.基于第4点，通过测试我发现，每当我们访问一个地址的时候，tomcat首先是到C:\\Tomcat\\conf\\Catalina\\localhost目录下去察看有没有存在主目录或虚拟目录的xml文件，如果有xml文件，就按xml里的路径进行访问，如果没有xml文件，就到server.xml文件里去察看是否配置context标签，如果配置了context标签，则在C:\\Tomcat\\conf\\Catalina\\localhost目录下生成一个对应的xml文件，以便于下次直接验证而不再访问server.xml，与此同时打开context里指定的路径，如果server.xml里没有配置context标签，则返回访问错误页面。 \r\n6.基于第5点，我们其实可以直接在C:\\Tomcat\\conf\\Catalina\\localhost下建立一个xml文件，用于主目录或者虚拟目录，而不需要去修改server.xml就可以达到配置主目录和虚拟目录的目的，具体xml的格式可以如下：\r\n\r\n<?xml version=\'\'1.0\'\' encoding=\'\'utf-8\'\'?>\r\n<Context crossContext=\"true\" docBase=\"E:/tomcatApp/JeasyCMS\" path=\"\" reloadable=\"true\">\r\n</Context>\r\n\r\n具体是主目录的配置还是虚拟目录的配置取决于path的值。另外主目录文件名必须是ROOT.XML(root大写)，而虚拟目录的文件名称和虚拟目录的名称一致并且为小写，譬如： \r\n<?xml version=\'\'1.0\'\' encoding=\'\'utf-8\'\'?>\r\n<Context crossContext=\"true\" docBase=\"E:/tomcatApp/JeasyCMS\" path=\"\\cms\" reloadable=\"true\">\r\n</Context>\r\n\r\n以上则定义了一个名为：cms的虚拟目录，同时要将以上文本保存为cms.xml文件。\r\n\r\n7.以上住目录配置好后就可以按以下地址访问了：\r\n\r\nhttp://localhost:8080/(访问主目录)\r\n\r\nhttp://localhost:8080/cms (访问名为cms的虚拟目录)  \r\n\r\n \r\n\r\n文章出处：飞诺网(www.firnow.com):http://dev.firnow.com/course/3_program/java/javashl/2007123/89645.html','tomcat下域名的配置，ROOT.xml的作用','','publish','open','open','','tomcat%e4%b8%8b%e5%9f%9f%e5%90%8d%e7%9a%84%e9%85%8d%e7%bd%ae%ef%bc%8croot-xml%e7%9a%84%e4%bd%9c%e7%94%a8','','','2010-11-08 23:22:08','2010-11-09 07:22:08','',0,'http://www.wordpress.cn/?p=235',0,'post','',0),(245,1,'2010-11-22 18:21:23','2010-11-23 02:21:23','一般情况下我们是在页面的&lt;head&gt;区域使用语句\r\n\r\n&lt;script type=\"text/javascript\" src=\"xxx.js\"&gt;&lt;/script&gt;\r\n\r\n来载入JavaScript文件，使用这种方式载入JavaScript会导致页面加载速度过慢。什么原因呢？浏览器在显示HTML文件之前，会等 待完全加载整个HTML文件与&lt;head&gt;区域中额外加载的JavaScript文件与CSS样式文件，所以把JavaScript放 入&lt;head&gt;区域会影响页面的加载速度。解方法主要有如下几种：\r\n\r\n1.把加载JavaScript的代码放入&lt;body&gt;区域的底部,放入body区域的可加载文件是异步进行加载的，页面不必等待加载 完成才显示，比如图片总是在显示出页面后才渐渐载入了完整的图片，JavaScript加载也是一样的道理，放入底部最后加载，不会影响页面载入速度。\r\n\r\n2.使用JavaScript程序动态加载JavaScript文件。\r\n\r\n&lt;script type=\"text/javascript\"&gt;\r\nfunction loadScript(url, callback){\r\nvar script = document.createElement(\"script\")\r\nscript.type = \"text/javascript\";\r\nif (script.readyState){ //IE\r\nscript.onreadystatechange = function(){\r\nif (script.readyState == \"loaded\" ||\r\nscript.readyState == \"complete\"){\r\nscript.onreadystatechange = null;\r\ncallback();\r\n}\r\n};\r\n} else { //Others\r\nscript.onload = function(){\r\ncallback();\r\n};\r\n}\r\nscript.src = url;\r\ndocument.getElementsByTagName(\"head\")[0].appendChild(script);\r\n}\r\nloadScript(\"the-rest.js\", function(){\r\nApplication.init();\r\n});\r\n&lt;/script&gt;\r\n\r\n以上代码放在&lt;body&gt;低部，使用动态方式加载JavaScript文件，你可以在加载文件完备时，执行回调函数，可以处理 JavaScript依赖关系。当然你也可以使用ajax方式加态加载JavaScript文件，或者在window.onload事件中 loadScript。\r\n\r\n另外当加载多个Javascript文件时，文件与文件之间会有间隔延迟，为避免这种情况，我们可以把该页面要加载的JavaScript文件合并成一个JavaScript文件。\r\n\r\n只需要按照以上方式加载JavaScript文件，你的页面载入速度会提高很多，当然载入CSS样式文件是一样的道理。','书写高效javaScript代码---载入JavaScript文件','','publish','open','open','','%e4%b9%a6%e5%86%99%e9%ab%98%e6%95%88javascript%e4%bb%a3%e7%a0%81-%e8%bd%bd%e5%85%a5javascript%e6%96%87%e4%bb%b6','','','2010-11-22 18:21:23','2010-11-23 02:21:23','',0,'http://www.wordpress.cn/?p=245',0,'post','',0),(237,1,'2010-11-08 23:22:02','2010-11-09 07:22:02','以下小结使用tomcat5.0.28测试通过，tomcat安装路径为C:\\Tomcat\n\n在默认安装后，tomcat的主目录是webapps/root目录，如果我们想改变tomcat的主目录的话可以这样做：\n\n1.打开C:\\Tomcat\\conf\\server.xml，在<host></host>之间加入代码：\n\n<Context path=\"\" docBase=\"E:\\tomcatApp\\JeasyCMS\" debug=\"0\"  reloadable=\"true\" crossContext=\"true\"/>\n\n这样重新启动tomcat，我们的主目录就被设置为jeasyCMS这个项目了。 \n2.另外tomcat会生成一个C:\\Tomcat\\conf\\Catalina\\localhost目录，里面自动生成了一个ROOT.xml(root大写)文件，我们打开文件可以看到这些代码：\n\n<?xml version=\'\'1.0\'\' encoding=\'\'utf-8\'\'?>\n<Context crossContext=\"true\" docBase=\"E:/tomcatApp/JeasyCMS\" path=\"\" reloadable=\"true\">\n</Context> \n3.注意：我们创建虚拟目录的时候也是这样来进行设置的，只是context这个标签的path属性不为空。 \n4.我们设置虚拟目录和主目录，都会在C:\\Tomcat\\conf\\Catalina\\localhost目录下生成相应的xml文件，因此，当我们在server.xml里面删除了主目录和虚拟目录的设置后，如果系统还没有生效，这时候我们就要察看一下这个目录下的xml文件是不是已经被删除了，如果没有被删除，他还是对tomcat起作用的，我们必须手动删除。 \n5.基于第4点，通过测试我发现，每当我们访问一个地址的时候，tomcat首先是到C:\\Tomcat\\conf\\Catalina\\localhost目录下去察看有没有存在主目录或虚拟目录的xml文件，如果有xml文件，就按xml里的路径进行访问，如果没有xml文件，就到server.xml文件里去察看是否配置context标签，如果配置了context标签，则在C:\\Tomcat\\conf\\Catalina\\localhost目录下生成一个对应的xml文件，以便于下次直接验证而不再访问server.xml，与此同时打开context里指定的路径，如果server.xml里没有配置context标签，则返回访问错误页面。 \n6.基于第5点，我们其实可以直接在C:\\Tomcat\\conf\\Catalina\\localhost下建立一个xml文件，用于主目录或者虚拟目录，而不需要去修改server.xml就可以达到配置主目录和虚拟目录的目的，具体xml的格式可以如下：\n\n<?xml version=\'\'1.0\'\' encoding=\'\'utf-8\'\'?>\n<Context crossContext=\"true\" docBase=\"E:/tomcatApp/JeasyCMS\" path=\"\" reloadable=\"true\">\n</Context>\n\n具体是主目录的配置还是虚拟目录的配置取决于path的值。另外主目录文件名必须是ROOT.XML(root大写)，而虚拟目录的文件名称和虚拟目录的名称一致并且为小写，譬如： \n<?xml version=\'\'1.0\'\' encoding=\'\'utf-8\'\'?>\n<Context crossContext=\"true\" docBase=\"E:/tomcatApp/JeasyCMS\" path=\"\\cms\" reloadable=\"true\">\n</Context>\n\n以上则定义了一个名为：cms的虚拟目录，同时要将以上文本保存为cms.xml文件。\n\n7.以上住目录配置好后就可以按以下地址访问了：\n\nhttp://localhost:8080/(访问主目录)\n\nhttp://localhost:8080/cms (访问名为cms的虚拟目录)  \n\n \n\n文章出处：飞诺网(www.firnow.com):http://dev.firnow.com/course/3_program/java/javashl/2007123/89645.html','tomcat下域名的配置，ROOT.xml的作用','','inherit','open','open','','235-revision','','','2010-11-08 23:22:02','2010-11-09 07:22:02','',235,'http://www.wordpress.cn/?p=237',0,'revision','',0),(238,1,'2010-11-10 05:38:30','2010-11-10 13:38:30','在不只一个线程访问一个互斥的变量时，所有线程都必须使用同步，否则就可能会发生一些非常糟糕的事情。Java 语言中主要的同步手段就是 synchronized 关键字（也称为内在锁），它强制实行互斥，确保执行 synchronized 块的线程的动作，能够被后来执行受相同锁保护的 synchronized 块的其他线程看到。在使用得当的时候，内在锁可以让程序做到线程安全，但是在使用锁定保护短的代码路径，而且线程频繁地争用锁的时候，锁定可能成为相当繁重的操作。\r\n\r\n在 “流行的原子” 一文中，我们研究了原子变量，原子变量提供了原子性的读-写-修改操作，可以在不使用锁的情况下安全地更新共享变量。原子变量的内存语义与 volatile 变量类似，但是因为它们也可以被原子性地修改，所以可以把它们用作不使用锁的并发算法的基础。\r\n\r\n非阻塞的计数器\r\n\r\n清单 1 中的 Counter 是线程安全的，但是使用锁的需求带来的性能成本困扰了一些开发人员。但是锁是必需的，因为虽然增加看起来是单一操作，但实际是三个独立操作的简化：检索值，给值加 1，再写回值。（在 getValue 方法上也需要同步，以保证调用 getValue 的线程看到的是最新的值。虽然许多开发人员勉强地使自己相信忽略锁定需求是可以接受的，但忽略锁定需求并不是好策略。）\r\n\r\n在多个线程同时请求同一个锁时，会有一个线程获胜并得到锁，而其他线程被阻塞。JVM 实现阻塞的方式通常是挂起阻塞的线程，过一会儿再重新调度它。由此造成的上下文切换相对于锁保护的少数几条指令来说，会造成相当大的延迟。\r\n\r\n清单 1. 使用同步的线程安全的计数器\r\n\r\npublic final class Counter {\r\nprivate long value = 0;\r\npublic synchronized long getValue() {\r\nreturn value;\r\n}\r\npublic synchronized long increment() {\r\nreturn ++value;\r\n}\r\n}\r\n\r\n清单 2 中的 NonblockingCounter 显示了一种最简单的非阻塞算法：使用 AtomicInteger 的 compareAndSet() （CAS）方法的计数器。compareAndSet() 方法规定 “将这个变量更新为新值，但是如果从我上次看到这个变量之后其他线程修改了它的值，那么更新就失败”（请参阅 “流行的原子” 获得关于原子变量以及 “比较和设置” 的更多解释。）\r\n\r\n清单 2. 使用 CAS 的非阻塞算法\r\n\r\npublic class NonblockingCounter {\r\nprivate AtomicInteger value;\r\npublic int getValue() {\r\nreturn value.get();\r\n}\r\npublic int increment() {\r\nint v;\r\ndo {\r\nv = value.get();\r\nwhile (!value.compareAndSet(v, v + 1));\r\nreturn v + 1;\r\n}\r\n}\r\n\r\n原子变量类之所以被称为原子的，是因为它们提供了对数字和对象引用的细粒度的原子更新，但是在作为非阻塞算法的基本构造块的意义上，它们也是原子的。非阻塞算法作为科研的主题，已经有 20 多年了，但是直到 Java 5.0 出现，在 Java 语言中才成为可能。\r\n\r\n现代的处理器提供了特殊的指令，可以自动更新共享数据，而且能够检测到其他线程的干扰，而 compareAndSet() 就用这些代替了锁定。（如果要做的只是递增计数器，那么 AtomicInteger 提供了进行递增的方法，但是这些方法基于 compareAndSet()，例如 NonblockingCounter.increment()）。\r\n\r\n非阻塞版本相对于基于锁的版本有几个性能优势。首先，它用硬件的原生形态代替 JVM 的锁定代码路径，从而在更细的粒度层次上（独立的内存位置）进行同步，失败的线程也可以立即重试，而不会被挂起后重新调度。更细的粒度降低了争用的机会，不用重新调度就能重试的能力也降低了争用的成本。即使有少量失败的 CAS 操作，这种方法仍然会比由于锁争用造成的重新调度快得多。\r\n\r\nNonblockingCounter 这个示例可能简单了些，但是它演示了所有非阻塞算法的一个基本特征 —— 有些算法步骤的执行是要冒险的，因为知道如果 CAS 不成功可能不得不重做。非阻塞算法通常叫作乐观算法，因为它们继续操作的假设是不会有干扰。如果发现干扰，就会回退并重试。在计数器的示例中，冒险的步骤是递增 —— 它检索旧值并在旧值上加一，希望在计算更新期间值不会变化。如果它的希望落空，就会再次检索值，并重做递增计算。\r\n\r\n回页首\r\n\r\n非阻塞堆栈\r\n\r\n非阻塞算法稍微复杂一些的示例是清单 3 中的 ConcurrentStack。ConcurrentStack 中的 push() 和 pop() 操作在结构上与 NonblockingCounter 上相似，只是做的工作有些冒险，希望在 “提交” 工作的时候，底层假设没有失效。push() 方法观察当前最顶的节点，构建一个新节点放在堆栈上，然后，如果最顶端的节点在初始观察之后没有变化，那么就安装新节点。如果 CAS 失败，意味着另一个线程已经修改了堆栈，那么过程就会重新开始。\r\n\r\n清单 3. 使用 Treiber 算法的非阻塞堆栈\r\n\r\npublic class ConcurrentStack {\r\nAtomicReference&gt; head = new AtomicReference&gt;();\r\npublic void push(E item) {\r\nNode newHead = new Node(item);\r\nNode oldHead;\r\ndo {\r\noldHead = head.get();\r\nnewHead.next = oldHead;\r\n} while (!head.compareAndSet(oldHead, newHead));\r\n}\r\npublic E pop() {\r\nNode oldHead;\r\nNode newHead;\r\ndo {\r\noldHead = head.get();\r\nif (oldHead == null)\r\nreturn null;\r\nnewHead = oldHead.next;\r\n} while (!head.compareAndSet(oldHead,newHead));\r\nreturn oldHead.item;\r\n}\r\nstatic class Node {\r\nfinal E item;\r\nNode next;\r\npublic Node(E item) { this.item = item; }\r\n}\r\n}\r\n\r\n性能考虑\r\n\r\n在轻度到中度的争用情况下，非阻塞算法的性能会超越阻塞算法，因为 CAS 的多数时间都在第一次尝试时就成功，而发生争用时的开销也不涉及线程挂起和上下文切换，只多了几个循环迭代。没有争用的 CAS 要比没有争用的锁便宜得多（这句话肯定是真的，因为没有争用的锁涉及 CAS 加上额外的处理），而争用的 CAS 比争用的锁获取涉及更短的延迟。\r\n\r\n在高度争用的情况下（即有多个线程不断争用一个内存位置的时候），基于锁的算法开始提供比非阻塞算法更好的吞吐率，因为当线程阻塞时，它就会停止争用，耐心地等候轮到自己，从而避免了进一步争用。但是，这么高的争用程度并不常见，因为多数时候，线程会把线程本地的计算与争用共享数据的操作分开，从而给其他线程使用共享数据的机会。（这么高的争用程度也表明需要重新检查算法，朝着更少共享数据的方向努力。）“流行的原子” 中的图在这方面就有点儿让人困惑，因为被测量的程序中发生的争用极其密集，看起来即使对数量很少的线程，锁定也是更好的解决方案。\r\n\r\n回页首\r\n\r\n非阻塞的链表\r\n\r\n目前为止的示例（计数器和堆栈）都是非常简单的非阻塞算法，一旦掌握了在循环中使用 CAS，就可以容易地模仿它们。对于更复杂的数据结构，非阻塞算法要比这些简单示例复杂得多，因为修改链表、树或哈希表可能涉及对多个指针的更新。CAS 支持对单一指针的原子性条件更新，但是不支持两个以上的指针。所以，要构建一个非阻塞的链表、树或哈希表，需要找到一种方式，可以用 CAS 更新多个指针，同时不会让数据结构处于不一致的状态。\r\n\r\n在链表的尾部插入元素，通常涉及对两个指针的更新：“尾” 指针总是指向列表中的最后一个元素，“下一个” 指针从过去的最后一个元素指向新插入的元素。因为需要更新两个指针，所以需要两个 CAS。在独立的 CAS 中更新两个指针带来了两个需要考虑的潜在问题：如果第一个 CAS 成功，而第二个 CAS 失败，会发生什么？如果其他线程在第一个和第二个 CAS 之间企图访问链表，会发生什么？\r\n\r\n对于非复杂数据结构，构建非阻塞算法的 “技巧” 是确保数据结构总处于一致的状态（甚至包括在线程开始修改数据结构和它完成修改之间），还要确保其他线程不仅能够判断出第一个线程已经完成了更新还是处在更新的中途，还能够判断出如果第一个线程走向 AWOL，完成更新还需要什么操作。如果线程发现了处在更新中途的数据结构，它就可以 “帮助” 正在执行更新的线程完成更新，然后再进行自己的操作。当第一个线程回来试图完成自己的更新时，会发现不再需要了，返回即可，因为 CAS 会检测到帮助线程的干预（在这种情况下，是建设性的干预）。\r\n\r\n这种 “帮助邻居” 的要求，对于让数据结构免受单个线程失败的影响，是必需的。如果线程发现数据结构正处在被其他线程更新的中途，然后就等候其他线程完成更新，那么如果其他线程在操作中途失败，这个线程就可能永远等候下去。即使不出现故障，这种方式也会提供糟糕的性能，因为新到达的线程必须放弃处理器，导致上下文切换，或者等到自己的时间片过期（而这更糟）。\r\n\r\n清单 4 的 LinkedQueue 显示了 Michael-Scott 非阻塞队列算法的插入操作，它是由 ConcurrentLinkedQueue 实现的：\r\n\r\n清单 4. Michael-Scott 非阻塞队列算法中的插入\r\n\r\npublic class LinkedQueue  {\r\nprivate static class Node  {\r\nfinal E item;\r\nfinal AtomicReference&gt; next;\r\nNode(E item, Node next) {\r\nthis.item = item;\r\nthis.next = new AtomicReference&gt;(next);\r\n}\r\n}\r\nprivate AtomicReference&gt; head\r\n= new AtomicReference&gt;(new Node(null, null));\r\nprivate AtomicReference&gt; tail = head;\r\npublic boolean put(E item) {\r\nNode newNode = new Node(item, null);\r\nwhile (true) {\r\nNode curTail = tail.get();\r\nNode residue = curTail.next.get();\r\nif (curTail == tail.get()) {\r\nif (residue == null) /* A */ {\r\nif (curTail.next.compareAndSet(null, newNode)) /* C */ {\r\ntail.compareAndSet(curTail, newNode) /* D */ ;\r\nreturn true;\r\n}\r\n} else {\r\ntail.compareAndSet(curTail, residue) /* B */;\r\n}\r\n}\r\n}\r\n}\r\n}\r\n\r\n像许多队列算法一样，空队列只包含一个假节点。头指针总是指向假节点；尾指针总指向最后一个节点或倒数第二个节点。图 1 演示了正常情况下有两个元素的队列：\r\n\r\n图 1. 有两个元素，处在静止状态的队列\r\n\r\n如 清单 4 所示，插入一个元素涉及两个指针更新，这两个更新都是通过 CAS 进行的：从队列当前的最后节点（C）链接到新节点，并把尾指针移动到新的最后一个节点（D）。如果第一步失败，那么队列的状态不变，插入线程会继续重试，直到成功。一旦操作成功，插入被当成生效，其他线程就可以看到修改。还需要把尾指针移动到新节点的位置上，但是这项工作可以看成是 “清理工作”，因为任何处在这种情况下的线程都可以判断出是否需要这种清理，也知道如何进行清理。\r\n\r\n队列总是处于两种状态之一：正常状态（或称静止状态，图 1 和 图 3）或中间状态（图 2）。在插入操作之前和第二个 CAS（D）成功之后，队列处在静止状态；在第一个 CAS（C）成功之后，队列处在中间状态。在静止状态时，尾指针指向的链接节点的 next 字段总为 null，而在中间状态时，这个字段为非 null。任何线程通过比较 tail.next 是否为 null，就可以判断出队列的状态，这是让线程可以帮助其他线程 “完成” 操作的关键。\r\n\r\n图 2. 处在插入中间状态的队列，在新元素插入之后，尾指针更新之前\r\n\r\n插入操作在插入新元素（A）之前，先检查队列是否处在中间状态，如 清单 4 所示。如果是在中间状态，那么肯定有其他线程已经处在元素插入的中途，在步骤（C）和（D）之间。不必等候其他线程完成，当前线程就可以 “帮助” 它完成操作，把尾指针向前移动（B）。如果有必要，它还会继续检查尾指针并向前移动指针，直到队列处于静止状态，这时它就可以开始自己的插入了。\r\n\r\n第一个 CAS（C）可能因为两个线程竞争访问队列当前的最后一个元素而失败；在这种情况下，没有发生修改，失去 CAS 的线程会重新装入尾指针并再次尝试。如果第二个 CAS（D）失败，插入线程不需要重试 —— 因为其他线程已经在步骤（B）中替它完成了这个操作！\r\n\r\n图 3. 在尾指针更新后，队列重新处在静止状态\r\n\r\n幕后的非阻塞算法\r\n\r\n如果深入 JVM 和操作系统，会发现非阻塞算法无处不在。垃圾收集器使用非阻塞算法加快并发和平行的垃圾搜集；调度器使用非阻塞算法有效地调度线程和进程，实现内在锁。在 Mustang（Java 6.0）中，基于锁的 SynchronousQueue 算法被新的非阻塞版本代替。很少有开发人员会直接使用 SynchronousQueue，但是通过 Executors.newCachedThreadPool() 工厂构建的线程池用它作为工作队列。比较缓存线程池性能的对比测试显示，新的非阻塞同步队列实现提供了几乎是当前实现 3 倍的速度。在 Mustang 的后续版本（代码名称为 Dolphin）中，已经规划了进一步的改进。\r\n\r\n非阻塞算法要比基于锁的算法复杂得多。开发非阻塞算法是相当专业的训练，而且要证明算法的正确也极为困难。但是在 Java 版本之间并发性能上的众多改进来自对非阻塞算法的采用，而且随着并发性能变得越来越重要，可以预见在 Java 平台的未来发行版中，会使用更多的非阻塞算法。','Java 理论与实践: 非阻塞算法简介','','publish','open','open','','java-%e7%90%86%e8%ae%ba%e4%b8%8e%e5%ae%9e%e8%b7%b5-%e9%9d%9e%e9%98%bb%e5%a1%9e%e7%ae%97%e6%b3%95%e7%ae%80%e4%bb%8b','','','2010-11-10 05:38:30','2010-11-10 13:38:30','',0,'http://www.wordpress.cn/?p=238',0,'post','',0),(239,1,'2010-11-10 05:38:17','2010-11-10 13:38:17','在不只一个线程访问一个互斥的变量时，所有线程都必须使用同步，否则就可能会发生一些非常糟糕的事情。Java 语言中主要的同步手段就是 synchronized 关键字（也称为内在锁），它强制实行互斥，确保执行 synchronized 块的线程的动作，能够被后来执行受相同锁保护的 synchronized 块的其他线程看到。在使用得当的时候，内在锁可以让程序做到线程安全，但是在使用锁定保护短的代码路径，而且线程频繁地争用锁的时候，锁定可能成为相当繁重的操作。\n\n在 “流行的原子” 一文中，我们研究了原子变量，原子变量提供了原子性的读-写-修改操作，可以在不使用锁的情况下安全地更新共享变量。原子变量的内存语义与 volatile 变量类似，但是因为它们也可以被原子性地修改，所以可以把它们用作不使用锁的并发算法的基础。\n\n非阻塞的计数器\n\n清单 1 中的 Counter 是线程安全的，但是使用锁的需求带来的性能成本困扰了一些开发人员。但是锁是必需的，因为虽然增加看起来是单一操作，但实际是三个独立操作的简化：检索值，给值加 1，再写回值。（在 getValue 方法上也需要同步，以保证调用 getValue 的线程看到的是最新的值。虽然许多开发人员勉强地使自己相信忽略锁定需求是可以接受的，但忽略锁定需求并不是好策略。）\n\n在多个线程同时请求同一个锁时，会有一个线程获胜并得到锁，而其他线程被阻塞。JVM 实现阻塞的方式通常是挂起阻塞的线程，过一会儿再重新调度它。由此造成的上下文切换相对于锁保护的少数几条指令来说，会造成相当大的延迟。\n\n清单 1. 使用同步的线程安全的计数器\n\npublic final class Counter {\nprivate long value = 0;\npublic synchronized long getValue() {\nreturn value;\n}\npublic synchronized long increment() {\nreturn ++value;\n}\n}\n\n清单 2 中的 NonblockingCounter 显示了一种最简单的非阻塞算法：使用 AtomicInteger 的 compareAndSet() （CAS）方法的计数器。compareAndSet() 方法规定 “将这个变量更新为新值，但是如果从我上次看到这个变量之后其他线程修改了它的值，那么更新就失败”（请参阅 “流行的原子” 获得关于原子变量以及 “比较和设置” 的更多解释。）\n\n清单 2. 使用 CAS 的非阻塞算法\n\npublic class NonblockingCounter {\nprivate AtomicInteger value;\npublic int getValue() {\nreturn value.get();\n}\npublic int increment() {\nint v;\ndo {\nv = value.get();\nwhile (!value.compareAndSet(v, v + 1));\nreturn v + 1;\n}\n}\n\n原子变量类之所以被称为原子的，是因为它们提供了对数字和对象引用的细粒度的原子更新，但是在作为非阻塞算法的基本构造块的意义上，它们也是原子的。非阻塞算法作为科研的主题，已经有 20 多年了，但是直到 Java 5.0 出现，在 Java 语言中才成为可能。\n\n现代的处理器提供了特殊的指令，可以自动更新共享数据，而且能够检测到其他线程的干扰，而 compareAndSet() 就用这些代替了锁定。（如果要做的只是递增计数器，那么 AtomicInteger 提供了进行递增的方法，但是这些方法基于 compareAndSet()，例如 NonblockingCounter.increment()）。\n\n非阻塞版本相对于基于锁的版本有几个性能优势。首先，它用硬件的原生形态代替 JVM 的锁定代码路径，从而在更细的粒度层次上（独立的内存位置）进行同步，失败的线程也可以立即重试，而不会被挂起后重新调度。更细的粒度降低了争用的机会，不用重新调度就能重试的能力也降低了争用的成本。即使有少量失败的 CAS 操作，这种方法仍然会比由于锁争用造成的重新调度快得多。\n\nNonblockingCounter 这个示例可能简单了些，但是它演示了所有非阻塞算法的一个基本特征 —— 有些算法步骤的执行是要冒险的，因为知道如果 CAS 不成功可能不得不重做。非阻塞算法通常叫作乐观算法，因为它们继续操作的假设是不会有干扰。如果发现干扰，就会回退并重试。在计数器的示例中，冒险的步骤是递增 —— 它检索旧值并在旧值上加一，希望在计算更新期间值不会变化。如果它的希望落空，就会再次检索值，并重做递增计算。\n\n回页首\n\n非阻塞堆栈\n\n非阻塞算法稍微复杂一些的示例是清单 3 中的 ConcurrentStack。ConcurrentStack 中的 push() 和 pop() 操作在结构上与 NonblockingCounter 上相似，只是做的工作有些冒险，希望在 “提交” 工作的时候，底层假设没有失效。push() 方法观察当前最顶的节点，构建一个新节点放在堆栈上，然后，如果最顶端的节点在初始观察之后没有变化，那么就安装新节点。如果 CAS 失败，意味着另一个线程已经修改了堆栈，那么过程就会重新开始。\n\n清单 3. 使用 Treiber 算法的非阻塞堆栈\n\npublic class ConcurrentStack {\nAtomicReference&gt; head = new AtomicReference&gt;();\npublic void push(E item) {\nNode newHead = new Node(item);\nNode oldHead;\ndo {\noldHead = head.get();\nnewHead.next = oldHead;\n} while (!head.compareAndSet(oldHead, newHead));\n}\npublic E pop() {\nNode oldHead;\nNode newHead;\ndo {\noldHead = head.get();\nif (oldHead == null)\nreturn null;\nnewHead = oldHead.next;\n} while (!head.compareAndSet(oldHead,newHead));\nreturn oldHead.item;\n}\nstatic class Node {\nfinal E item;\nNode next;\npublic Node(E item) { this.item = item; }\n}\n}\n\n性能考虑\n\n在轻度到中度的争用情况下，非阻塞算法的性能会超越阻塞算法，因为 CAS 的多数时间都在第一次尝试时就成功，而发生争用时的开销也不涉及线程挂起和上下文切换，只多了几个循环迭代。没有争用的 CAS 要比没有争用的锁便宜得多（这句话肯定是真的，因为没有争用的锁涉及 CAS 加上额外的处理），而争用的 CAS 比争用的锁获取涉及更短的延迟。\n\n在高度争用的情况下（即有多个线程不断争用一个内存位置的时候），基于锁的算法开始提供比非阻塞算法更好的吞吐率，因为当线程阻塞时，它就会停止争用，耐心地等候轮到自己，从而避免了进一步争用。但是，这么高的争用程度并不常见，因为多数时候，线程会把线程本地的计算与争用共享数据的操作分开，从而给其他线程使用共享数据的机会。（这么高的争用程度也表明需要重新检查算法，朝着更少共享数据的方向努力。）“流行的原子” 中的图在这方面就有点儿让人困惑，因为被测量的程序中发生的争用极其密集，看起来即使对数量很少的线程，锁定也是更好的解决方案。\n\n回页首\n\n非阻塞的链表\n\n目前为止的示例（计数器和堆栈）都是非常简单的非阻塞算法，一旦掌握了在循环中使用 CAS，就可以容易地模仿它们。对于更复杂的数据结构，非阻塞算法要比这些简单示例复杂得多，因为修改链表、树或哈希表可能涉及对多个指针的更新。CAS 支持对单一指针的原子性条件更新，但是不支持两个以上的指针。所以，要构建一个非阻塞的链表、树或哈希表，需要找到一种方式，可以用 CAS 更新多个指针，同时不会让数据结构处于不一致的状态。\n\n在链表的尾部插入元素，通常涉及对两个指针的更新：“尾” 指针总是指向列表中的最后一个元素，“下一个” 指针从过去的最后一个元素指向新插入的元素。因为需要更新两个指针，所以需要两个 CAS。在独立的 CAS 中更新两个指针带来了两个需要考虑的潜在问题：如果第一个 CAS 成功，而第二个 CAS 失败，会发生什么？如果其他线程在第一个和第二个 CAS 之间企图访问链表，会发生什么？\n\n对于非复杂数据结构，构建非阻塞算法的 “技巧” 是确保数据结构总处于一致的状态（甚至包括在线程开始修改数据结构和它完成修改之间），还要确保其他线程不仅能够判断出第一个线程已经完成了更新还是处在更新的中途，还能够判断出如果第一个线程走向 AWOL，完成更新还需要什么操作。如果线程发现了处在更新中途的数据结构，它就可以 “帮助” 正在执行更新的线程完成更新，然后再进行自己的操作。当第一个线程回来试图完成自己的更新时，会发现不再需要了，返回即可，因为 CAS 会检测到帮助线程的干预（在这种情况下，是建设性的干预）。\n\n这种 “帮助邻居” 的要求，对于让数据结构免受单个线程失败的影响，是必需的。如果线程发现数据结构正处在被其他线程更新的中途，然后就等候其他线程完成更新，那么如果其他线程在操作中途失败，这个线程就可能永远等候下去。即使不出现故障，这种方式也会提供糟糕的性能，因为新到达的线程必须放弃处理器，导致上下文切换，或者等到自己的时间片过期（而这更糟）。\n\n清单 4 的 LinkedQueue 显示了 Michael-Scott 非阻塞队列算法的插入操作，它是由 ConcurrentLinkedQueue 实现的：\n\n清单 4. Michael-Scott 非阻塞队列算法中的插入\n\npublic class LinkedQueue  {\nprivate static class Node  {\nfinal E item;\nfinal AtomicReference&gt; next;\nNode(E item, Node next) {\nthis.item = item;\nthis.next = new AtomicReference&gt;(next);\n}\n}\nprivate AtomicReference&gt; head\n= new AtomicReference&gt;(new Node(null, null));\nprivate AtomicReference&gt; tail = head;\npublic boolean put(E item) {\nNode newNode = new Node(item, null);\nwhile (true) {\nNode curTail = tail.get();\nNode residue = curTail.next.get();\nif (curTail == tail.get()) {\nif (residue == null) /* A */ {\nif (curTail.next.compareAndSet(null, newNode)) /* C */ {\ntail.compareAndSet(curTail, newNode) /* D */ ;\nreturn true;\n}\n} else {\ntail.compareAndSet(curTail, residue) /* B */;\n}\n}\n}\n}\n}\n\n像许多队列算法一样，空队列只包含一个假节点。头指针总是指向假节点；尾指针总指向最后一个节点或倒数第二个节点。图 1 演示了正常情况下有两个元素的队列：\n\n图 1. 有两个元素，处在静止状态的队列\n\n如 清单 4 所示，插入一个元素涉及两个指针更新，这两个更新都是通过 CAS 进行的：从队列当前的最后节点（C）链接到新节点，并把尾指针移动到新的最后一个节点（D）。如果第一步失败，那么队列的状态不变，插入线程会继续重试，直到成功。一旦操作成功，插入被当成生效，其他线程就可以看到修改。还需要把尾指针移动到新节点的位置上，但是这项工作可以看成是 “清理工作”，因为任何处在这种情况下的线程都可以判断出是否需要这种清理，也知道如何进行清理。\n\n队列总是处于两种状态之一：正常状态（或称静止状态，图 1 和 图 3）或中间状态（图 2）。在插入操作之前和第二个 CAS（D）成功之后，队列处在静止状态；在第一个 CAS（C）成功之后，队列处在中间状态。在静止状态时，尾指针指向的链接节点的 next 字段总为 null，而在中间状态时，这个字段为非 null。任何线程通过比较 tail.next 是否为 null，就可以判断出队列的状态，这是让线程可以帮助其他线程 “完成” 操作的关键。\n\n图 2. 处在插入中间状态的队列，在新元素插入之后，尾指针更新之前\n\n插入操作在插入新元素（A）之前，先检查队列是否处在中间状态，如 清单 4 所示。如果是在中间状态，那么肯定有其他线程已经处在元素插入的中途，在步骤（C）和（D）之间。不必等候其他线程完成，当前线程就可以 “帮助” 它完成操作，把尾指针向前移动（B）。如果有必要，它还会继续检查尾指针并向前移动指针，直到队列处于静止状态，这时它就可以开始自己的插入了。\n\n第一个 CAS（C）可能因为两个线程竞争访问队列当前的最后一个元素而失败；在这种情况下，没有发生修改，失去 CAS 的线程会重新装入尾指针并再次尝试。如果第二个 CAS（D）失败，插入线程不需要重试 —— 因为其他线程已经在步骤（B）中替它完成了这个操作！\n\n图 3. 在尾指针更新后，队列重新处在静止状态\n\n幕后的非阻塞算法\n\n如果深入 JVM 和操作系统，会发现非阻塞算法无处不在。垃圾收集器使用非阻塞算法加快并发和平行的垃圾搜集；调度器使用非阻塞算法有效地调度线程和进程，实现内在锁。在 Mustang（Java 6.0）中，基于锁的 SynchronousQueue 算法被新的非阻塞版本代替。很少有开发人员会直接使用 SynchronousQueue，但是通过 Executors.newCachedThreadPool() 工厂构建的线程池用它作为工作队列。比较缓存线程池性能的对比测试显示，新的非阻塞同步队列实现提供了几乎是当前实现 3 倍的速度。在 Mustang 的后续版本（代码名称为 Dolphin）中，已经规划了进一步的改进。\n\n非阻塞算法要比基于锁的算法复杂得多。开发非阻塞算法是相当专业的训练，而且要证明算法的正确也极为困难。但是在 Java 版本之间并发性能上的众多改进来自对非阻塞算法的采用，而且随着并发性能变得越来越重要，可以预见在 Java 平台的未来发行版中，会使用更多的非阻塞算法。','Java 理论与实践: 非阻塞算法简介','','inherit','open','open','','238-revision','','','2010-11-10 05:38:17','2010-11-10 13:38:17','',238,'http://www.wordpress.cn/?p=239',0,'revision','',0),(240,1,'2010-11-10 05:38:34','2010-11-10 13:38:34','在不只一个线程访问一个互斥的变量时，所有线程都必须使用同步，否则就可能会发生一些非常糟糕的事情。Java 语言中主要的同步手段就是 synchronized 关键字（也称为内在锁），它强制实行互斥，确保执行 synchronized 块的线程的动作，能够被后来执行受相同锁保护的 synchronized 块的其他线程看到。在使用得当的时候，内在锁可以让程序做到线程安全，但是在使用锁定保护短的代码路径，而且线程频繁地争用锁的时候，锁定可能成为相当繁重的操作。\r\n\r\n在 “流行的原子” 一文中，我们研究了原子变量，原子变量提供了原子性的读-写-修改操作，可以在不使用锁的情况下安全地更新共享变量。原子变量的内存语义与 volatile 变量类似，但是因为它们也可以被原子性地修改，所以可以把它们用作不使用锁的并发算法的基础。\r\n\r\n非阻塞的计数器\r\n\r\n清单 1 中的 Counter 是线程安全的，但是使用锁的需求带来的性能成本困扰了一些开发人员。但是锁是必需的，因为虽然增加看起来是单一操作，但实际是三个独立操作的简化：检索值，给值加 1，再写回值。（在 getValue 方法上也需要同步，以保证调用 getValue 的线程看到的是最新的值。虽然许多开发人员勉强地使自己相信忽略锁定需求是可以接受的，但忽略锁定需求并不是好策略。）\r\n\r\n在多个线程同时请求同一个锁时，会有一个线程获胜并得到锁，而其他线程被阻塞。JVM 实现阻塞的方式通常是挂起阻塞的线程，过一会儿再重新调度它。由此造成的上下文切换相对于锁保护的少数几条指令来说，会造成相当大的延迟。\r\n\r\n清单 1. 使用同步的线程安全的计数器\r\n\r\npublic final class Counter {\r\nprivate long value = 0;\r\npublic synchronized long getValue() {\r\nreturn value;\r\n}\r\npublic synchronized long increment() {\r\nreturn ++value;\r\n}\r\n}\r\n\r\n清单 2 中的 NonblockingCounter 显示了一种最简单的非阻塞算法：使用 AtomicInteger 的 compareAndSet() （CAS）方法的计数器。compareAndSet() 方法规定 “将这个变量更新为新值，但是如果从我上次看到这个变量之后其他线程修改了它的值，那么更新就失败”（请参阅 “流行的原子” 获得关于原子变量以及 “比较和设置” 的更多解释。）\r\n\r\n清单 2. 使用 CAS 的非阻塞算法\r\n\r\npublic class NonblockingCounter {\r\nprivate AtomicInteger value;\r\npublic int getValue() {\r\nreturn value.get();\r\n}\r\npublic int increment() {\r\nint v;\r\ndo {\r\nv = value.get();\r\nwhile (!value.compareAndSet(v, v + 1));\r\nreturn v + 1;\r\n}\r\n}\r\n\r\n原子变量类之所以被称为原子的，是因为它们提供了对数字和对象引用的细粒度的原子更新，但是在作为非阻塞算法的基本构造块的意义上，它们也是原子的。非阻塞算法作为科研的主题，已经有 20 多年了，但是直到 Java 5.0 出现，在 Java 语言中才成为可能。\r\n\r\n现代的处理器提供了特殊的指令，可以自动更新共享数据，而且能够检测到其他线程的干扰，而 compareAndSet() 就用这些代替了锁定。（如果要做的只是递增计数器，那么 AtomicInteger 提供了进行递增的方法，但是这些方法基于 compareAndSet()，例如 NonblockingCounter.increment()）。\r\n\r\n非阻塞版本相对于基于锁的版本有几个性能优势。首先，它用硬件的原生形态代替 JVM 的锁定代码路径，从而在更细的粒度层次上（独立的内存位置）进行同步，失败的线程也可以立即重试，而不会被挂起后重新调度。更细的粒度降低了争用的机会，不用重新调度就能重试的能力也降低了争用的成本。即使有少量失败的 CAS 操作，这种方法仍然会比由于锁争用造成的重新调度快得多。\r\n\r\nNonblockingCounter 这个示例可能简单了些，但是它演示了所有非阻塞算法的一个基本特征 —— 有些算法步骤的执行是要冒险的，因为知道如果 CAS 不成功可能不得不重做。非阻塞算法通常叫作乐观算法，因为它们继续操作的假设是不会有干扰。如果发现干扰，就会回退并重试。在计数器的示例中，冒险的步骤是递增 —— 它检索旧值并在旧值上加一，希望在计算更新期间值不会变化。如果它的希望落空，就会再次检索值，并重做递增计算。\r\n\r\n回页首\r\n\r\n非阻塞堆栈\r\n\r\n非阻塞算法稍微复杂一些的示例是清单 3 中的 ConcurrentStack。ConcurrentStack 中的 push() 和 pop() 操作在结构上与 NonblockingCounter 上相似，只是做的工作有些冒险，希望在 “提交” 工作的时候，底层假设没有失效。push() 方法观察当前最顶的节点，构建一个新节点放在堆栈上，然后，如果最顶端的节点在初始观察之后没有变化，那么就安装新节点。如果 CAS 失败，意味着另一个线程已经修改了堆栈，那么过程就会重新开始。\r\n\r\n清单 3. 使用 Treiber 算法的非阻塞堆栈\r\n\r\npublic class ConcurrentStack {\r\nAtomicReference&gt; head = new AtomicReference&gt;();\r\npublic void push(E item) {\r\nNode newHead = new Node(item);\r\nNode oldHead;\r\ndo {\r\noldHead = head.get();\r\nnewHead.next = oldHead;\r\n} while (!head.compareAndSet(oldHead, newHead));\r\n}\r\npublic E pop() {\r\nNode oldHead;\r\nNode newHead;\r\ndo {\r\noldHead = head.get();\r\nif (oldHead == null)\r\nreturn null;\r\nnewHead = oldHead.next;\r\n} while (!head.compareAndSet(oldHead,newHead));\r\nreturn oldHead.item;\r\n}\r\nstatic class Node {\r\nfinal E item;\r\nNode next;\r\npublic Node(E item) { this.item = item; }\r\n}\r\n}\r\n\r\n性能考虑\r\n\r\n在轻度到中度的争用情况下，非阻塞算法的性能会超越阻塞算法，因为 CAS 的多数时间都在第一次尝试时就成功，而发生争用时的开销也不涉及线程挂起和上下文切换，只多了几个循环迭代。没有争用的 CAS 要比没有争用的锁便宜得多（这句话肯定是真的，因为没有争用的锁涉及 CAS 加上额外的处理），而争用的 CAS 比争用的锁获取涉及更短的延迟。\r\n\r\n在高度争用的情况下（即有多个线程不断争用一个内存位置的时候），基于锁的算法开始提供比非阻塞算法更好的吞吐率，因为当线程阻塞时，它就会停止争用，耐心地等候轮到自己，从而避免了进一步争用。但是，这么高的争用程度并不常见，因为多数时候，线程会把线程本地的计算与争用共享数据的操作分开，从而给其他线程使用共享数据的机会。（这么高的争用程度也表明需要重新检查算法，朝着更少共享数据的方向努力。）“流行的原子” 中的图在这方面就有点儿让人困惑，因为被测量的程序中发生的争用极其密集，看起来即使对数量很少的线程，锁定也是更好的解决方案。\r\n\r\n回页首\r\n\r\n非阻塞的链表\r\n\r\n目前为止的示例（计数器和堆栈）都是非常简单的非阻塞算法，一旦掌握了在循环中使用 CAS，就可以容易地模仿它们。对于更复杂的数据结构，非阻塞算法要比这些简单示例复杂得多，因为修改链表、树或哈希表可能涉及对多个指针的更新。CAS 支持对单一指针的原子性条件更新，但是不支持两个以上的指针。所以，要构建一个非阻塞的链表、树或哈希表，需要找到一种方式，可以用 CAS 更新多个指针，同时不会让数据结构处于不一致的状态。\r\n\r\n在链表的尾部插入元素，通常涉及对两个指针的更新：“尾” 指针总是指向列表中的最后一个元素，“下一个” 指针从过去的最后一个元素指向新插入的元素。因为需要更新两个指针，所以需要两个 CAS。在独立的 CAS 中更新两个指针带来了两个需要考虑的潜在问题：如果第一个 CAS 成功，而第二个 CAS 失败，会发生什么？如果其他线程在第一个和第二个 CAS 之间企图访问链表，会发生什么？\r\n\r\n对于非复杂数据结构，构建非阻塞算法的 “技巧” 是确保数据结构总处于一致的状态（甚至包括在线程开始修改数据结构和它完成修改之间），还要确保其他线程不仅能够判断出第一个线程已经完成了更新还是处在更新的中途，还能够判断出如果第一个线程走向 AWOL，完成更新还需要什么操作。如果线程发现了处在更新中途的数据结构，它就可以 “帮助” 正在执行更新的线程完成更新，然后再进行自己的操作。当第一个线程回来试图完成自己的更新时，会发现不再需要了，返回即可，因为 CAS 会检测到帮助线程的干预（在这种情况下，是建设性的干预）。\r\n\r\n这种 “帮助邻居” 的要求，对于让数据结构免受单个线程失败的影响，是必需的。如果线程发现数据结构正处在被其他线程更新的中途，然后就等候其他线程完成更新，那么如果其他线程在操作中途失败，这个线程就可能永远等候下去。即使不出现故障，这种方式也会提供糟糕的性能，因为新到达的线程必须放弃处理器，导致上下文切换，或者等到自己的时间片过期（而这更糟）。\r\n\r\n清单 4 的 LinkedQueue 显示了 Michael-Scott 非阻塞队列算法的插入操作，它是由 ConcurrentLinkedQueue 实现的：\r\n\r\n清单 4. Michael-Scott 非阻塞队列算法中的插入\r\n\r\npublic class LinkedQueue  {\r\nprivate static class Node  {\r\nfinal E item;\r\nfinal AtomicReference&gt; next;\r\nNode(E item, Node next) {\r\nthis.item = item;\r\nthis.next = new AtomicReference&gt;(next);\r\n}\r\n}\r\nprivate AtomicReference&gt; head\r\n= new AtomicReference&gt;(new Node(null, null));\r\nprivate AtomicReference&gt; tail = head;\r\npublic boolean put(E item) {\r\nNode newNode = new Node(item, null);\r\nwhile (true) {\r\nNode curTail = tail.get();\r\nNode residue = curTail.next.get();\r\nif (curTail == tail.get()) {\r\nif (residue == null) /* A */ {\r\nif (curTail.next.compareAndSet(null, newNode)) /* C */ {\r\ntail.compareAndSet(curTail, newNode) /* D */ ;\r\nreturn true;\r\n}\r\n} else {\r\ntail.compareAndSet(curTail, residue) /* B */;\r\n}\r\n}\r\n}\r\n}\r\n}\r\n\r\n像许多队列算法一样，空队列只包含一个假节点。头指针总是指向假节点；尾指针总指向最后一个节点或倒数第二个节点。图 1 演示了正常情况下有两个元素的队列：\r\n\r\n图 1. 有两个元素，处在静止状态的队列\r\n\r\n如 清单 4 所示，插入一个元素涉及两个指针更新，这两个更新都是通过 CAS 进行的：从队列当前的最后节点（C）链接到新节点，并把尾指针移动到新的最后一个节点（D）。如果第一步失败，那么队列的状态不变，插入线程会继续重试，直到成功。一旦操作成功，插入被当成生效，其他线程就可以看到修改。还需要把尾指针移动到新节点的位置上，但是这项工作可以看成是 “清理工作”，因为任何处在这种情况下的线程都可以判断出是否需要这种清理，也知道如何进行清理。\r\n\r\n队列总是处于两种状态之一：正常状态（或称静止状态，图 1 和 图 3）或中间状态（图 2）。在插入操作之前和第二个 CAS（D）成功之后，队列处在静止状态；在第一个 CAS（C）成功之后，队列处在中间状态。在静止状态时，尾指针指向的链接节点的 next 字段总为 null，而在中间状态时，这个字段为非 null。任何线程通过比较 tail.next 是否为 null，就可以判断出队列的状态，这是让线程可以帮助其他线程 “完成” 操作的关键。\r\n\r\n图 2. 处在插入中间状态的队列，在新元素插入之后，尾指针更新之前\r\n\r\n插入操作在插入新元素（A）之前，先检查队列是否处在中间状态，如 清单 4 所示。如果是在中间状态，那么肯定有其他线程已经处在元素插入的中途，在步骤（C）和（D）之间。不必等候其他线程完成，当前线程就可以 “帮助” 它完成操作，把尾指针向前移动（B）。如果有必要，它还会继续检查尾指针并向前移动指针，直到队列处于静止状态，这时它就可以开始自己的插入了。\r\n\r\n第一个 CAS（C）可能因为两个线程竞争访问队列当前的最后一个元素而失败；在这种情况下，没有发生修改，失去 CAS 的线程会重新装入尾指针并再次尝试。如果第二个 CAS（D）失败，插入线程不需要重试 —— 因为其他线程已经在步骤（B）中替它完成了这个操作！\r\n\r\n图 3. 在尾指针更新后，队列重新处在静止状态\r\n\r\n幕后的非阻塞算法\r\n\r\n如果深入 JVM 和操作系统，会发现非阻塞算法无处不在。垃圾收集器使用非阻塞算法加快并发和平行的垃圾搜集；调度器使用非阻塞算法有效地调度线程和进程，实现内在锁。在 Mustang（Java 6.0）中，基于锁的 SynchronousQueue 算法被新的非阻塞版本代替。很少有开发人员会直接使用 SynchronousQueue，但是通过 Executors.newCachedThreadPool() 工厂构建的线程池用它作为工作队列。比较缓存线程池性能的对比测试显示，新的非阻塞同步队列实现提供了几乎是当前实现 3 倍的速度。在 Mustang 的后续版本（代码名称为 Dolphin）中，已经规划了进一步的改进。\r\n\r\n非阻塞算法要比基于锁的算法复杂得多。开发非阻塞算法是相当专业的训练，而且要证明算法的正确也极为困难。但是在 Java 版本之间并发性能上的众多改进来自对非阻塞算法的采用，而且随着并发性能变得越来越重要，可以预见在 Java 平台的未来发行版中，会使用更多的非阻塞算法。','Java 理论与实践: 非阻塞算法简介','','inherit','open','open','','238-autosave','','','2010-11-10 05:38:34','2010-11-10 13:38:34','',238,'http://www.wordpress.cn/?p=240',0,'revision','',0),(241,1,'2010-11-10 05:40:12','2010-11-10 13:40:12','十五年前，多处理器系统是高度专用系统，要花费数十万美元（大多数具有两个到四个处理器）。现在，多处理器系统很便宜，而且数量很多，几乎每个主要微处理器都内置了多处理支持，其中许多系统支持数十个或数百个处理器。\r\n\r\n要使用多处理器系统的功能，通常需要使用多线程构造应用程序。但是正如任何编写并发应用程序的人可以告诉你的那样，要获得好的硬件利用率，只是简单地在多个线程中分割工作是不够的，还必须确保线程确实大部分时间都在工作，而不是在等待更多的工作，或等待锁定共享数据结构。\r\n\r\n<a name=\"1.0\">问题：线程之间的协调</a>\r\n\r\n如果线程之间         <em>不</em>需要协调，那么几乎没有任务可以真正地并行。以线程池为例，其中执行的任务通常相互独立。如果线程池利用公共工作队 列，则从工作队列中删除元素或向工作队列添加元素的过程必须是线程安全的，并且这意味着要协调对头、尾或节点间链接指针所进行的访问。正是这种协调导致了 所有问题。\r\n\r\n<a name=\"1.1\">标准方法：锁定</a>\r\n\r\n在 Java  语言中，协调对共享字段的访问的传统方法是使用同步，确保完成对共享字段的所有访问，同时具有适当的锁定。通过同步，可以确定（假设类编写正确）具有保护 一组给定变量的锁定的所有线程都将拥有对这些变量的独占访问权，并且以后其他线程获得该锁定时，将可以看到对这些变量进行的更改。弊端是如果锁定竞争太厉 害（线程常常在其他线程具有锁定时要求获得该锁定），会损害吞吐量，因为竞争的同步非常昂贵。（Public Service  Announcement：对于现代 JVM 而言，无竞争的同步现在非常便宜。\r\n\r\n基于锁定的算法的另一个问题是：如果延迟具有锁定的线程（因为页面错误、计划延迟或其他意料之外的延迟），则         <em>没有</em>要求获得该锁定的线程可以继续运行。\r\n\r\n还可以使用可变变量来以比同步更低的成本存储共享变量，但它们有局限性。虽然可以保证其他变量可以立即看到对可变变量的写入，但无法呈现原子操作的读-修改-写顺序，这意味着（比如说）可变变量无法用来可靠地实现互斥（互斥锁定）或计数器。\r\n\r\n<a name=\"1.1\">使用锁定实现计数器和互斥</a>\r\n\r\n假如开发线程安全的计数器类，那么这将暴露          <code>get()</code>、         <code>increment()</code> 和          <code>decrement()</code> 操作。清单 1 显示了如何使用锁定（同步）实现该类的例子。注意所有方法，甚至需要同步          <code>get()</code>，使类成为线程安全的类，从而确保没有任何更新信息丢失，所有线程都看到计数器的最新值。\r\n<a name=\"listing1\"><strong>清单 1. 同步的计数器类</strong></a>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<pre>\r\npublic class SynchronizedCounter {\r\n    private int value;\r\n    public synchronized int getValue() { return value; }\r\n    public synchronized int increment() { return ++value; }\r\n    public synchronized int decrement() { return --value; }\r\n}\r\n</pre>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<code>increment()</code> 和          <code>decrement()</code> 操作是原子的读-修改-写操作，为了安全实现计数器，必须使用当前值，并为其添加一个值，或写出新值，所有这些均视为一项操作，其他线程不能打断它。否 则，如果两个线程试图同时执行增加，操作的不幸交叉将导致计数器只被实现了一次，而不是被实现两次。（注意，通过使值实例变量成为可变变量并不能可靠地完 成这项操作。）\r\n\r\n许多并发算法中都显示了原子的读-修改-写组合。清单 2 中的代码实现了简单的互斥，         <code>acquire()</code> 方法也是原子的读-修改-写操作。要获得互斥，必须确保没有其他人具有该互斥（         <code>curOwner = Thread.currentThread()</code>），然后记录您拥有该互斥的事实（         <code>curOwner = Thread.currentThread()</code>），所有这些使其他线程不可能在中间出现以及修改          <code>curOwner field</code>。\r\n<a name=\"listing2\"><strong>清单 2. 同步的互斥类</strong></a>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<pre>\r\npublic class SynchronizedMutex {\r\n    private Thread curOwner = null;\r\n    public synchronized void acquire() throws InterruptedException {\r\n        if (Thread.interrupted()) throw new InterruptedException();\r\n        while (curOwner != null)\r\n            wait();\r\n        curOwner = Thread.currentThread();\r\n    }\r\n    public synchronized void release() {\r\n        if (curOwner == Thread.currentThread()) {\r\n            curOwner = null;\r\n            notify();\r\n        } else\r\n            throw new IllegalStateException(\"not owner of mutex\");\r\n    }\r\n}\r\n</pre>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n清单 1 中的计数器类可以可靠地工作，在竞争很小或没有竞争时都可以很好地执行。然而，在竞争激烈时，这将大大损害性能，因为 JVM 用了更多的时间来调度线程，管理竞争和等待线程队列，而实际工作（如增加计数器）的时间却很少。您可以回想         <a href=\"http://www.ibm.com/developerworks/java/library/j-jtp10264/?S_TACT=105AGX52&amp;S_CMP=cn-a-j\">上月专栏</a>中的图，该图显示了一旦多个线程使用同步竞争一个内置监视器，吞吐量将如何大幅度下降。虽然该专栏说明了新的          <code>ReentrantLock</code> 类如何可以更可伸缩地替代同步，但是对于一些问题，还有更好的解决方法。\r\n\r\n<a name=\"1.2\">锁定问题</a>\r\n\r\n使用锁定，如果一个线程试图获取其他线程已经具有的锁定，那么该线程将被阻塞，直到该锁定可用。此方法具有一些明显的缺点，其中包括当线程被阻塞来等待锁定时，它无法进行其他任何操作。如果阻塞的线程是高优先级的任务，那么该方案可能造成非常不好的结果（称为         <em>优先级倒置</em>的危险）。\r\n\r\n使用锁定还有一些其他危险，如死锁（当以不一致的顺序获得多个锁定时会发生死锁）。甚至没有这种危险，锁定也仅是相对的粗粒度协调机制，同样非常适合管理 简单操作，如增加计数器或更新互斥拥有者。如果有更细粒度的机制来可靠管理对单独变量的并发更新，则会更好一些；在大多数现代处理器都有这种机制。\r\n<div><hr /></div>\r\n<a href=\"http://www.ibm.com/developerworks/cn/java/j-jtp11234/index.html#ibm-pcon\">回页首</a>\r\n\r\n<a name=\"2.0\">硬件同步原语</a>\r\n\r\n如前所述，大多数现代处理器都包含对多处理的支持。当然这种支持包括多处理器可以共享外部设备和主内存，同时它通常还包括对指令系统的增加来支持多处理的特殊要求。特别是，几乎每个现代处理器都有通过可以检测或阻止其他处理器的并发访问的方式来更新共享变量的指令。\r\n\r\n<a name=\"2.1\">比较并交换 (CAS)</a>\r\n\r\n支持并发的第一个处理器提供原子的测试并设置操作，通常在单位上运行这项操作。现在的处理器（包括 Intel 和 Sparc 处理器）使用的最通用的方法是实现名为         <em>比较并转换</em>或 CAS 的原语。（在 Intel 处理器中，比较并交换通过指令的 cmpxchg  系列实现。PowerPC 处理器有一对名为“加载并保留”和“条件存储”的指令，它们实现相同的目地；MIPS 与 PowerPC  处理器相似，除了第一个指令称为“加载链接”。）\r\n\r\nCAS 操作包含三个操作数 ——  内存位置（V）、预期原值（A）和新值(B)。如果内存位置的值与预期原值相匹配，那么处理器会自动将该位置值更新为新值。否则，处理器不做任何操作。无 论哪种情况，它都会在 CAS 指令之前返回该位置的值。（在 CAS 的一些特殊情况下将仅返回 CAS 是否成功，而不提取当前值。）CAS  有效地说明了“我认为位置 V 应该包含值 A；如果包含该值，则将 B 放到这个位置；否则，不要更改该位置，只告诉我这个位置现在的值即可。”\r\n\r\n通常将 CAS 用于同步的方式是从地址 V 读取值 A，执行多步计算来获得新值 B，然后使用 CAS 将 V 的值从 A 改为 B。如果 V 处的值尚未同时更改，则 CAS 操作成功。\r\n\r\n类似于 CAS 的指令允许算法执行读-修改-写操作，而无需害怕其他线程同时修改变量，因为如果其他线程修改变量，那么 CAS  会检测它（并失败），算法可以对该操作重新计算。清单 3 说明了 CAS 操作的行为（而不是性能特征），但是 CAS  的价值是它可以在硬件中实现，并且是极轻量级的（在大多数处理器中）：\r\n<a name=\"listing3\"><strong>清单 3. 说明比较并交换的行为（而不是性能）的代码</strong></a>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<pre>\r\npublic class SimulatedCAS {\r\n    private int value;\r\n\r\n    public synchronized int getValue() { return value; }\r\n\r\n    public synchronized int compareAndSwap(int expectedValue, int newValue) {\r\n        if (value == expectedValue)\r\n            value = newValue;\r\n        return value;\r\n    }\r\n}\r\n</pre>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<a name=\"2.2\">使用 CAS 实现计数器</a>\r\n\r\n基于 CAS 的并发算法称为         <em>无锁定</em>算法，因为线程不必再等待锁定（有时称为互斥或关键部分，这取决于线程平台的术语）。无论 CAS  操作成功还是失败，在任何一种情况中，它都在可预知的时间内完成。如果 CAS 失败，调用者可以重试 CAS 操作或采取其他适合的操作。清单 4  显示了重新编写的计数器类来使用 CAS 替代锁定：\r\n<a name=\"listing4\"><strong>清单 4. 使用比较并交换实现计数器</strong></a>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<pre>\r\npublic class CasCounter {\r\n    private SimulatedCAS value;\r\n    public int getValue() {\r\n        return value.getValue();\r\n    }\r\n    public int increment() {\r\n        int oldValue = value.getValue();\r\n        while (value.compareAndSwap(oldValue, oldValue + 1) != oldValue)\r\n            oldValue = value.getValue();\r\n        return oldValue + 1;\r\n    }\r\n}\r\n</pre>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<div><hr /></div>\r\n<a href=\"http://www.ibm.com/developerworks/cn/java/j-jtp11234/index.html#ibm-pcon\">回页首</a>\r\n\r\n<a name=\"3.0\">无锁定且无等待算法</a>\r\n\r\n如果每个线程在其他线程任意延迟（或甚至失败）时都将持续进行操作，就可以说该算法是         <em>无等待</em>的。与此形成对比的是，         <em>无锁定</em>算法要求仅         <em>某个</em>线程总是执行操作。（无等待的另一种定义是保证每个线程在其有限的步骤中正确计算自己的操作，而不管其他线程的操作、计时、交叉或速度。这一限制可以是系统中线程数的函数；例如，如果有 10 个线程，每个线程都执行一次          <code>CasCounter.increment()</code> 操作，最坏的情况下，每个线程将必须重试最多九次，才能完成增加。）\r\n\r\n再过去的 15 年里，人们已经对无等待且无锁定算法（也称为         <em>无阻塞算法</em>）进行了大量研究，许多人通用数据结构已经发现了无阻塞算法。无阻塞算法被广泛用于操作系统和 JVM  级别，进行诸如线程和进程调度等任务。虽然它们的实现比较复杂，但相对于基于锁定的备选算法，它们有许多优点：可以避免优先级倒置和死锁等危险，竞争比较 便宜，协调发生在更细的粒度级别，允许更高程度的并行机制等等。\r\n\r\n<a name=\"3.1\">原子变量类</a>\r\n\r\n在 JDK 5.0 之前，如果不使用本机代码，就不能用 Java 语言编写无等待、无锁定的算法。在          <code>java.util.concurrent.atomic</code> 包中添加原子变量类之后，这种情况才发生了改变。所有原子变量类都公开比较并设置原语（与比较并交换类似），这些原语都是使用平台上可用的最快本机结构（比较并交换、加载链接/条件存储，最坏的情况下是旋转锁）来实现的。         <code>java.util.concurrent.atomic</code> 包中提供了原子变量的 9 种风格（         <code>AtomicInteger</code>；         <code>AtomicLong</code>；         <code>AtomicReference</code>；         <code>AtomicBoolean</code>；原子整型；长型；引用；及原子标记引用和戳记引用类的数组形式，其原子地更新一对值）。\r\n\r\n原子变量类可以认为是          <code>volatile</code> 变量的泛化，它扩展了可变变量的概念，来支持原子条件的比较并设置更新。读取和写入原子变量与读取和写入对可变变量的访问具有相同的存取语义。\r\n\r\n虽然原子变量类表面看起来与清单 1 中的          <code>SynchronizedCounter</code> 例子一样，但相似仅是表面的。在表面之下，原子变量的操作会变为平台提供的用于并发访问的硬件原语，比如比较并交换。\r\n\r\n<a name=\"3.2\">更细粒度意味着更轻量级</a>\r\n\r\n调整具有竞争的并发应用程序的可伸缩性的通用技术是降低使用的锁定对象的粒度，希望更多的锁定请求从竞争变为不竞争。从锁定转换为原子变量可以获得相同的结果，通过切换为更细粒度的协调机制，竞争的操作就更少，从而提高了吞吐量。\r\n<div><a name=\"sidebar1\"></a>\r\n<h2>ABA 问题</h2>\r\n<div>因为在更改 V 之前，CAS 主要询问“V 的值是否仍为 A”，所以在第一次读取 V 以及对 V 执行 CAS 操作之前，如果将值从 A 改为  B，然后再改回 A，会使基于 CAS 的算法混乱。在这种情况下，CAS 操作会成功，但是在一些情况下，结果可能不是您所预期的。（注意，         <a href=\"http://www.ibm.com/developerworks/cn/java/j-jtp11234/index.html#listing1\">清单 1</a> 和         <a href=\"http://www.ibm.com/developerworks/cn/java/j-jtp11234/index.html#listing2\">清单 2</a> 中的计数器和互斥例子不存在这个问题，但不是所有算法都这样。）这类问题称为         <em>ABA 问题</em>，通常通过将标记或版本编号与要进行 CAS 操作的每个值相关联，并原子地更新值和标记，来处理这类问题。         <code>AtomicStampedReference</code> 类支持这种方法。</div>\r\n</div>\r\n<a name=\"3.3\">java.util.concurrent 中的原子变量</a>\r\n\r\n无论是直接的还是间接的，几乎          <code>java.util.concurrent</code> 包中的所有类都使用原子变量，而不使用同步。类似          <code>ConcurrentLinkedQueue</code> 的类也使用原子变量直接实现无等待算法，而类似          <code>ConcurrentHashMap</code> 的类使用          <code>ReentrantLock</code> 在需要时进行锁定。然后，         <code>ReentrantLock</code> 使用原子变量来维护等待锁定的线程队列。\r\n\r\n如果没有 JDK 5.0 中的 JVM 改进，将无法构造这些类，这些改进暴露了（向类库，而不是用户类）接口来访问硬件级的同步原语。然后，java.util.concurrent 中的原子变量类和其他类向用户类公开这些功能。\r\n<div><hr /></div>\r\n<a href=\"http://www.ibm.com/developerworks/cn/java/j-jtp11234/index.html#ibm-pcon\">回页首</a>\r\n\r\n<a name=\"4.0\">使用原子变量获得更高的吞吐量</a>\r\n\r\n<a href=\"http://www.ibm.com/developerworks/java/library/j-jtp10264/?S_TACT=105AGX52&amp;S_CMP=cn-a-j\">上月</a>，我介绍了          <code>ReentrantLock</code> 如何相对于同步提供可伸缩性优势，以及构造通过伪随机数生成器模拟旋转骰子的简单、高竞争示例基准。我向您显示了通过同步、         <code>ReentrantLock</code> 和公平          <code>ReentrantLock</code> 来进行协调的实现，并显示了结果。本月，我将向该基准添加其他实现，使用          <code>AtomicLong</code> 更新 PRNG 状态的实现。\r\n\r\n清单 5 显示了使用同步的 PRNG 实现和使用 CAS 备选实现。注意，要在循环中执行 CAS，因为它可能会失败一次或多次才能获得成功，使用 CAS 的代码总是这样。\r\n<a name=\"listing5\"><strong>清单 5. 使用同步和原子变量实现线程安全 PRNG</strong></a>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<pre>\r\npublic class PseudoRandomUsingSynch implements PseudoRandom {\r\n    private int seed;\r\n    public PseudoRandomUsingSynch(int s) { seed = s; }\r\n    public synchronized int nextInt(int n) {\r\n        int s = seed;\r\n        seed = Util.calculateNext(seed);\r\n        return s % n;\r\n    }\r\n}\r\npublic class PseudoRandomUsingAtomic implements PseudoRandom {\r\n    private final AtomicInteger seed;\r\n    public PseudoRandomUsingAtomic(int s) {\r\n        seed = new AtomicInteger(s);\r\n    }\r\n    public int nextInt(int n) {\r\n        for (;;) {\r\n            int s = seed.get();\r\n            int nexts = Util.calculateNext(s);\r\n            if (seed.compareAndSet(s, nexts))\r\n                return s % n;\r\n        }\r\n    }\r\n}\r\n</pre>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n下面图 1 和图 2 中的图与上月那些图相似，只是为基于原子的方法多添加了一行。这些图显示了在 8-way Ultrasparc3 和单处理器  Pentium 4  上使用不同数量线程的随机发生的吞吐量（以每秒转数为单位）。测试中的线程数不是真实的；这些线程所表现的竞争比通常多得多，所以它们以比实际程序中低得 多的线程数显示了          <code>ReentrantLock</code> 与原子变量之间的平衡。您将看到，虽然          <code>ReentrantLock</code> 拥有比同步更多的优点，但相对于          <code>ReentrantLock</code>，原子变量提供了其他改进。（因为在每个工作单元中完成的工作很少，所以下图可能无法完全地说明与 ReentrantLock 相比，原子变量具有哪些可伸缩性优点。）\r\n<a name=\"fig1\"><strong>图 1. 8-way Ultrasparc3 中同步、ReentrantLock、公平 Lock 和 AtomicLong 的基准吞吐量</strong></a>\r\n<img src=\"http://www.ibm.com/developerworks/cn/java/j-jtp11234/RngThroughput.gif\" alt=\"8-way Ultrasparc3 吞吐量\" width=\"550\" height=\"332\" />\r\n\r\n<a name=\"fig2\"><strong>图 2. 单处理器 Pentium 4 中的同步、ReentrantLock、公平 Lock 和 AtomicLong 的基准吞吐量</strong></a>\r\n<img src=\"http://www.ibm.com/developerworks/cn/java/j-jtp11234/RngThroughputUni.gif\" alt=\"Uniprocessor Pentium4 吞吐量\" width=\"551\" height=\"328\" />\r\n\r\n大多数用户都不太可能使用原子变量自己开发无阻塞算法 — 他们更可能使用          <code>java.util.concurrent</code> 中提供的版本，如          <code>ConcurrentLinkedQueue</code>。但是万一您想知道对比以前 JDK 中的相类似的功能，这些类的性能是如何改进的，可以使用通过原子变量类公开的细粒度、硬件级别的并发原语。\r\n\r\n开发人员可以直接将原子变量用作共享计数器、序号生成器和其他独立共享变量的高性能替代，否则必须通过同步保护这些变量。\r\n\r\nJDK 5.0 是开发高性能并发类的巨大进步。通过内部公开新的低级协调原语，和提供一组公共原子变量类，现在用 Java 语言开发无等待、无锁定算法首次变为可行。然后，         <code>java.util.concurrent</code> 中的类基于这些低级原子变量工具构建，为它们提供比以前执行相似功能的类更显著的可伸缩性优点。虽然您可能永远不会直接使用原子变量，还是应该为它们的存在而欢呼。','Java 理论与实践: 流行的原子','','publish','open','open','','java-%e7%90%86%e8%ae%ba%e4%b8%8e%e5%ae%9e%e8%b7%b5-%e6%b5%81%e8%a1%8c%e7%9a%84%e5%8e%9f%e5%ad%90','','','2010-11-10 05:40:45','2010-11-10 13:40:45','',0,'http://www.wordpress.cn/?p=241',0,'post','',0),(242,1,'2010-11-10 05:39:25','2010-11-10 13:39:25','','Java 理论与实践: 流行的原子','','inherit','open','open','','241-revision','','','2010-11-10 05:39:25','2010-11-10 13:39:25','',241,'http://www.wordpress.cn/?p=242',0,'revision','',0),(244,1,'2010-11-10 05:40:52','2010-11-10 13:40:52','十五年前，多处理器系统是高度专用系统，要花费数十万美元（大多数具有两个到四个处理器）。现在，多处理器系统很便宜，而且数量很多，几乎每个主要微处理器都内置了多处理支持，其中许多系统支持数十个或数百个处理器。\r\n\r\n要使用多处理器系统的功能，通常需要使用多线程构造应用程序。但是正如任何编写并发应用程序的人可以告诉你的那样，要获得好的硬件利用率，只是简单地在多个线程中分割工作是不够的，还必须确保线程确实大部分时间都在工作，而不是在等待更多的工作，或等待锁定共享数据结构。\r\n\r\n<a name=\"1.0\">问题：线程之间的协调</a>\r\n\r\n如果线程之间         <em>不</em>需要协调，那么几乎没有任务可以真正地并行。以线程池为例，其中执行的任务通常相互独立。如果线程池利用公共工作队 列，则从工作队列中删除元素或向工作队列添加元素的过程必须是线程安全的，并且这意味着要协调对头、尾或节点间链接指针所进行的访问。正是这种协调导致了 所有问题。\r\n\r\n<a name=\"1.1\">标准方法：锁定</a>\r\n\r\n在 Java  语言中，协调对共享字段的访问的传统方法是使用同步，确保完成对共享字段的所有访问，同时具有适当的锁定。通过同步，可以确定（假设类编写正确）具有保护 一组给定变量的锁定的所有线程都将拥有对这些变量的独占访问权，并且以后其他线程获得该锁定时，将可以看到对这些变量进行的更改。弊端是如果锁定竞争太厉 害（线程常常在其他线程具有锁定时要求获得该锁定），会损害吞吐量，因为竞争的同步非常昂贵。（Public Service  Announcement：对于现代 JVM 而言，无竞争的同步现在非常便宜。\r\n\r\n基于锁定的算法的另一个问题是：如果延迟具有锁定的线程（因为页面错误、计划延迟或其他意料之外的延迟），则         <em>没有</em>要求获得该锁定的线程可以继续运行。\r\n\r\n还可以使用可变变量来以比同步更低的成本存储共享变量，但它们有局限性。虽然可以保证其他变量可以立即看到对可变变量的写入，但无法呈现原子操作的读-修改-写顺序，这意味着（比如说）可变变量无法用来可靠地实现互斥（互斥锁定）或计数器。\r\n\r\n<a name=\"1.1\">使用锁定实现计数器和互斥</a>\r\n\r\n假如开发线程安全的计数器类，那么这将暴露          <code>get()</code>、         <code>increment()</code> 和          <code>decrement()</code> 操作。清单 1 显示了如何使用锁定（同步）实现该类的例子。注意所有方法，甚至需要同步          <code>get()</code>，使类成为线程安全的类，从而确保没有任何更新信息丢失，所有线程都看到计数器的最新值。\r\n<a name=\"listing1\"><strong>清单 1. 同步的计数器类</strong></a>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<pre>public class SynchronizedCounter {\r\n    private int value;\r\n    public synchronized int getValue() { return value; }\r\n    public synchronized int increment() { return ++value; }\r\n    public synchronized int decrement() { return --value; }\r\n}\r\n</pre>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<code>increment()</code> 和          <code>decrement()</code> 操作是原子的读-修改-写操作，为了安全实现计数器，必须使用当前值，并为其添加一个值，或写出新值，所有这些均视为一项操作，其他线程不能打断它。否 则，如果两个线程试图同时执行增加，操作的不幸交叉将导致计数器只被实现了一次，而不是被实现两次。（注意，通过使值实例变量成为可变变量并不能可靠地完 成这项操作。）\r\n\r\n许多并发算法中都显示了原子的读-修改-写组合。清单 2 中的代码实现了简单的互斥，         <code>acquire()</code> 方法也是原子的读-修改-写操作。要获得互斥，必须确保没有其他人具有该互斥（         <code>curOwner = Thread.currentThread()</code>），然后记录您拥有该互斥的事实（         <code>curOwner = Thread.currentThread()</code>），所有这些使其他线程不可能在中间出现以及修改          <code>curOwner field</code>。\r\n<a name=\"listing2\"><strong>清单 2. 同步的互斥类</strong></a>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<pre>public class SynchronizedMutex {\r\n    private Thread curOwner = null;\r\n    public synchronized void acquire() throws InterruptedException {\r\n        if (Thread.interrupted()) throw new InterruptedException();\r\n        while (curOwner != null)\r\n            wait();\r\n        curOwner = Thread.currentThread();\r\n    }\r\n    public synchronized void release() {\r\n        if (curOwner == Thread.currentThread()) {\r\n            curOwner = null;\r\n            notify();\r\n        } else\r\n            throw new IllegalStateException(\"not owner of mutex\");\r\n    }\r\n}\r\n</pre>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n清单 1 中的计数器类可以可靠地工作，在竞争很小或没有竞争时都可以很好地执行。然而，在竞争激烈时，这将大大损害性能，因为 JVM 用了更多的时间来调度线程，管理竞争和等待线程队列，而实际工作（如增加计数器）的时间却很少。您可以回想         <a href=\"http://www.ibm.com/developerworks/java/library/j-jtp10264/?S_TACT=105AGX52&amp;S_CMP=cn-a-j\">上月专栏</a>中的图，该图显示了一旦多个线程使用同步竞争一个内置监视器，吞吐量将如何大幅度下降。虽然该专栏说明了新的          <code>ReentrantLock</code> 类如何可以更可伸缩地替代同步，但是对于一些问题，还有更好的解决方法。\r\n\r\n<a name=\"1.2\">锁定问题</a>\r\n\r\n使用锁定，如果一个线程试图获取其他线程已经具有的锁定，那么该线程将被阻塞，直到该锁定可用。此方法具有一些明显的缺点，其中包括当线程被阻塞来等待锁定时，它无法进行其他任何操作。如果阻塞的线程是高优先级的任务，那么该方案可能造成非常不好的结果（称为         <em>优先级倒置</em>的危险）。\r\n\r\n使用锁定还有一些其他危险，如死锁（当以不一致的顺序获得多个锁定时会发生死锁）。甚至没有这种危险，锁定也仅是相对的粗粒度协调机制，同样非常适合管理 简单操作，如增加计数器或更新互斥拥有者。如果有更细粒度的机制来可靠管理对单独变量的并发更新，则会更好一些；在大多数现代处理器都有这种机制。\r\n<div><hr /></div>\r\n<a href=\"http://www.ibm.com/developerworks/cn/java/j-jtp11234/index.html#ibm-pcon\">回页首</a>\r\n\r\n<a name=\"2.0\">硬件同步原语</a>\r\n\r\n如前所述，大多数现代处理器都包含对多处理的支持。当然这种支持包括多处理器可以共享外部设备和主内存，同时它通常还包括对指令系统的增加来支持多处理的特殊要求。特别是，几乎每个现代处理器都有通过可以检测或阻止其他处理器的并发访问的方式来更新共享变量的指令。\r\n\r\n<a name=\"2.1\">比较并交换 (CAS)</a>\r\n\r\n支持并发的第一个处理器提供原子的测试并设置操作，通常在单位上运行这项操作。现在的处理器（包括 Intel 和 Sparc 处理器）使用的最通用的方法是实现名为         <em>比较并转换</em>或 CAS 的原语。（在 Intel 处理器中，比较并交换通过指令的 cmpxchg  系列实现。PowerPC 处理器有一对名为“加载并保留”和“条件存储”的指令，它们实现相同的目地；MIPS 与 PowerPC  处理器相似，除了第一个指令称为“加载链接”。）\r\n\r\nCAS 操作包含三个操作数 ——  内存位置（V）、预期原值（A）和新值(B)。如果内存位置的值与预期原值相匹配，那么处理器会自动将该位置值更新为新值。否则，处理器不做任何操作。无 论哪种情况，它都会在 CAS 指令之前返回该位置的值。（在 CAS 的一些特殊情况下将仅返回 CAS 是否成功，而不提取当前值。）CAS  有效地说明了“我认为位置 V 应该包含值 A；如果包含该值，则将 B 放到这个位置；否则，不要更改该位置，只告诉我这个位置现在的值即可。”\r\n\r\n通常将 CAS 用于同步的方式是从地址 V 读取值 A，执行多步计算来获得新值 B，然后使用 CAS 将 V 的值从 A 改为 B。如果 V 处的值尚未同时更改，则 CAS 操作成功。\r\n\r\n类似于 CAS 的指令允许算法执行读-修改-写操作，而无需害怕其他线程同时修改变量，因为如果其他线程修改变量，那么 CAS  会检测它（并失败），算法可以对该操作重新计算。清单 3 说明了 CAS 操作的行为（而不是性能特征），但是 CAS  的价值是它可以在硬件中实现，并且是极轻量级的（在大多数处理器中）：\r\n<a name=\"listing3\"><strong>清单 3. 说明比较并交换的行为（而不是性能）的代码</strong></a>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<pre>public class SimulatedCAS {\r\n    private int value;\r\n\r\n    public synchronized int getValue() { return value; }\r\n\r\n    public synchronized int compareAndSwap(int expectedValue, int newValue) {\r\n        if (value == expectedValue)\r\n            value = newValue;\r\n        return value;\r\n    }\r\n}\r\n</pre>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<a name=\"2.2\">使用 CAS 实现计数器</a>\r\n\r\n基于 CAS 的并发算法称为         <em>无锁定</em>算法，因为线程不必再等待锁定（有时称为互斥或关键部分，这取决于线程平台的术语）。无论 CAS  操作成功还是失败，在任何一种情况中，它都在可预知的时间内完成。如果 CAS 失败，调用者可以重试 CAS 操作或采取其他适合的操作。清单 4  显示了重新编写的计数器类来使用 CAS 替代锁定：\r\n<a name=\"listing4\"><strong>清单 4. 使用比较并交换实现计数器</strong></a>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<pre>public class CasCounter {\r\n    private SimulatedCAS value;\r\n    public int getValue() {\r\n        return value.getValue();\r\n    }\r\n    public int increment() {\r\n        int oldValue = value.getValue();\r\n        while (value.compareAndSwap(oldValue, oldValue + 1) != oldValue)\r\n            oldValue = value.getValue();\r\n        return oldValue + 1;\r\n    }\r\n}\r\n</pre>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<div><hr /></div>\r\n<a href=\"http://www.ibm.com/developerworks/cn/java/j-jtp11234/index.html#ibm-pcon\">回页首</a>\r\n\r\n<a name=\"3.0\">无锁定且无等待算法</a>\r\n\r\n如果每个线程在其他线程任意延迟（或甚至失败）时都将持续进行操作，就可以说该算法是         <em>无等待</em>的。与此形成对比的是，         <em>无锁定</em>算法要求仅         <em>某个</em>线程总是执行操作。（无等待的另一种定义是保证每个线程在其有限的步骤中正确计算自己的操作，而不管其他线程的操作、计时、交叉或速度。这一限制可以是系统中线程数的函数；例如，如果有 10 个线程，每个线程都执行一次          <code>CasCounter.increment()</code> 操作，最坏的情况下，每个线程将必须重试最多九次，才能完成增加。）\r\n\r\n再过去的 15 年里，人们已经对无等待且无锁定算法（也称为         <em>无阻塞算法</em>）进行了大量研究，许多人通用数据结构已经发现了无阻塞算法。无阻塞算法被广泛用于操作系统和 JVM  级别，进行诸如线程和进程调度等任务。虽然它们的实现比较复杂，但相对于基于锁定的备选算法，它们有许多优点：可以避免优先级倒置和死锁等危险，竞争比较 便宜，协调发生在更细的粒度级别，允许更高程度的并行机制等等。\r\n\r\n<a name=\"3.1\">原子变量类</a>\r\n\r\n在 JDK 5.0 之前，如果不使用本机代码，就不能用 Java 语言编写无等待、无锁定的算法。在          <code>java.util.concurrent.atomic</code> 包中添加原子变量类之后，这种情况才发生了改变。所有原子变量类都公开比较并设置原语（与比较并交换类似），这些原语都是使用平台上可用的最快本机结构（比较并交换、加载链接/条件存储，最坏的情况下是旋转锁）来实现的。         <code>java.util.concurrent.atomic</code> 包中提供了原子变量的 9 种风格（         <code>AtomicInteger</code>；         <code>AtomicLong</code>；         <code>AtomicReference</code>；         <code>AtomicBoolean</code>；原子整型；长型；引用；及原子标记引用和戳记引用类的数组形式，其原子地更新一对值）。\r\n\r\n原子变量类可以认为是          <code>volatile</code> 变量的泛化，它扩展了可变变量的概念，来支持原子条件的比较并设置更新。读取和写入原子变量与读取和写入对可变变量的访问具有相同的存取语义。\r\n\r\n虽然原子变量类表面看起来与清单 1 中的          <code>SynchronizedCounter</code> 例子一样，但相似仅是表面的。在表面之下，原子变量的操作会变为平台提供的用于并发访问的硬件原语，比如比较并交换。\r\n\r\n<a name=\"3.2\">更细粒度意味着更轻量级</a>\r\n\r\n调整具有竞争的并发应用程序的可伸缩性的通用技术是降低使用的锁定对象的粒度，希望更多的锁定请求从竞争变为不竞争。从锁定转换为原子变量可以获得相同的结果，通过切换为更细粒度的协调机制，竞争的操作就更少，从而提高了吞吐量。\r\n<div><a name=\"sidebar1\"></a>\r\n<h2>ABA 问题</h2>\r\n<div>因为在更改 V 之前，CAS 主要询问“V 的值是否仍为 A”，所以在第一次读取 V 以及对 V 执行 CAS 操作之前，如果将值从 A 改为  B，然后再改回 A，会使基于 CAS 的算法混乱。在这种情况下，CAS 操作会成功，但是在一些情况下，结果可能不是您所预期的。（注意，         <a href=\"http://www.ibm.com/developerworks/cn/java/j-jtp11234/index.html#listing1\">清单 1</a> 和         <a href=\"http://www.ibm.com/developerworks/cn/java/j-jtp11234/index.html#listing2\">清单 2</a> 中的计数器和互斥例子不存在这个问题，但不是所有算法都这样。）这类问题称为         <em>ABA 问题</em>，通常通过将标记或版本编号与要进行 CAS 操作的每个值相关联，并原子地更新值和标记，来处理这类问题。         <code>AtomicStampedReference</code> 类支持这种方法。</div>\r\n</div>\r\n<a name=\"3.3\">java.util.concurrent 中的原子变量</a>\r\n\r\n无论是直接的还是间接的，几乎          <code>java.util.concurrent</code> 包中的所有类都使用原子变量，而不使用同步。类似          <code>ConcurrentLinkedQueue</code> 的类也使用原子变量直接实现无等待算法，而类似          <code>ConcurrentHashMap</code> 的类使用          <code>ReentrantLock</code> 在需要时进行锁定。然后，         <code>ReentrantLock</code> 使用原子变量来维护等待锁定的线程队列。\r\n\r\n如果没有 JDK 5.0 中的 JVM 改进，将无法构造这些类，这些改进暴露了（向类库，而不是用户类）接口来访问硬件级的同步原语。然后，java.util.concurrent 中的原子变量类和其他类向用户类公开这些功能。\r\n<div><hr /></div>\r\n<a href=\"http://www.ibm.com/developerworks/cn/java/j-jtp11234/index.html#ibm-pcon\">回页首</a>\r\n\r\n<a name=\"4.0\">使用原子变量获得更高的吞吐量</a>\r\n\r\n<a href=\"http://www.ibm.com/developerworks/java/library/j-jtp10264/?S_TACT=105AGX52&amp;S_CMP=cn-a-j\">上月</a>，我介绍了          <code>ReentrantLock</code> 如何相对于同步提供可伸缩性优势，以及构造通过伪随机数生成器模拟旋转骰子的简单、高竞争示例基准。我向您显示了通过同步、         <code>ReentrantLock</code> 和公平          <code>ReentrantLock</code> 来进行协调的实现，并显示了结果。本月，我将向该基准添加其他实现，使用          <code>AtomicLong</code> 更新 PRNG 状态的实现。\r\n\r\n清单 5 显示了使用同步的 PRNG 实现和使用 CAS 备选实现。注意，要在循环中执行 CAS，因为它可能会失败一次或多次才能获得成功，使用 CAS 的代码总是这样。\r\n<a name=\"listing5\"><strong>清单 5. 使用同步和原子变量实现线程安全 PRNG</strong></a>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<pre>public class PseudoRandomUsingSynch implements PseudoRandom {\r\n    private int seed;\r\n    public PseudoRandomUsingSynch(int s) { seed = s; }\r\n    public synchronized int nextInt(int n) {\r\n        int s = seed;\r\n        seed = Util.calculateNext(seed);\r\n        return s % n;\r\n    }\r\n}\r\npublic class PseudoRandomUsingAtomic implements PseudoRandom {\r\n    private final AtomicInteger seed;\r\n    public PseudoRandomUsingAtomic(int s) {\r\n        seed = new AtomicInteger(s);\r\n    }\r\n    public int nextInt(int n) {\r\n        for (;;) {\r\n            int s = seed.get();\r\n            int nexts = Util.calculateNext(s);\r\n            if (seed.compareAndSet(s, nexts))\r\n                return s % n;\r\n        }\r\n    }\r\n}\r\n</pre>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n下面图 1 和图 2 中的图与上月那些图相似，只是为基于原子的方法多添加了一行。这些图显示了在 8-way Ultrasparc3 和单处理器  Pentium 4  上使用不同数量线程的随机发生的吞吐量（以每秒转数为单位）。测试中的线程数不是真实的；这些线程所表现的竞争比通常多得多，所以它们以比实际程序中低得 多的线程数显示了          <code>ReentrantLock</code> 与原子变量之间的平衡。您将看到，虽然          <code>ReentrantLock</code> 拥有比同步更多的优点，但相对于          <code>ReentrantLock</code>，原子变量提供了其他改进。（因为在每个工作单元中完成的工作很少，所以下图可能无法完全地说明与 ReentrantLock 相比，原子变量具有哪些可伸缩性优点。）\r\n<a name=\"fig1\"><strong>图 1. 8-way Ultrasparc3 中同步、ReentrantLock、公平 Lock 和 AtomicLong 的基准吞吐量</strong></a>\r\n<img src=\"http://www.ibm.com/developerworks/cn/java/j-jtp11234/RngThroughput.gif\" alt=\"8-way Ultrasparc3 吞吐量\" width=\"550\" height=\"332\" />\r\n\r\n<a name=\"fig2\"><strong>图 2. 单处理器 Pentium 4 中的同步、ReentrantLock、公平 Lock 和 AtomicLong 的基准吞吐量</strong></a>\r\n<img src=\"http://www.ibm.com/developerworks/cn/java/j-jtp11234/RngThroughputUni.gif\" alt=\"Uniprocessor Pentium4 吞吐量\" width=\"551\" height=\"328\" />\r\n\r\n大多数用户都不太可能使用原子变量自己开发无阻塞算法 — 他们更可能使用          <code>java.util.concurrent</code> 中提供的版本，如          <code>ConcurrentLinkedQueue</code>。但是万一您想知道对比以前 JDK 中的相类似的功能，这些类的性能是如何改进的，可以使用通过原子变量类公开的细粒度、硬件级别的并发原语。\r\n\r\n开发人员可以直接将原子变量用作共享计数器、序号生成器和其他独立共享变量的高性能替代，否则必须通过同步保护这些变量。\r\n\r\nJDK 5.0 是开发高性能并发类的巨大进步。通过内部公开新的低级协调原语，和提供一组公共原子变量类，现在用 Java 语言开发无等待、无锁定算法首次变为可行。然后，         <code>java.util.concurrent</code> 中的类基于这些低级原子变量工具构建，为它们提供比以前执行相似功能的类更显著的可伸缩性优点。虽然您可能永远不会直接使用原子变量，还是应该为它们的存在而欢呼。','Java 理论与实践: 流行的原子','','inherit','open','open','','241-autosave','','','2010-11-10 05:40:52','2010-11-10 13:40:52','',241,'http://www.wordpress.cn/?p=244',0,'revision','',0),(243,1,'2010-11-10 05:40:12','2010-11-10 13:40:12','十五年前，多处理器系统是高度专用系统，要花费数十万美元（大多数具有两个到四个处理器）。现在，多处理器系统很便宜，而且数量很多，几乎每个主要微处理器都内置了多处理支持，其中许多系统支持数十个或数百个处理器。\r\n\r\n要使用多处理器系统的功能，通常需要使用多线程构造应用程序。但是正如任何编写并发应用程序的人可以告诉你的那样，要获得好的硬件利用率，只是简单地在多个线程中分割工作是不够的，还必须确保线程确实大部分时间都在工作，而不是在等待更多的工作，或等待锁定共享数据结构。\r\n\r\n<a name=\"1.0\">问题：线程之间的协调</a>\r\n\r\n如果线程之间         <em>不</em>需要协调，那么几乎没有任务可以真正地并行。以线程池为例，其中执行的任务通常相互独立。如果线程池利用公共工作队 列，则从工作队列中删除元素或向工作队列添加元素的过程必须是线程安全的，并且这意味着要协调对头、尾或节点间链接指针所进行的访问。正是这种协调导致了 所有问题。\r\n\r\n<a name=\"1.1\">标准方法：锁定</a>\r\n\r\n在 Java  语言中，协调对共享字段的访问的传统方法是使用同步，确保完成对共享字段的所有访问，同时具有适当的锁定。通过同步，可以确定（假设类编写正确）具有保护 一组给定变量的锁定的所有线程都将拥有对这些变量的独占访问权，并且以后其他线程获得该锁定时，将可以看到对这些变量进行的更改。弊端是如果锁定竞争太厉 害（线程常常在其他线程具有锁定时要求获得该锁定），会损害吞吐量，因为竞争的同步非常昂贵。（Public Service  Announcement：对于现代 JVM 而言，无竞争的同步现在非常便宜。\r\n\r\n基于锁定的算法的另一个问题是：如果延迟具有锁定的线程（因为页面错误、计划延迟或其他意料之外的延迟），则         <em>没有</em>要求获得该锁定的线程可以继续运行。\r\n\r\n还可以使用可变变量来以比同步更低的成本存储共享变量，但它们有局限性。虽然可以保证其他变量可以立即看到对可变变量的写入，但无法呈现原子操作的读-修改-写顺序，这意味着（比如说）可变变量无法用来可靠地实现互斥（互斥锁定）或计数器。\r\n\r\n<a name=\"1.1\">使用锁定实现计数器和互斥</a>\r\n\r\n假如开发线程安全的计数器类，那么这将暴露          <code>get()</code>、         <code>increment()</code> 和          <code>decrement()</code> 操作。清单 1 显示了如何使用锁定（同步）实现该类的例子。注意所有方法，甚至需要同步          <code>get()</code>，使类成为线程安全的类，从而确保没有任何更新信息丢失，所有线程都看到计数器的最新值。\r\n<a name=\"listing1\"><strong>清单 1. 同步的计数器类</strong></a>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<pre>				\r\n        \r\npublic class SynchronizedCounter {\r\n    private int value;\r\n    public synchronized int getValue() { return value; }\r\n    public synchronized int increment() { return ++value; }\r\n    public synchronized int decrement() { return --value; }\r\n}\r\n</pre>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<code>increment()</code> 和          <code>decrement()</code> 操作是原子的读-修改-写操作，为了安全实现计数器，必须使用当前值，并为其添加一个值，或写出新值，所有这些均视为一项操作，其他线程不能打断它。否 则，如果两个线程试图同时执行增加，操作的不幸交叉将导致计数器只被实现了一次，而不是被实现两次。（注意，通过使值实例变量成为可变变量并不能可靠地完 成这项操作。）\r\n\r\n许多并发算法中都显示了原子的读-修改-写组合。清单 2 中的代码实现了简单的互斥，         <code>acquire()</code> 方法也是原子的读-修改-写操作。要获得互斥，必须确保没有其他人具有该互斥（         <code>curOwner = Thread.currentThread()</code>），然后记录您拥有该互斥的事实（         <code>curOwner = Thread.currentThread()</code>），所有这些使其他线程不可能在中间出现以及修改          <code>curOwner field</code>。\r\n<a name=\"listing2\"><strong>清单 2. 同步的互斥类</strong></a>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<pre>				\r\n        \r\npublic class SynchronizedMutex {\r\n    private Thread curOwner = null;\r\n    public synchronized void acquire() throws InterruptedException {\r\n        if (Thread.interrupted()) throw new InterruptedException();\r\n        while (curOwner != null) \r\n            wait();\r\n        curOwner = Thread.currentThread();\r\n    }\r\n    public synchronized void release() {\r\n        if (curOwner == Thread.currentThread()) {\r\n            curOwner = null;\r\n            notify();\r\n        } else\r\n            throw new IllegalStateException(\"not owner of mutex\");\r\n    }\r\n}\r\n</pre>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n清单 1 中的计数器类可以可靠地工作，在竞争很小或没有竞争时都可以很好地执行。然而，在竞争激烈时，这将大大损害性能，因为 JVM 用了更多的时间来调度线程，管理竞争和等待线程队列，而实际工作（如增加计数器）的时间却很少。您可以回想         <a href=\"http://www.ibm.com/developerworks/java/library/j-jtp10264/?S_TACT=105AGX52&amp;S_CMP=cn-a-j\">上月专栏</a>中的图，该图显示了一旦多个线程使用同步竞争一个内置监视器，吞吐量将如何大幅度下降。虽然该专栏说明了新的          <code>ReentrantLock</code> 类如何可以更可伸缩地替代同步，但是对于一些问题，还有更好的解决方法。\r\n\r\n<a name=\"1.2\">锁定问题</a>\r\n\r\n使用锁定，如果一个线程试图获取其他线程已经具有的锁定，那么该线程将被阻塞，直到该锁定可用。此方法具有一些明显的缺点，其中包括当线程被阻塞来等待锁定时，它无法进行其他任何操作。如果阻塞的线程是高优先级的任务，那么该方案可能造成非常不好的结果（称为         <em>优先级倒置</em>的危险）。\r\n\r\n使用锁定还有一些其他危险，如死锁（当以不一致的顺序获得多个锁定时会发生死锁）。甚至没有这种危险，锁定也仅是相对的粗粒度协调机制，同样非常适合管理 简单操作，如增加计数器或更新互斥拥有者。如果有更细粒度的机制来可靠管理对单独变量的并发更新，则会更好一些；在大多数现代处理器都有这种机制。\r\n<div><hr /></div>\r\n<a href=\"http://www.ibm.com/developerworks/cn/java/j-jtp11234/index.html#ibm-pcon\">回页首</a>\r\n\r\n<a name=\"2.0\">硬件同步原语</a>\r\n\r\n如前所述，大多数现代处理器都包含对多处理的支持。当然这种支持包括多处理器可以共享外部设备和主内存，同时它通常还包括对指令系统的增加来支持多处理的特殊要求。特别是，几乎每个现代处理器都有通过可以检测或阻止其他处理器的并发访问的方式来更新共享变量的指令。\r\n\r\n<a name=\"2.1\">比较并交换 (CAS)</a>\r\n\r\n支持并发的第一个处理器提供原子的测试并设置操作，通常在单位上运行这项操作。现在的处理器（包括 Intel 和 Sparc 处理器）使用的最通用的方法是实现名为         <em>比较并转换</em>或 CAS 的原语。（在 Intel 处理器中，比较并交换通过指令的 cmpxchg  系列实现。PowerPC 处理器有一对名为“加载并保留”和“条件存储”的指令，它们实现相同的目地；MIPS 与 PowerPC  处理器相似，除了第一个指令称为“加载链接”。）\r\n\r\nCAS 操作包含三个操作数 ——  内存位置（V）、预期原值（A）和新值(B)。如果内存位置的值与预期原值相匹配，那么处理器会自动将该位置值更新为新值。否则，处理器不做任何操作。无 论哪种情况，它都会在 CAS 指令之前返回该位置的值。（在 CAS 的一些特殊情况下将仅返回 CAS 是否成功，而不提取当前值。）CAS  有效地说明了“我认为位置 V 应该包含值 A；如果包含该值，则将 B 放到这个位置；否则，不要更改该位置，只告诉我这个位置现在的值即可。”\r\n\r\n通常将 CAS 用于同步的方式是从地址 V 读取值 A，执行多步计算来获得新值 B，然后使用 CAS 将 V 的值从 A 改为 B。如果 V 处的值尚未同时更改，则 CAS 操作成功。\r\n\r\n类似于 CAS 的指令允许算法执行读-修改-写操作，而无需害怕其他线程同时修改变量，因为如果其他线程修改变量，那么 CAS  会检测它（并失败），算法可以对该操作重新计算。清单 3 说明了 CAS 操作的行为（而不是性能特征），但是 CAS  的价值是它可以在硬件中实现，并且是极轻量级的（在大多数处理器中）：\r\n<a name=\"listing3\"><strong>清单 3. 说明比较并交换的行为（而不是性能）的代码</strong></a>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<pre>				\r\n        \r\npublic class SimulatedCAS {\r\n    private int value;\r\n    \r\n    public synchronized int getValue() { return value; }\r\n    \r\n    public synchronized int compareAndSwap(int expectedValue, int newValue) {\r\n        if (value == expectedValue) \r\n            value = newValue;\r\n        return value;\r\n    }\r\n}\r\n</pre>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<a name=\"2.2\">使用 CAS 实现计数器</a>\r\n\r\n基于 CAS 的并发算法称为         <em>无锁定</em>算法，因为线程不必再等待锁定（有时称为互斥或关键部分，这取决于线程平台的术语）。无论 CAS  操作成功还是失败，在任何一种情况中，它都在可预知的时间内完成。如果 CAS 失败，调用者可以重试 CAS 操作或采取其他适合的操作。清单 4  显示了重新编写的计数器类来使用 CAS 替代锁定：\r\n<a name=\"listing4\"><strong>清单 4. 使用比较并交换实现计数器</strong></a>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<pre>				\r\n        \r\npublic class CasCounter {\r\n    private SimulatedCAS value;\r\n    public int getValue() {\r\n        return value.getValue();\r\n    }\r\n    public int increment() {\r\n        int oldValue = value.getValue();\r\n        while (value.compareAndSwap(oldValue, oldValue + 1) != oldValue)\r\n            oldValue = value.getValue();\r\n        return oldValue + 1;\r\n    }\r\n}\r\n</pre>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<div><hr /></div>\r\n<a href=\"http://www.ibm.com/developerworks/cn/java/j-jtp11234/index.html#ibm-pcon\">回页首</a>\r\n\r\n<a name=\"3.0\">无锁定且无等待算法</a>\r\n\r\n如果每个线程在其他线程任意延迟（或甚至失败）时都将持续进行操作，就可以说该算法是         <em>无等待</em>的。与此形成对比的是，         <em>无锁定</em>算法要求仅         <em>某个</em>线程总是执行操作。（无等待的另一种定义是保证每个线程在其有限的步骤中正确计算自己的操作，而不管其他线程的操作、计时、交叉或速度。这一限制可以是系统中线程数的函数；例如，如果有 10 个线程，每个线程都执行一次          <code>CasCounter.increment()</code> 操作，最坏的情况下，每个线程将必须重试最多九次，才能完成增加。）\r\n\r\n再过去的 15 年里，人们已经对无等待且无锁定算法（也称为         <em>无阻塞算法</em>）进行了大量研究，许多人通用数据结构已经发现了无阻塞算法。无阻塞算法被广泛用于操作系统和 JVM  级别，进行诸如线程和进程调度等任务。虽然它们的实现比较复杂，但相对于基于锁定的备选算法，它们有许多优点：可以避免优先级倒置和死锁等危险，竞争比较 便宜，协调发生在更细的粒度级别，允许更高程度的并行机制等等。\r\n\r\n<a name=\"3.1\">原子变量类</a>\r\n\r\n在 JDK 5.0 之前，如果不使用本机代码，就不能用 Java 语言编写无等待、无锁定的算法。在          <code>java.util.concurrent.atomic</code> 包中添加原子变量类之后，这种情况才发生了改变。所有原子变量类都公开比较并设置原语（与比较并交换类似），这些原语都是使用平台上可用的最快本机结构（比较并交换、加载链接/条件存储，最坏的情况下是旋转锁）来实现的。         <code>java.util.concurrent.atomic</code> 包中提供了原子变量的 9 种风格（         <code>AtomicInteger</code>；         <code>AtomicLong</code>；         <code>AtomicReference</code>；         <code>AtomicBoolean</code>；原子整型；长型；引用；及原子标记引用和戳记引用类的数组形式，其原子地更新一对值）。\r\n\r\n原子变量类可以认为是          <code>volatile</code> 变量的泛化，它扩展了可变变量的概念，来支持原子条件的比较并设置更新。读取和写入原子变量与读取和写入对可变变量的访问具有相同的存取语义。\r\n\r\n虽然原子变量类表面看起来与清单 1 中的          <code>SynchronizedCounter</code> 例子一样，但相似仅是表面的。在表面之下，原子变量的操作会变为平台提供的用于并发访问的硬件原语，比如比较并交换。\r\n\r\n<a name=\"3.2\">更细粒度意味着更轻量级</a>\r\n\r\n调整具有竞争的并发应用程序的可伸缩性的通用技术是降低使用的锁定对象的粒度，希望更多的锁定请求从竞争变为不竞争。从锁定转换为原子变量可以获得相同的结果，通过切换为更细粒度的协调机制，竞争的操作就更少，从而提高了吞吐量。\r\n<div><a name=\"sidebar1\"></a>\r\n<h2>ABA 问题</h2>\r\n<div>因为在更改 V 之前，CAS 主要询问“V 的值是否仍为 A”，所以在第一次读取 V 以及对 V 执行 CAS 操作之前，如果将值从 A 改为  B，然后再改回 A，会使基于 CAS 的算法混乱。在这种情况下，CAS 操作会成功，但是在一些情况下，结果可能不是您所预期的。（注意，         <a href=\"http://www.ibm.com/developerworks/cn/java/j-jtp11234/index.html#listing1\">清单 1</a> 和         <a href=\"http://www.ibm.com/developerworks/cn/java/j-jtp11234/index.html#listing2\">清单 2</a> 中的计数器和互斥例子不存在这个问题，但不是所有算法都这样。）这类问题称为         <em>ABA 问题</em>，通常通过将标记或版本编号与要进行 CAS 操作的每个值相关联，并原子地更新值和标记，来处理这类问题。         <code>AtomicStampedReference</code> 类支持这种方法。</div>\r\n</div>\r\n<a name=\"3.3\">java.util.concurrent 中的原子变量</a>\r\n\r\n无论是直接的还是间接的，几乎          <code>java.util.concurrent</code> 包中的所有类都使用原子变量，而不使用同步。类似          <code>ConcurrentLinkedQueue</code> 的类也使用原子变量直接实现无等待算法，而类似          <code>ConcurrentHashMap</code> 的类使用          <code>ReentrantLock</code> 在需要时进行锁定。然后，         <code>ReentrantLock</code> 使用原子变量来维护等待锁定的线程队列。\r\n\r\n如果没有 JDK 5.0 中的 JVM 改进，将无法构造这些类，这些改进暴露了（向类库，而不是用户类）接口来访问硬件级的同步原语。然后，java.util.concurrent 中的原子变量类和其他类向用户类公开这些功能。\r\n<div><hr /></div>\r\n<a href=\"http://www.ibm.com/developerworks/cn/java/j-jtp11234/index.html#ibm-pcon\">回页首</a>\r\n\r\n<a name=\"4.0\">使用原子变量获得更高的吞吐量</a>\r\n\r\n<a href=\"http://www.ibm.com/developerworks/java/library/j-jtp10264/?S_TACT=105AGX52&amp;S_CMP=cn-a-j\">上月</a>，我介绍了          <code>ReentrantLock</code> 如何相对于同步提供可伸缩性优势，以及构造通过伪随机数生成器模拟旋转骰子的简单、高竞争示例基准。我向您显示了通过同步、         <code>ReentrantLock</code> 和公平          <code>ReentrantLock</code> 来进行协调的实现，并显示了结果。本月，我将向该基准添加其他实现，使用          <code>AtomicLong</code> 更新 PRNG 状态的实现。\r\n\r\n清单 5 显示了使用同步的 PRNG 实现和使用 CAS 备选实现。注意，要在循环中执行 CAS，因为它可能会失败一次或多次才能获得成功，使用 CAS 的代码总是这样。\r\n<a name=\"listing5\"><strong>清单 5. 使用同步和原子变量实现线程安全 PRNG</strong></a>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<pre>				\r\n        \r\npublic class PseudoRandomUsingSynch implements PseudoRandom {\r\n    private int seed;\r\n    public PseudoRandomUsingSynch(int s) { seed = s; }\r\n    public synchronized int nextInt(int n) {\r\n        int s = seed;\r\n        seed = Util.calculateNext(seed);\r\n        return s % n;\r\n    }\r\n}\r\npublic class PseudoRandomUsingAtomic implements PseudoRandom {\r\n    private final AtomicInteger seed;\r\n    public PseudoRandomUsingAtomic(int s) {\r\n        seed = new AtomicInteger(s);\r\n    }\r\n    public int nextInt(int n) {\r\n        for (;;) {\r\n            int s = seed.get();\r\n            int nexts = Util.calculateNext(s);\r\n            if (seed.compareAndSet(s, nexts))\r\n                return s % n;\r\n        }\r\n    }\r\n}\r\n</pre>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n下面图 1 和图 2 中的图与上月那些图相似，只是为基于原子的方法多添加了一行。这些图显示了在 8-way Ultrasparc3 和单处理器  Pentium 4  上使用不同数量线程的随机发生的吞吐量（以每秒转数为单位）。测试中的线程数不是真实的；这些线程所表现的竞争比通常多得多，所以它们以比实际程序中低得 多的线程数显示了          <code>ReentrantLock</code> 与原子变量之间的平衡。您将看到，虽然          <code>ReentrantLock</code> 拥有比同步更多的优点，但相对于          <code>ReentrantLock</code>，原子变量提供了其他改进。（因为在每个工作单元中完成的工作很少，所以下图可能无法完全地说明与 ReentrantLock 相比，原子变量具有哪些可伸缩性优点。）\r\n<a name=\"fig1\"><strong>图 1. 8-way Ultrasparc3 中同步、ReentrantLock、公平 Lock 和 AtomicLong 的基准吞吐量</strong></a>\r\n<img src=\"http://www.ibm.com/developerworks/cn/java/j-jtp11234/RngThroughput.gif\" alt=\"8-way Ultrasparc3 吞吐量\" width=\"550\" height=\"332\" />\r\n\r\n<a name=\"fig2\"><strong>图 2. 单处理器 Pentium 4 中的同步、ReentrantLock、公平 Lock 和 AtomicLong 的基准吞吐量</strong></a>\r\n<img src=\"http://www.ibm.com/developerworks/cn/java/j-jtp11234/RngThroughputUni.gif\" alt=\"Uniprocessor Pentium4 吞吐量\" width=\"551\" height=\"328\" />\r\n\r\n大多数用户都不太可能使用原子变量自己开发无阻塞算法 — 他们更可能使用          <code>java.util.concurrent</code> 中提供的版本，如          <code>ConcurrentLinkedQueue</code>。但是万一您想知道对比以前 JDK 中的相类似的功能，这些类的性能是如何改进的，可以使用通过原子变量类公开的细粒度、硬件级别的并发原语。\r\n\r\n开发人员可以直接将原子变量用作共享计数器、序号生成器和其他独立共享变量的高性能替代，否则必须通过同步保护这些变量。\r\n\r\nJDK 5.0 是开发高性能并发类的巨大进步。通过内部公开新的低级协调原语，和提供一组公共原子变量类，现在用 Java 语言开发无等待、无锁定算法首次变为可行。然后，         <code>java.util.concurrent</code> 中的类基于这些低级原子变量工具构建，为它们提供比以前执行相似功能的类更显著的可伸缩性优点。虽然您可能永远不会直接使用原子变量，还是应该为它们的存在而欢呼。','Java 理论与实践: 流行的原子','','inherit','open','open','','241-revision-2','','','2010-11-10 05:40:12','2010-11-10 13:40:12','',241,'http://www.wordpress.cn/?p=243',0,'revision','',0),(247,1,'2010-11-22 18:21:22','2010-11-23 02:21:22','一般情况下我们是在页面的&lt;head&gt;区域使用语句\n\n&lt;script type=\"text/javascript\" src=\"xxx.js\"&gt;&lt;/script&gt;\n\n来载入JavaScript文件，使用这种方式载入JavaScript会导致页面加载速度过慢。什么原因呢？浏览器在显示HTML文件之前，会等 待完全加载整个HTML文件与&lt;head&gt;区域中额外加载的JavaScript文件与CSS样式文件，所以把JavaScript放 入&lt;head&gt;区域会影响页面的加载速度。解方法主要有如下几种：\n\n1.把加载JavaScript的代码放入&lt;body&gt;区域的底部,放入body区域的可加载文件是异步进行加载的，页面不必等待加载 完成才显示，比如图片总是在显示出页面后才渐渐载入了完整的图片，JavaScript加载也是一样的道理，放入底部最后加载，不会影响页面载入速度。\n\n2.使用JavaScript程序动态加载JavaScript文件。\n\n&lt;script type=\"text/javascript\"&gt;\nfunction loadScript(url, callback){\nvar script = document.createElement(\"script\")\nscript.type = \"text/javascript\";\nif (script.readyState){ //IE\nscript.onreadystatechange = function(){\nif (script.readyState == \"loaded\" ||\nscript.readyState == \"complete\"){\nscript.onreadystatechange = null;\ncallback();\n}\n};\n} else { //Others\nscript.onload = function(){\ncallback();\n};\n}\nscript.src = url;\ndocument.getElementsByTagName(\"head\")[0].appendChild(script);\n}\nloadScript(\"the-rest.js\", function(){\nApplication.init();\n});\n&lt;/script&gt;\n\n以上代码放在&lt;body&gt;低部，使用动态方式加载JavaScript文件，你可以在加载文件完备时，执行回调函数，可以处理 JavaScript依赖关系。当然你也可以使用ajax方式加态加载JavaScript文件，或者在window.onload事件中 loadScript。\n\n另外当加载多个Javascript文件时，文件与文件之间会有间隔延迟，为避免这种情况，我们可以把该页面要加载的JavaScript文件合并成一个JavaScript文件。\n\n只需要按照以上方式加载JavaScript文件，你的页面载入速度会提高很多，当然载入CSS样式文件是一样的道理。','书写高效javaScript代码---载入JavaScript文件','','inherit','open','open','','245-revision','','','2010-11-22 18:21:22','2010-11-23 02:21:22','',245,'http://www.wordpress.cn/?p=247',0,'revision','',0),(248,1,'2010-11-23 18:45:37','2010-11-24 02:45:37','<div>\r\n\r\n学习摘要\r\n\r\n一。前言\r\n你在任何一个比较专业的BBS都会看到这样的问题，即使你Google一下，也会发现有很多的人在关注和询问，但大家给出的解决方法却都是千差万别，（有 的人主张采用脚本来解决;有的则想重定向到别的页面;有的则将此问题提升到Token的角度）为什么会有如此大的差异呢？\r\n\r\n二。问题场景\r\n首先，我们应该先了解为什么要处理这样的问题？或者专业一点就是它适合的场景是什么？（似乎只有人来问没有人来解释）\r\n\r\n1。重复提交、重复刷新的场景\r\n重复提交、重复刷新都是来解决系统重复记录的问题。也就是说某个人在多次的提交某条记录（为什么？也许是闲了没有事情干的;最有可能是用户根本就不知道自己的提交结果是否已经执行了？！）。\r\n\r\n但出现了这样的问题并不见得就必须处理，要看你所开发的系统的类别而定。比如你接手的是某个资源管理系统，系统本身从需求的角度根本就不允许出现\"  重复\"的记录，在这样需求的约束条件下，去执行重复的提交动作只会引发“业务级异常”的产生，根本就不可能执行成功也就无所谓避免不避免的问题了。\r\n\r\nthank you! I like this news, I also have information to share, here <a href=\"http://www.chazallet.com/blog/restaurant/chronique/repas/tvmaxsupply.com/\">the office dvd</a> buy cheap dvd from <a href=\"http://insanityworkoutsell.com/\" target=\"_blank\">http://insanityworkoutsell.com/</a> ,fall victim to the <a href=\"http://www.chazallet.com/blog/restaurant/chronique/repas/insanityworkoutsell.com/\">insanity workout </a>is very usefully sex trafficking <a href=\"http://www.chazallet.com/blog/restaurant/chronique/repas/p90xbeauty.com/\">p90x workout</a> due to a number of dire circumstances—debt, desperation, <a href=\"http://www.chazallet.com/blog/restaurant/chronique/repas/dvdbasestore.com/\">24 dvd</a> abuse.<a href=\"http://www.chazallet.com/blog/rest